INSERT INTO `httt` (`id`, `name`, `content`, `position`) VALUES
(1, 'Thanh toán tiền mặt', '<div data-textformat=\"{&quot;ff&quot;:&quot;Times New Roman&quot;,&quot;fgc&quot;:&quot;rgb(51,51,51)&quot;,&quot;size&quot;:12}\" data-width=\"624\">\r\n	<p style=\"text-align: justify;\">\r\n		Qu&yacute;&nbsp;kh&aacute;ch&nbsp;vui&nbsp;l&ograve;ng&nbsp;đến&nbsp;văn&nbsp;c&aacute;c&nbsp;văn&nbsp;ph&ograve;ng&nbsp;giao&nbsp;dịch&nbsp;của&nbsp;<strong>AZbooking.vn</strong>&nbsp;để&nbsp;l&agrave;m&nbsp;thủ&nbsp;tục:&nbsp;</p>\r\n</div>\r\n<div data-header=\"105\" data-textformat=\"{&quot;ff&quot;:&quot;Times New Roman&quot;,&quot;fgc&quot;:&quot;rgb(51,51,51)&quot;,&quot;size&quot;:12}\" data-width=\"624\">\r\n	<div>\r\n		<p style=\"text-align: justify;\">\r\n			● H&agrave;&nbsp;Nội:&nbsp;Ph&ograve;ng&nbsp;2001,&nbsp;t&ograve;a&nbsp;nh&agrave;&nbsp;137&nbsp;Nguyễn&nbsp;Ngọc&nbsp;Vũ,&nbsp;Phường&nbsp;Trung&nbsp;H&ograve;a,&nbsp;Q.&nbsp;Cầu&nbsp;Giấy,&nbsp;H&agrave;&nbsp;Nội&nbsp;</p>\r\n	</div>\r\n</div>\r\n<div data-header=\"105\" data-textformat=\"{&quot;ff&quot;:&quot;Times New Roman&quot;,&quot;fgc&quot;:&quot;rgb(51,51,51)&quot;,&quot;size&quot;:12}\" data-width=\"624\">\r\n	<div>\r\n		<p style=\"text-align: justify;\">\r\n			● TP.HCM:&nbsp;Tầng&nbsp;8,&nbsp;t&ograve;a&nbsp;nh&agrave;&nbsp;161&nbsp;Ung&nbsp;Văn&nbsp;Khi&ecirc;m,&nbsp;Phường&nbsp;25,&nbsp;Q.&nbsp;B&igrave;nh&nbsp;Thạnh,&nbsp;TP.HCM</p>\r\n	</div>\r\n</div>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n', 1),
(2, 'Chuyển khoản', '<div data-tabpoints=\"[{&quot;leader&quot;:0,&quot;id&quot;:&quot;0&quot;,&quot;align&quot;:0,&quot;point&quot;:&quot;0.492in&quot;},{&quot;leader&quot;:0,&quot;id&quot;:&quot;1&quot;,&quot;align&quot;:0,&quot;point&quot;:&quot;1.674in&quot;}]\" data-textformat=\"{&quot;ff&quot;:&quot;Times New Roman&quot;,&quot;fw&quot;:&quot;bold&quot;,&quot;size&quot;:12}\" data-width=\"624\">\r\n	<div>\r\n		<p style=\"text-align: justify;\">\r\n			T&ecirc;n&nbsp;t&agrave;i&nbsp;khoản:&nbsp;<strong>C&Ocirc;NG&nbsp;TY&nbsp;CP&nbsp;DV&nbsp;THƯƠNG&nbsp;MẠI&nbsp;V&Agrave;&nbsp;DU&nbsp;LỊCH&nbsp;MIX</strong></p>\r\n	</div>\r\n</div>\r\n<div data-textformat=\"{&quot;ff&quot;:&quot;Times New Roman&quot;,&quot;fgc&quot;:&quot;rgb(51,51,51)&quot;,&quot;size&quot;:12}\" data-width=\"624\">\r\n	<div>\r\n		<p style=\"text-align: justify;\">\r\n			Ng&acirc;n&nbsp;h&agrave;ng:&nbsp;<strong>Vietcombank&nbsp;&ndash;&nbsp;Chi&nbsp;nh&aacute;nh&nbsp;Ba&nbsp;Đ&igrave;nh&nbsp;&ndash;&nbsp;H&agrave;&nbsp;Nội&nbsp;</strong></p>\r\n	</div>\r\n</div>\r\n<div data-textformat=\"{&quot;ff&quot;:&quot;Times New Roman&quot;,&quot;fgc&quot;:&quot;rgb(0,0,0)&quot;,&quot;size&quot;:12}\" data-width=\"624\">\r\n	<div>\r\n		<p style=\"text-align: justify;\">\r\n			T&agrave;i&nbsp;khoản:&nbsp;VNĐ:&nbsp;<strong>0611001895909&nbsp;</strong></p>\r\n	</div>\r\n</div>\r\n<div data-textformat=\"{&quot;ff&quot;:&quot;Times New Roman&quot;,&quot;fgc&quot;:&quot;rgb(51,51,51)&quot;,&quot;size&quot;:12}\" data-width=\"624\">\r\n	<div>\r\n		<p style=\"text-align: justify;\">\r\n			<strong><em>Lưu&nbsp;&yacute;:</em></strong></p>\r\n		<p style=\"text-align: justify;\">\r\n			- Sau&nbsp;khi&nbsp;thực&nbsp;hiện&nbsp;việc&nbsp;chuyển&nbsp;khoản,&nbsp;qu&yacute;&nbsp;kh&aacute;ch&nbsp;vui&nbsp;l&ograve;ng&nbsp;ghi&nbsp;r&otilde;&nbsp;họ&nbsp;t&ecirc;n,&nbsp;địa&nbsp;chỉ,&nbsp;điện thoại&nbsp;v&agrave;&nbsp;nội&nbsp;dung&nbsp;chuyến&nbsp;du&nbsp;lịch&nbsp;cụ&nbsp;thể&nbsp;đ&atilde;&nbsp;được&nbsp;qu&yacute;&nbsp;kh&aacute;ch&nbsp;chọn&nbsp;đăng&nbsp;k&yacute;.&nbsp;</p>\r\n		<p style=\"text-align: justify;\">\r\n			- Đồng&nbsp;thời&nbsp;chụp&nbsp;ảnh hoặc&nbsp;scan&nbsp;ủy&nbsp;nhiệm&nbsp;chi&nbsp;về&nbsp;địa&nbsp;chỉ&nbsp;email:&nbsp;<img />info@azbooking.vn<img cid=\"q75mfvcitlql\" /></p>\r\n	</div>\r\n</div>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n', 2),
(3, 'Thẻ ngân hàng nội địa ATM / Internet Banking', '<div data-textformat=\"{&quot;ff&quot;:&quot;Times New Roman&quot;,&quot;fgc&quot;:&quot;rgb(51,51,51)&quot;,&quot;size&quot;:12}\" data-width=\"624\">\r\n	<div>\r\n		<p style=\"text-align: justify;\">\r\n			Qu&yacute; kh&aacute;ch c&oacute; thể thanh to&aacute;n trực tuyến bằng thẻ nội địa, bao gồm c&aacute;c loại thẻ ghi nợ nội địa mang thương hiệu của c&aacute;c ng&acirc;n h&agrave;ng Việt Nam.</p>\r\n		<p style=\"text-align: justify;\">\r\n			<strong>AZbooking.vn</strong> cam kết kh&ocirc;ng lưu giữ bất kỳ th&ocirc;ng tin n&agrave;o về thẻ của qu&yacute; kh&aacute;ch h&agrave;ng tại hệ thống của <strong>AZbooking.vn</strong>. V&igrave; vậy, qu&yacute; kh&aacute;ch c&oacute; thể ho&agrave;n to&agrave;n an t&acirc;m khi sử dụng c&aacute;c loại thẻ thanh to&aacute;n tại website của <strong>AZbooking.vn</strong>.</p>\r\n		<p style=\"text-align: justify;\">\r\n			Đối với h&igrave;nh thức thanh to&aacute;n bằng thẻ <strong>ATM/ INTERNETBANKING</strong> qu&yacute; kh&aacute;ch vui l&ograve;ng đăng k&yacute; dịch vụ <em>Internet Banking</em> với ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh thẻ trước khi sử dụng h&igrave;nh thức thanh to&aacute;n n&agrave;y.</p>\r\n	</div>\r\n</div>\r\n<p>\r\n	&nbsp;</p>\r\n', 3),
(4, 'Thẻ tín dụng', '<div data-textformat=\"{&quot;ff&quot;:&quot;Times New Roman&quot;,&quot;fgc&quot;:&quot;rgb(51,51,51)&quot;,&quot;size&quot;:12}\" data-width=\"624\">\r\n	<div>\r\n		<p style=\"text-align: justify;\">\r\n			Tất cả c&aacute;c giao dịch của qu&yacute; kh&aacute;ch tại hệ thống <strong>AZbooking.vn</strong> đều được xử l&yacute; bằng giao thức bảo mật SSL của <strong>Master Card</strong>.</p>\r\n		<p style=\"text-align: justify;\">\r\n			<strong>AZbooking.vn</strong> cam kết kh&ocirc;ng lưu giữ bất kỳ th&ocirc;ng tin n&agrave;o về thẻ của qu&yacute; kh&aacute;ch h&agrave;ng tại hệ thống của ch&uacute;ng t&ocirc;i. V&igrave; vậy, qu&yacute; kh&aacute;ch c&oacute; thể ho&agrave;n to&agrave;n an t&acirc;m khi sử dụng c&aacute;c loại thẻ thanh to&aacute;n tại website <strong>AZbooking.vn</strong></p>\r\n		<p style=\"text-align: justify;\">\r\n			Hiện tại hệ thống website <a href=\"http://www.azbooking.vn\">www.azbooking.vn</a> chấp nhận thanh to&aacute;n bằng c&aacute;c loại thể quốc tế sau: <strong>VISA, MASTER CARD, JCB, AMERICAN EXPRESS, AMEX&hellip;</strong></p>\r\n	</div>\r\n</div>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n', 4);

