INSERT INTO `news` (`id`, `danhmuc_id`, `name`, `name_url`, `img`, `view`, `content`, `title`, `keyword`, `description`, `created`) VALUES
(6, 1, 'Ngỡ ngàng trước 43 địa điểm tuyệt vời nhất để đi du xuân 2017 với giá chỉ từ 300k', 'ngo-ngang-truoc-43-dia-diem-tuyet-voi-nhat-de-di-du-xuan-2017-voi-gia-chi-tu-300k', 'http://tourdulichhanquoc.net/uploads/images/%5EFADD6AA3B308C6C1A82F049575034B1B89143D47DB3276F5E3%5Epimgpsh_fullsize_distr.jpg', 120, '<p style=\"text-align: justify;\">\r\n	M&ugrave;a xu&acirc;n l&agrave; m&ugrave;a của dự tươi mới, m&ugrave;a của ăn chơi v&agrave; lễ hội. Đầu năm, mọi người c&oacute; xu hướng đi ch&ugrave;a cầu may, tham gia c&aacute;c chương tr&igrave;nh lễ hội vui chơi c&ugrave;ng gia đ&igrave;nh v&agrave; bạn b&egrave;.&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Sau tết l&agrave; loạt c&aacute;c&nbsp;dịp nghỉ lễ&nbsp;nối tiếp nhau như <strong>giỗ tổ H&ugrave;ng Vương</strong> hay dịp nghỉ d&agrave;i nhất l&agrave;&nbsp;<strong>30/4-1/5</strong>&nbsp;tới, bạn muốn được c&ugrave;ng gia đ&igrave;nh kh&aacute;m ph&aacute; v&agrave; thư gi&atilde;n ở một nơi thật l&agrave; xa? Nhưng đang băn khoăn kh&ocirc;ng biết n&ecirc;n đi đ&acirc;u? Gi&aacute; cả như thế n&agrave;o?</p>\r\n<p style=\"text-align: justify;\">\r\n	Vậy h&atilde;y để&nbsp;<strong>AZbooking </strong>chia sẻ cho c&aacute;c bạn&nbsp;43 điểm đ&aacute;ng đến nhất trong năm con g&agrave; n&agrave;y nh&eacute;.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Những địa điểm đáng đi nhất trong dịp 30 tháng 4 và 1 tháng 5-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/%5EFADD6AA3B308C6C1A82F049575034B1B89143D47DB3276F5E3%5Epimgpsh_fullsize_distr.jpg\" style=\"height: 399px; width: 600px;\" title=\"Những địa điểm đáng đi nhất trong dịp 30 tháng 4 và 1 tháng 5-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Với những mức gi&aacute; từ 300k đến 1 triệu, từ 1 triệu đến 2 triệu, từ 2 triệu đến 3 triệu, từ 3 triệu đến 4 triệu, từ 4 triệu đến 5 triệu, từ 5 triệu đến 10 triệu, từ 10 triệu đến 15 triệu, từ 15 triệu đến 30 triệu , từ 30 triệu đến 45 triệu, từ 45 triệu đến 60 triệu, từ 60 triệu đến 90 triệu v&agrave; từ 90 triệu đến 100 triệu.</p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">1.Với mức chi từ 300k đến 1 triệu th&igrave; ch&uacute;ng ta c&oacute; thể đi đ&acirc;u?</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	Với những cặp đ&ocirc;i trẻ muốn đi du lịch nhưng kinh ph&iacute; chưa cao n&ecirc;n ngại nghi&ecirc;n cứu những điểm đi du lịch v&agrave;o dịp n&agrave;y v&igrave; nghĩ sẽ rất đắt do sợ bị chặt ch&eacute;m nhưng đi v&agrave;o những dịp kh&aacute;c th&igrave; lại kh&oacute; v&igrave; bận nhiều c&ocirc;ng việc. Hiệu được suy nghĩ n&agrave;y Mixtourist sẽ tư vấn cho c&aacute;c bạn những điểm đến cực k&igrave; th&uacute; vị m&agrave; gi&aacute; cực k&igrave; ph&ugrave; hợp với t&uacute;i tiền.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	1.1&nbsp;&nbsp; Đền H&ugrave;ng - hướng về cội nguồn</h3>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Hướng về cội nguồn Phú Thọ-Mixtoruist\" src=\"http://tourdulichhanquoc.net/uploads/images/gio-to-Hung-Vuong-Mixtourist.jpg\" style=\"height: 314px; width: 600px;\" title=\"Hướng về cội nguồn Phú Thọ-Mixtoruist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	R&otilde; r&agrave;ng&nbsp;<strong>Đền H&ugrave;ng -&nbsp;Ch&ugrave;a M&iacute;a</strong> l&agrave; lựa chọn hết sức tuyệt vời cho bạn v&agrave; gia đ&igrave;nh v&agrave;o dịp cuối tuần. Vừa để t&igrave;m lại cho t&acirc;m chữ an vừa hiểu về cội nguồn v&agrave; cầu may mắn cho gia đ&igrave;nh người th&acirc;n trong năm mới 2017 m&agrave; chi ph&iacute; kh&ocirc;ng cao.</p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu lo sợ v&agrave;o những dịp n&agrave;y hay bị chặt ch&eacute;m th&igrave; h&atilde;y để ch&uacute;ng t&ocirc;i gi&uacute;p bạn nh&eacute;! Li&ecirc;n hệ ngay <strong>AZbooking </strong>để đăng k&yacute; một <strong>tour du lịch Đền H&ugrave;ng gi&aacute; rẻ </strong>nhất chỉ với&nbsp;<strong>300k</strong></p>\r\n<h3 style=\"text-align: justify;\">\r\n	1.2 Đền Ph&ugrave; Đổng ( Đền Gi&oacute;ng)</h3>\r\n<p style=\"text-align: justify;\">\r\n	<strong>Đền Ph&ugrave; Đổng</strong>&nbsp;hay c&ograve;n được gọi l&agrave;&nbsp;<strong>đền Gi&oacute;ng</strong>&nbsp;nơi v&ocirc; c&ugrave;ng linh thi&ecirc;ng nơi&nbsp;thờ th&aacute;nh Gi&oacute;ng&nbsp;ở Gia L&acirc;m, ngoại th&agrave;nh H&agrave; Nội.</p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu như Đền H&ugrave;ng bạn đ&atilde; đi nhiều lần v&agrave; c&oacute; thể Đền H&ugrave;ng xa qu&aacute; th&igrave; đền Gi&oacute;ng l&agrave; lựa chọn cực k&igrave; ph&ugrave; hợp cho bạn. Vừa cầu được may m&agrave; việc di chuy&ecirc;n dễ d&agrave;ng hơn.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Đền Phù Đổng ( Đền Gióng)-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/tim-hieu-hoi-giong-o-den-phu-dong-va-den-soc-Mixtourist.jpg\" style=\"height: 389px; width: 600px;\" title=\"Đền Phù Đổng ( Đền Gióng)-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	1.3&nbsp;&nbsp; Đền Trần &ndash; ch&ugrave;a Phố Minh - ch&ugrave;a Cố Lễ</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Một địa điểm nữa bạn cũng c&oacute; thể chọn l&agrave;&nbsp;Đền Trần- ch&ugrave;a Phố Minh-ch&ugrave;a Cố Lễ&nbsp;ở Nam Định. Đền&nbsp;thờ vua thời nh&agrave; Trần&nbsp;n&ecirc;n kiến tr&uacute;c kh&aacute; đặc biệt từ xa xưa chưa kể bạn c&ograve;n được đến với&nbsp;ch&ugrave;a Phố Minh- ng&ocirc;i ch&ugrave;a thời L&yacute;&nbsp;v&agrave; được triều Trần mở rộng v&agrave;o năm 1262.Nghe đến vậy th&ocirc;i l&agrave; bạn cũng đ&atilde; thấy được phần n&agrave;o sự cổ k&iacute;nh trang nghi&ecirc;m nơi đ&acirc;y rồi v&agrave; đến đ&acirc;y rồi Mix tin chắc bạn sẽ t&igrave;m được điều g&igrave; đ&oacute; trong t&acirc;m m&agrave; gi&aacute; ăn uống v&agrave; nghỉ ngơi vẫn rất rẻ.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Đền Trần – chùa Phố Minh- chùa Cố Lễ-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/%C4%90%E1%BB%81n%20Tr%E1%BA%A7n%20%E2%80%93%20ch%C3%B9a%20Ph%E1%BB%91%20Minh-%20ch%C3%B9a%20C%E1%BB%91%20L%E1%BB%85-Mixtourist.jpg\" style=\"height: 378px; width: 600px;\" title=\"Đền Trần – chùa Phố Minh- chùa Cố Lễ-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;&lt;Để biết b&iacute; quyết đ&oacute; mời bạn truy cập tại&nbsp;<a href=\"http://mixtourist.com.vn/\" title=\"công ty chuyên tour du lịch lễ hội\">mixtourist.com.vn</a>&gt;&gt;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	1.4 C&ocirc;n Sơn - Kiếp Bạc</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn y&ecirc;u qu&ecirc; hương Việt Nam? Bạn đam m&ecirc;&nbsp;thơ Nguyễn Tr&atilde;i? Đ&atilde; c&oacute; &iacute;t nhất hơn 1 lần bạn muốn c&oacute; 1 h&agrave;nh tr&igrave;nh đến với những địa danh trong thơ Nguyễn Tr&atilde;i? Vậy th&igrave; c&ograve;n trần trừ g&igrave; nữa m&agrave; kh&ocirc;ng 1 lần t&igrave;m đến nơi đ&acirc;y để được gần cuộc sống của nh&agrave; thơ hơn bao giờ hết. Để t&igrave;m hiểu xem tại sao nh&agrave; thơ lại y&ecirc;u nơi đ&acirc;y đến thế.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Côn Sơn-Kiếp Bạc-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/con-son-kiep-bac-Mixtourist.jpg\" style=\"height: 450px; width: 600px;\" title=\"Côn Sơn-Kiếp Bạc-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<nếu a=\"\" href=\"http://mixtourist.com.vn/\" i=\"\" n=\"\" sau=\"\" t=\"\" title=\"công ty chuyên tour du lịch lễ hội\" trang=\"\" web:=\"\" xem=\"\">Mixtourist.com.vn&nbsp;để c&oacute; gi&aacute; hơn 300k&gt;&gt;</nếu></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	1.5 Mai Ch&acirc;u</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Mai Ch&acirc;u&nbsp;- thung lũng tuyệt đẹp chốn nh&acirc;n gian. Đ&oacute; l&agrave; những nhận x&eacute;t m&agrave; Mix thường được nghe c&aacute;c bạn chia sẻ. Mặc d&ugrave;&nbsp;Mai Ch&acirc;u&nbsp;kh&ocirc;ng c&oacute; nhiều điểm để đi như người anh em Mộc Ch&acirc;u nhưng nhờ kh&ocirc;ng kh&iacute; v&ocirc; c&ugrave;ng &ocirc;n h&ograve;a v&agrave; cả những n&eacute;t ẩm thực rất ri&ecirc;ng v&agrave; v&ocirc; v&agrave;n những điều th&uacute; vị kh&aacute;c.Tất cả đ&atilde; khiến cho&nbsp;Mai Ch&acirc;u&nbsp;chưa bao giờ cũ trong l&ograve;ng những mọi người đam m&ecirc; du lịch kh&aacute;m ph&aacute;.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Mai Châu-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/mai-chau-vao-xuan-Mixtourist.jpg\" style=\"height: 400px; width: 600px;\" title=\"Mai Châu-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	1.6 Hoa Lư - Vườn Chim Thung Nham</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn muốn thăm&nbsp;kinh đ&ocirc; đầu ti&ecirc;n của Việt Nam? Bạn muốn một lần được đặt ch&acirc;n đến qu&ecirc; hương của người anh h&ugrave;ng nổi tiếng lẫy lừng Đinh Bộ Lĩnh? V&agrave; bạn y&ecirc;u thi&ecirc;n nhi&ecirc;n? Bạn muốn trốn khỏi nơi phồn hoa đ&ocirc; thị để được h&ograve;a m&igrave;nh v&agrave;o thi&ecirc;n nhi&ecirc;n? Vừa được thư gi&atilde;n với thi&ecirc;n nhi&ecirc;n mu&ocirc;ng th&uacute; v&agrave; được trở về c&otilde;i t&acirc;m an c&ograve;n g&igrave; tuyệt vời hơn đ&uacute;ng kh&ocirc;ng?</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Thung Nham-Thung Chim-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Hoa-Lu-Thung-Nham-Ninh-Binh-Mixtourist.jpg\" style=\"height: 400px; width: 600px;\" title=\"Thung Nham-Thung Chim-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	1.7. Tam Cốc-B&iacute;ch Động</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Với những c&aacute;i t&ecirc;n mĩ miều như &ldquo;Vịnh Hạ Long tr&ecirc;n cạn&rdquo; hay &ldquo;Nam Thi&ecirc;n Đế Nhị Động&rdquo; m&agrave; những người y&ecirc;u du lịch d&agrave;nh tặng cho&nbsp;Tam Cốc B&iacute;ch Động&nbsp;cũng đủ phần n&agrave;o thấy được vẻ đẹp ngọt ng&agrave;o l&atilde;ng mạn của n&oacute;. Chẳng thế m&agrave; nơi đ&acirc;y được coi l&agrave; nơi đ&aacute;ng đến nhất cho những ng&agrave;y nghỉ cuối tuần muốn thay đổi kh&ocirc;ng kh&iacute; nhưng kh&ocirc;ng c&oacute; thời gian đi xa hay kinh ph&iacute; kh&ocirc;ng cao.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Tam Cốc-Bích Động-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Tam-coc-Bich-dong-Mixtourist.jpg\" style=\"height: 394px; width: 600px;\" title=\"Tam Cốc-Bích Động-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<hãy a=\"\" href=\"http://mixtourist.com.vn/\" nhanh=\"\" p=\"\" tay=\"\" title=\"công ty chuyên tour du lịch lễ hội\" truy=\"\">Mixtourist.com.vn&nbsp;ngay để biết th&ecirc;m b&iacute; quyết kh&aacute;m ph&aacute; vẻ đẹp nơi đ&acirc;y nhưng gi&aacute; lại rẻ th&ocirc;i n&agrave;o&gt;&gt;</hãy></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	1.10 Mộc Ch&acirc;u</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Mộc Ch&acirc;u Sơn La&nbsp;cũng sẽ l&agrave; 1 điểm đến l&iacute; tưởng cho cả c&aacute;c bạn bởi nơi đ&acirc;y c&oacute; v&ocirc; v&agrave;n những điều th&uacute; vị m&agrave; chi ph&iacute; kh&ocirc;ng cao.Chỉ với số tiến dưới 1 triệu đồng nhưng bạn c&oacute; thể chinh phục 1 nơi m&agrave; d&acirc;n du lịch thường n&oacute;i với nhau kh&ocirc;ng c&ograve;n từ g&igrave; về vẻ đẹp c&oacute; thể d&ugrave;ng mi&ecirc;u tả về&nbsp;Mộc Ch&acirc;u. Tại sao lại vậy?</p>\r\n<p style=\"text-align: justify;\">\r\n	Bởi đ&acirc;y l&agrave; một v&ugrave;ng đất hiền h&ograve;a hiếu kh&aacute;ch với những đồi ch&egrave; xanh ng&aacute;t c&ugrave;ng với những dải lụa trắng được kết bằng những b&ocirc;ng hoa cải trắng đầy sức sống, những b&ocirc;ng hoa mận v&agrave; những ch&eacute;n rượu ng&ocirc; đặc biệt. Tất cả đ&atilde; l&agrave;m n&ecirc;n 1&nbsp;Mộc Ch&acirc;u&nbsp;tuyệt vời trong mắt kh&aacute;ch du lịch thập phương.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Mộc Châu-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/hoa-cai-trang-Moc-Chau-Mixtourist.jpg\" style=\"height: 400px; width: 600px;\" title=\"Mộc Châu-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	2.Với mức chi từ 1 triệu đến 2 triệu</h2>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	2.1 Sapa</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Với mức chi như vậy th&igrave; r&otilde; r&agrave;ng&nbsp;Sapa&nbsp;ch&iacute;nh l&agrave; lựa chọn tuyệt vời nhất. V&igrave; thời gian sau tết&nbsp;Sapa&nbsp;v&ocirc; c&ugrave;ng đẹp v&agrave; nhiều lễ hội đặc biệt Đ&acirc;y c&ugrave;ng l&agrave; thời điểm v&agrave;o vụ cấy trồng,thời tiết ban ng&agrave;y th&igrave; m&aacute;t lạnh c&ograve;n ban đ&ecirc;m th&igrave; kh&ocirc;, buổi s&aacute;ng nhiều m&acirc;y ở trong ph&ograve;ng kh&aacute;ch sạn bạn c&oacute; thể cảm nhận điều n&agrave;y khi những đ&aacute;m m&acirc;y bay đến v&agrave; đi ngay trước mặt bạn khiến bạn dễ c&oacute; cảm gi&aacute;c đang lạc v&agrave;o chốn bồng lai ti&ecirc;n cảnh.</p>\r\n<p style=\"text-align: justify;\">\r\n	Đ&acirc;y cũng sẽ l&agrave; trải nghiệm th&uacute; vị cho c&aacute;c bạn đ&uacute;ng kh&ocirc;ng?</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Sapa-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Sapa-Mixtourist.jpg\" style=\"height: 400px; width: 600px;\" title=\"Sapa-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	2.2&nbsp;Tam Đảo v&agrave; T&acirc;y Thi&ecirc;n</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	C&aacute;ch H&agrave; Nội kh&ocirc;ng xa,&nbsp;Tam Đảo&nbsp;v&agrave;&nbsp;ch&ugrave;a T&acirc;y Thi&ecirc;n&nbsp;lu&ocirc;n l&agrave; địa điểm thu h&uacute;t kh&aacute;ch du lịch đặc biệt l&agrave; v&agrave;o dịp sau tết bởi kh&ocirc;ng chỉ&nbsp;hấp dẫn nhờ phong cảnh thi&ecirc;n nhi&ecirc;n đẹp, n&uacute;i non tr&ugrave;ng điệp thơ mộng hữu t&igrave;nh m&agrave; c&ograve;n l&agrave; điểm t&acirc;m linh linh thi&ecirc;ng ở miền Bắc.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Tam Dao-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/tam-dao-Mixtourist.jpg\" style=\"height: 329px; width: 600px;\" title=\"Tam Dao-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu muốn một lần đến đ&acirc;y th&igrave; c&ograve;n băn khoăn g&igrave; nữa m&agrave; kh&ocirc;ng xem ngay b&agrave;i viết n&agrave;y để c&oacute; chuẩn bị cho m&igrave;nh 1 h&agrave;nh tr&igrave;nh chu đ&aacute;o nhất dịp đầu năm.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	3. Với mức chi từ 2 triệu đến 3 triệu</h2>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	3.1 Thung Nham - Thung Chim</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Nhiều bạn hay bị nhầm 2 địa điểm n&agrave;y v&agrave; nghĩ ch&uacute;ng giống nhau nhưng kh&ocirc;ng ch&uacute;ng tuy c&oacute; c&aacute;i t&ecirc;n na n&aacute; giống nhưng ch&uacute;ng kh&ocirc;ng ho&agrave;n to&agrave;n giống nhau như những g&igrave; bạn nghĩ. Nhưng Mix c&oacute; thể khẳng định chắc chắn rằng đ&acirc;y l&agrave; lựa chọn v&ocirc; c&ugrave;ng s&aacute;ng suốt cho nhưng cặp đ&ocirc;i th&iacute;ch kh&aacute;m ph&aacute; trong m&ugrave;a lễ hội k&eacute;o d&agrave;i sắp tới m&agrave; lại rất &iacute;t tốn k&eacute;m nh&eacute;.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Thung Nham-Thung Chim-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Thung-Nham-hoa-lu-Mixtourist.jpg\" style=\"height: 409px; width: 600px;\" title=\"Thung Nham-Thung Chim-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	3.2 H&agrave;nh tr&igrave;nh di sản miền Trung</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn đ&atilde; từng nghe về di sản miền Trung? Cũng như bạn đ&atilde; thực sự hiểu r&otilde; ngay cả khi bạn l&agrave; người con miền Trung chưa? Nếu chưa th&igrave; đ&acirc;y l&agrave; dịp cực k&igrave; l&iacute; tưởng để t&igrave;m hiểu về 1 n&eacute;t đẹp v&ocirc; c&ugrave;ng ti&ecirc;u biểu của người d&acirc;n miền Trung n&oacute;i ri&ecirc;ng v&agrave; Việt Nam n&oacute;i chung.Bởi lẽ bạn c&oacute; biết rằng bạn sẽ được kh&aacute;m ph&aacute;&nbsp;đặc sản Đ&agrave; Nẵng,ch&ugrave;a Linh Ứng.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Hành trình miền Trung - Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/mien-trung-Mixtourist.jpg\" style=\"height: 401px; width: 600px;\" title=\"Hành trình miền Trung-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;&lt; Mọi chi tiết li&ecirc;n hệ&nbsp;<a href=\"http://mixtourist.com.vn/\" title=\"công ty chuyên tour du lịch lễ hội\">mixtourist.com.vn</a>&nbsp;để c&oacute; biết được những chi ph&iacute; cần thiết nhất &gt;&gt;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	3.3 C&ocirc; T&ocirc;</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Những d&ograve;ng biển trong xanh v&agrave;o xu&acirc;n của&nbsp;C&ocirc; T&ocirc;&nbsp;chắc chắn khiến c&aacute;c bạn c&oacute; những kỉ niệm kh&oacute; qu&ecirc;n v&igrave; bầu trời kh&ocirc;ng nắng gay gắt như m&ugrave;a h&egrave; n&ecirc;n bạn v&agrave; người ấy sẽ kh&ocirc;ng cần qu&aacute; lo lắng. Chưa kể đ&acirc;y l&agrave; dịp m&agrave; rất &iacute;t tham gia n&ecirc;n r&otilde; r&agrave;ng việc cảm nhận hơi thở của biển v&agrave; kh&ocirc;ng kh&iacute; nhẹ nh&agrave;ng kh&ocirc;ng ồn &agrave;o cũng l&agrave; 1 lựa chọn đ&aacute;ng suy ngẫm đ&uacute;ng kh&ocirc;ng n&agrave;o?</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Cô Tô - Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Co-To-Mixtourist.jpg\" style=\"height: 451px; width: 600px;\" title=\"Cô Tô - Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<nếu a=\"\" href=\"http://mixtourist.com.vn/\" i=\"\" m=\"\" n=\"\" ngay=\"\" quan=\"\" t=\"\" title=\"công ty chuyên tour du lịch lễ hội\" xem=\"\">Mixtourist.com.vn&nbsp;để c&oacute; chuẩn bị nhanh th&ocirc;i n&agrave;o.&gt;&gt;</nếu></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	3.4 Đ&agrave; Nẵng-B&agrave; N&agrave;-Hội An</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Đ&agrave; Nẵng&nbsp;điểm đến ao ước nhiều cặp đ&ocirc;i v&agrave; đ&acirc;y sẽ l&agrave; dịp qu&aacute; tuyệt vời để bạn tận hưởng Đ&agrave; Nẵng v&agrave;o xu&acirc;n đ&uacute;ng kh&ocirc;ng n&agrave;o? Chưa kể sẽ thật thiếu s&oacute;t nếu bạn kh&ocirc;ng d&agrave;nh lu&ocirc;n chuyến du lịch của m&igrave;nh n&agrave;y để đi&nbsp;B&agrave; N&agrave;&nbsp;v&agrave;&nbsp;Hội An. Nhưng nhiều bạn sẽ thắc mắc về nếu như vậy lại v&agrave;o dịp xu&acirc;n đến sẽ rất đắt nhưng thực ra kh&ocirc;ng ho&agrave;n to&agrave;n đ&uacute;ng như thế biết c&aacute;ch.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Đà Nẵng-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Da-nang-Mixtourist.jpg\" style=\"height: 424px; width: 600px;\" title=\"Đà Nẵng-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<vậy a=\"\" ao=\"\" c=\"\" href=\"http://mixtourist.com.vn/\" i=\"\" kia=\"\" m=\"\" n=\"\" ng=\"\" ngay=\"\" nh=\"\" o=\"\" p=\"\" title=\"công ty chuyên tour du lịch lễ hội\" u=\"\" xem=\"\" y=\"\">Mixtourist.com.vn&nbsp;ngay nh&eacute;&gt;&gt;</vậy></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	3.5 H&agrave; Giang</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Tr&aacute;i với nhiều bạn muốn đến đền ch&ugrave;a hay biển th&igrave; c&oacute; rất nhiều bạn lại muốn c&ugrave;ng người m&igrave;nh thương đi ngắm n&uacute;i rừng&nbsp;H&agrave; Giang&nbsp;với những con suối r&iacute; r&aacute;ch nhưng b&ocirc;ng hoa mơ hoa mận v&agrave; c&aacute;c lễ hội của người d&acirc;n tộc thiểu số nhưng c&aacute;c bạn lại ngại v&igrave; gi&aacute; cả v&agrave; kh&ocirc;ng biết thu&ecirc; xe như thế n&agrave;o? Ăn uống ra sao cho tiết kiệm tr&aacute;nh chặt ch&eacute;m.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Lễ hội Lồng Tồng đặc sắc của người dân Sapa-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/le-hoi-xuong-dong-sapa-Mixtourist.jpg\" style=\"height: 436px; width: 600px;\" title=\"Lễ hội Lồng Tồng đặc sắc của người dân Sapa-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	4. Với mức chi ph&iacute; từ 3 đến 4 triệu</h2>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	4.1 M&ugrave; Cang Chải</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Nhiều người khi nhắc đến hay nghe thấy c&aacute;i t&ecirc;n&nbsp;M&ugrave; Cang Chải&nbsp;l&agrave; nghĩ đến 1 nơi ngh&egrave;o kh&oacute; nhưng ở nơi đ&oacute; kh&ocirc;ng chỉ c&oacute; vậy đ&acirc;u nh&eacute;. Giữa nơi ngh&egrave;o kh&oacute; ấy lại l&agrave; 1 thi&ecirc;n đường du lịch nơi những cặp đ&ocirc;i c&oacute; thể kh&aacute;m ph&aacute; n&uacute;i rừng thung lũng nổi tiếng như&nbsp;thung lũng T&uacute; Lệ.</p>\r\n<p style=\"text-align: justify;\">\r\n	Ngo&agrave;i việc chi&ecirc;m ngưỡng vẻ đẹp thi&ecirc;n nhi&ecirc;n ra th&igrave; bạn c&ograve;n c&oacute; thể được h&ograve;a m&igrave;nh v&agrave;o nếp sống của người người Th&aacute;i hay những c&aacute;nh ruộng bậc thang. Vậy đ&acirc;y cũng c&oacute; thể sẽ l&agrave; điểm đến rất l&iacute; tưởng cho những cặp đ&ocirc;i cho dịp tới.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Xuân Mù Cang Chải-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Xuan-Mu-cang-chai-Mixtourist.jpg\" style=\"height: 403px; width: 600px;\" title=\"Xuân Mù Cang Chải-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;&lt; H&atilde;y click ngay&nbsp;<a href=\"http://mixtourist.com.vn/\" title=\"công ty chuyên tour du lịch lễ hội\">Mixtourist</a>&nbsp;để biết l&agrave;m sao c&oacute; chuyến đi rẻ m&agrave; vẫn &yacute; nghĩa nh&eacute;&gt;&gt;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	4.2 Nha Trang</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Với nền nhiệt đới &ocirc;n h&ograve;a kh&ocirc;ng lạnh buốt như miền Bắc hay thất thường như miền Nam n&ecirc;n đ&acirc;y c&agrave;ng ph&ugrave; hợp hơn nếu bạn c&oacute; trẻ nhỏ hay người th&acirc;n lớn tuổi theo k&egrave;m. Đặc biệt cảm gi&aacute;c h&iacute;t thở gi&oacute; biển của m&ugrave;a xu&acirc;n tuyệt vời đ&uacute;ng kh&ocirc;ng?</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Nha Trang-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Nhatrang-Mixtourist.jpg\" style=\"height: 359px; width: 600px;\" title=\"Nha Trang-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	4.3 Mũi N&eacute;-Mỹ Tho-Bến Tre</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn l&agrave; người con Việt Nam? Bạn đ&atilde; nghe những địa danh n&agrave;y nhưng chưa thực sự hiểu r&otilde;? Bạn c&oacute; 1 k&igrave; nghỉ kh&aacute; d&agrave;i ph&iacute;a trước? Vậy c&ograve;n trần trừ g&igrave; kh&ocirc;ng tham kh&aacute;m ph&aacute; dải đất miền Nam cần lao m&agrave; nhiều đặc sản n&agrave;y? Nơi những điệu h&aacute;t ngọt lịm đi v&agrave;o l&ograve;ng bao thế hệ Việt Nam.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Lễ hội ở Bến Tre-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/ben-tre-Mixtourist.jpg\" style=\"height: 433px; width: 600px;\" title=\"Lễ hội ở Bến Tre-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu thực sự y&ecirc;u thương v&agrave; muốn kh&aacute;m ph&aacute; nơi đ&acirc;y th&igrave; bạn h&atilde;y đọc ngay b&agrave;i viết tr&ecirc;n&nbsp;<a href=\"http://mixtourist.com.vn/\" title=\"công ty chuyên tour du lịch lễ hội\">Mixtourist</a>&nbsp;n&agrave;y để c&oacute; cho m&igrave;nh chuyến đi t&iacute;ch kiệm v&agrave; đầy kỉ niệm n&agrave;o.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	4.4 Đảo Ph&uacute; Quốc</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Ph&uacute; Quốc thiền đường đảo Ngọc d&acirc;n gian&nbsp;l&agrave; những lời nhận x&eacute;t m&agrave; Mix nghe được nhiều nhất khi c&aacute;c bạn y&ecirc;u du lịch nhận x&eacute;t về nơi đ&acirc;y. Chưa dừng lại đ&oacute; đ&acirc;y c&ograve;n l&agrave; h&ograve;n đảo lớn nhất Việt Nam.Ngo&agrave;i ra nơi đ&acirc;y c&ograve;n được Unesco c&ocirc;ng nhận l&agrave; khu dự trữ sinh quyển thế giới. Wow vậy đ&acirc;y cũng c&oacute; thể điểm đến thăm gi&aacute; rẻ cho c&aacute;c bạn trong dịp đầu xu&acirc;n đ&uacute;ng kh&ocirc;ng?</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Đảo ngọc Phú Quốc-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/dao-ngoc-phu-quoc-%20Mixtourist.jpg\" style=\"height: 450px; width: 600px;\" title=\"Đảo ngọc Phú Quốc-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<hãy a=\"\" href=\"http://mixtourist.com.vn/\" i=\"\" n=\"\" ngay=\"\" sau=\"\" t=\"\" xem=\"\">Mixtourist.com.vn&nbsp;để chuẩn bị nhanh v&agrave; nhận cho m&igrave;nh v&agrave; người ấy những ưu đ&atilde;i khủng nh&eacute;.&gt;&gt;</hãy></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	4.5 Đ&agrave; Lạt</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Đ&agrave; Lạt&nbsp;chắc hẳn kh&ocirc;ng c&ograve;n xa lạ với c&aacute;c bạn đ&uacute;ng kh&ocirc;ng? D&ugrave; ch&uacute;ng ta chưa hay đ&atilde; đi đều c&oacute; những ấn tưởng rất tốt về mảnh đất của t&igrave;nh y&ecirc;u n&agrave;y. V&agrave; trong dịp nghỉ lễ n&agrave;y th&igrave;&nbsp;Đ&agrave; Lạt&nbsp;cũng l&agrave; địa điểm đ&aacute;ng để chọn lựa v&igrave; gi&aacute; nếu biết sẽ kh&ocirc;ng đắt đ&uacute;ng kh&ocirc;ng? Chưa kể kh&iacute; hậu h&agrave;i h&ograve;a lại được tr&igrave;m đắm v&agrave;o thung lũng t&igrave;nh y&ecirc;u hay những n&eacute;t th&uacute; vị kh&aacute;c của&nbsp;Đ&agrave; Lạt mộng mơ&nbsp;th&igrave; qu&aacute; tuyệt.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Mùa xuân ở Đà Lạt-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/don-xuan-cung-hoa-mai-anh-dao-o-da-lat-Mixtourist.jpg\" style=\"height: 377px; width: 600px;\" title=\"Mùa xuân ở Đà Lạt-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<mời a=\"\" c=\"\" href=\"http://mixtourist.com.vn/\" i=\"\" n=\"\" t=\"\" title=\"công ty chuyên tour du lịch lễ hội\" y=\"\">Mixtourist&nbsp;để c&oacute; cho m&igrave;nh v&agrave; gia đ&igrave;nh những kinh nghiệm tham gia kh&aacute;m ph&aacute; Đ&agrave; Lạt gi&aacute; rẻ nh&eacute;.&gt;&gt;</mời></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	4.6 C&ocirc;n Đảo</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	C&ocirc;n Đảo&nbsp;xinh tươi năm ngo&agrave;i khơi của bở biển Nam Bộ, đ&acirc;y vẫn được coi l&agrave; b&atilde;i biển kh&aacute; hoang xơ n&ecirc;n nếu muốn thay đổi gi&oacute; hay thử cảm gi&aacute;c một lần được lạc giữa đảo hoang c&ugrave;ng người m&igrave;nh y&ecirc;u c&acirc;u hải sản tươi sống đ&ecirc;m đến ngắm sao tr&ecirc;n bầu trời giữa biển khơi th&igrave; quả l&agrave; tuyệt đ&uacute;ng kh&ocirc;ng ạ?</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Côn Đảo-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Con-dao-Mixtourist.jpg\" style=\"height: 364px; width: 600px;\" title=\"Côn Đảo-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Vậy c&ograve;n trần trừ g&igrave; m&agrave; kh&ocirc;ng chuẩn bị x&aacute;ch balo l&ecirc;n v&agrave; đi th&ocirc;i n&agrave;o anh chị ơi.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	5.Chỉ với chi ph&iacute; từ 4 đến 5 triệu bạn c&oacute; thế đi?</h2>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	5.1 Huế</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Huế&nbsp;với những n&eacute;t ri&ecirc;ng vẫn đang thu h&uacute;t rất nhiều sự quan t&acirc;m của người y&ecirc;u du lịch. V&agrave;o xu&acirc;n l&agrave; khoảng thời gian tuyệt vời nhất của&nbsp;Huế&nbsp;bởi những những hoạt động du xu&acirc;n hấp dẫn như&nbsp;Ngũ L&acirc;n Nghịch Xu&acirc;n&nbsp;hay thưởng thức những m&oacute;n&nbsp;đặc sản chỉ Huế&nbsp;mới c&oacute; như b&aacute;nh đ&uacute;c hay những&nbsp;quần thể di t&iacute;ch cố đ&ocirc; Huế.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Mùa xuân trên xứ Huế-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/mua-xuan-tren-xu-Hue-Mixtourist.jpg\" style=\"height: 402px; width: 600px;\" title=\"Mùa xuân trên xứ Huế-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	5.2 S&agrave;i G&ograve;n</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	S&agrave;i G&ograve;n&nbsp;v&agrave;o xu&acirc;n với v&ocirc; v&agrave;n những n&eacute;t đẹp đặc trưng nhộn nhịp n&aacute;o nhiệt c&ugrave;ng với thời tiết kh&ocirc;ng lạnh buốt như miền Bắc sẽ l&agrave; điểm đến l&iacute; tưởng v&agrave;o dịp nghỉ lễ của nhiều cặp đ&ocirc;i.Những c&aacute;nh hoa b&ecirc;n&nbsp;con đường Nguyễn Huệ&nbsp;nổi tiếng hay những&nbsp;m&oacute;n ăn S&agrave;i G&ograve;n&nbsp;ng&acirc;y ngất bốn phương chắc chắn sẽ khiến bạn kh&ocirc;ng thể n&agrave;o qu&ecirc;n được.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Mùa xuân ở Sài Gòn-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/mua-xuan-sai-gon-Mixtourist.jpg\" style=\"height: 400px; width: 600px;\" title=\"Mùa xuân ở Sài Gòn-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	5.3 Quế L&acirc;m &ndash; Nam Ninh</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Đến một mảnh đất xa lạ để kh&aacute;m ph&aacute; những n&eacute;t văn h&oacute;a kh&aacute;c cũng l&agrave; &yacute; tưởng hay đ&uacute;ng kh&ocirc;ng ạ? V&ugrave;ng đất&nbsp;Quế L&acirc;m, Nam Ninh&nbsp;kh&ocirc;ng qu&aacute; xa Việt Nam nhưng c&oacute; rất nhiều điều th&uacute; vị như Nơi đ&acirc;y c&oacute; c&aacute;c&nbsp;khu rừng rậm nguy&ecirc;n thủymang đậm m&agrave;u xanh tinh khiết, những đỉnh n&uacute;i v&agrave; hang động h&ugrave;ng vĩ, hiểm trở; những con suối, th&aacute;c nước bay nhảy; những thửa ruộng bậc thang tr&ecirc;n sườn n&uacute;i kỳ tuyệt thi&ecirc;n hạ;</p>\r\n<p style=\"text-align: justify;\">\r\n	B&ecirc;n cạnh đ&oacute; nơi đ&acirc;y c̣&ograve;n l&agrave; nơi cư tr&uacute; của hơn 10 d&acirc;n tộc thiểu số như&nbsp;Choang, Dao, Hmong, Đồng..v.v. h&atilde;y những m&oacute;n ẩm thực nơi đ&acirc;y cũng rất th&uacute; vị nh&eacute; như&nbsp;M&igrave; Quế L&acirc;m&nbsp;đ&atilde; l&agrave;&nbsp;m&oacute;n ăn s&aacute;ng ch&iacute;nh của d&acirc;n địa phương từ&nbsp;thời nh&agrave; Thanh&nbsp;v&agrave; nổi tiếng với hương vị tuyệt vời hay&nbsp;tương ớt rất đặc biệt nơi đ&acirc;y.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Nam Ninh-Trung Quốc-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/du-lich-nam-ninh-trung-quoc-Mixtourist.jpg\" style=\"height: 450px; width: 600px;\" title=\"Nam Ninh-Trung Quốc-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<mời a=\"\" href=\"http://mixtourist.com.vn/\" i=\"\" m=\"\" n=\"\" sao=\"\" t=\"\" title=\"công ty chuyên tour du lịch lễ hội\" u=\"\" y=\"\">Mix&nbsp;lại muốn giới thiệu nơi n&agrave;y nh&eacute;.&gt;&gt;</mời></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	5.4 Campuchia</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn đam m&ecirc; quần thể&nbsp;Angkor Wat&nbsp;tuyệt đẹp của đất nước Campuchia? Hay bạn cũng muốn một lần được đến thăm ch&ugrave;a Bạc nhưng chưa c&oacute; kinh ph&iacute; v&agrave; thời gian? Bạn cũng th&iacute;ch trải nghiệm đường bộ hơn l&agrave; h&igrave;nh thức kh&aacute;c vừa tiết kiệm vừa lại c&oacute; những kỉ niệm thực tế hơn?</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Chùa Vàng-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/kinh-nghiem-du-lich-campuchia-Mixtourist.jpg\" style=\"height: 397px; width: 600px;\" title=\"Chùa Vàng-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	6.Chỉ với chi ph&iacute; từ 5 đến 10 triệu bạn c&oacute; thể đi</h2>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	6.1&nbsp; Biển Mũi N&eacute;</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Địa điểm tiếp theo m&agrave; Mix muốn giới thiệu l&agrave;&nbsp;biển Mũi N&eacute;-Phan Thiết.Phan Thiết được biết đến thi&ecirc;n nhi&ecirc;n ưu đ&atilde;i với nhiều cảnh đẹp, b&atilde;i biển trong xanh v&agrave; những đồi c&aacute;t trắng, v&agrave;ng m&ecirc;nh m&ocirc;ng tưởng như kh&ocirc; cằn nhưng lại l&atilde;ng mạn.Đến Mũi N&eacute; bạn sẽ c&oacute; thế đi&nbsp;Suối Ti&ecirc;n&nbsp;(với một b&ecirc;n l&agrave; đồi n&uacute;i c&aacute;t, một b&ecirc;n l&agrave; dặng dừa),</p>\r\n<p style=\"text-align: justify;\">\r\n	chơi ở&nbsp;đồi C&aacute;t Đỏ&nbsp;(c&aacute;ch Mũi N&eacute; chừng 5 -6 km), chơi ở&nbsp;đồi C&aacute;t Trắng&nbsp;(B&agrave;u Trắng, c&aacute;ch Mũi N&eacute; hơn 30km). Ngo&agrave;i ra bạn c&ograve;n c&oacute; thể thưởng thức&nbsp;mỳ Quảng Phan Thiết&nbsp;nổi tiếng khắp nơi hay c&aacute; lồi x&ocirc;i mỡ cũng rất th&uacute; vị nh&eacute; m&agrave; gi&aacute; kh&ocirc;ng đắt.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Những món ngon ở Mũi Né-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/nhung-mon-ngon-nhat-dinh-phai-thu-o-mui-ne-Mixtourist.jpg\" style=\"height: 428px; width: 600px;\" title=\"Những món ngon ở Mũi Né-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<nếu a=\"\" href=\"http://mixtourist.com.vn/\" i=\"\" kinh=\"\" m=\"\" mau=\"\" n=\"\" ng=\"\" t=\"\" title=\"công ty chuyên tour du lịch lễ hội\" trong=\"\">Mixtourist&nbsp;ngay th&ocirc;i&gt;&gt;</nếu></p>\r\n<h3 style=\"text-align: justify;\">\r\n	6.2 Du lịch bằng Trực Thăng</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Nghe c&oacute; vẻ lạ nhỉ? Du lịch g&igrave; m&agrave; lại bằng trực thăng m&agrave; gi&aacute; rẻ thế? Mọi chuyện đều c&oacute; thể nếu bạn muốn c&ugrave;ng người m&igrave;nh y&ecirc;u ghi lại những kỉ niệm theo c&aacute;ch ri&ecirc;ng của m&igrave;nh m&agrave; gi&aacute; kh&ocirc;ng qu&aacute; đắt th&igrave; đ&acirc;y r&otilde; r&agrave;ng sẽ l&agrave; lựa chọn v&ocirc; c&ugrave;ng ph&ugrave; hợp.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Ngắm cảnh từ trên máy bay-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/maybay-Mixtourist.png\" style=\"height: 253px; width: 600px;\" title=\"Ngắm cảnh từ trên máy bay-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	6.3 &nbsp;Quảng Ch&acirc;u &ndash; Thẩm Quyến ( Trung Quốc)</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Một địa điểm d&agrave;nh cho cho c&aacute;c bạn muốn &ldquo; vượt bi&ecirc;n&rdquo; &nbsp;c&ugrave;ng người m&igrave;nh y&ecirc;u thương c&oacute; thể tham khảo đ&oacute; ch&iacute;nh l&agrave;&nbsp;Quảng Ch&acirc;u- Thẩm Quyến của Trung Quốc. Bởi v&igrave; v&agrave;o thời gian n&agrave;y bạn v&agrave; người ấy sẽ c&oacute; thể kh&aacute;m ph&aacute; rất nhiều điều chỉ c&oacute; ở đ&acirc;y như&nbsp;c&ocirc;ng vi&ecirc;n Việt T&uacute;&nbsp;ấn hay&nbsp;phố đi bộ Bắc Kinh&nbsp;hay bạn cũng c&oacute; thể thăm&nbsp;nh&agrave; tưởng niệm T&ocirc;n Trung Sơn&nbsp;hoặc thưởng thức &ldquo;&nbsp;miền ẩm thực&rdquo; nơi đ&acirc;y như vịt x&agrave;o chanh&nbsp;Cao Phong, c&aacute;c m&oacute;n ch&aacute;o, x&ocirc;i b&aacute;t bảo&hellip;;</p>\r\n<p style=\"text-align: justify;\">\r\n	b&aacute;nh bao, x&aacute; x&iacute;u Quảng Ch&acirc;u&hellip;l&agrave; những m&oacute;n ăn mang đậm phong c&aacute;ch nghệ thuật ẩm thực truyền thống Quảng Ch&acirc;u; c&aacute;c m&oacute;n ăn như&nbsp;g&agrave; Long Cương, c&aacute; muối Đại B&agrave;ng, vải Nam Sơn, l&ecirc; đường Thạch Nham, đ&agrave;o ngọt..l&agrave; những đặc sản nổi tiếng của Th&acirc;m Quyến.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Đặc sản Quảng Châu-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/10-mon-dac-san-o-quang-ninh-me-hoac-thuc-khach-Mixtourist.jpg\" style=\"height: 371px; width: 600px;\" title=\"Đặc sản Quảng Châu-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<nếu a=\"\" href=\"http://mixtourist.com.vn/\" i=\"\" m=\"\" n=\"\" ngay=\"\" nh=\"\" t=\"\" title=\"công ty chuyên tour du lịch lễ hội\" y=\"\">Mixtourist&nbsp;để c&oacute; những kinh nghiệm du lịch tuyệt vời nhất nh&eacute;&gt;&gt;</nếu></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	6.4 Malaysia</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Một người h&agrave;ng x&oacute;m nữa m&agrave; ch&uacute;ng ta cũng c&oacute; thể chọn đ&oacute; ch&iacute;nh l&agrave;&nbsp;Malaysia. Đất nước của m&oacute;n&nbsp;Mỳ Assam Laksa&nbsp;hay&nbsp;cơm c&agrave; ri Nasi Kandar&nbsp;l&agrave; m&oacute;n ăn c&oacute; nguồn gốc từ cộng đồng người Ấn Độ ở Malaysia n&agrave;y được b&agrave;y b&aacute;n phổ biến tr&ecirc;n c&aacute;c qu&aacute;n ăn vỉa h&egrave; ở Penang. Điểm tạo n&ecirc;n sự kh&aacute;c biệt cho Nasi Kandar ch&iacute;nh l&agrave; nhờ nước sốt c&agrave; ri với gia vị cay nồng, ấm n&oacute;ng v&agrave; c&ograve;n v&ocirc; v&agrave;n những m&oacute;n ăn ngon kh&aacute;c chưa kể phong cảnh nơi đ&acirc;y cũng v&ocirc; c&ugrave;ng tuyệt vời.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Mỳ Assam Laksa -Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/am-thuc-Mixtourist.jpg\" style=\"height: 400px; width: 600px;\" title=\"Mỳ Assam Laksa -Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	6.5 Bangkok-Pattaya</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Hai địa điềm n&agrave;y từ l&acirc;u đ&atilde; kh&ocirc;ng c&ograve;n l&agrave; điểm đến qu&aacute; xa lạ của mọi người v&igrave; ở đ&acirc;y c&oacute; rất nhiều điều th&uacute; vị chưa kể gi&aacute; cả kh&aacute; rẻ nếu bạn biết chọn nữa nh&eacute;. Chưa kể n&oacute; cũng kh&ocirc;ng ch&ecirc;nh lệch nhiệt độ nhiều lắm so với Việt Nam v&agrave; đ&acirc;y chắc chắn hứa hẹn l&agrave; điểm đến cực k&igrave; ph&ugrave; hợp cho mọi người v&agrave;o dịp đầu xu&acirc;n năm mới 2017.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Thái Lan-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Thai-Lan-Mixtourist.jpg\" style=\"height: 398px; width: 600px;\" title=\"Thái Lan-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	7. Với chi ph&iacute; từ 10 đến 15 triệu th&igrave; ch&uacute;ng ta c&oacute; thể đi đ&acirc;u?</h2>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	7.1 Singapore</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Singapore&nbsp;l&agrave; đảo quốc với nhiều địa điểm hấp dẫn nổi tiếng khắp thế giới như&nbsp;C&ocirc;ng vi&ecirc;n Merlion&nbsp;hay nh&agrave; h&aacute;t tr&ecirc;n vịnh Esplanade nổi tiếng. Ngo&agrave;i ra c&ograve;n v&ocirc; v&agrave;n điều th&uacute; vị kh&aacute;c nữa nhất l&agrave; kh&aacute;c như c&aacute;c lễ hội đậm đ&agrave; bản sắc d&acirc;n tộc như lễ hội Hindu diễn ra v&agrave;o th&aacute;ng 2 hằng năm hay lễ hội ẩm thực nơi giới thiệu những m&oacute;n ẩm thực đậm đ&agrave; bản sắc d&acirc;n tộc Singapore.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"nhà hát trên vịnh Esplanade -Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/nh%C3%A0%20h%C3%A1t%20tr%C3%AAn%20v%E1%BB%8Bnh%20Esplanade%20-Mixtourist.jpg\" style=\"height: 374px; width: 600px;\" title=\"nhà hát trên vịnh Esplanade -Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	7.2 Myanmar</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn l&agrave; người&nbsp;t&acirc;m hướng phật? Bạn muốn đến nơi m&agrave; ngập tr&agrave;n trong kh&ocirc;ng kh&iacute; ch&ugrave;a chiền v&agrave; phong cảnh đẹp tuyệt vời của&nbsp;Myanmar? Vậy&nbsp;Myanmar&nbsp;ch&iacute;nh l&agrave; lựa chọn số 1 d&agrave;nh cho bạn. B&ecirc;n cạnh những cảnh quan do con người tạo ra Myanmar c&ograve;n c&oacute; những c&aacute;nh rừng nguy&ecirc;n sinh v&agrave; những d&atilde;y n&uacute;i h&ugrave;ng vĩ, những b&atilde;i biển c&ograve;n hoang xơ trong vắt v&agrave; những di sản rực rỡ của mấy ng&agrave;n năm.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Myanmar-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Myanmar-Mixtourist.jpg\" style=\"height: 400px; width: 600px;\" title=\"Myanmar-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;&lt; Nhanh tay k&iacute;ch ngay&nbsp;<a href=\"http://tourdulichmyanmar.vn/news/287/223/Du-lich-Myanmar-cung-Mix-Tourist\" title=\"công ty chuyên tour du lịch Myanmar\">du lịch Myanmar</a>&nbsp;với gi&aacute; cực hấp dẫn ngay b&acirc;y giờ th&ocirc;i&gt;&gt;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	8. Với mức chi từ 15 đến 30 triệu bạn c&oacute; thể đi đến đ&acirc;u?</h2>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	8.1 Nhật Bản</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Sẽ kh&ocirc;ng c&ograve;n g&igrave; tuyệt vời hơn nếu bạn chọn&nbsp;Nhật Bản&nbsp;cho điểm đi sắp tới của bạn v&igrave; đ&acirc;y l&agrave;&nbsp;thời gian hoa anh đ&agrave;o nởtrải d&agrave;i khắp cả nước. Đi dưới những t&aacute;n hoa anh đ&agrave;o cũng người m&igrave;nh thương ghi lại những khoảnh khắc tuyệt vời v&agrave; thưởng thức những&nbsp;m&oacute;n ăn nướng với ch&uacute;t rượu&nbsp;ấm nồng thật kh&ocirc;ng c&ograve;n g&igrave; tuyệt vời hơn đ&uacute;ng k?</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Nhật Bản-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/lau-dai-Hirosaki-Nhat-Ban-Mixtourist.jpg\" style=\"height: 387px; width: 600px;\" title=\"Nhật Bản-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<nhanh a=\"\" href=\"http://tourdulichnhatban.org/\" i=\"\" n=\"\" ngay=\"\" t=\"\" tay=\"\" title=\"công ty chuyên tour du lịch Nhật Bản\" xem=\"\">du lịch Nhật Bản&nbsp;để chuẩn bị khai ph&aacute; 1 m&ugrave;a lễ hội hoa anh đ&agrave;o ngay th&ocirc;i.&gt;&gt;</nhanh></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	8.2 Đảo Bali- Indonesia</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Đảo Bali&nbsp;l&agrave; một trong những h&ograve;n đảo nổi tiếng nhất của&nbsp;Indonesia Bali&nbsp;với những b&atilde;i biển trong xanh, thị trấn Ubud &ndash; một trong c&aacute;c trung t&acirc;m nghệ thuật v&agrave; văn h&oacute;a ch&iacute;nh của Bali &ndash; với những ng&ocirc;i đền v&agrave; nghệ thuật đi&ecirc;u khắc; đ&oacute;n b&igrave;nh minh tr&ecirc;n&nbsp;đỉnh Pananjakan&nbsp;v&agrave;&nbsp;ngắm n&uacute;i lửa Bromo &amp; Semeru; th&agrave;nh phố Yogyakarta với&nbsp;Đền thờ Phật Borobudur.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Bali-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/du-lich-dao-bali-indonesia-Mixtourist.jpg\" style=\"height: 450px; width: 600px;\" title=\"Bali-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	8.3 Seoul - Đảo Jeju (H&agrave;n Quốc)</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Tham gia kh&aacute;m ph&aacute;&nbsp;H&agrave;n Quốc&nbsp;v&agrave;o thời gian đầu năm cũng kh&aacute; tuyệt vời đ&uacute;ng kh&ocirc;ng n&agrave;o? Sẽ kh&ocirc;ng cần phải giới thiệu qu&aacute; nhiều bởi những phong cảnh cũng như những&nbsp;lễ hội v&agrave; ẩm thực&nbsp;nơi đ&acirc;y cũng kh&ocirc;ng c&ograve;n qu&aacute; xa lạ nữa đ&uacute;ng kh&ocirc;ng ạ?</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Đảo Jeju-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/dao-jeju-Mixtourist.jpg\" style=\"height: 400px; width: 600px;\" title=\"Đảo Jeju-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<vậy a=\"\" href=\"http://tourdulichhanquoc.net/du-lich-han-quoc/\" i=\"\" n=\"\" ng=\"\" ngay=\"\" t=\"\" title=\"công ty chuyên tour du lịch Hàn Quốc\" xem=\"\">du lịch H&agrave;n Quốc&nbsp;để chuẩn bị cho m&igrave;nh v&agrave; gia đ&igrave;nh ngay th&ocirc;i.&gt;&gt;</vậy></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	9.Với mức chi ph&iacute; từ 30 đến 45 triệu th&igrave; c&oacute; thể kh&aacute;m ph&aacute; được những đ&acirc;u?</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	9.1 Maldives</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Maldives&nbsp;được mệnh danh l&agrave; thi&ecirc;n đường của những thi&ecirc;n đường. Bởi nơi đ&acirc;y ch&ocirc;ng tựa như những chuỗi ngọc trai của v&ugrave;ng biển Ấn Độ Dương.</p>\r\n<p style=\"text-align: justify;\">\r\n	Đến với&nbsp;Maldives&nbsp;v&agrave;o thời gian n&agrave;y bạn sẽ được tắm những &aacute;nh nắng tran h&ograve;a nhưng kh&ocirc;ng gay gắt chưa kể quần đảo tạo 1 hệ sinh th&aacute;i v&ocirc; c&ugrave;ng phong ph&uacute; những vỉa san h&ocirc; k&eacute;o d&agrave;i như bất tận quốc đảo n&agrave;y lu&ocirc;n được xếp hạng l&agrave; một trong những địa điểm du lịch trăng mật l&atilde;ng mạn nhất thế giới nhưng cũng l&agrave; nơi du lịch đắt đỏ nhất thế giới.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Maldives-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Man-dem-o-Maldives-Mixtourist.jpg\" style=\"height: 251px; width: 600px;\" title=\"Maldives-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	9.2 Dubai</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Dubai&nbsp;nổi tiếng những điều &ldquo; dị&rdquo; nhất thế giới như những&nbsp;<a href=\"http://dulichdubai.vn/news/235/223/Nhung-le-hoi-hang-nam-o-Dubai-Phan-1/\" title=\"lễ hội Dubai-Mixtourist\">lễ hội Dubai</a>&nbsp;m&agrave; nổi tiếng nhất ch&iacute;nh l&agrave; lễ hội mua sắm hay những m&oacute;n ăn m&agrave; th&agrave;nh phần ch&iacute;nh lại l&agrave; 1 nguy&ecirc;n liệu cực k&igrave; đặc biệt nh&eacute;. Ngo&agrave;i ra c&aacute;c bạn c&ograve;n c&oacute; thể ngắm&nbsp;c&ocirc;ng vi&ecirc;n hoa giữa xa mạc,thật k&igrave; diệu đ&uacute;ng kh&ocirc;ng?</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Dubai-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Dubai-Mixtourist.jpg\" style=\"height: 375px; width: 600px;\" title=\"Dubai-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;&lt; C&ograve;n trần trừ g&igrave; nữa m&agrave; kh&ocirc;ng xem ngay b&agrave;i tr&ecirc;n&nbsp;<a href=\"http://dulichdubai.vn/\" title=\"công ty chuyên tour du lịch Dubai\">du lịch Dubai</a>&nbsp;để x&aacute;ch balo l&ecirc;n v&agrave; đi đến đất nước &quot;dị&quot; nhất n&agrave;y th&ocirc;i&gt;&gt;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	10.Với mức chi từ 45 đến 60 triệu th&igrave; đ&acirc;u l&agrave; những nơi đ&aacute;ng đi nhất?</h2>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	10.1 Bờ Đ&ocirc;ng nước Mỹ</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Bờ Đ&ocirc;ng nước Mỹ từ l&acirc;u kh&ocirc;ng c&ograve;n l&agrave; địa điểm qu&aacute; xa lạ với những người đam m&ecirc; du lịch nhưng c&oacute; thể v&igrave; bạn chưa c&oacute; thời gian hoặc băn khoăn kh&ocirc;ng biết chi ph&iacute; ở đ&acirc;y khoảng bao nhi&ecirc;u? Kh&ocirc;ng biết thủ tục visa thế n&agrave;o?hay đi thế th&igrave; c&oacute; cần c&aacute;i n&agrave;y c&aacute;i kia kh&ocirc;ng? Tất cả dường như nhiều khi khiến bạn ngại nhưng từ b&acirc;y giờ h&atilde;y đừng ngại ng&ugrave;ng g&igrave; hết v&igrave; thời gian qua đi kh&ocirc;ng thể lấy lại được.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Bờ Đông nước Mỹ-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/B%E1%BB%9D%20%C4%90%C3%B4ng%20n%C6%B0%E1%BB%9Bc%20M%E1%BB%B9-Mixtourist.jpg\" style=\"height: 337px; width: 600px;\" title=\"Bờ Đông nước Mỹ-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<nếu a=\"\" c=\"\" href=\"http://tourdulichmy.vn/\" i=\"\" m=\"\" n=\"\" ngay=\"\" o=\"\" quan=\"\" tham=\"\" title=\"công ty chuyên tour du lịch Mỹ\" website=\"\" y=\"\">du lịch Mỹ&nbsp;ngay th&ocirc;i&gt;&gt;</nếu></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	10.2 Bờ T&acirc;y nước Mỹ</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Bờ T&acirc;y&nbsp;th&igrave; kh&aacute;c g&igrave;&nbsp;Bờ Đ&ocirc;ng? Tại sao Mix lại chia nhỏ ra v&agrave; cho rằng 2 sự lựa chọn ri&ecirc;ng?</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Bờ Tây nước Mỹ-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/B%E1%BB%9D%20T%C3%A2y%20n%C6%B0%E1%BB%9Bc%20M%E1%BB%B9-Mixtourist.jpg\" style=\"height: 372px; width: 600px;\" title=\"Bờ Tây nước Mỹ-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<vậy a=\"\" c=\"\" href=\"http://tourdulichmy.vn/\" title=\"công ty chuyên tour du lịch Mỹ\" y=\"\">b&agrave;i viết n&agrave;y&nbsp;để biết n&oacute; đặc biệt sao nh&eacute;.&gt;&gt;</vậy></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	10.3 Ch&acirc;u &Uacute;c</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu bạn y&ecirc;u th&iacute;ch nước &Uacute;c th&igrave; đ&acirc;y r&otilde; r&agrave;ng l&agrave; thời gian tuyệt vời để bạn v&agrave; người m&igrave;nh y&ecirc;u kh&aacute;m ph&aacute; n&oacute;. Bởi những phong cảnh n&ecirc;n thơ của nhiều đất nước kh&aacute;c nhau trong 1 ch&acirc;u lục m&agrave; gi&aacute; qu&aacute; cao nếu biết chuẩn bị.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Châu Úc-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/sydney-opera-house-du-lich-uc-Mixtourist.jpg\" style=\"height: 338px; width: 600px;\" title=\"Châu Úc-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<vậy a=\"\" hay=\"\" href=\"http://dulichaustralia.com.vn/\" i=\"\" n=\"\" newzealand=\"\" ng=\"\" o=\"\" p=\"\" t=\"\" tham=\"\" title=\"công ty du lịch Úc\" u=\"\" xinh=\"\">du lich Australia&nbsp;n&agrave;y ngay th&ocirc;i &gt;&gt;</vậy></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	10.4 Nga</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Những cảnh đẹp ở&nbsp;nước Nga&nbsp;thơ mộng v&agrave; đắm say như thủ đ&ocirc; Matxcơva&nbsp;lộng lẫy hay&nbsp;điện Kremlin&nbsp;v&agrave;&nbsp;Quảng trường Đỏ tr&aacute;ng lệ, con đường m&ugrave;a thu l&aacute; v&agrave;ng đẹp như tranh... l&agrave; những h&igrave;nh ảnh đẹp tr&ecirc;n xứ sở bạch dương m&agrave; ch&uacute;ng t&ocirc;i chụp lại được v&agrave; muốn chia sẻ với bạn.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Nga-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Nga-Mixtourist.jpg\" style=\"height: 300px; width: 600px;\" title=\"Nga-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<nếu 1=\"\" a=\"\" c=\"\" href=\"http://tourdulichnga.vn/\" i=\"\" m=\"\" n=\"\" ng=\"\" nga=\"\" ngay=\"\" nh=\"\" t=\"\" title=\"công ty chuyên tour du lịch Nga\" y=\"\">du lich Nga&nbsp;ngay th&ocirc;i.&gt;&gt;</nếu></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	10.5 Vương quốc Anh</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn y&ecirc;u&nbsp;London&nbsp;hiện đại hay bạn l&agrave; thần tượng những chiến binh b&oacute;ng đ&aacute; bạn muốn được một l&agrave;n đến nơi&nbsp; được mệnh danh l&agrave; chảo lửa của thế giới? Vậy c&ograve;n chần chừ g&igrave; nữa m&agrave; kh&ocirc;ng thỏa m&atilde;n đam m&ecirc; của m&igrave;nh ngay th&ocirc;i.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Vương Quốc Anh-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/canh-dep-mua-thu-tren-khap-vuong-quoc-anh-Mixtourist.jpg\" style=\"height: 399px; width: 600px;\" title=\"Vương Quốc Anh-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Chưa kể đến&nbsp;London&nbsp;v&agrave;o thời gian n&agrave;y bạn sẽ c&oacute; nhiều thời gian kh&aacute;m ph&aacute; phong cảnh ở trời t&acirc;y hay những cuộc sống hiện đại nơi đ&acirc;y.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	11. Với mức chi ph&iacute; từ từ 60 đến 90 triệu ch&uacute;ng ta c&oacute; thể đi đ&acirc;u?</h2>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	11.1 Ph&aacute;p</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Ph&aacute;p&nbsp;đất nước của t&igrave;nh y&ecirc;u với d&ograve;ng&nbsp;s&ocirc;ng Sence thơ mộng&nbsp;hay&nbsp;th&aacute;p Effel nổi tiếng&nbsp;khắp thế giới. Ch&iacute;nh v&igrave; thế&nbsp;Ph&aacute;p&nbsp;chưa bao giờ l&agrave; cũ trong l&ograve;ng mọi người.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Pháp - Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Phap-Mixtourist.jpg\" style=\"height: 338px; width: 600px;\" title=\"Pháp - Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	11.2 Nam Phi</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Nam Phi, l&agrave; một quốc gia nằm ở mũi ph&iacute;a nam&nbsp;lục địa Ch&acirc;u Phi. Chắc hẳn&nbsp;Nam Phi&nbsp;l&agrave; v&ugrave;ng đất kh&ocirc;ng c&oacute; g&igrave; xa lạ với nhiều người nhưng n&oacute; c&oacute; rất nhiều điều th&uacute; vị m&agrave; Mix bật m&iacute; cho bạn&nbsp; x&iacute;u nh&eacute; như đ&acirc;y nơi duy nhất c&oacute; 3 thủ đ&ocirc; tr&ecirc;n thế giới với những n&eacute;t giao thoa rất &ldquo;lạ&rdquo;.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Nam Phi-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/Nam-Phi-Mixtourist.jpg\" style=\"height: 390px; width: 600px;\" title=\"Nam Phi-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	C&ograve;n nhiều điều th&uacute; vị hơn nữa m&agrave; khi đến&nbsp;Nam Phi&nbsp;rồi bạn sẽ kh&ocirc;ng hối hận v&igrave; chọn nh&eacute;.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	12. Với mức chi ph&iacute; tr&ecirc;n 90 triệu th&igrave; n&ecirc;n đi đ&acirc;u nhất?</h2>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	12.1 Ch&acirc;u &Acirc;u</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Ch&acirc;u &Acirc;u&nbsp;nổi tiếng với những đất nước ph&aacute;t triển v&agrave; gi&agrave;u c&oacute; nhất thế giới n&ecirc;n chất lượng dịch vụ v&agrave; du lịch đều tuyệt vời nhất. Nếu bạn y&ecirc;u qu&yacute; c&aacute;c&nbsp;m&oacute;n ăn&nbsp; &Yacute;&nbsp;hay phong cảnh nơi đ&acirc;y nhưng bạn cũng muốn kh&aacute;m ph&aacute; cả nước&nbsp;Ph&aacute;p l&atilde;ng mạn&nbsp;hay&nbsp;Thụy Sĩ cổ điển&nbsp;hay những&nbsp;cối xay gi&oacute; H&agrave; Lan,những&nbsp;b&ocirc;ng hoa tuy l&iacute;p&nbsp;nổi tiếng khắp thế giới được trồng bạt ng&agrave;n nơi đ&acirc;y nhưng bạn kh&ocirc;ng c&oacute; thời gian chuẩn bị hay chưa c&oacute; nhiều kinh nghiệm chuẩn bị.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Các nước châu Âu-Mixtourist\" src=\"http://tourdulichhanquoc.net/uploads/images/cac-nuoc-chau-au-Mixtourist.jpg\" style=\"height: 335px; width: 600px;\" title=\"Các nước châu Âu-Mixtourist\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&lt;<vậy a=\"\" c=\"\" href=\"http://dulichchauau.org/\" i=\"\" n=\"\" t=\"\" title=\"công ty chuyên tour du lịch châu Âu\" y=\"\">du lịch ch&acirc;u &Acirc;u&nbsp;để c&oacute; cho m&igrave;nh những th&ocirc;ng tin hữu &iacute;ch nh&eacute;.&gt;&gt;</vậy></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	12.2 Canada</h3>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn y&ecirc;u&nbsp;m&agrave;u v&agrave;ng của những chiếc l&aacute; phong&nbsp;hay đơn giản bạn muốn được đến nơi được coi&nbsp;cội nguồn của rượu bia vang&nbsp;nổi tiếng khắp thế giới. Chưa dừng lại đ&oacute;&nbsp;Canada&nbsp;c&ograve;n nổi tiếng với những lễ hội như&nbsp;lễ hội &Aacute;nh S&aacute;ng&amp;nb</p>\r\n', 'Ngỡ ngàng trước 43 địa điểm tuyệt vời nhất để đi du xuân 2017 từ 300k', 'tour du lịch lễ hội, tour lễ hội, tour du xuân, tour du lịch tháng 2, tour du lịch tháng 3', 'Mùa xuân là mùa của dự tươi mới, mùa của ăn chơi và lễ hội. Đầu năm, mọi người có xu hướng đi chùa cầu may, tham gia các chương trình lễ hội vui chơi cùng gia đình và bạn', '2017-03-08 22:01:44');
INSERT INTO `news` (`id`, `danhmuc_id`, `name`, `name_url`, `img`, `view`, `content`, `title`, `keyword`, `description`, `created`) VALUES
(7, 1, 'Đi du lịch Thái Lan mùa nào đẹp nhất ?', 'di-du-lich-thai-lan-mua-nao-dep-nhat-', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/nhung-dieu-ban-can-dac-biet-luu-y-khi-di-du-lich-thai-lan.png', 103, '<p style=\"text-align: justify;\">\r\n	Đất nước Th&aacute;i Lan đang ng&agrave;y c&agrave;ng trở th&agrave;nh một điểm du lịch hấp dẫn rất nhiều du kh&aacute;ch ở khắp nơi tr&ecirc;n thế giới, đặc biệt l&agrave; người d&acirc;n Việt Nam. Kh&ocirc;ng chỉ bởi nơi đ&acirc;y c&oacute; nhiều cảnh quan đẹp, nền văn h&oacute;a đặc sắc, những n&eacute;t ẩm thực phong ph&uacute;, đa dạng m&agrave; c&ograve;n bởi chi ph&iacute; cho một chuyến <strong>du lịch Th&aacute;i Lan</strong> kh&aacute; rẻ, chỉ trong khoảng từ 6 đến 9 triệu đồng.</p>\r\n<p style=\"text-align: justify;\">\r\n	Vậy <em>thời điểm n&agrave;o l&agrave; th&iacute;ch hợp nhất để bạn đi du lịch Th&aacute;i Lan</em>? <em>N&ecirc;n đi Th&aacute;i Lan v&agrave;o m&ugrave;a n&agrave;o đẹp nhất</em>? Dưới đ&acirc;y <strong><a href=\"http://azbooking.vn/\"><span style=\"color:#ff0000;\">Azbooking</span></a></strong>&nbsp;xin bật m&iacute; &quot;thời điểm l&yacute; tưởng&quot; để c&aacute;c bạn c&oacute; thể tận hưởng một <strong><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-chau-a/du-lich-thai-lan/\"><span style=\"color:#ff0000;\">tour du lịch Th&aacute;i Lan</span></a> </strong>trọn vẹn nhất nh&eacute;!</p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">Thời tiết ở Th&aacute;i Lan</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	Th&aacute;i Lan l&agrave; một đất nước nhiệt đới xinh đẹp của Đ&ocirc;ng Nam &Aacute; vừa c&oacute; biển,vừa c&oacute;&nbsp;thi&ecirc;n đường mua sắm v&agrave; ăn uống v&ocirc; c&ugrave;ng nổi tiếng. C&aacute;c bạn sẽ rất th&iacute;ch th&uacute; khi đến với Th&aacute;i Lan bởi c&aacute;c danh lam thắng cảnh tuyệt đẹp, hệ thống ch&ugrave;a chiền nguy nga, tr&aacute;ng lệ v&agrave; v&ocirc; số c&aacute;c lễ hội truyền thống đặc sắc,&hellip;.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/nhiet-do-o-thai-lan-azbooking.jpg\" style=\"width: 631px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	L&atilde;nh thổ Th&aacute;i Lan nằm trọn vẹn trong v&ugrave;ng kh&iacute; hậu nhiệt đới, n&oacute;ng ẩm quanh năm với nhiệt độ trung b&igrave;nh khoảng 28-35 độ C với 3 m&ugrave;a r&otilde; rệt&nbsp;:</p>\r\n<ul>\r\n	<li style=\"text-align: justify;\">\r\n		M&ugrave;a kh&ocirc; m&aacute;t &ndash; từ th&aacute;ng 11 đến th&aacute;ng 4.</li>\r\n	<li style=\"text-align: justify;\">\r\n		M&ugrave;a kh&ocirc; n&oacute;ng- từ th&aacute;ng 4 đến th&aacute;ng 6.</li>\r\n	<li style=\"text-align: justify;\">\r\n		M&ugrave;a mưa- từ th&aacute;ng 6 đến th&aacute;ng 10.</li>\r\n</ul>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">Thời điểm n&agrave;o th&iacute;ch hợp&nbsp;để bạn du lịch Th&aacute;i Lan?</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	Với diện t&iacute;ch trải d&agrave;i v&agrave; thi&ecirc;n nhi&ecirc;n đa dạng c&ugrave;ng v&ocirc; số c&aacute;c lễ hội truyền thống, mỗi địa danh lại sở hữu một khoảng thời gian l&yacute; tưởng ri&ecirc;ng để du kh&aacute;ch thoải m&aacute;i kh&aacute;m lựa chọn để kh&aacute;m ph&aacute;.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#000000;\"><span style=\"font-size:16px;\">1. Th&aacute;i Lan m&ugrave;a kh&ocirc; m&aacute;t c&oacute; g&igrave;?</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Kh&aacute;c với c&aacute;c nước trong c&ugrave;ng khu vực như Singapore hay Malaysia, <strong>thời tiết Th&aacute;i Lan</strong> c&oacute; th&ecirc;m cả kiểu kh&iacute; hậu m&aacute;t mẻ v&agrave;o khoảng th&aacute;ng 11 đến th&aacute;ng 4. Đến với Th&aacute;i Lan m&ugrave;a n&agrave;y, bạn c&oacute; thể thoải m&aacute;i thăm qua mọi ng&otilde; ng&aacute;ch v&agrave; kh&aacute;m ph&aacute; nền ẩm thực đường phố kh&aacute; độc đ&aacute;o m&agrave; kh&ocirc;ng lo ch&aacute;y nắng hay mưa nh&eacute;.</p>\r\n<p style=\"text-align: justify;\">\r\n	Kh&ocirc;ng chỉ c&oacute; cơ hội vui chơi v&agrave; hưởng thụ trong bầu kh&ocirc;ng kh&iacute; m&aacute;t mẻ dễ chịu trong những th&aacute;ng m&ugrave;a kh&ocirc; m&aacute;t, du kh&aacute;ch c&ograve;n c&oacute; cơ hội trải nghiệm chuỗi lễ hội truyền thống lớn tại <strong>xứ sở Ch&ugrave;a V&agrave;ng</strong>.</p>\r\n<p>\r\n	Bạn c&oacute; thể xin Phật mọi điều may mắn tốt l&agrave;nh cho gia đ&igrave;nh v&agrave; người th&acirc;n trong<strong><em> </em></strong><strong>lễ&nbsp;hội hoa đăng Loy Krathong</strong> v&agrave;o th&aacute;ng 12 h&agrave;ng năm.</p>\r\n<div>\r\n	&nbsp;</div>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/le-hoi-anh-sang-Loy-Krathong.jpg\" style=\"height: 340px; width: 650px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Hay chơi bắn s&uacute;ng nước trong dịp<em><strong> Tết t&eacute; nước Songkran</strong></em> truyền thống của Th&aacute;i Lan để x&oacute;a tan mọi buồn phiền &acirc;u lo trong năm cũ.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/le-hoi-te-nuoc-2.jpg\" style=\"width: 650px; height: 432px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu bạn l&agrave; một người y&ecirc;u th&iacute;ch <em><strong>văn h&oacute;a Th&aacute;i Lan</strong></em> cũng như muốn h&ograve;a m&igrave;nh v&agrave;o cuộc chiến nước lớn nhất thế giới, h&atilde;y đăng k&iacute; <span style=\"color:#ff0000;\"><strong><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-chau-a/du-lich-thai-lan/\">tour du lịch Th&aacute;i Lan th&aacute;ng 4</a>&nbsp;</strong></span>của <span style=\"color:#ff0000;\"><strong><a href=\"http://azbooking.vn/\">Azbooking</a></strong></span>&nbsp;ngay nh&eacute;!</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\">2. C&oacute; g&igrave; th&uacute; vị ở Th&aacute;i Lan m&ugrave;a kh&ocirc; n&oacute;ng?</span></h3>\r\n<p style=\"text-align: justify;\">\r\n	M&ugrave;a n&oacute;ng ở Th&aacute;i k&eacute;o d&agrave;i khoảng 2 th&aacute;ng bắt đầu từ th&aacute;ng 4 với nền nhiệt khoảng 35 độ C rất kh&oacute; chịu v&agrave; ẩm ướt.</p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu thời tiết m&ugrave;a n&agrave;y gay gắt v&agrave; v&ocirc; c&ugrave;ng kh&oacute; chịu ở <strong>Bangkok</strong>, th&igrave; bạn đừng vội lo lắng, c&oacute; rất nhiều địa điểm th&uacute; vị kh&aacute;c đang chờ bạn kh&aacute;m ph&aacute; đ&oacute;.</p>\r\n<p style=\"text-align: justify;\">\r\n	Nghỉ m&aacute;t ở <strong>b&atilde;i biển quyến rũ Pattaya </strong>v&agrave; chơi c&aacute;c trờ chơi v&ocirc; c&ugrave;ng hấp dẫn tr&ecirc;n biển.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<strong><img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/pattaya-azbooking.jpg\" style=\"width: 650px; height: 430px;\" /></strong></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Hay trải m&igrave;nh tr&ecirc;n b&atilde;i c&aacute;t ở<strong> h&ograve;n đảo Phuket hoang sơ, y&ecirc;n b&igrave;nh</strong> sẽ x&oacute;a tan tất cả nắng n&oacute;ng v&agrave; mệt mỏi.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/bien-Phuket-azbooking.jpg\" style=\"width: 650px; height: 433px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	B&ecirc;n cạnh đ&oacute;, tận hưởng khung cảnh &ecirc;m đềm nhưng kh&ocirc;ng k&eacute;m phần quyến rũ ở <strong>ph&iacute;a Bắc Th&aacute;i Lan </strong>cũng l&agrave; sự lựa chọn của rất nhiều du kh&aacute;ch.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/mien-nui-phia-bac-Thai-Lan.jpg\" style=\"width: 650px; height: 460px;\" /></p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\">3. Trải nghiệm m&ugrave;a mưa l&atilde;ng mạn tr&ecirc;n đất Th&aacute;i</span></h3>\r\n<p style=\"text-align: justify;\">\r\n	<strong>M&ugrave;a mưa ở Th&aacute;i Lan</strong> trải d&agrave;i từ th&aacute;ng 6 đến th&aacute;ng 10 với c&aacute;c cơn mưa r&agrave;o diễn ra thường xuy&ecirc;n, lượng mưa lớn trung b&igrave;nh khoảng 300mm/th&aacute;ng. Tuy nhi&ecirc;n đ&acirc;y cũng kh&ocirc;ng phải thời điểm tồi để đến Th&aacute;i Lan v&agrave; đặc biệt l&agrave; những b&atilde;i biển.</p>\r\n<p style=\"text-align: justify;\">\r\n	Những cơn mưa sẽ ngớt dần v&agrave;o buổi chiều tạo n&ecirc;n khung cảnh mặt trời xuống thấp l&uacute;c ho&agrave;ng h&ocirc;n thật tuyệt vời rất th&iacute;ch hợp cho những cặp đ&ocirc;i y&ecirc;u th&iacute;ch l&atilde;ng mạn ngọt ng&agrave;o.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/hoang-hon-tren-bien.jpg\" style=\"width: 650px; height: 406px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	V&agrave; điều tuyệt vời nữa l&agrave; bạn sẽ được ăn uống sang chảnh v&agrave; ở kh&aacute;ch sạn tiện nghi nhất với gi&aacute; cực rẻ đ&oacute;.</p>\r\n<p style=\"text-align: justify;\">\r\n	H&atilde;y chọn cho m&igrave;nh một thời điểm ph&ugrave; hợp v&agrave; đăng k&iacute;<span style=\"color:#ff0000;\"> </span><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-chau-a/du-lich-thai-lan/\"><span style=\"color:#ff0000;\"><strong>tour du lịch Th&aacute;i Lan trọn g&oacute;i</strong></span></a><span style=\"color:#ff0000;\"> </span>gi&aacute; rẻ của ch&uacute;ng t&ocirc;i để chuyến du lịch của bạn ho&agrave;n hảo v&agrave; tiết kiệm nhất.</p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu bạn c&oacute; bất cứ thắc mắc về du lịch Th&aacute;i Lan hay cần <span style=\"color:#000000;\"><strong>Azbooking</strong> </span>chia sẻ th&ecirc;m về<span style=\"color:#ff0000;\"><strong> kinh nghiệm du lịch</strong></span> kh&aacute;c hoặc cần tư vấn về c&aacute;c tour du lịch kh&aacute;c, h&atilde;y gọi ngay tới hotline <span style=\"color:#0000cd;\"><strong>04 6281 4340</strong></span> để được tư vấn miễn ph&iacute; nh&eacute;!</p>\r\n', 'Nên đi du lịch Thái Lan vào mùa nào ?', 'du lịch thái lan mùa nào đẹp, thái lan mùa nào đẹp nhất, thời điểm thích hợp để đi thái lan', 'Thái Lan ngày càng trở thành điểm du lịch hấp dẫn nhiều du khách bởi những địa điểm thăm quan nổi tiếng, nền văn hóa đặc sắc và những món ăn ngon. Đặc biệt du lịch Thái L', '2017-03-02 14:09:45'),
(8, 1, 'Du lịch Thái Lan cần chuẩn bị những gì ?', 'du-lich-thai-lan-can-chuan-bi-nhung-gi-', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/Canh%20sac%20ben%20bo%20song%20Chao%20Phraya.jpg', 98, '<p>\r\n	Th&aacute;i Lan&nbsp;lu&ocirc;n l&agrave; điểm đến hấp dẫn thu h&uacute;t rất nhiều du kh&aacute;ch tr&ecirc;n thế giới. Bạn đang l&ecirc;n kế hoạch cho cả gia đ&igrave;nh m&igrave;nh đi tham quan v&agrave; vui chơi tại Th&aacute;i Lan m&agrave; băn khoăn chưa biết m&igrave;nh cần chuẩn bị những g&igrave;?</p>\r\n<p>\r\n	H&atilde;y c&ugrave;ng <a href=\"http://azbooking.vn\"><span style=\"color:#ff0000;\"><strong>Azbooking</strong></span></a> điểm qua những thứ cần chuẩn bị trước khi đi<a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-chau-a/du-lich-thai-lan/\"><span style=\"color:#0000cd;\"> <i>du lịch Th&aacute;i Lan&nbsp;</i></span></a>nh&eacute;!</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">1. Hộ chiếu v&agrave; visa du lịch Th&aacute;i Lan</span></span></h2>\r\n<p>\r\n	Bạn c&oacute; tin rằng chỉ với một chiếc hộ chiếu l&agrave; c&oacute; thể x&aacute;ch balo vi vu tr&ecirc;n đất Th&aacute;i cả th&aacute;ng ? Đ&uacute;ng vậy, hiện nay c&ocirc;ng d&acirc;n nước Việt Nam kh&ocirc;ng cần xin visa trong thời gian lưu tr&uacute; dưới 30 ng&agrave;y khi bạn du lịch bằng đường h&agrave;ng kh&ocirc;ng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/ho-chieu.jpg\" style=\"width: 650px; height: 488px;\" /></p>\r\n<p>\r\n	Tất cả giấy tờ bạn cần chỉ l&agrave; một hộ chiếu c&ograve;n hạn tr&ecirc;n 6 th&aacute;ng, v&eacute; m&aacute;y bay du lịch tới Th&aacute;i Lan hoặc giấy x&aacute;c nhận nếu mua v&eacute; qua mạng th&ocirc;i nha! Nếu bạn chưa c&oacute; hộ chiếu th&igrave; đừng lo lắng, v&igrave; thủ tục l&agrave;m hộ chiếu cực k&igrave; đơn giản v&agrave; nhanh gọn. Bạn chỉ cần mang theo chứng minh thư, sổ hộ khẩu, 200.000 đồng l&ecirc;n Ph&ograve;ng quản l&yacute; Xuất Nhập cảnh tại địa phương v&agrave; chờ 5-7 ng&agrave;y l&agrave; đ&atilde; sở hữu ngay &ldquo;ch&igrave;a kh&oacute;a đi du lịch nước ngo&agrave;i&rdquo; rồi.</p>\r\n<p>\r\n	&gt;&gt;Xem th&ecirc;m <em><a href=\"http://www.xuatnhapcanh.com.vn/c11t80-thu-tuc-xin-cap-ho-chieu-lan-dau-tai-cong-an-tinh-thanh-pho.htm\">thủ tục xin cấp hộ chiếu</a></em></p>\r\n<p>\r\n	Tuy nghi&ecirc;n nếu bạn c&oacute; &yacute; định thăm người th&acirc;n hoặc du lịch Th&aacute;i Lan trong thời gian d&agrave;i hơn, bạn lưu &yacute; buộc phải xin Visa Th&aacute;i Lan do văn ph&ograve;ng l&atilde;nh sự Th&aacute;i Lan cấp.</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">2. Chuẩn bị h&agrave;nh l&yacute; du lịch Th&aacute;i Lan</span></span></h2>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\"><img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/hanh-li-du-l%E1%BB%8Bc-thai-azbooking.jpg\" style=\"width: 650px; height: 378px;\" /></span></span></p>\r\n<p>\r\n	Kh&aacute;ch đi&nbsp;du lịch Th&aacute;i Lan &nbsp;n&ecirc;n chuẩn bị quần &aacute;o, chuẩn bị gi&agrave;y d&eacute;p chủ yếu l&agrave; gi&agrave;y/d&eacute;p đế thấp thoải m&aacute;i, nếu định mang gi&agrave;y cao g&oacute;t chỉ n&ecirc;n mang 1 đ&ocirc;i để tạo d&aacute;ng khi chụp ảnh th&ocirc;i nh&eacute;&hellip;v&igrave; đi bộ kh&aacute; nhiều !</p>\r\n<p>\r\n	Một lời khuy&ecirc;n cho c&aacute;c t&iacute;n đồ mua sắm l&agrave; c&aacute;c bạn mang &iacute;t quần &aacute;o m&ugrave;a h&egrave; đi th&ocirc;i v&igrave; Th&aacute;i Lan rất n&oacute;ng, v&agrave; d&agrave;nh chỗ trong vali để mua đồ về nữa. V&igrave; c&aacute;c khu chợ v&agrave; trung t&acirc;m mua sắm tại Th&aacute;i Lan gi&aacute; cả rất rẻ, lại dễ d&agrave;ng mặc cả v&agrave; quần &aacute;o b&ecirc;n đ&oacute; th&igrave; rất tốt nữa.</p>\r\n<p>\r\n	Bạn n&ecirc;n mang theo mũ v&agrave; một chiếc &ocirc; để tr&aacute;nh nắng mưa, một &iacute;t thuốc ph&ograve;ng cảm c&uacute;m, thuốc dị ứng thời tiết, hay thuốc đau bụng,&hellip;để sử dụng ngay khi cần.</p>\r\n<p>\r\n	&gt;&gt; Mời bạn xem th&ecirc;m<em><a href=\"http://azbooking.vn/tin-tuc/kinh-nghiem-du-lich/di-du-lich-thai-lan-mua-nao-dep-nhat-.html\"><span style=\"color:#000000;\"> </span><span style=\"color:#0000cd;\">thời tiết Th&aacute;i Lan</span></a></em></p>\r\n<p>\r\n	B&ecirc;n cạnh đ&oacute; 1 chiếc m&aacute;y ảnh đời mới hay điện thoại th&ocirc;ng minh cũng l&agrave; h&agrave;nh trang kh&ocirc;ng thể thiếu khi bạn đến thăm quan vui chơi tại một quốc gia đẹp như Th&aacute;i Lan.</p>\r\n<h2>\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">3. Đổi tiền</span></span></h2>\r\n<p>\r\n	Trước mỗi chuyến xuất ngoại, ngo&agrave;i hộ chiếu v&agrave; h&agrave;nh l&yacute; ra, th&igrave; tiền cũng l&agrave; một phần quan trọng trong chuyến du lịch của bạn.</p>\r\n<p>\r\n	Đơn vị tiền tệ của Th&aacute;i Lan l&agrave;&nbsp;đồng Baht&nbsp;(Th&aacute;i Bạt &ndash; k&yacute; hiệu THB. Th&aacute;i Lan ban h&agrave;nh cả tiền giấy v&agrave; tiền xu với nhiều mệnh gi&aacute; kh&aacute;c nhau. Tiền giấy ở Th&aacute;i Lan c&oacute; mệnh gi&aacute; cao nhất l&agrave; 1.000 baht v&agrave; thấp nhất l&agrave; 20 baht, c&ograve;n tiền xu th&igrave; c&oacute; gi&aacute; trị từ 10 baht trở xuống.</p>\r\n<p>\r\n	Bạn n&ecirc;n đổi tiền Baht hoặc Đ&ocirc; la Mỹ ngay tại c&aacute;c Ng&acirc;n h&agrave;ng hoặc tại c&aacute;c đại l&yacute; thu đổi tiền Ngoại tệ tại Việt Nam trước khi đi du lịch để tr&aacute;nh c&aacute;c khoản ph&iacute; cao hoặc c&aacute;c sự cố kh&ocirc;ng đ&aacute;ng c&oacute;. V&agrave; bạn n&ecirc;n ch&uacute; &yacute; rằng theo quy định của Luật Ph&aacute;p Th&aacute;i Lan th&igrave; người du lịch phải mang theo tối thiểu 700 USD (tương đương 20.000 baht) v&agrave; tối đa l&agrave; 5.000 USD (kh&ocirc;ng cần khai b&aacute;o Hải quan khi nhập cảnh Th&aacute;i Lan).</p>\r\n<p>\r\n	Nếu bạn chưa kịp đổi tiền trước khi đi th&igrave; cũng đừng lo lắng, v&igrave; tất cả c&aacute;c trung t&acirc;m thương mại, kh&aacute;ch sạn, nh&agrave; h&agrave;ng ở Th&aacute;i Lan đều c&oacute; h&igrave;nh thức thanh to&aacute;n bằng thẻ t&iacute;n dụng. Tuy nhi&ecirc;n khi bạn sử dụng thẻ bạn sẽ phải chịu một khoảng phụ ph&iacute; kh&ocirc;ng nhỏ v&igrave; quy đổi ngoại tệ.</p>\r\n<p>\r\n	Với những th&ocirc;ng tin về kinh nghiệm chuẩn bị h&agrave;nh trang đi du lịch Th&aacute;i Lan, <a href=\"http://azbooking.vn/\"><span style=\"color:#ff0000;\">Azbooking</span></a> hi vọng bạn sẽ c&oacute; chuyến du lịch như mong muốn!</p>\r\n<p>\r\n	Mọi thắc mắc về thủ tục, giấy tờ khi đi du lịch Th&aacute;i Lan v&agrave; <strong>chương tr&igrave;nh tour du lịch Th&aacute;i Lan trọn g&oacute;i</strong> xin gọi ngay về cho <strong>Azbooking</strong> tại <span style=\"color: rgb(255, 0, 0);\"><strong>hotline 0904.578.388</strong></span> hoặc <span style=\"color: rgb(255, 0, 0);\">04.6281.4340</span> để được giải đ&aacute;p.</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><em><strong>C&oacute; thể bạn quan t&acirc;m</strong></em></span></p>\r\n<p>\r\n	<em><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-chau-a/du-lich-thai-lan/\"><span style=\"color:#0000cd;\">Du lịch Th&aacute;i Lan trọn g&oacute;i</span></a></em></p>\r\n<p>\r\n	<em><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-chau-a/du-lich-thai-lan/du-lich-bangkok-pattaya-4-ngay-3-dem-sieu-tiet-kiem.html\"><span style=\"color:#0000cd;\">Tour du lịch Th&aacute;i lan gi&aacute; si&ecirc;u rẻ</span></a></em></p>\r\n<p>\r\n	<em><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-chau-a/du-lich-thai-lan/tour-du-lich-thai-lanbangkok-pattaya-4-ngay-3-dem.html\"><span style=\"color:#0000cd;\">Tour du lịch Bangkok-Pattaya 4 Ng&agrave;y 3 Đ&ecirc;m</span></a></em></p>\r\n<p>\r\n	&nbsp;</p>\r\n', '', 'cần mang theo gì khi đi thái lan, du lịch thái lan cần những gì, thu tục khi đi du lịch thái lan', '', '2017-03-06 12:20:21');
INSERT INTO `news` (`id`, `danhmuc_id`, `name`, `name_url`, `img`, `view`, `content`, `title`, `keyword`, `description`, `created`) VALUES
(9, 2, 'Tổng hợp 69 địa điểm từ Bắc chí Nam làm say lòng mọi phượt thủ', 'tong-hop-69-dia-diem-tu-bac-chi-nam-lam-say-long-moi-phuot-thu', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/69-dia-diem-phuot-lam-say-long-moi-phuot-thu-mixtourist.jpg', 14527, '<p style=\"text-align: justify;\">\r\n	Đi phượt l&agrave; sở th&iacute;ch của hầu hết giới trẻ hiện nay. Đi phượt để kh&aacute;m ph&aacute;, th&iacute;ch mạo hiểm, thỏa m&atilde;n đam m&ecirc;, du lịch thậm ch&iacute; l&agrave; t&igrave;m &quot;gấu&quot; cho m&igrave;nh. Vậy thời điểm n&agrave;o l&agrave; th&iacute;ch hợp nhất để đi phượt? Từ bắc tới nam c&oacute; những địa điểm phượt n&agrave;o được giới trẻ y&ecirc;u th&iacute;ch nhất?</p>\r\n<p style=\"text-align: justify;\">\r\n	B&acirc;y giờ h&atilde;y c&ugrave;ng&nbsp;<strong>AZbooking&nbsp;</strong>đi l&agrave;m r&otilde; &quot;chủ đề HOT&quot; n&agrave;y trong b&agrave;i viết dưới đ&acirc;y nh&eacute;!</p>\r\n<p style=\"text-align: justify; margin-left: 40px;\">\r\n	<strong><em><span style=\"background-color:#d3d3d3;\">&quot;H&igrave;nh ảnh sử dụng trong b&agrave;i đều được sưu tầm từ FB v&agrave; google n&ecirc;n bản quyền thuộc về t&aacute;c giả&quot;</span></em></strong></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: 16px;\">I. Địa điểm phượt ở miền Bắc</span></span></h2>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"font-size: 14px;\"><span style=\"color: rgb(0, 0, 255);\">1. Mộc Ch&acirc;u (Sơn La)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Mộc Ch&acirc;u&nbsp;l&agrave; một địa danh đ&atilde; trở n&ecirc;n kh&aacute; quen thuộc với d&acirc;n du lịch bụi, h&agrave;ng năm ước t&iacute;nh c&oacute; h&agrave;ng triệu lượt người trẻ đổ về Mộc Ch&acirc;u để thăm quan. Với đường đi kh&aacute; thuận lợi, nhiều điểm thăm quan l&ocirc;i cuốn, Mộc Ch&acirc;u c&oacute; sức h&uacute;t lớn với d&acirc;n phượt từ chuy&ecirc;n nghiệp đến kh&ocirc;ng chuy&ecirc;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-moc-chau-son-la-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-moc-chau-son-la-mixtourist.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn đi xe m&aacute;y từ H&agrave; Nội chạy theo quốc lộ 6, nếu bạn l&agrave; người ưa mạo hiểm v&agrave; kh&aacute;m ph&aacute;, bạn n&ecirc;n chọn đi đường 6 cũ. Đường n&agrave;y tuy đ&atilde; xuống cấp v&agrave; kh&aacute; l&agrave; kh&oacute; đi, tuy nhi&ecirc;n cảnh lại rất đẹp. C&oacute; những con đường uốn lượn chạy theo s&ocirc;ng Đ&agrave;, bạn c&oacute; thấy một số Th&aacute;c nước đẹp, dừng ch&acirc;n b&ecirc;n hồ thủy điện H&ograve;a B&igrave;nh chơi.</p>\r\n<p style=\"text-align: justify;\">\r\n	Đường 6 cũ cũng dẫn bạn qua nhiều bản l&agrave;ng người M&ocirc;ng, v&agrave;o m&ugrave;a xu&acirc;n hoa Đ&agrave;o, Mận nở rất đẹp.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-moc-chau-son-la-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-moc-chau-son-la-mixtourist1.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Thời gian du lịch Mộc Ch&acirc;u n&ecirc;n đi l&agrave; 3 ng&agrave;y hoặc 2,5 ng&agrave;y. Nếu đi 2,5 ng&agrave;y th&igrave; ng&agrave;y đầu ngủ Mai Ch&acirc;u, ng&agrave;y thứ 2 kh&aacute;m ph&aacute; Mộc Ch&acirc;u, ng&agrave;y 3 kết hợp thăm quan một số điểm tr&ecirc;n đường chạy về H&agrave; Nội.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-moc-chau-son-la-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-moc-chau-son-la-mixtourist11.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Mộc Ch&acirc;u nổi tiếng với hoa đẹp, v&agrave; con người th&acirc;n thiện. M&ugrave;a hoa c&oacute; 2 m&ugrave;a, m&ugrave;a thu đ&ocirc;ng (giữa th&aacute;ng 10 đến th&aacute;ng 1 dương) c&oacute; hoa cải v&agrave; d&atilde; quỳ; m&ugrave;a xu&acirc;n (th&aacute;ng 2 v&agrave; 3 dương) c&oacute; hoa mận, đ&agrave;o, hoa mận ở Mộc Ch&acirc;u trắng muốt đẹp hơn rất nhiều c&aacute;c hoa mận ở nơi kh&aacute;c.</p>\r\n<p style=\"text-align: justify;\">\r\n	&gt;&gt;&gt;&gt;Xem th&ecirc;m<a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-bac/du-lich-tay-bac/tour-du-lich-ha-noi--mai-chau-moc-chau-ha-noi.html\"> <strong>tour du lịch M&ocirc;c Ch&acirc;u gi&aacute; rẻ </strong></a>để c&ugrave;ng nhau đi h&aacute;i mận n&agrave;o!</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">2.&nbsp;Mai Ch&acirc;u ( H&ograve;a B&igrave;nh)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	C&aacute;ch H&agrave; Nội khoảng 170km, Mai Ch&acirc;u l&agrave; điểm du lịch văn h&oacute;a được du kh&aacute;ch trong v&agrave; ngo&agrave;i nước đều mong muốn đến thăm.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-mai-chau-hoa-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-mai-chau-hoa-binh-mixtourist3.jpg\" style=\"width: 600px; height: 397px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Mai Ch&acirc;u hiện ra trong mắt kh&aacute;ch du lịch l&agrave; một thung lũng xinh tươi, ng&uacute;t ng&agrave;n m&agrave;u xanh đồng ruộng, ẩn hiện c&ugrave;ng những nếp nh&agrave; nằm n&eacute;p m&igrave;nh trong d&atilde;y n&uacute;i phủ k&iacute;n m&acirc;y mờ. Địa danh nổi tiếng nơi đ&acirc;y ch&iacute;nh l&agrave; bản L&aacute;c, với 100% người d&acirc;n tộc Th&aacute;i sinh sống.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-mai-chau-hoa-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-mai-chau-hoa-binh-mixtourist2.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Mai Ch&acirc;u l&agrave; một điểm đến cho những ai th&iacute;ch du lịch phong cảnh, du lịch nghỉ dưỡng, du lịch văn h&oacute;a.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-mai-chau-hoa-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-mai-chau-hoa-binh-mixtourist1.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-mai-chau-hoa-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-mai-chau-hoa-binh-mixtourist11.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Một điểm đến đầy đủ những phong c&aacute;ch du lịch, đ&aacute;p ứng mọi nhu cầu của nhiều đối tượng v&agrave; sở th&iacute;ch kh&aacute;c nhau. Đ&acirc;y cũng l&agrave; một điểm đến rất ph&ugrave; hợp với Sinh Vi&ecirc;n, c&aacute;c bạn c&oacute; thể tổ chức đốt lửa trại, team building, tổ chức c&aacute;c hoạt động x&atilde; hội gi&uacute;p d&acirc;n cư địa phương.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-mai-chau-hoa-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-mai-chau-hoa-binh-mixtourist.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n<div>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">3. Tam Đảo (Vĩnh Ph&uacute;c)</span></span></h3>\r\n	<p style=\"text-align: justify;\">\r\n		Khu du lịch Tam Đảo nằm tr&ecirc;n d&atilde;y n&uacute;i Tam Đảo ở độ cao tr&ecirc;n 900 m so với mực nước biển. C&aacute;ch thủ đ&ocirc; H&agrave; Nội khoảng 80 km bao gồm 50 km theo quốc lộ 2 v&agrave; khoảng 24 km theo đường quốc lộ 2B trong đ&oacute; c&oacute; 13 km đường đ&egrave;o.&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-tam-dao-son-la-mixtourist1\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-tam-dao-son-la-mixtourist.jpg\" style=\"width: 600px; height: 410px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Nằm ở ph&iacute;a Bắc huyện Tam Đảo, khu du lịch Tam Đảo được bao bọc bởi rừng nguy&ecirc;n sinh Quốc gia Tam Đảo, c&oacute; kh&iacute; hậu trong sạch, m&aacute;t mẻ, nhiệt độ trung b&igrave;nh trong năm khoảng 18&deg;C. L&agrave; điều kiện rất thuận lợi để ph&aacute;t triển c&aacute;c loại h&igrave;nh du lịch nghỉ dưỡng, du lịch sinh th&aacute;i&hellip;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-tam-dao-son-la-mixtourist1\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-tam-dao-son-la-mixtourist111.jpg\" style=\"width: 600px; height: 361px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Từ l&acirc;u Tam Đảo đ&atilde; l&agrave; khu nghỉ m&aacute;t nổi tiếng trong nước, mỗi năm đ&oacute;n h&agrave;ng trăm ng&agrave;n lượt kh&aacute;ch trong nước v&agrave; ngo&agrave;i nước đến tham quan, nghỉ dưỡng, nghi&ecirc;n cứu....</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-tam-dao-son-la-mixtourist1\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-tam-dao-son-la-mixtourist1.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Từ H&agrave; Nội đi ra hướng đường Phạm Văn Đồng (hoặc đường v&agrave;nh đai 3 thẳng l&ecirc;n) sau đ&oacute; đi tới cầu Thăng Long, xe m&aacute;y kh&ocirc;ng được ph&eacute;p đi tr&ecirc;n tầng 2 của cầu n&ecirc;n c&aacute;c bạn ch&uacute; &yacute; theo biển chỉ dẫn đi dưới tầng 1.</p>\r\n	<p style=\"text-align: justify;\">\r\n		Qua cầu Thăng Long, đi thẳng đường Nội B&agrave;i đến ng&atilde; 4 Nam Hồng (c&oacute; cầu vượt bắc ngang qua) c&aacute;c bạn rẽ tr&aacute;i theo hướng đi M&ecirc; Linh (Ph&uacute;c Y&ecirc;n), đi thẳng đường n&agrave;y theo biển chỉ dẫn để tiếp tục đi tới Vĩnh Y&ecirc;n.</p>\r\n	<p style=\"text-align: justify;\">\r\n		Ngay đầu v&agrave;o th&agrave;nh phố sẽ c&oacute; biển chỉ dẫn c&aacute;c bạn đi T&acirc;y Thi&ecirc;n - Tam Đảo.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-tam-dao-son-la-mixtourist1\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-tam-dao-son-la-mixtourist11.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		<strong>Ch&uacute; &yacute;:</strong>&nbsp;Kh&ocirc;ng đi XE GA l&ecirc;n Tam Đảo bởi &nbsp;mức độ nguy hiểm kh&aacute; cao. Xe số khi xuống dốc bản th&acirc;n việc g&agrave;i số thấp (1-2) đ&atilde; l&agrave; một c&aacute;ch để giảm tốc độ kh&aacute; nhiều, xe ga th&igrave; kh&ocirc;ng l&agrave;m được như vậy n&ecirc;n qu&aacute;n t&iacute;nh tạo ra khi xuống dốc rất lớn dẫn tới việc bạn sẽ phải d&ugrave;ng phanh nhiều -&gt; tăng rủi ro của việc ch&aacute;y phanh dẫn tới mất kiểm so&aacute;t v&agrave; xảy ra tai nạn. Thời gian vừa qua rất nhiều vụ tai nạn do đi xe ga khi xuống dốc ở Tam Đảo, nhiều vụ g&acirc;y tử vong hoặc nhẹ hơn th&igrave; g&atilde;y ch&acirc;n g&atilde;y tay. V&igrave; vậy c&aacute;c bạn phượt l&ecirc;n đ&acirc;y phải cực kỳ cẩn thận nha!</p>\r\n	<p style=\"text-align: justify;\">\r\n		&gt;&gt;&gt;&gt;Xem ngay <strong><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-bac/du-lich-quang-ninh/tour-du-lich-ha-noi-dao-quan-lan-ha-noi-.html\">tour du lịch Tam Đảo</a><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-bac/du-lich-ha-noi/tour-du-lich-ha-noi-tam-dao-tay-thien-ha-noi.html\">&nbsp;- T&acirc;y Thi&ecirc;n.</a></strong></p>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">4. H&agrave;m Lợn (H&agrave; Nội)</span></span></h3>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-ham-lon-ha-noi-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ham-lon-ha-noi-mixtourist11111.jpg\" style=\"width: 600px; height: 337px;\" /></p>\r\n</div>\r\n<p style=\"text-align: justify;\">\r\n	Nằm c&aacute;ch H&agrave; Nội 40 km theo hướng cao tốc Bắc Thăng Long - Nội B&agrave;i n&ecirc;n việc di chuyển tới đ&acirc;y kh&ocirc;ng qu&aacute; kh&oacute;, chỉ mất khoảng tr&ecirc;n dưới một giờ đồng hồ. Do vậy bạn c&oacute; thể chinh phục đỉnh n&uacute;i n&agrave;y trong ng&agrave;y hoặc lưu lại l&acirc;u hơn nếu muốn ngắm ho&agrave;ng h&ocirc;n v&agrave; b&igrave;nh minh tr&ecirc;n đỉnh n&uacute;i.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ham-lon-ha-noi-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ham-lon-ha-noi-mixtourist111.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Du kh&aacute;ch c&oacute; thể bắt đầu leo n&uacute;i H&agrave;m Lợn ngay hoặc tham quan hồ N&uacute;i B&agrave;u trước khi l&ecirc;n đường. Hồ N&uacute;i B&agrave;u l&agrave; một hồ nước nằm ngay s&aacute;t ch&acirc;n n&uacute;i, giữa những đồi th&ocirc;ng v&agrave; keo xanh mướt. Ch&iacute;nh vẻ hoang sơ đ&atilde; g&oacute;p phần tạo n&ecirc;n sự quyến rũ cho hồ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ham-lon-ha-noi-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ham-lon-ha-noi-mixtourist11.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	C&oacute; hai con đường l&ecirc;n đỉnh n&uacute;i. Con đường thứ nhất l&agrave; đường m&ograve;n kh&aacute; bằng phẳng, dễ đi v&agrave; &iacute;t bụi rậm. Thời gian di chuyển chỉ mất từ 2 tiếng đến 2 tiếng rưỡi. Con đường thứ hai l&agrave; men theo suối nhiều thử th&aacute;ch ph&ugrave; hợp với những ai th&iacute;ch mạo hiểm v&agrave; kh&aacute;m ph&aacute;.</p>\r\n<p style=\"text-align: justify;\">\r\n	Với thời gian di chuyển khoảng 4 giờ, du kh&aacute;ch sẽ phải băng qua những con suối nhỏ hay vượt qua bụi rậm. Mặc d&ugrave; c&oacute; nhiều đoạn phải tự mở đường cho m&igrave;nh nhưng đ&oacute; lại ch&iacute;nh l&agrave; điểm thu h&uacute;t của con đường chinh phục ngọn n&uacute;i n&agrave;y.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ham-lon-ha-noi-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ham-lon-ha-noi-mixtourist.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Gần đỉnh n&uacute;i c&oacute; hẳn một khu vực kh&aacute; bằng phẳng. Sau khi chinh phục độ cao 462 m l&ecirc;n đỉnh H&agrave;m Lợn, du kh&aacute;ch c&oacute; thể trở lại khu vực n&agrave;y để cắm trại, nghỉ ngơi chuẩn bị cho việc xuống n&uacute;i hoặc chờ đ&oacute;n b&igrave;nh minh hay ho&agrave;ng h&ocirc;n.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">5. H&aacute;ng Tề Chơ &ndash; &quot;Đệ Nhất Th&aacute;c&quot;</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Xa hơn một ch&uacute;t bạn c&oacute; thẻ lựa chọn cho m&igrave;nh địa điểm đ&oacute; l&agrave; H&aacute;ng Tề Chơ. Nơi đ&acirc;y c&oacute; thể n&oacute;i l&agrave; tuyệt vời nếu bạn đi phượt đến đ&acirc;y một lần.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-hang-te-cho-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-hang-te-cho-mixtourist.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	H&aacute;ng Tề Chơ l&agrave; t&ecirc;n của một bản l&agrave;ng người M&ocirc;ng thuộc x&atilde; L&agrave;ng Nh&igrave;, huyện Trạm Tấu, tỉnh Y&ecirc;n B&aacute;i. Đ&acirc;y l&agrave; một trong những cung đường được coi l&agrave; &quot;tử địa&quot; với d&acirc;n phượt mạo hiểm.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-hang-te-cho-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-hang-te-cho-mixtourist1.jpg\" style=\"width: 600px; height: 418px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Nơi đ&acirc;y quanh năm m&acirc;y m&ugrave; bao phủ v&agrave; c&oacute; lẽ cũng hiếm dấu ch&acirc;n người &ldquo;ngoại bang&rdquo; như bao bản l&agrave;ng kh&aacute;c nếu kh&ocirc;ng c&oacute; một con th&aacute;c h&ugrave;ng vĩ v&agrave; đầy huyền thoại: th&aacute;c H&aacute;ng Tề Chơ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-hang-te-cho-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-hang-te-cho-mixtourist11.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">6. M&ugrave; Cang Chải (Y&ecirc;n B&aacute;i)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	M&ugrave; Cang Chải thuộc địa b&agrave;n một huyện v&ugrave;ng cao của tỉnh Y&ecirc;n B&aacute;i, c&aacute;ch H&agrave; Nội chừng 300km về ph&iacute;a T&acirc;y Bắc. Đ&acirc;y l&agrave; một điểm du lịch kh&aacute; quen thuộc với giới trẻ, vẻ thơ mộng v&agrave; h&ugrave;ng vĩ nơi đ&acirc;y c&oacute; sức quyến rũ lạ k&igrave; đối với d&acirc;n phượt.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-mua-cang-chai-yen-bai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-mua-cang-chai-yen-bai-mixtourist11111.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-mua-cang-chai-yen-bai-mixtourist11.jpg\" style=\"width: 600px; height: 314px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-mua-cang-chai-yen-bai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-mua-cang-chai-yen-bai-mixtourist.jpg\" style=\"width: 600px; height: 342px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Những năm gần đ&acirc;y, địa danh n&agrave;y ng&agrave;y c&agrave;ng trở n&ecirc;n gần gũi v&agrave; nổi tiếng hơn kh&ocirc;ng chỉ với kh&aacute;ch du lịch &ldquo;bụi&rdquo; trong nước m&agrave; cả với du kh&aacute;ch nước ngo&agrave;i.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-mua-cang-chai-yen-bai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-mua-cang-chai-yen-bai-mixtourist1111.jpg\" style=\"width: 600px; height: 259px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Quốc lộ 32 chạy dọc theo những sườn n&uacute;i hun h&uacute;t gi&oacute;, con suối đỏ ngầu cuộn chảy, chạy qua đồi th&ocirc;ng v&agrave; những thửa ruộng bậc thang tầng tầng lớp lớp, ng&uacute;t ng&agrave;n l&ecirc;n tận lưng chừng trời. Thị trấn M&ugrave; Căng Chải nhỏ xinh nằm gọn giữa hai sườn n&uacute;i, c&ograve;n kh&aacute; ngh&egrave;o n&agrave;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-mua-cang-chai-yen-bai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-mua-cang-chai-yen-bai-mixtourist1.jpg\" style=\"width: 600px; height: 490px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-mua-cang-chai-yen-bai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-mua-cang-chai-yen-bai-mixtourist111.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Sự h&ugrave;ng vĩ của n&uacute;i rừng, cảnh đẹp cũng những c&aacute;nh đồng l&uacute;a bạt ng&agrave;n ở thung lũng Mường L&ograve; (Nghĩa Lộ), m&ugrave;i thơm ng&agrave;o ngạt của nếp T&uacute; Lệ v&agrave; sự kỳ vĩ của những kiệt t&aacute;c ruộng bậc thang tại M&ugrave; Cang Chải sẽ l&agrave; những trải nghiệm th&uacute; vị m&agrave; bạn kh&oacute; c&oacute; thể qu&ecirc;n.</p>\r\n<h3 helvetica=\"\" style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: OpS, \" text-align:=\"\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">7. Sapa (L&agrave;o Cai)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Sapa vẫn thường được gọi với c&aacute;i t&ecirc;n y&ecirc;u kiều &ldquo;xứ sở sương m&ugrave;&rdquo;, đ&atilde; kh&ocirc;ng c&ograve;n lạ lẫm trong mắt những người y&ecirc;u du lịch. Nơi đ&acirc;y lu&ocirc;n mang n&eacute;t hấp dẫn, ấn tượng ri&ecirc;ng đến kỳ lạ, m&agrave; d&ugrave; c&oacute; đến bao nhi&ecirc;u lần đi nữa vẫn c&ograve;n điều để kh&aacute;m ph&aacute;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-sapa-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-sapa-lao-cai-mixtourist1111.jpg\" style=\"width: 600px; height: 359px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-sapa-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-sapa-lao-cai-mixtourist111.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Sa Pa h&ocirc;m nay c&oacute; thể kh&ocirc;ng c&ograve;n nguy&ecirc;n sơ, tĩnh lặng như trước đ&acirc;y nhưng vẫn đủ sức đưa những tr&aacute;i tim thị th&agrave;nh t&igrave;m về một tho&aacute;ng b&igrave;nh y&ecirc;n sau những ồn &agrave;o, tấp nập của phố thị.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-sapa-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-sapa-lao-cai-mixtouristt.jpg\" style=\"width: 600px; height: 387px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-sapa-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-sapa-lao-cai-mixtourist11111.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Về Sa Pa đ&uacute;ng độ l&uacute;a ch&iacute;n lại c&agrave;ng kh&oacute; l&ograve;ng cưỡng lại trước vẻ đẹp rực rỡ, h&ugrave;ng vĩ, ng&uacute;t ng&agrave;n tầm mắt của những thửa ruộng bậc thang. Từ trung t&acirc;m thị trấn Sa Pa đi c&aacute;c ngả Tả Ph&igrave;n, Tả Van, Trung Chải,... đ&acirc;u đ&acirc;u cũng ngạt ng&agrave;o hương l&uacute;a ch&iacute;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-sapa-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-sapa-lao-cai-mixtourist.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-sapa-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-sapa-lao-cai-mixtourist1.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-sapa-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-sapa-lao-cai-mixtourist11.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	C&aacute;c tay l&aacute;i sẽ kh&ocirc;ng &iacute;t lần phải dừng lại để ngắm nh&igrave;n, chi&ecirc;m ngưỡng phong cảnh thi&ecirc;n nhi&ecirc;n đẹp như tranh vẽ. Sapa l&agrave; điểm đến l&iacute; tưởng cho những ai th&iacute;ch sự huyền b&iacute; v&agrave; một ch&uacute;t g&igrave; đ&oacute; nhẹ nh&agrave;ng n&ecirc;n thơ.</p>\r\n<p style=\"text-align: justify;\">\r\n	&gt;&gt;&gt;&gt;Xem th&ecirc;m<strong> <a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-bac/du-lich-sapa/tour-du-lich-ha-noi-sapa-cao-toc-ha-noi.html\">tour du lịch Sa Pa gi&aacute; rẻ 2017!</a></strong></p>\r\n<h3 helvetica=\"\" style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: OpS, \" text-align:=\"\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">8. Hồ Pa Khoang (Điện Bi&ecirc;n)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Hồ Pa Khoang thuộc địa phận x&atilde; Mường Phăng, huyện Điện Bi&ecirc;n, tỉnh Điện Bi&ecirc;n, c&aacute;ch th&agrave;nh phố Điện Bi&ecirc;n Phủ gần 20km.&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Hồ P&aacute; Khoang nằm giữa một v&ugrave;ng thi&ecirc;n nhi&ecirc;n cảnh đẹp h&ugrave;ng vĩ, ẩn hiện trong m&acirc;y trời non nước.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ho-pa-khoang-dien-bien-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-pa-khoang-dien-bien-mixtourist1.jpg\" style=\"width: 600px; height: 380px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	V&agrave;o m&ugrave;a đ&ocirc;ng sương mờ bu&ocirc;ng phủ tạo một phong cảnh huyền ảo, thấp tho&aacute;ng nơi xa l&agrave; những d&atilde;y n&uacute;i trập tr&ugrave;ng, những nếp nh&agrave; xinh xắn.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ho-pa-khoang-dien-bien-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-pa-khoang-dien-bien-mixtourist11.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	M&ugrave;a h&egrave; kh&ocirc;ng kh&iacute; nơi đ&acirc;y thật dễ chịu với những luồng gi&oacute; nam m&aacute;t dịu. Tất cả tạo n&ecirc;n một khung cảnh &ecirc;m đềm, thơ mộng, một thắng cảnh quyến rũ l&ograve;ng người.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ho-pa-khoang-dien-bien-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-pa-khoang-dien-bien-mixtourist11111.jpg\" style=\"width: 600px; height: 385px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Trong khu vực l&ograve;ng hồ c&oacute; c&aacute;c bản d&acirc;n tộc Th&aacute;i, Khơ M&uacute; l&agrave; những d&acirc;n tộc c&ograve;n giữ được những phong tục tập qu&aacute;n của c&aacute;c d&acirc;n tộc v&ugrave;ng T&acirc;y Bắc. N&eacute;t đẹp văn h&oacute;a l&agrave; điều kh&oacute; bỏ qua khi đến đ&acirc;y.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ho-pa-khoang-dien-bien-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-pa-khoang-dien-bien-mixtourist111.jpg\" style=\"text-align: center; width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Tuy nhi&ecirc;n cuộc h&agrave;nh tr&igrave;nh l&ecirc;n với hồ P&aacute; Khoang kh&ocirc;ng hề đơn giản, bởi đường đi nhiều kh&uacute;c khủy, đ&egrave;o dốc cao v&agrave; kh&oacute; đi. Nơi đ&acirc;y d&agrave;nh cho những d&acirc;n phượt chuy&ecirc;n nghiệp, ưa mạo hiểm.</p>\r\n<div>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">9. Vườn Quốc gia Xu&acirc;n Sơn (Ph&uacute; Thọ)</span></span></h3>\r\n	<p style=\"text-align: justify;\">\r\n		Nằm ở phần cuối của d&atilde;y Ho&agrave;ng Li&ecirc;n Sơn tr&ecirc;n địa b&agrave;n huyện T&acirc;n Sơn - tỉnh Ph&uacute; Thọ, Vườn Quốc gia (VQG) Xu&acirc;n Sơn c&oacute; diện t&iacute;ch tr&ecirc;n 15.000 ha, c&oacute; nhiều gi&aacute; trị đặc sắc về mặt cảnh quan, m&ocirc;i trường, t&agrave;i nguy&ecirc;n, động thực vật đa dạng, phong ph&uacute; v&agrave; những n&eacute;t văn h&oacute;a độc đ&aacute;o của đồng b&agrave;o d&acirc;n tộc thiểu số trong v&ugrave;ng.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-vuon-quoc-gia-xuan-son-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vuon-quoc-gia-xuan-son-mixtourist.jpg\" style=\"width: 600px; height: 445px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		VQG Xu&acirc;n Sơn c&aacute;ch H&agrave; Nội 120km, c&aacute;ch TP Việt Tr&igrave; 80km, ph&iacute;a t&acirc;y nam tiếp gi&aacute;p với tỉnh H&ograve;a B&igrave;nh, ph&iacute;a t&acirc;y bắc gi&aacute;p tỉnh Sơn La.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-vuon-quoc-gia-xuan-son-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vuon-quoc-gia-xuan-son-mixtourist11111.jpg\" style=\"width: 600px; height: 381px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Nơi đ&acirc;y hội nhiều yếu tố thuận lợi để thu h&uacute;t du kh&aacute;ch đến tham quan. Thế mạnh của VQG l&agrave; gi&aacute; trị cảnh quan, gi&aacute; trị sinh th&aacute;i, đặc biệt l&agrave; sự nguy&ecirc;n sơ của một v&ugrave;ng đất vốn chưa bị t&aacute;c động nhiều từ b&ecirc;n ngo&agrave;i.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-vuon-quoc-gia-xuan-son-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vuon-quoc-gia-xuan-son-mixtourist.png\" style=\"width: 600px; height: 399px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Từ H&agrave; nội đi qua Sơn t&acirc;y theo đường 32 tới Thanh Sơn, chạy tiếp tới ng&atilde; ba c&oacute; c&aacute;i cầu đi Ph&uacute; Thọ th&igrave; trước cầu rẽ tr&aacute;i l&agrave; lối đi Cổ Tiết, chạy theo đường QL xuy&ecirc;n qua khu rừng trồng phi lao v&agrave; cả ch&egrave; bạn sẽ nh&igrave;n thấy biển chỉ đường tới &ldquo;Vườn Quốc Gia Xu&acirc;n Sơn&rdquo;.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-vuon-quoc-gia-xuan-son-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vuon-quoc-gia-xuan-son-mixtourist111.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">10. Đồng Cao (Bắc Giang)</span></span></h3>\r\n	<p style=\"text-align: justify;\">\r\n		Đồng Cao l&agrave; một địa điểm mới được d&acirc;n du lịch bụi kh&aacute;m ph&aacute; gần đ&acirc;y, n&ecirc;n chưa thể tiếp cận nếu đi du lịch theo tour kiểu truyền thống.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-dong-cao-bac-giang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dong-cao-bac-giang-mixtourist.jpg\" style=\"width: 600px; height: 370px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Hiện tại mới chỉ c&oacute; c&aacute;c nh&oacute;m du lịch bụi bằng xe m&aacute;y l&agrave; c&oacute; thể đi đến cao nguy&ecirc;n n&agrave;y.&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-dong-cao-bac-giang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dong-cao-bac-giang-mixtourist111.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Nằm c&aacute;ch H&agrave; Nội khoảng 150 km v&agrave; thuộc địa phận tỉnh Bắc Giang, qu&atilde;ng đường di chuyển tương đối kh&oacute; đi v&agrave; cần nhiều kỹ năng l&aacute;i xe, b&ugrave; lại bạn sẽ kh&ocirc;ng thể n&agrave;o qu&ecirc;n khoảnh khắc đ&oacute;n &aacute;nh b&igrave;nh minh tr&ecirc;n cao nguy&ecirc;n xanh mướt.&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-dong-cao-bac-giang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dong-cao-bac-giang-mixtourist11.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-dong-cao-bac-giang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dong-cao-bac-giang-mixtourist2.jpg\" style=\"width: 600px; height: 422px;\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-dong-cao-bac-giang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dong-cao-bac-giang-mixtourist1.JPG\" style=\"width: 600px; height: 394px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Cắm trại, đốt lửa, ngắm sao v&agrave; đ&oacute;n &aacute;nh b&igrave;nh m&igrave;nh l&agrave; hoạt động được y&ecirc;u th&iacute;ch nhất.</p>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">11. Th&aacute;c Bản Giốc (Cao Bằng)</span></span></h3>\r\n	<p style=\"text-align: justify;\">\r\n		Th&aacute;c Bản Giốc được đ&aacute;nh gi&aacute; l&agrave; th&aacute;c nước xuy&ecirc;n bi&ecirc;n giới lớn thứ 2 tr&ecirc;n thế giới, nằm giữa bi&ecirc;n giới Việt &ndash; Trung, được chia th&agrave;nh th&aacute;c ch&iacute;nh v&agrave; th&aacute;c phụ v&agrave; ngăn c&aacute;ch bởi cột mốc 53 theo c&ocirc;ng ước Ph&aacute;p &ndash; Thanh từ năm 1887.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-thac-ban-gioc-cao-bang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-thac-ban-gioc-cao-bang-mixtourist.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Hiện nay, to&agrave;n bộ phần th&aacute;c phụ thuộc về l&atilde;nh thổ Việt Nam v&agrave; thuộc địa phận&nbsp;x&atilde; &ETH;&agrave;m Thủy, huyện Tr&ugrave;ng Kh&aacute;nh, Cao Bằng. C&ograve;n phần th&aacute;c ch&iacute;nh th&igrave; được chia đ&ocirc;i.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-thac-ban-gioc-cao-bang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-thac-ban-gioc-cao-bang-mixtourist1.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Th&aacute;ng 8 l&agrave; thời điểm du lịch th&aacute;c Bản Giốc th&iacute;ch hợp v&agrave; tuyệt vời nhất. Bởi đ&acirc;y l&agrave; l&uacute;c th&aacute;c h&ugrave;ng vĩ v&agrave; đẹp nhất, đứng từ tr&ecirc;n cao nh&igrave;n xuống c&ograve;n c&oacute; thể thấy cầu vồng.&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-thac-ban-gioc-cao-bang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-thac-ban-gioc-cao-bang-mixtourist2.jpg\" style=\"width: 600px; height: 394px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Theo những phượt thủ chuy&ecirc;n nghiệp, th&igrave; từ H&agrave; Nội c&aacute;c bạn c&oacute; thể l&ecirc;n th&aacute;c Bản Giốc theo&nbsp;hướng dẫn c&aacute;ch di chuyển v&agrave; lộ tr&igrave;nh tham quan th&aacute;c Bản Giốc từ H&agrave; Nội bằng xe m&aacute;y&nbsp;theo 1 trong 3 cung đường sau đ&acirc;y:</p>\r\n	<ul>\r\n		<li style=\"text-align: justify;\">\r\n			Theo QL 1: H&agrave; Nội &ndash; Thất Kh&ecirc; &ndash; Tr&ugrave;ng Kh&aacute;nh &ndash; Bản Giốc (qu&atilde;ng đường khoảng 340 km).</li>\r\n		<li style=\"text-align: justify;\">\r\n			Theo QL 3 v&agrave; QL 1B&nbsp;: H&agrave; Nội &ndash; Th&aacute;i Nguy&ecirc;n &ndash; Thất Kh&ecirc; &ndash; Tr&ugrave;ng Kh&aacute;nh &ndash; Bản Giốc. Từ H&agrave; Nội đến Th&aacute;i Nguy&ecirc;n c&aacute;c bạn c&oacute; thể đi QL3 mới để r&uacute;t ngắn lộ tr&igrave;nh.</li>\r\n		<li style=\"text-align: justify;\">\r\n			Theo QL 3: H&agrave; Nội &ndash; Th&aacute;i Nguy&ecirc;n &ndash; Bắc Kạn &ndash; Cao Bằng &ndash; Tr&ugrave;ng Kh&aacute;nh &ndash; Bản Giốc.</li>\r\n	</ul>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-thac-ban-gioc-cao-bang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-thac-ban-gioc-cao-bang-mixtourist2.jpg\" style=\"width: 600px; height: 394px;\" /></p>\r\n	<p>\r\n		&gt;&gt;&gt;&gt;Xem ngay <strong><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-bac/du-lich-tay-bac/tour-du-lich-ha-noi-ho-ba-be-cao-bang-lang-son-ha-noi.html\">tour du lịch Hồ Ba Bể - Cao Bằng - Lạng Sơn.</a></strong></p>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">12. Vườn quốc gia Ba V&igrave; (H&agrave; Nội)</span></span></h3>\r\n	<p style=\"text-align: justify;\">\r\n		Đi đến Ba V&igrave; c&oacute; lẽ l&agrave; h&agrave;nh tr&igrave;nh đơn giản nhất trong danh s&aacute;ch, với khoảng c&aacute;ch chỉ 50 km, chuyến đi n&agrave;y ho&agrave;n to&agrave;n mang t&iacute;nh chất thư gi&atilde;n.&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-vuon-quoc-gia-ba-vi-ha-noi-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vuon-quoc-gia-ba-vi-ha-noi-mixtourist2.jpg\" style=\"width: 600px; height: 341px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Kh&ocirc;ng kh&iacute; m&aacute;t mẻ của Ba V&igrave; sẽ đem lại cho bạn cảm gi&aacute;c thư th&aacute;i nhất.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-vuon-quoc-gia-ba-vi-ha-noi-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vuon-quoc-gia-ba-vi-ha-noi-mixtourist.png\" style=\"width: 600px; height: 334px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Tại đ&acirc;y kh&aacute;ch du lịch thường đến thăm đền thờ B&aacute;c Hồ, đền Thượng, khu nh&agrave; thờ cổ v&agrave; c&aacute;c vườn phong lan.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-vuon-quoc-gia-ba-vi-ha-noi-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vuon-quoc-gia-ba-vi-ha-noi-mixtourist1.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Ba V&igrave; cũng thường được chọn l&agrave;m nơi chụp ảnh cưới do c&oacute; phong cảnh rất l&atilde;ng mạn v&agrave; gần với thi&ecirc;n nhi&ecirc;n. Bạn cũng n&ecirc;n kết hợp tham quan vườn quốc gia Ba V&igrave; c&ugrave;ng với c&aacute;c khu du lịch dưới ch&acirc;n n&uacute;i.</p>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">13. Hồ Thung Nai (H&ograve;a B&igrave;nh)</span></span></h3>\r\n	<p style=\"text-align: justify;\">\r\n		C&aacute;ch H&agrave; Nội khoảng 100km, Thung Nai (một x&atilde; thuộc Cao Phong, H&ograve;a B&igrave;nh) l&agrave; một điểm du lịch l&yacute; tưởng cho c&aacute;c d&acirc;n phượt v&agrave;o m&ugrave;a h&egrave; bởi kh&iacute; hậu v&ocirc; c&ugrave;ng m&aacute;t mẻ.&nbsp;&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-ho-thung-nai-quang-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-thung-nai-quang-binh-mixtourist.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Bạn c&oacute; thể tới Thung Nai bằng c&aacute;ch đi xe m&aacute;y từ H&agrave; Nội hoặc&nbsp;đi xe kh&aacute;ch tới H&ograve;a B&igrave;nh&nbsp;rồi thu&ecirc; xe &ocirc;m hoặc taxi để v&agrave;o Thung Nai.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-ho-thung-nai-quang-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-thung-nai-quang-binh-mixtourist1.jpg\" style=\"width: 600px; height: 252px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		N&ecirc;n tới Thung Nai v&agrave;o m&ugrave;a h&egrave;, khoảng th&aacute;ng 5 đến th&aacute;ng 8 để tận hưởng cảm gi&aacute;c m&aacute;t mẻ, xa rời với c&aacute;i n&oacute;ng oi bức của miền Bắc v&agrave;o dịp n&agrave;y.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-ho-thung-nai-quang-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-thung-nai-quang-binh-mixtourist11.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		V&agrave;o c&aacute;c m&ugrave;a kh&aacute;c bạn cũng c&oacute; thể đến Thung Nai, tuy nhi&ecirc;n sẽ kh&oacute; để c&oacute; thể cởi bỏ quần &aacute;o v&agrave; nhảy ầm xuống d&ograve;ng nước m&aacute;t lạnh của l&ograve;ng hồ được đ&acirc;u. Nếu c&oacute; thể th&igrave; h&atilde;y lựa chọn ng&agrave;y đi v&agrave;o đ&uacute;ng ng&agrave;y rằm, buổi tối ngắm trăng giữa m&ecirc;nh m&ocirc;ng biển nước l&agrave; một trải nghiệm kh&aacute; th&uacute; vị cho bạn đấy.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-ho-thung-nai-quang-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-thung-nai-quang-binh-mixtourist111.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Nằm ngay tr&ecirc;n l&ograve;ng hồ thủy điện H&ograve;a B&igrave;nh n&ecirc;n bạn đừng bỏ lỡ cơ hội tham quan c&aacute;c điểm du lịch l&ograve;ng hồ nh&eacute;. Một số địa điểm nổi tiếng như&nbsp;Động Th&aacute;c Bờ,&nbsp;đền B&agrave; Ch&uacute;a Th&aacute;c Bờ, Suối Trạch &hellip;. đều c&aacute;ch nơi bạn nghỉ khoảng 20-30&prime; đi thuyền.</p>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"font-size:14px;\"><span style=\"color: rgb(0, 0, 255);\">14. Vịnh Lan Hạ (Hải Ph&ograve;ng)</span></span></h3>\r\n	<p style=\"text-align: justify;\">\r\n		Chẳng c&ograve;n g&igrave; th&uacute; vị hơn khi c&oacute; thể tự tay ch&egrave;o thuyền kayak trong vịnh Lan Hạ, C&aacute;t B&agrave;, Hải Ph&ograve;ng.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-vinh-lan-ha-hai-phong-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vinh-lan-ha-hai-phong-mixtourist111.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Cảm gi&aacute;c tự do giữa s&ocirc;ng nước v&agrave; th&aacute;m hiểm những bờ c&aacute;t hoang vắng kh&ocirc;ng một b&oacute;ng người l&agrave; điều h&aacute;o hức nhất đối với d&acirc;n du lịch bụi. Mức gi&aacute; cho c&aacute;c hoạt động n&agrave;y kh&ocirc;ng hề lớn nếu bạn tự chuẩn bị đồ ăn v&agrave; lều để ngủ qua đ&ecirc;m.&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-vinh-lan-ha-hai-phong-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vinh-lan-ha-hai-phong-mixtourist.jpg\" style=\"width: 600px; height: 354px;\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-vinh-lan-ha-hai-phong-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vinh-lan-ha-hai-phong-mixtourist1.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Nếu bạn c&oacute; sức khỏe v&agrave; kh&ocirc;ng sợ s&ocirc;ng nước, h&atilde;y thử tham gia v&agrave; cảm nhận chuyến đi m&agrave; c&oacute; thể l&agrave;m c&aacute;nh tay của bạn mỏi nhừ cả ng&agrave;y sau đ&oacute;.</p>\r\n	<p style=\"text-align: justify;\">\r\n		&gt;&gt;&gt;&gt;Xem th&ecirc;m <strong><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-bac/du-lich-hai-phong/tour-du-lich-ha-noi-cat-ba.html\">tour du lịch C&aacute;t B&agrave;, Hải Ph&ograve;ng.</a></strong></p>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">15. Trekking T&acirc;y Y&ecirc;n Tử (Quảng Ninh)</span></span></h3>\r\n	<p style=\"text-align: justify;\">\r\n		<strong>T&acirc;y Y&ecirc;n Tử&nbsp;</strong>c&oacute; lẽ l&agrave; địa điểm khiến d&acirc;n du lịch bụi phải đi bộ với h&agrave;nh tr&igrave;nh d&agrave;i nhất, với khoảng 20 km đường n&uacute;i để đến Am Ngọa V&acirc;n v&agrave; ch&ugrave;a Hồ Thi&ecirc;n.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-yen-tu-quang-ninh-mixtourist1\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-yen-tu-quang-ninh-mixtourist11.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-yen-tu-quang-ninh-mixtourist1\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-yen-tu-quang-ninh-mixtourist1.jpg\" style=\"width: 600px; height: 328px;\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-yen-tu-quang-ninh-mixtourist1\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-yen-tu-quang-ninh-mixtourist.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Để thực hiện chuyến đi, bạn nhất thiết phải c&oacute; một thể lực tốt, sự chuẩn bị kỹ c&agrave;ng v&agrave; một ch&uacute;t t&igrave;m hiểu về văn h&oacute;a t&acirc;m linh để cảm nhận h&agrave;nh tr&igrave;nh một c&aacute;ch s&acirc;u sắc nhất.</p>\r\n	<p style=\"text-align: justify;\">\r\n		&gt;&gt;&gt;&gt;Xem th&ecirc;m <strong><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-bac/tour-du-lich-le-hoi/tour-du-lich-ha-noi-yen-tu--ba-vang--cai-bau-cua-ong--co-be-cua-suot--ha-noi.html\">tour du lịch lễ hội tỉnh Quảng Ninh.</a></strong></p>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">16. B&atilde;i D&agrave;i (Quảng Ninh)</span></span></h3>\r\n	<p style=\"text-align: justify;\">\r\n		B&atilde;i D&agrave;i l&agrave; một bờ biển kh&aacute; vắng vẻ tại V&acirc;n Đồn, Quảng Ninh c&aacute;ch H&agrave; Nội khoảng hơn 200 km. Nếu đi với t&iacute;nh chất du lịch bụi, bạn n&ecirc;n chuẩn bị lều, v&agrave; t&uacute;i ngủ để qua đ&ecirc;m tại bờ biển.</p>\r\n	<p style=\"text-align: justify;\">\r\n		&gt;&gt;&gt;&gt;Xem th&ecirc;m <strong><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-bac/du-lich-quang-ninh/tour-du-lich-ha-noi-dao-co-to-ha-noi-.html\">tour du lịch C&ocirc; T&ocirc;, Quảng Ninh.</a></strong></p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-bai-dai-quang-ninh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bai-dai-quang-ninh-mixtourist.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Sẽ rất tuyệt nếu tắm biển với một buổi chiều nắng ấm v&agrave; ngắm nh&igrave;n kỳ quan thi&ecirc;n nhi&ecirc;n vịnh Hạ Long trong &aacute;nh ho&agrave;ng h&ocirc;n.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-bai-dai-quang-ninh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bai-dai-quang-ninh-mixtourist1.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Đồ hải sản ở đ&acirc;y kh&aacute; rẻ v&agrave; bạn cũng c&oacute; thể dễ d&agrave;ng tổ chức một bữa tiệc nướng nhỏ ngo&agrave;i trời.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-bai-dai-quang-ninh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bai-dai-quang-ninh-mixtourist11.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-bai-dai-quang-ninh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bai-dai-quang-ninh-mixtourist111.jpg\" style=\"width: 600px; height: 600px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Nếu đ&atilde; đến B&atilde;i D&agrave;i, bạn cũng n&ecirc;n đến thăm một thiền viện lớn ở cuối b&atilde;i biển n&agrave;y, nơi đ&acirc;y rất th&iacute;ch hợp để đ&oacute;n b&igrave;nh minh. Phương tiện di chuyển được y&ecirc;u th&iacute;ch để đến đ&acirc;y l&agrave; xe m&aacute;y.</p>\r\n	<p style=\"text-align: justify;\">\r\n		&gt;&gt;&gt;&gt;Xem th&ecirc;m c&aacute;c<strong><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-bac/du-lich-quang-ninh/\"> tour du lịch biển Quảng Ninh.</a></strong></p>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">17. B&igrave;nh Li&ecirc;u (Quảng Ninh)</span></span></h3>\r\n	<p style=\"text-align: justify;\">\r\n		Bình Li&ecirc;u là m&ocirc;̣t trong những cung phượt&nbsp;mới được n&ocirc;̉i l&ecirc;n&nbsp;chừng một tháng&nbsp;trở lại đ&acirc;y tr&ecirc;n các di&ecirc;̃n đàn, group phượt.&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-binh-lieu-quang-ninh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-binh-lieu-quang-ninh-mixtourist11.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Bạn đi theo tuy&ecirc;́n xe khách Hà N&ocirc;̣i &ndash; Móng Cái tức từ Hà N&ocirc;̣i chạy ra QL5 rẽ vào QL1 ( đi hướng v&ecirc;̀ Lạng Sơn) tới c&acirc;̀u 18 ( Bắc Ninh) rẽ phải đường v&ecirc;̀ Phả Lại qua Sao Đỏ, U&ocirc;ng Bí, Hòn Gai, C&acirc;̉m Phả, M&ocirc;ng Dương, đ&ecirc;́n ngã 3 Ti&ecirc;n Y&ecirc;n rẽ trái khoảng 28km v&ecirc;̀ hướng Hoành M&ocirc; là đ&ecirc;́n Bình Li&ecirc;u.</p>\r\n	<p style=\"text-align: justify;\">\r\n		&gt;&gt;&gt;&gt;Xem ngay <strong><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-bac/du-lich-quang-ninh/tour-du-lich-ha-noi-bien-tra-co-mong-cai-ha-noi-.html\">tour du lịch Tr&agrave; Cổ, Quảng Ninh.</a></strong></p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-binh-lieu-quang-ninh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-binh-lieu-quang-ninh-mixtourist1.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Với thời tiết của B&igrave;nh Li&ecirc;u, c&aacute;c bạn c&oacute; thể chọn đi phượt quanh năm.</p>\r\n	<p style=\"text-align: justify;\">\r\n		&ndash; M&ugrave;a xu&acirc;n c&oacute; c&aacute;c lễ hội như lễ hội Đ&igrave;nh Lục N&agrave; (từ 16-18 th&aacute;ng Gi&ecirc;ng &Acirc;m lịch, Hội h&aacute;t So&oacute;ng Cọ giao duy&ecirc;n ng&agrave;y 16/3 &acirc;m lịch, ng&agrave;y &ldquo;Ki&ecirc;ng gi&oacute;&rdquo; ng&agrave;y 4/4 &acirc;m lịch, c&aacute;c ng&agrave;y hội đều đậm chất &ldquo;chợ t&igrave;nh&rdquo; v&ugrave;ng cao), ngo&agrave;i ra c&ograve;n c&oacute; m&ugrave;a hoa Trẩu cuối th&aacute;ng 3, đầu th&aacute;ng 4 kh&aacute; đẹp&hellip;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-binh-lieu-quang-ninh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-binh-lieu-quang-ninh-mixtourist111.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		&ndash; M&ugrave;a h&egrave; v&agrave; m&ugrave;a thu l&agrave; m&ugrave;a của những th&aacute;c Khe Vằn, Khe Tiền, S&ocirc;ng Mo&oacute;c đầy nước, ruộng bậc thang xanh v&agrave; m&ugrave;a lúa vàng gặt th&aacute;ng 7 và tháng 12&hellip; với nhiều loại hoa rừng khoe sắc.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-binh-lieu-quang-ninh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-binh-lieu-quang-ninh-mixtourist1111.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		&ndash; Đầu m&ugrave;a đ&ocirc;ng, khi thời tiết chuyển m&ugrave;a c&oacute; l&uacute;a v&agrave;ng v&agrave;&nbsp;hoa sở, lễ mừng cơm mới &hellip; n&ecirc;́u gặp may mắn thì bạn có trải nghi&ecirc;̣m tuy&ecirc;́t rơi ở Bình Li&ecirc;u.&nbsp;V&agrave; nhiều điều th&uacute; vị kh&aacute;c.</p>\r\n	<p style=\"text-align: justify;\">\r\n		&gt;&gt;&gt;&gt;Xem th&ecirc;m <strong><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-bac/du-lich-quang-ninh/tour-du-lich-ha-noi-dao-quan-lan-ha-noi-.html\">tour du lịch Quan Lạn, Quảng Ninh</a></strong></p>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">19. Hồ N&uacute;i Cốc (Th&aacute;i Nguy&ecirc;n)</span></span></h3>\r\n	<p style=\"text-align: justify;\">\r\n		C&oacute; thể với nhiều người&nbsp;Hồ N&uacute;i Cốc&nbsp;chỉ l&agrave; một hồ nước ngọt nh&acirc;n tạo kh&ocirc;ng c&oacute; g&igrave; đặc biệt. Nhưng đ&acirc;y l&agrave; l&agrave; một địa điểm du lịch giải tỏa stress ng&agrave;y cuối tuần rất tuyệt vời.&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-ho-nui-coc-thai-nguyen-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-nui-coc-thai-nguyen-mixtourist.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Du lịch Hồ N&uacute;i Cốc&nbsp;kh&ocirc;ng đơn thuần l&agrave; nghỉ ngơi, tĩnh t&acirc;m m&agrave; c&ograve;n l&agrave; cơ hội để du kh&aacute;ch đắm m&igrave;nh trong kh&ocirc;ng gian huyền thoại về t&igrave;nh y&ecirc;u tuyệt đẹp của N&agrave;ng C&ocirc;ng v&agrave; Ch&agrave;ng Cốc.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-ho-nui-coc-thai-nguyen-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-nui-coc-thai-nguyen-mixtourist111.jpg\" style=\"width: 600px; height: 354px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Theo những du kh&aacute;ch gi&agrave;u&nbsp;kinh nghiệm du lịch Hồ N&uacute;i Cốc tự t&uacute;c kh&aacute;m ph&aacute;&nbsp;th&igrave; bạn n&ecirc;n đi v&agrave;o khoảng thời gian từ th&aacute;ng 3 đến th&aacute;ng 9.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-ho-nui-coc-thai-nguyen-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-nui-coc-thai-nguyen-mixtourist1111.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Hồ N&uacute;i Cốc c&aacute;ch H&agrave; Nội khoảng 100km v&agrave; c&aacute;ch trung t&acirc;m TP.Th&aacute;i Nguy&ecirc;n khoảng 16km.&nbsp;Bạn đi theo đường cao tốc H&agrave; Nội-Th&aacute;i Nguy&ecirc;n cũ (qua s&acirc;n bay Nội B&agrave;i) l&ecirc;n TP.Th&aacute;i Nguy&ecirc;n. Sau đ&oacute; bạn c&oacute; thể đi theo lộ tr&igrave;nh sau:</p>\r\n	<ul>\r\n		<li style=\"text-align: justify;\">\r\n			<p>\r\n				TP.Th&aacute;i Nguy&ecirc;n &ndash; Đ&aacute;n &ndash; T&acirc;n Cương &ndash; Hồ N&uacute;i Cốc: Nếu đi theo lộ tr&igrave;nh n&agrave;y bạn sẽ c&oacute; cơ hội ngắm nh&igrave;n những đồi ch&egrave; T&acirc;n Cương bạt ng&agrave;n, trải rộng 2 b&ecirc;n đường đi. Nếu may mắn đi v&agrave;o đ&uacute;ng m&ugrave;a ch&egrave; th&igrave; bạn sẽ bị những đồi ch&egrave; thoai thoải, xanh rờn ở đ&acirc;y h&uacute;t hồn.</p>\r\n		</li>\r\n		<li style=\"text-align: center;\">\r\n			<img alt=\"Khu trưng bày các sản phẩm làng nghề truyền thống” trên đảo Núi Cái\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-nui-coc-thai-nguyen-mixtourist11.jpg\" style=\"width: 600px; height: 399px;\" /></li>\r\n	</ul>\r\n	<p style=\"text-align: justify;\">\r\n		Một điểm dừng ch&acirc;n kh&aacute; l&yacute; th&uacute; ở Hồ N&uacute;i Cốc m&agrave; &iacute;t ai biết đến, đ&oacute; l&agrave;&nbsp;&ldquo;Khu trưng b&agrave;y c&aacute;c sản phẩm l&agrave;ng nghề truyền thống&rdquo; tr&ecirc;n đảo N&uacute;i C&aacute;i (đảo lớn nhất Hồ N&uacute;i Cốc). Khi l&ecirc;n đảo bạn sẽ phải leo 108 bậc thang để l&ecirc;n thăm Nh&agrave; Cổ 200 tuổi bằng gỗ lim v&agrave; chi&ecirc;m ngưỡng hơn 1000 hiện vật đại diện cho 90 l&agrave;ng nghề truyền thống tr&ecirc;n cả nước.</p>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">20. Hồ Quan Sơn (H&agrave; Nội)</span></span></h3>\r\n	<p style=\"text-align: justify;\">\r\n		Hồ Quan Sơn&nbsp;thuộc huyện Mỹ Đức&nbsp;l&agrave; một&nbsp;điểm du lịch cuối tuần hấp dẫn, được nhiều người lựa chọn cho chuyến&nbsp;du lịch&nbsp;quanh H&agrave; Nội 1 ng&agrave;y. Hồ c&oacute; phong cảnh tuyệt đẹp với những ngọn n&uacute;i đ&aacute; v&ocirc;i b&ecirc;n mặt hồ, c&ugrave;ng thảm thực vật phong ph&uacute; tr&ecirc;n mặt nước như sen, trang trang&hellip;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-ho-quan-son-ha-noi-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-quan-son-ha-noi-mixtourist111.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Đặc biệt, nếu như bạn đi v&agrave;o m&ugrave;a hoa sen nở vừa c&oacute; thể leo n&uacute;i v&agrave; dạo quanh hồ ngắm vườn sen tr&agrave;n ngập tr&ecirc;n mặt hồ tuyệt đẹp.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-ho-quan-son-ha-noi-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-quan-son-ha-noi-mixtourist1.jpg\" style=\"width: 600px; height: 395px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Bạn đi phượt bằng xe m&aacute;y n&ecirc;n đi theo đường&nbsp;qua thị trấn V&acirc;n Đ&igrave;nh -&gt; men theo đ&ecirc; -&gt; tới ng&atilde; tư thị trấn Đại Nghĩa đi thẳng qua c&aacute;nh đồng l&agrave; tới Hồ Quan Sơn.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-ho-quan-son-ha-noi-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ho-quan-son-ha-noi-mixtourist.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Tới Hồ Quan Sơn, bạn mua gi&aacute; v&eacute; tham quan l&agrave; 25.000đ/người, 1 thuyền chở khoảng 4 người. Dạo một v&ograve;ng xung quanh hồ v&agrave; thưởng thức khung cảnh s&ocirc;ng nướng, n&uacute;i rừng tuyệt đẹp.</p>\r\n	<h3 style=\"text-align: justify;\">\r\n		<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">21. C&ocirc;n Sơn Kiếp Bạc (Hải Dương)</span></span></h3>\r\n	<p style=\"text-align: justify;\">\r\n		C&ocirc;n Sơn &ndash; Kiếp Bạc&nbsp;l&agrave; địa danh du lịch nổi tiếng của Hải Dương, đến với nơi đ&acirc;y bạn kh&ocirc;ng chỉ tham quan c&aacute;c di t&iacute;ch lịch sử m&agrave; c&ograve;n được h&ograve;a m&igrave;nh v&agrave;o thi&ecirc;n nhi&ecirc;n hữu t&igrave;nh.&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-con-son-kiep-bac-hai-duong-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-con-son-kiep-bac-hai-duong-mixtourist.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Để đến được đ&acirc;y, c&aacute;c phượt thủ đi theo lộ tr&igrave;nh di chuyển từ H&agrave; Nội du lịch C&ocirc;n Sơn &ndash; Kiếp Bạc: Cầu Thanh Tr&igrave; -&gt; Đi thẳng l&ecirc;n đường I -&gt; rẽ sang đường 18 hướng đi Phả Lại -&gt; Đi thẳng đến cầu Phả Lại -&gt; Đi th&ecirc;m 50km đến ng&atilde; 3 Sao Đỏ -&gt; Đi thẳng 1km theo hướng đi Quảng Ninh -&gt; Rẽ tr&aacute;i sẽ thấy biển b&aacute;o&nbsp;đi C&ocirc;n Sơn &ndash; Kiếp Bạc -&gt; Đi thẳng l&agrave; tới nơi.</p>\r\n	<ul>\r\n		<li style=\"text-align: justify;\">\r\n			Lưu &yacute;: Nếu đi theo lộ tr&igrave;nh n&agrave;y, bạn cần cẩn thận đoạn từ đường 18 đến cầu Phả lại, đoạn n&agrave;y đường đẹp, dễ đi nhưng lại hay bị bắn tốc độ v&agrave; phạt sai l&agrave;n. V&igrave; vậy h&atilde;y đi cẩn thận nh&eacute;.</li>\r\n	</ul>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"phuot-con-son-kiep-bac-hai-duong-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-con-son-kiep-bac-hai-duong-mixtourist3.JPG\" style=\"width: 600px; height: 399px;\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		Để cho thuận tiện đường đi v&agrave; tiết kiệm thời gian sức lực, bạn n&ecirc;n tham quan C&ocirc;n Sơn trước, sau đ&oacute; mới sang đền Kiếp Bạc. Ở&nbsp;C&ocirc;n Sơn c&oacute; 2 điểm tham quan m&agrave; bạn kh&ocirc;ng n&ecirc;n bỏ qua đ&oacute; l&agrave; ch&ugrave;a C&ocirc;n Sơn v&agrave; đền thờ Nguyễn Tr&atilde;i.</p>\r\n	<p style=\"text-align: justify;\">\r\n		B&ecirc;n cạnh đ&oacute;, bạn cũng n&ecirc;n đi những danh thắng kh&aacute;c như: B&agrave;n Cờ Ti&ecirc;n, Giếng Ngọc, Suối Thạch B&agrave;n, đền thờ Trần Nguy&ecirc;n H&atilde;n&hellip;.</p>\r\n</div>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: 16px;\"><strong>B&ecirc;n cạnh đ&oacute;, c&ograve;n c&aacute;c địa điểm săn m&acirc;y ở miền Bắc được c&aacute;c phượt thủ cực kỳ y&ecirc;u th&iacute;ch như:&nbsp;</strong></span></p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">19. Đỉnh Lảo Thẩn (L&agrave;o Cai)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	<strong>Đỉnh Lảo Thần</strong>&nbsp;l&agrave; địa điểm ho&agrave;n hảo để leo n&uacute;i v&agrave; săn m&acirc;y tr&ecirc;n đại ng&agrave;n với địa h&igrave;nh kh&aacute; dễ chịu, cảnh cực đẹp, chi ph&iacute; cũng kh&ocirc;ng hề tốn k&eacute;m.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dinh-lao-than-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dinh-lao-than-lao-cai-mixtourist.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	<strong>Đỉnh Lảo Thẩn&nbsp;</strong>nhọn v&agrave; tho&aacute;ng, địa h&igrave;nh leo n&uacute;i kh&ocirc;ng qu&aacute; phức tạp, chỉ mất 5-6 tiếng l&agrave; c&oacute; thể l&ecirc;n đến điểm nghỉ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dinh-lao-than-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dinh-lao-than-lao-cai-mixtourist1.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Từ đ&oacute; mất khoảng 1 tiếng leo dốc l&agrave; l&ecirc;n đến đỉnh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dinh-lao-than-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dinh-lao-than-lao-cai-mixtourist11.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dinh-lao-than-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dinh-lao-than-lao-cai-mixtourist1111.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Ngắm m&acirc;y trời với những cảnh sắc hiện dần theo &aacute;nh mặt trời, những biển m&acirc;y m&ecirc;nh m&ocirc;ng v&ocirc; c&ugrave;ng tận, lấp l&oacute; sau những phiến đ&aacute; to nhỏ dẽ cho ta những cảm nhận v&ocirc; c&ugrave;ng th&uacute; vị.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">20. T&agrave; X&ugrave;a (Sơn La)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	<strong>T&agrave; X&ugrave;a</strong>&nbsp;được gọi l&agrave; thi&ecirc;n đường m&acirc;y ở Việt Nam, v&igrave; thế những năm gần đ&acirc;y thu h&uacute;t kh&aacute; đ&ocirc;ng phượt thủ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ta-xua-son-la-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ta-xua-son-la-mixtourist.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ta-xua-son-la-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ta-xua-son-la-mixtourist11.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ta-xua-son-la-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ta-xua-son-la-mixtourist111.jpg\" style=\"width: 600px; height: 401px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Chỉ cần vượt qua qu&atilde;ng đường hơn 200km từ H&agrave; Nội, trong đ&oacute; đoạn đường từ thị trấn Bắc Y&ecirc;n l&ecirc;n T&agrave; X&ugrave;a khoảng 13km, bạn c&oacute; thể nh&igrave;n thấy những đ&aacute;m m&acirc;y hờ hững vắt qua n&uacute;i.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ta-xua-son-la-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ta-xua-son-la-mixtourist1111.jpg\" style=\"width: 600px; height: 354px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ta-xua-son-la-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ta-xua-son-la-mixtourist11111.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Khung cảnh như &ldquo;chốn thần ti&ecirc;n&rdquo; m&ecirc;nh m&ocirc;ng m&acirc;y bao phủ n&uacute;i chắc chắn kh&ocirc;ng l&agrave;m bạn thất vọng.</p>\r\n<p style=\"text-align: justify;\">\r\n	&gt;&gt;&gt;&gt;Xem ngay<strong><a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-bac/du-lich-dong-bac/tour-du-lich-ha-noi-ha-giang-ha-noi.html\"> tour du lịch H&agrave; Giang gi&aacute; rẻ.</a></strong></p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">21. Bạch Mộc Lương Tử (L&agrave;o Cai)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Bạch Mộc Lương Tử&nbsp;l&agrave; một trong 10 ngọn n&uacute;i cao nhất Việt Nam thu h&uacute;t rất đ&ocirc;ng đảo d&acirc;n du lịch bụi đến chinh phục.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bach-moc-luong-tu-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bach-moc-luong-tu-lao-cai-mixtourist.jpg\" style=\"width: 600px; height: 296px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bach-moc-luong-tu-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bach-moc-luong-tu-lao-cai-mixtourist1.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Trong d&atilde;y n&uacute;i n&agrave;y c&oacute; n&uacute;i Muối với độ cao hơn 3000 rất hoang sơ với m&acirc;y trắng bồng bềnh đỉnh n&uacute;i.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bach-moc-luong-tu-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bach-moc-luong-tu-lao-cai-mixtourist11.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bach-moc-luong-tu-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bach-moc-luong-tu-lao-cai-mixtourist1111.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bach-moc-luong-tu-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bach-moc-luong-tu-lao-cai-mixtourist11111.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn c&oacute; thể đến nơi đ&acirc;y v&agrave;o m&ugrave;a đ&ocirc;ng khi trời &iacute;t mưa v&agrave; đường đi kh&ocirc; r&aacute;o&hellip;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">22. N&uacute;i Muối (L&agrave;o Cai)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	N&uacute;i Muối nằm tr&ecirc;n cung đường chinh phục đỉnh Bạch Mộc Lương Tử cao thứ 4 Việt Nam.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Th&ocirc;ng thường, d&acirc;n du lịch sẽ trekking h&agrave;nh tr&igrave;nh l&ecirc;n đỉnh Bạch Mộc v&agrave; chọn n&uacute;i Muối l&agrave;m điểm để thỏa m&atilde;n với những bức h&igrave;nh đẹp, bởi đ&acirc;y l&agrave; nơi ngắm m&acirc;y đẹp nhất trong cả d&atilde;y Bạch Mộc.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bach-moc-luong-tu-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bach-moc-luong-tu-lao-cai-mixtourist111.jpg\" style=\"width: 600px; height: 429px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Để leo n&uacute;i Muối, chinh phục d&atilde;y Bạch Mộc Lương Tử bạn phải mất 3 ng&agrave;y 2 đ&ecirc;m leo n&uacute;i.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-nui-muoi-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-nui-muoi-lao-cai-mixtourist.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-nui-muoi-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-nui-muoi-lao-cai-mixtourist1.jpg\" style=\"width: 600px; height: 429px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Hiện c&oacute; hai hướng leo, một l&agrave; từ x&atilde; S&agrave;ng Ma S&aacute;o, huyện B&aacute;t X&aacute;t, tỉnh L&agrave;o Cai.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-nui-muoi-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-nui-muoi-lao-cai-mixtourist11.jpg\" style=\"width: 600px; height: 334px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Hai l&agrave; từ bản Dền Sung, x&atilde; Sin S&uacute;i Hồ, huyện Phong Thổ, tỉnh Lai Ch&acirc;u. Bạn c&oacute; thể kết hợp leo l&ecirc;n một đường v&agrave; về một đường để c&oacute; nhiều trải nghiệm hơn.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">23. H&aacute;ng Đồng (Sơn La)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	H&aacute;ng Đồng l&agrave; một trong những n&oacute;c nh&agrave; của tỉnh Sơn La với độ cao tr&ecirc;n 2.000m. Địa h&igrave;nh ở đ&acirc;y như một l&ograve;ng chảo khổng lồ, tựa lưng v&agrave;o d&atilde;y T&agrave; X&ugrave;a nguy&ecirc;n sinh ph&iacute;a sau, mặt hướng về huyện Bắc Y&ecirc;n nằm ph&iacute;a dưới thung lũng s&acirc;u hun h&uacute;t.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-hang-dong-son-la-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-hang-dong-son-la-mixtourist.jpg\" style=\"width: 600px; height: 359px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-hang-dong-son-la-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-hang-dong-son-la-mixtourist1.jpg\" style=\"width: 600px; height: 362px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	H&aacute;ng Đồng&nbsp;cũng l&agrave; một điểm săn m&acirc;y v&ocirc; c&ugrave;ng th&uacute; vị bởi quanh năm H&aacute;ng Đồng lu&ocirc;n ch&igrave;m trong m&acirc;y m&ugrave; bao phủ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-hang-dong-son-la-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-hang-dong-son-la-mixtourist11.jpg\" style=\"width: 600px; height: 354px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Tuy nhi&ecirc;n l&yacute; tưởng nhất để săn m&acirc;y H&aacute;ng Đồng l&agrave; từ th&aacute;ng 9 đến th&aacute;ng 3 năm sau bởi trời chuyển lạnh đi k&egrave;m những cơn mưa ph&ugrave;n c&ugrave;ng độ ẩm cao ở đ&acirc;y sẽ l&agrave; điều kiện ho&agrave;n hảo cho biển m&acirc;y mỗi s&aacute;ng sớm, c&oacute; h&ocirc;m đến tận trưa vẫn chưa tan.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">24. Y T&yacute; (L&agrave;o Cai)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Y T&yacute;&nbsp;c&oacute; độ cao 1805 m quanh năm sương m&ugrave; bao phủ rất th&uacute; vị.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-y-ty-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-y-ty-lao-cai-mixtourist1.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-y-ty-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-y-ty-lao-cai-mixtourist111.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đến&nbsp;Y T&yacute;&nbsp;bạn như lạc v&agrave;o thi&ecirc;n đường m&acirc;y với những dải m&acirc;y uốn lượn bao quanh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-y-ty-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-y-ty-lao-cai-mixtourist1111.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-y-ty-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-y-ty-lao-cai-mixtourist11111.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-y-ty-lao-cai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-y-ty-lao-cai-mixtourist.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đặc biệt th&uacute; vị hơn khi c&oacute; những &aacute;nh nắng xuy&ecirc;n qua những tầng m&acirc;y tạo n&ecirc;n khung cảnh huyền ảo đến ngỡ ng&agrave;ng.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 14px;\">25. S&igrave;n Hồ (Lai Ch&acirc;u)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	S&igrave;n Hồ l&agrave; một huyện ngh&egrave;o v&ugrave;ng cao của tỉnh Lai Ch&acirc;u. &Iacute;t c&oacute; ai nghĩ sẽ đến đ&acirc;y du lịch hay giải tr&iacute; g&igrave; đ&oacute; lắm, nhưng d&acirc;n du lịch bụi, đặc biệt l&agrave; những tay săn ảnh lại m&ecirc; đắm chỗ n&agrave;y, bởi đường l&ecirc;n đ&acirc;y tuy kh&oacute;, chạy xe mệt mỏi c&oacute; khi cả ng&agrave;y mới tới, nhưng kết quả nhận được lại v&ocirc; c&ugrave;ng &ldquo;đ&atilde; mắt&rdquo;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-sin-ho-lai-chau-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-sin-ho-lai-chau-mixtourist11.jpg\" style=\"width: 600px; height: 401px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Hiếm c&oacute; chỗ n&agrave;o m&acirc;y được nhuộm một m&agrave;u mỡ g&agrave; trong &aacute;nh ho&agrave;ng h&ocirc;n như ở đ&acirc;y, m&acirc;y tr&agrave;n v&agrave;o thung lung, b&acirc;y lấp lửng ngay dưới ch&acirc;n, cảm gi&aacute;c đứng cao tr&ecirc;n m&acirc;y quả thực kh&ocirc;ng thể tả th&agrave;nh lời.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-sin-ho-lai-chau-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-sin-ho-lai-chau-mixtourist1.jpg\" style=\"width: 600px; height: 231px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Trung t&acirc;m S&igrave;n Hồ vẫn chưa đủ cho trải nghiệm n&agrave;y khi m&agrave; l&agrave;ng M&ocirc; mới l&agrave; đ&iacute;ch đến. C&agrave;ng l&ecirc;n cao cảnh c&agrave;ng đẹp, th&uacute; săn m&acirc;y khi ấy dường như mới được thỏa m&atilde;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-sin-ho-lai-chau-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-sin-ho-lai-chau-mixtourist.jpg\" style=\"width: 600px; height: 448px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-sin-ho-lai-chau-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-sin-ho-lai-chau-mixtourist111.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Ở c&aacute;i nơi khỉ ho c&ograve; g&aacute;y, ngắm lũ trẻ chơi đ&ugrave;a, to&eacute;t miệng cười, nh&igrave;n biển m&acirc;y cuộn dưới ch&acirc;n, giữa kh&ocirc;ng gian n&uacute;i rừng n&agrave;y c&ograve;n g&igrave; hạnh ph&uacute;c hơn nữa?</p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: 16px;\">II. Địa điểm phượt ở miền Trung</span></span></h2>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">24. P&ugrave; Lu&ocirc;ng (Thanh H&oacute;a)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Với 130km xuất ph&aacute;t từ th&agrave;nh phố Thanh H&oacute;a một chặng đường kh&ocirc;ng hề ngắn để c&aacute;c&nbsp;phượt&nbsp;gia c&oacute; thể cảm nhận được chuyến đi đầy l&yacute; th&uacute; l&agrave; đến với P&ugrave; Lu&ocirc;ng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-pu-luong-thanh-hoa-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-pu-luong-thanh-hoa-mixtourist11111.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Nơi đ&acirc;y đẹp kh&ocirc;ng chỉ bởi cảnh sắc hữu t&igrave;nh, những thửa ru​ộng bậc thang, n&uacute;i rừng hoang sơ c&ugrave;ng nếp nh&agrave; giản dị n&eacute;p m&igrave;nh b&ecirc;n sườn đồi từ l&acirc;u đ&atilde; l&agrave; miền đất hứa của những người y&ecirc;u th&iacute;ch kh&aacute;m ph&aacute;, v&agrave; l&agrave; niềm mơ ước của nhiều phượt thủ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-pu-luong-thanh-hoa-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-pu-luong-thanh-hoa-mixtourist.jpg\" style=\"width: 600px; height: 401px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Thời điểm tuyệt nhất để đến thăm P&ugrave; Lu&ocirc;ng l&agrave; khoảng th&aacute;ng 6 v&agrave; th&aacute;ng 10, khi l&uacute;a rộ l&ecirc;n ch&iacute;n v&agrave;ng tr&ecirc;n c&aacute;c thửa ruộng bậc thang b&ecirc;n sườn đồi, điểm t&ocirc; cho thảm xanh P&ugrave; Lu&ocirc;ng vẻ tr&ugrave; ph&uacute;, y&ecirc;n b&igrave;nh v&agrave; thơ mộng. ​</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-pu-luong-thanh-hoa-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-pu-luong-thanh-hoa-mixtourist1.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Một địa danh kh&ocirc;ng thể bỏ qua khi đến P&ugrave; Lu&ocirc;ng l&agrave; th&aacute;c nước bản Hi&ecirc;u. Tr&ecirc;n đường từ thị trấn Phố Đo&agrave;n v&agrave;o bản, c&aacute;c phượt thủ c&oacute; thể bắt gặp rất nhiều guồng nước đặc trưng của người Th&aacute;i. Qua cầu treo, vượt qua con dốc tr&ecirc;n đồi đất bạn sẽ phải ồ l&ecirc;n th&iacute;ch th&uacute; khi bắt gặp th&aacute;c nước bản Hi&ecirc;u đang reo vui tu&ocirc;n chảy.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-pu-luong-thanh-hoa-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-pu-luong-thanh-hoa-mixtourist1111.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-pu-luong-thanh-hoa-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-pu-luong-thanh-hoa-mixtourist111.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu đi v&agrave;o thứ 5 hoặc chủ nhật, bạn&nbsp;h&atilde;y đến chơi chợ phi&ecirc;n Phố Đ&ograve;n, nơi trao đổi mua b&aacute;n những sản vật v&ugrave;ng cao rất th&uacute; vị của con người nơi đ&acirc;y,&nbsp;với c&aacute;c mặt h&agrave;ng tự cung tự cấp như thổ cẩm, rượu cần, rau rừng, c&ocirc;n tr&ugrave;ng, hoa quả tươi...</p>\r\n<p style=\"text-align: justify;\">\r\n	&gt;&gt;&gt;&gt;Xem ngay <a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-trung/du-lich-pu-luong/\"><strong>tour du lịch P&ugrave; Lu&ocirc;ng, Thanh H&oacute;a </strong></a>của AzBooking nh&eacute;!</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">25. B&agrave; N&agrave; (Đ&agrave; Nẵng)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Thuộc địa phận x&atilde; H&ograve;a Ninh, huyện H&ograve;a Vang, c&aacute;ch trung t&acirc;m th&agrave;nh phố Đ&agrave; Nẵng 35 km về ph&iacute;a t&acirc;y nam, B&agrave; N&agrave; được v&iacute; như chốn bồng lai ti&ecirc;n cảnh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ba-na-da-nang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ba-na-da-nang-mixtourist11.jpg\" style=\"width: 600px; height: 600px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ba-na-da-nang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ba-na-da-nang-mixtourist1.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Trải qua một ng&agrave;y ở đ&acirc;y, bạn c&oacute; thể tận hưởng kh&iacute; hậu của cả bốn m&ugrave;a. Buổi s&aacute;ng, tiết xu&acirc;n dịu m&aacute;t, buổi trưa m&ugrave;a hạ oi ả, buổi chiều se lạnh sang thu v&agrave; đ&ecirc;m xuống đ&ocirc;ng về lạnh gi&aacute;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ba-na-da-nang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ba-na-da-nang-mixtourist.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Hệ thống c&aacute;p treo sẽ cho bạn trải nghiệm cảm gi&aacute;c như đang bồng bềnh giữa m&acirc;y trời, từ tr&ecirc;n cao ngắm nh&igrave;n to&agrave;n bộ phong cảnh kỳ vĩ rộng lớn.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ba-na-da-nang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ba-na-da-nang-mixtourist1111.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ba-na-da-nang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ba-na-da-nang-mixtourist111.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Ch&ugrave;a Linh Ứng, hầm rượu vang, vườn hoa, những tr&ograve; chơi mạo hiểm lu&ocirc;n hấp dẫn c&aacute;c du kh&aacute;ch đến đ&acirc;y.&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&gt;&gt;&gt;&gt;Xem ngay c&aacute;c <a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-trung/du-lich-da-nang/\"><strong>tour du lịch Đ&agrave; Nẵng gi&aacute; rẻ 2017.</strong></a></p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">26. Đảo L&yacute; Sơn (Quảng Ng&atilde;i)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	L&yacute; Sơn l&agrave; một huyện đảo của tỉnh Quảng Ng&atilde;i, nằm c&aacute;ch bờ khoảng 30 km, gồm 3 h&ograve;n đảo l&agrave; Đảo Lớn, Đảo B&eacute; v&agrave; h&ograve;n M&ugrave; Cu.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dao-ly-son-quang-ngai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dao-ly-son-quang-ngai-mixtourist.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đảo Lớn c&oacute; 3 di t&iacute;ch quốc gia l&agrave; đ&igrave;nh l&agrave;ng An Hải, &Acirc;m Linh tự v&agrave; di chỉ văn h&oacute;a Sa Huỳnh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dao-ly-son-quang-ngai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dao-ly-son-quang-ngai-mixtourist11.jpg\" style=\"width: 600px; height: 331px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đảo B&eacute; sở hữu một b&atilde;i tắm đẹp tuyệt vời với l&agrave;n c&aacute;t trắng mịn, được bao bọc bằng những v&aacute;ch đ&aacute; nham thạch kỳ vĩ, th&iacute;ch hợp để tổ chức cắm trại qua đ&ecirc;m.Đảo L&yacute; Sơn (Quảng Ng&atilde;i).</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dao-ly-son-quang-ngai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dao-ly-son-quang-ngai-mixtourist111.jpg\" style=\"width: 600px; height: 393px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dao-ly-son-quang-ngai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dao-ly-son-quang-ngai-mixtourist1.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dao-ly-son-quang-ngai-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dao-ly-son-quang-ngai-mixtourist1111.jpg\" style=\"width: 600px; height: 374px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	C&oacute; rất nhiều những điểm đến kh&ocirc;ng thể bỏ qua tr&ecirc;n đảo L&yacute; Sơn như ch&ugrave;a Hang, cổng T&ograve; V&ograve;, hang C&acirc;u, cột cờ Tổ quốc tr&ecirc;n đỉnh Thới Lới, ch&ugrave;a Đục v&agrave; Quan &Acirc;m Đ&agrave;i... Đặc sản ở L&yacute; Sơn rất phong ph&uacute; như gỏi rong biển, gỏi tỏi, ốc tượng, cua Huỳnh Đế, c&aacute; t&agrave; ma&hellip; Nơi đ&acirc;y sẽ cho bạn nhiều trải nghiệm đ&aacute;ng nhớ.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#0000ff;\">27. C&ugrave; Lao Xanh (B&igrave;nh Định)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Từ cảng Quy Nhơn, bạn c&oacute; thể ra đảo C&ugrave; Lao Xanh trong khoảng 50 ph&uacute;t bằng t&agrave;u cao tốc.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-cu-lao-xanh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-cu-lao-xanh-mixtourist.jpg\" style=\"width: 600px; height: 356px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	M&agrave;u xanh của c&acirc;y cối, biển trời tạo th&agrave;nh một bức tranh khiến bạn phải trầm trồ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-cu-lao-xanh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-cu-lao-xanh-mixtourist1.jpg\" style=\"width: 600px; height: 375px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-cu-lao-xanh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dao-ly-son-quang-ngai-mixtourist.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn c&oacute; thể chinh phục ngọn hải đăng được x&acirc;y dựng từ thời Ph&aacute;p, cao đến 119 m, đi đến suối Giếng Ti&ecirc;n tinh khiết, hay men theo những con đường nhỏ quanh b&atilde;i biển ngắm biển, những kh&oacute;m hoa dại rất đẹp v&agrave; c&oacute; thể tận mắt thấy những b&atilde;i c&aacute;t nơi r&ugrave;a đẻ trứng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-cu-lao-xanh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-cu-lao-xanh-mixtourist1111.jpg\" style=\"width: 600px; height: 388px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-cu-lao-xanh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-cu-lao-xanh-mixtourist11.jpg\" style=\"width: 600px; height: 381px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-cu-lao-xanh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-cu-lao-xanh-mixtourist111.jpg\" style=\"width: 600px; height: 415px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Buổi chiều ngắm cảnh ho&agrave;ng h&ocirc;n ở l&agrave;ng ch&agrave;i, bạn sẽ c&oacute; một kỷ niệm đẹp v&agrave; thi vị cho cuộc h&agrave;nh tr&igrave;nh.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">28. Biển B&igrave;nh Ti&ecirc;n (Ninh Thuận)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	C&aacute;ch th&agrave;nh phố Phan Rang 30 km, biển B&igrave;nh Ti&ecirc;n được bao bọc 3 ph&iacute;a l&agrave; rừng c&acirc;y v&agrave; n&uacute;i đ&aacute; n&ecirc;n mặt biển kh&aacute; y&ecirc;n ả.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bien-binh-tien-ninh-thuan-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bien-binh-tien-ninh-thuan-mixtourist.jpg\" style=\"width: 600px; height: 397px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Giữa kh&ocirc;ng kh&iacute; trong l&agrave;nh, bạn c&oacute; thể thoải m&aacute;i tản bộ tr&ecirc;n lớp c&aacute;t trắng trải d&agrave;i mềm mịn, tắm biển thỏa th&iacute;ch hay thư thả ngồi ngắm cảnh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bien-binh-tien-ninh-thuan-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bien-binh-tien-ninh-thuan-mixtourist11.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Sau đ&oacute; băng rừng khoảng 15 ph&uacute;t, bạn c&oacute; thể ng&acirc;m m&igrave;nh trong con suối trong veo từ n&uacute;i Ch&uacute;a đổ ra biển B&igrave;nh Ti&ecirc;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bien-binh-tien-ninh-thuan-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bien-binh-tien-ninh-thuan-mixtourist1.jpg\" style=\"width: 600px; height: 397px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bien-binh-tien-ninh-thuan-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bien-binh-tien-ninh-thuan-mixtourist111.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	B&atilde;i biển n&agrave;y c&ograve;n l&agrave; chốn l&yacute; tưởng cho những đ&ecirc;m cắm trại. Bạn c&oacute; thể c&ugrave;ng ngư d&acirc;n ở l&agrave;ng ch&agrave;i ven biển ra khơi đ&aacute;nh bắt hải sản tươi ngon. Thưởng thức những m&oacute;n do ch&iacute;nh tay m&igrave;nh thu về chắc chắn bạn sẽ thấy hương vị của ch&uacute;ng th&ecirc;m phần đậm đ&agrave; đặc sắc.&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">29. Đảo Ph&uacute; Qu&yacute; (B&igrave;nh Thuận)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Đảo Ph&uacute; Qu&yacute; (c&ugrave; lao Thu, c&ugrave; lao Khoai Xứ) l&agrave; một huyện đảo thuộc tỉnh B&igrave;nh Thuận, c&aacute;ch th&agrave;nh phố Phan Thiết 111 km.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dao-phu-quy-binh-thuan-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dao-phu-quy-binh-thuan-mixtourist.jpg\" style=\"width: 600px; height: 266px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Nằm giữa biển trời m&ecirc;nh m&ocirc;ng với h&igrave;nh th&ugrave; kỳ th&uacute;, đảo Ph&uacute; Qu&yacute; hấp dẫn du kh&aacute;ch nhờ những b&atilde;i biển trong veo, d&atilde;y đ&aacute; san h&ocirc; đầy m&agrave;u sắc cho đến bầu kh&ocirc;ng kh&iacute; trong l&agrave;nh, m&aacute;t rượi.Đảo Ph&uacute; Qu&yacute; (B&igrave;nh Thuận).</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dao-phu-quy-binh-thuan-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dao-phu-quy-binh-thuan-mixtourist1.jpg\" style=\"width: 600px; height: 413px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Rất nhiều hoạt động th&uacute; vị d&agrave;nh cho bạn như ngắm b&igrave;nh minh ở mũi Doi Thầy (Mộ Thầy) hoặc ngắm ho&agrave;ng h&ocirc;n ở n&uacute;i Cấm, gh&eacute; thăm H&ograve;n Đen, H&ograve;n Trứng, H&ograve;n Giữa, H&ograve;n Đỏ, H&ograve;n Tranh, t&igrave;m hiểu nghề truyền thống lặn bắt t&ocirc;m h&ugrave;m, ngắm bộ xương c&aacute; voi khổng lồ ở Vạn An Thạnh, đi n&uacute;i Cao C&aacute;t, n&uacute;i Cấm, ch&ugrave;a Linh Bửu, Linh Quang, Thạnh L&acirc;m&hellip;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dao-phu-quy-binh-thuan-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dao-phu-quy-binh-thuan-mixtourist111.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dao-phu-quy-binh-thuan-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dao-phu-quy-binh-thuan-mixtourist1111.jpg\" style=\"width: 600px; height: 439px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dao-phu-quy-binh-thuan-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dao-phu-quy-ninh-thuan-mixtourist11.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn c&ograve;n c&oacute; thể đốt lửa trại, dạo biển hoặc c&acirc;u mực, c&acirc;u t&ocirc;m. Đừng qu&ecirc;n nếm thử hải sản đặc biệt l&agrave; cua mặt trăng c&oacute; thịt rất thơm ngon.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">30. Động Thi&ecirc;n Đường (Quảng B&igrave;nh)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Động Thi&ecirc;n Đường thuộc x&atilde; Sơn Trạch, huyện Bố Trạch, tỉnh Quảng B&igrave;nh, nằm ở Km 16 c&aacute;ch r&igrave;a nh&aacute;nh t&acirc;y đường Hồ Ch&iacute; Minh gần 4 km.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dong-thien-duong-quang-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dong-thien-duong-quang-binh-mixtourist.jpg\" style=\"width: 600px; height: 420px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dong-thien-duong-quang-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dong-thien-duong-quang-binh-mixtourist111.jpg\" style=\"width: 600px; height: 341px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đ&acirc;y l&agrave; một trong những động kh&ocirc; d&agrave;i nhất ch&acirc;u &Aacute; với chiều d&agrave;i 31,4 km. Cửa động chỉ vừa đủ một người đi qua, nhưng b&ecirc;n trong c&oacute; nơi mở rộng hơn 150 m, trần cao gần 80 m.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dong-thien-duong-quang-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dong-thien-duong-quang-binh-mixtourist1.jpg\" style=\"width: 600px; height: 402px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Kh&ocirc;ng kh&iacute; m&aacute;t lạnh ở mức 20 độ C bao tr&ugrave;m những khối nhũ đ&aacute;, măng đ&aacute; mu&ocirc;n h&igrave;nh vạn trạng. Hệ thống cầu thang gỗ v&agrave; &aacute;nh s&aacute;ng trắng soi đường sẽ dẫn bạn đến những địa điểm nổi tiếng như Cổ Th&aacute;p, cung Quần Ti&ecirc;n Hội Tụ, th&aacute;p Li&ecirc;n Hoa, Kỳ Hươu, Voi Ma M&uacute;t...</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dong-thien-duong-quang-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dong-thien-duong-quang-binh-mixtourist11.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dong-thien-duong-quang-binh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dong-thien-duong-quang-binh-mixtourist1111.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Vẻ đẹp tr&aacute;ng lệ của những tuyệt t&aacute;c thi&ecirc;n nhi&ecirc;n thực sự cho bạn cảm gi&aacute;c đặt ch&acirc;n đến chốn thi&ecirc;n đường huyền ảo.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">31. Vườn quốc gia P&ugrave; M&aacute;t (Nghệ An)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Thuộc địa phận 3 huyện Anh Sơn, Con Cu&ocirc;ng v&agrave; Tương Dương của tỉnh Nghệ An, vườn quốc gia P&ugrave; M&aacute;t mang vẻ đẹp h&ugrave;ng vĩ của thi&ecirc;n nhi&ecirc;n hoang sơ chưa hề bị khai th&aacute;c. Đến đ&acirc;y, bạn được chi&ecirc;m ngưỡng khu rừng nguy&ecirc;n sinh mang t&iacute;nh đa dạng sinh học cao bậc nhất Việt Nam với hơn một ng&agrave;n lo&agrave;i thực vật, hơn 50 lo&agrave;i th&uacute; v&agrave; gần 300 lo&agrave;i chim, c&oacute; cả c&aacute;c lo&agrave;i qu&yacute; hiếm như trĩ sao, g&agrave; l&ocirc;i&hellip;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-vuon-quoc-gia-pu-mat-nghe-an-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vuon-quoc-gia-pu-mat-nghe-an-mixtourist11.jpg\" style=\"width: 600px; height: 394px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Ngo&agrave;i ra c&ograve;n c&oacute; nhiều thắng cảnh v&ocirc; c&ugrave;ng tuyệt diệu như th&aacute;c Khe K&egrave;m, suối nước Mọc, s&ocirc;ng Giăng đưa bạn thả hồn trong kh&ocirc;ng gian m&aacute;t rượi r&eacute;o rắt tiếng chim. B&ecirc;n cạnh đ&oacute;, những n&eacute;t văn h&oacute;a độc đ&aacute;o của d&acirc;n tộc Th&aacute;i, H&#39;m&ocirc;ng, Đan Lai chứa đựng nhiều điều th&uacute; vị chờ bạn kh&aacute;m ph&aacute;.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">32. Suối C&aacute; Thần Cẩm Lương (Thanh H&oacute;a)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Suối C&aacute; Thần Cẩm Lương hay suối c&aacute; thần l&agrave;ng Ngọc, gắn với truyền thuyết về Thần Rắn, nằm b&ecirc;n ch&acirc;n n&uacute;i Trường Sinh, x&atilde; Cẩm Lương, huyện miền n&uacute;i Cẩm Thủy, tỉnh Thanh H&oacute;a, c&aacute;ch trung t&acirc;m th&agrave;nh phố 80 km về ph&iacute;a t&acirc;y bắc. Xuất ph&aacute;t từ mạch nước trong n&uacute;i đ&aacute; v&ocirc;i Bồ Um, suối chỉ d&agrave;i tr&ecirc;n 150 m nhưng c&oacute; một đ&agrave;n c&aacute; với h&agrave;ng ngh&igrave;n con được đồng b&agrave;o Mường cho l&agrave; c&oacute; linh t&iacute;nh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-suoi-ca-than-cam-duong-thanh-hoa-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-suoi-ca-than-cam-duong-thanh-hoa-mixtourist.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Nơi n&agrave;y c&oacute; những con c&aacute; nặng đến 30 kg, thường chỉ chui ra khỏi hang v&agrave;o m&ugrave;a nước lớn. Nước trong suối kh&ocirc;ng bao giờ cạn. Mực nước nơi s&acirc;u nhất v&agrave;o m&ugrave;a mưa từ 50-80 cm. Điều kỳ lạ nhất l&agrave; nước suối quanh năm trong như ngọc, kh&ocirc;ng hề c&oacute; m&ugrave;i tanh. Gần suối c&aacute; thần c&ograve;n c&oacute; đền Ngọc v&agrave; động C&acirc;y Đăng để du kh&aacute;ch tham quan.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">33. Vực Phun (Ph&uacute; Y&ecirc;n)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Vực Phun tọa lạc ở x&atilde; H&ograve;a Mỹ T&acirc;y, huyện T&acirc;y H&ograve;a, tỉnh Ph&uacute; Y&ecirc;n. Đi dọc quốc lộ 1A, đến đoạn thị trấn Ph&uacute; L&acirc;m th&igrave; rẽ qua Quốc Lộ 29, đi thẳng v&agrave;o v&ugrave;ng n&uacute;i Đ&aacute; đen rồi ngược về s&ocirc;ng B&aacute;nh L&aacute;i, bạn sẽ thấy một th&aacute;c nước trong vắt của d&ograve;ng suối C&aacute;i cao 15 m, tu&ocirc;n xuống giữa hai b&ecirc;n v&aacute;ch n&uacute;i đổ d&agrave;i.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-vuc-phun-phu-yen-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vuc-phun-phu-yen-mixtourist.jpg\" style=\"width: 600px; height: 402px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Thắng cảnh vừa kỳ vĩ vừa thơ mộng nằm lọt thỏm giữa bạt ng&agrave;n rừng c&acirc;y v&agrave; đồi n&uacute;i đ&oacute; ch&iacute;nh l&agrave; Vực Phun. Đến đ&acirc;y, bạn kh&ocirc;ng chỉ được thưởng ngoạn tuyệt t&aacute;c thi&ecirc;n nhi&ecirc;n m&agrave; c&ograve;n c&oacute; thể ch&egrave;o thuyền, cắm trại, leo n&uacute;i, tắm suối hay kết hợp gh&eacute; qua những di t&iacute;ch văn h&oacute;a như ch&ugrave;a Hương T&iacute;ch - một trong những ng&ocirc;i ch&ugrave;a cổ ở Ph&uacute; Y&ecirc;n.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">&nbsp;34. Vịnh Vũng R&ocirc; (Ph&uacute; Y&ecirc;n)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Vũng R&ocirc; l&agrave; một vịnh nhỏ thuộc x&atilde; H&ograve;a Xu&acirc;n Nam, huyện Đ&ocirc;ng H&ograve;a, tỉnh Ph&uacute; Y&ecirc;n, nằm dưới ch&acirc;n đ&egrave;o Cả. Với ba mặt đ&ocirc;ng, t&acirc;y, bắc được n&uacute;i cao bao bọc, ph&iacute;a nam c&oacute; đảo H&ograve;n Nưa chắn gi&oacute;, vịnh Vũng R&ocirc; quanh năm y&ecirc;n b&igrave;nh lặng s&oacute;ng. Trong vịnh c&oacute; 12 b&atilde;i nhỏ, quyến rũ du kh&aacute;ch nhờ khung cảnh thi&ecirc;n nhi&ecirc;n kỳ th&uacute; với n&uacute;i, đảo, nước biển xanh trong như ngọc b&iacute;ch, những triền c&aacute;t trắng mềm trải d&agrave;i.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-vinh-vung-ro-thanh-hoa-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vinh-vung-ro-thanh-hoa-mixtourist11.jpg\" style=\"width: 600px; height: 375px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn sẽ thỏa th&iacute;ch tắm biển, thưởng thức hải sản, thu&ecirc; thuyền của ngư d&acirc;n ra biển, chinh phục đỉnh n&uacute;i Đ&aacute; Bia cao hơn 700 m&eacute;t được mệnh danh l&agrave; &ldquo;phương Nam đệ nhất trụ&rdquo;, tham quan ngọn hải đăng, b&atilde;i M&ocirc;n, Mũi Điện.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">35. Bến En (Thanh H&oacute;a)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Thơ mộng, quyến rũ, ngọt ng&agrave;o v&agrave; b&iacute; ẩn&hellip; sự mi&ecirc;u tả ấy c&oacute; thể vẫn c&ograve;n chưa đủ để lột tả hết vẻ đẹp của Bến En. Đ&acirc;y cũng l&agrave; một trong những địa điểm d&agrave;nh cho d&acirc;n phượt để thay đổi kh&ocirc;ng kh&iacute; th&agrave;nh thị t&igrave;m đến nơi b&igrave;nh y&ecirc;n, tĩnh lặng. Nơi đ&acirc;y, c&aacute;ch trung t&acirc;m TP Thanh H&oacute;a chừng hơn 46km về ph&iacute;a T&acirc;y Nam, đi qua c&aacute;c phố thị đ&ocirc;ng đ&uacute;c v&agrave; ồn &agrave;o xe cộ, con đường dẫn đến Bến En cắt ngang qua những ng&ocirc;i l&agrave;ng nhỏ của đồng b&agrave;o người Th&aacute;i, người Thổ v&agrave; người Mường.</p>\r\n<p style=\"text-align: justify;\">\r\n	Bao quanh những ng&ocirc;i l&agrave;ng nhỏ l&agrave; c&aacute;c d&atilde;y n&uacute;i đ&aacute; v&ocirc;i chạy d&agrave;i, một kiểu địa h&igrave;nh đặc trưng của Thanh H&oacute;a. C&aacute;c phượt thủ tr&ecirc;n đường đi tha hồ ngắm cảnh v&agrave; kh&aacute;m ph&aacute; nhiều điều th&uacute; vị từ c&aacute;c ng&ocirc;i l&agrave;ng v&agrave; người d&acirc;n bản địa nơi đ&acirc;y nếu bất chợt muốn dừng ch&acirc;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ben-en-thanh-hoa-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ben-en-thanh-hoa-mixtourist1111.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Ngo&agrave;i thăm th&uacute; cảnh sắc thi&ecirc;n nhi&ecirc;n bạn cũng đừng qu&ecirc;n gh&eacute; qua Vườn quốc gia Bến En thuộc địa b&agrave;n 2 huyện Như Thanh v&agrave; Như Xu&acirc;n của tỉnh Thanh Ho&aacute;. Nơi đ&acirc;y được biết đến l&agrave; một quần thể sinh th&aacute;i c&oacute; hệ thống n&uacute;i - s&ocirc;ng - hồ rộng tr&ecirc;n 16.000ha, trong đ&oacute; c&oacute; hơn 8.500ha rừng nguy&ecirc;n sinh với hệ động thực vật phong ph&uacute;, đa dạng với h&agrave;ng trăm lo&agrave;i, bộ, họ...&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	C&oacute; đến đ&acirc;y, trực tiếp cảm nhận bằng mọi gi&aacute;c quan ch&uacute;ng ta&nbsp;mới thấy được n&eacute;t đẹp như huyền thoại của v&ugrave;ng non nước đan xen giữa vẻ trầm mặc của m&acirc;y n&uacute;i, sắc dịu d&agrave;ng n&ecirc;n thơ của đất trời v&agrave; hơi thở mới của cuộc sống hiện đại.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">36. Đến biển Sầm Sơn (Thanh H&oacute;a)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Nếu bạn đang đắn đo lựa chọn kh&ocirc;ng biết n&ecirc;n chọn b&atilde;i biển n&agrave;o cho chuyến đi phượt h&egrave; n&agrave;y, th&igrave; biển Sầm Sơn chắc chắn sẽ l&agrave; sự lựa chọn tuyệt vời d&agrave;nh cho bạn v&agrave; những người th&acirc;n y&ecirc;u.​ ​Bỏ qua những điều tiếng về nạn chặt ch&eacute;m du kh&aacute;ch, về sự ồn &agrave;o, mất vệ sinh... Ch&uacute;ng ta sẽ cảm nhận được ở nơi đ&acirc;y v&ocirc; số những điều tuyệt vời kh&aacute;c.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-sam-son-thanh-hoa-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-sam-son-thanh-hoa-mixtourist1111.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	​Tại Sầm Sơn, ngo&agrave;i việc thỏa th&iacute;ch tắm biển, bạn cũng đừng qu&ecirc;n trải nghiệm đươc ngắm b&igrave;nh minh s&aacute;ng sớm hay ho&agrave;ng h&ocirc;n l&uacute;c chiều t&agrave; v&agrave; tham gia k&eacute;o lưới c&ugrave;ng d&acirc;n ch&agrave;i. Kh&ocirc;ng những được h&ograve;a m&igrave;nh c&ugrave;ng cảnh sắc l&atilde;ng mạn n&agrave;y, bạn c&ograve;n c&oacute; cơ hội mua v&agrave; thưởng thức hải sản tươi ngon nhất ngay khi vừa được đ&aacute;nh bắt l&ecirc;n.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">37. Rừng quốc gia P&ugrave; M&aacute;t, Con Cu&ocirc;ng (Nghệ An)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	L&agrave; một huyện v&ugrave;ng cao bi&ecirc;n giới ph&iacute;a T&acirc;y Nam Nghệ An, lưng dựa v&agrave;o d&atilde;y Trường Sơn, c&oacute; đường bi&ecirc;n giới gi&aacute;p nước bạn L&agrave;o, c&aacute;ch TP&nbsp;Vinh 130km. Con Cu&ocirc;ng&nbsp;chủ yếu l&agrave; rừng, đặc biệt huyện c&oacute; vườn Quốc gia P&ugrave; M&aacute;t với diện t&iacute;ch 61.000ha. ​Đ&acirc;y l&agrave; khu bảo tồn nguy&ecirc;n sinh hiếm c&oacute; tại Việt Nam, với t&agrave;i nguy&ecirc;n rừng phong ph&uacute;, đa dạng, c&oacute; nhiều loại động thực vật qu&yacute; hiếm c&oacute; trong s&aacute;ch đỏ như Sao La, c&oacute; nhiều hang động kỳ th&uacute;, nhiều th&aacute;c nước đẹp, hấp dẫn l&agrave; tiềm năng du lịch sinh th&aacute;i rất lớn.​ ​&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-rung-quoc-gia-pu-mat-con-cuong-nghe-an-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-rung-quoc-gia-pu-mat-con-cuong-nghe-an-mixtourist11.jpg\" style=\"width: 600px; height: 383px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	C&aacute;c phượt thủ d&agrave;nh ra khoảng v&agrave;i tiếng để chạy xe v&agrave; di chuyển tới c&aacute;c danh thắng như: th&aacute;c Khe K&egrave;m (th&aacute;c Bộc Bố) chảy từ độ cao 150m, tạo n&ecirc;n một cảnh quan h&ugrave;ng vĩ, men theo dốc đ&aacute; tai m&egrave;o, qua đ&egrave;o, qua suối, đến tận ngọn Khe Bu, Khe Thơi, Khe Choăng ngắm nh&igrave;n những c&aacute;nh rừng pơ mu bạt ng&agrave;n, thăm rừng L&ugrave;n k&igrave; b&iacute; rộng khoảng 1600km2, tr&ecirc;n độ cao 1700m.​​</p>\r\n<p style=\"text-align: justify;\">\r\n	Ngo&agrave;i những thắng cảnh thi&ecirc;n nhi&ecirc;n, Con Cu&ocirc;ng c&ograve;n c&oacute; c&aacute;c di t&iacute;ch khảo cổ, di t&iacute;ch lịch sử, di t&iacute;ch lịch sử văn h&oacute;a, di t&iacute;ch c&aacute;ch mạng. Như th&agrave;nh cổ Tr&agrave; L&acirc;n tr&ecirc;n địa b&agrave;n x&atilde; Chi Kh&ecirc;, hữu ngạn s&ocirc;ng Lam. Đ&acirc;y l&agrave; chứng t&iacute;ch đ&atilde; được nhắc đến trong &quot;B&igrave;nh Ng&ocirc; Đại C&aacute;o&quot; của Nguyễn Tr&atilde;i với &quot;Miền Tr&agrave; L&acirc;n tr&uacute;c chẻ tro bay&hellip;&quot;, gắn liền với cuộc khởi nghĩa của nghĩa qu&acirc;n Lam Sơn do L&ecirc; Lợi chỉ huy&hellip;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">38. Biển Thi&ecirc;n Cầm (H&agrave; Tĩnh)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Thi&ecirc;n Cầm nghĩa l&agrave; &quot;cung đ&agrave;n biển&quot;, đ&acirc;y l&agrave; b&atilde;i biển đẹp nhất H&agrave; Tĩnh, nằm c&aacute;ch TP H&agrave; Tĩnh khoảng 20km về ph&iacute;a Đ&ocirc;ng Nam, Cửa Nhượng hay Thi&ecirc;n Cầm l&agrave; c&aacute;i t&ecirc;n mới được biết đến v&agrave; ca tụng như một b&atilde;i tắm vẫn c&ograve;n vẻ hoang sơ, m&ocirc;i trường trong sạch, b&atilde;i c&aacute;t thoải d&agrave;i y&ecirc;n tĩnh rất ph&ugrave; hợp cho c&aacute;c phượt thủ y&ecirc;u th&iacute;ch sự kh&aacute;m ph&aacute; v&agrave; độc lập. S&oacute;ng biển ở đ&acirc;y kh&ocirc;ng qu&aacute; to như Sầm Sơn song cũng đủ để tạo n&ecirc;n tiếng nhạc du dương như tiếng đ&agrave;n trời l&agrave;m n&ecirc;n sự t&iacute;ch của khu du lịch Thi&ecirc;n Cầm. ​ ​ ​</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bien-thien-cam-ha-tinh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bien-thien-cam-ha-tinh-mixtourist1111.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	​​​Dường như chỉ c&oacute; ở b&atilde;i biển Thi&ecirc;n Cầm, người ta mới c&oacute; thể nghe được trọn vẹn những &acirc;m thanh h&ograve;a quyện của s&oacute;ng, gi&oacute; r&igrave; r&agrave;o qua khe n&uacute;i. Tất cả tạo th&agrave;nh một bản nhạc du dương, bao bọc du kh&aacute;ch trong kh&ocirc;ng kh&iacute; của y&ecirc;n b&igrave;nh v&agrave; &ecirc;m&nbsp;ả tr&ecirc;n biển Thi&ecirc;n Cầm.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">39. Thung Nham - Thung Chim (Ninh B&igrave;nh)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Khu du lịch sinh th&aacute;i vườn chim Thung Nham c&aacute;ch th&agrave;nh phố Ninh B&igrave;nh khoảng 12km về ph&iacute;a đ&ocirc;ng, nằm trọn trong v&ugrave;ng l&otilde;i của quần thể danh thắng Tr&agrave;ng An, cạnh khu du lịch nổi tiếng Tam Cốc - B&iacute;ch Động. Từ ch&ugrave;a B&iacute;ch Động theo tuyến đường bộ khoảng 4km l&agrave; du kh&aacute;ch đ&atilde; đặt ch&acirc;n tới sứ sở của c&aacute;c lo&agrave;i chim.&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Từ H&agrave; Nội bạn đi đến Ninh Binh (khoảng 100km) theo&nbsp;quốc lộ 1, đến th&agrave;nh phố Ninh Binh bạn đi th&ecirc;m 4km nữa tr&ecirc;n đường 30 th&aacute;ng 6 bạn hỏi đường rẽ v&agrave;o&nbsp;cầu V&ograve;m&nbsp;(gần kh&aacute;ch sạn Quang Dũng)</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-thung-nham-thung-chim-ninh-binh-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-thung-nham-thung-chim-ninh-binh-mixtourist11.jpg\" style=\"width: 600px; height: 389px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đi v&agrave;o&nbsp;trong khu du lịch Tam Cốc&nbsp;khoảng 4 km nữa đi qua ng&atilde; rẽ v&agrave;o Thung Nắng (c&oacute; biển chỉ dẫn) th&ecirc;m khoảng 1~2 km gần đến ch&ugrave;a B&iacute;ch Động c&oacute; đường rẽ tr&aacute;i v&agrave;o th&ocirc;n Hải Nham (bạn c&oacute; thể hỏi người d&acirc;n đường v&agrave;o vườn chim Thung Nham) cứ đi thẳng tiếp 4km nữa sẽ tới cổng so&aacute;t v&eacute; v&agrave;o khu du lịch Thung Nham.</p>\r\n<p style=\"text-align: justify;\">\r\n	Tại đ&acirc;y c&oacute; rất nhiều c&aacute;c điểm tham quan như: Động V&aacute;i Giời, Hang Bụt, c&acirc;y Đa di chuyển, c&acirc;y Duối ngh&igrave;n năm, động Ba C&ocirc;, động Ti&ecirc;n C&aacute;, động Thủy Cung v&agrave; đặc biệt l&agrave; vườn chim - nơi cư tr&uacute; v&agrave; sinh sống của đa dạng c&aacute;c loại chim như c&ograve;, vạc, diệp, le le, m&ograve;ng k&eacute;t, ch&iacute;ch ch&ograve;e lửa, cho tới s&aacute;o đ&aacute;&hellip;</p>\r\n<p style=\"text-align: justify;\">\r\n	&gt;&gt;&gt;&gt;Xem ngay <a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-mien-bac/du-lich-ninh-binh/\"><strong>tour du lịch Thung Nham, Thung Chim - Ninh B&igrave;nh &nbsp;</strong></a></p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">40. Đ&egrave;o Hải V&acirc;n &ndash; Đầm Lập An &ndash; Vườn Quốc Gia Bạch M&atilde; (Thừa Thi&ecirc;n Huế)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Bắt đầu từ Hải V&acirc;n Quan, địa phận của tỉnh Thừa Thi&ecirc;n Huế tr&ecirc;n đ&egrave;o Hải V&acirc;n, k&eacute;o d&agrave;i đến vườn quốc gia Bạch M&atilde; chỉ vỏn vẹn chưa đầy 60km nhưng bảo đảm bạn sẽ ngất ng&acirc;y với những g&igrave; trước mắt. Đổ đ&egrave;o Hải V&acirc;n, bạn c&oacute; thể quan s&aacute;t vịnh Lăng C&ocirc;, đường dẫn v&agrave;o hầm Hải V&acirc;n từ tr&ecirc;n cao.</p>\r\n<div id=\"attachment_174638\">\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"Đèo Hải Vân nhìn từ Hải Vân Quan. Ảnh: Tiểu Duy\" src=\"https://cdn3.ivivu.com/2016/11/5-cung-duong-phuot-o-hue-neu-khong-di-ban-se-phai-hoi-tiec-ivivu-3.jpg\" style=\"height: 600px; width: 600px;\" title=\"Đèo Hải Vân nhìn từ Hải Vân Quan. Ảnh: Tiểu Duy\" /></p>\r\n</div>\r\n<div id=\"attachment_174639\">\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"Từ trên đỉnh đèo, bạn có thể nhìn toàn cảnh vịnh Lăng Cô. Ảnh: Tiểu Duy\" src=\"https://cdn3.ivivu.com/2016/11/5-cung-duong-phuot-o-hue-neu-khong-di-ban-se-phai-hoi-tiec-ivivu-1.jpg\" style=\"height: 600px; width: 600px;\" title=\"Từ trên đỉnh đèo, bạn có thể nhìn toàn cảnh vịnh Lăng Cô. Ảnh: Tiểu Duy\" /></p>\r\n</div>\r\n<div id=\"attachment_174640\">\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"Vịnh Lăng Cô và đường dẫn vào hầm Hải Vân. Ảnh: Hà Hải\" src=\"https://cdn3.ivivu.com/2016/11/5-cung-duong-phuot-o-hue-neu-khong-di-ban-se-phai-hoi-tiec-ivivu-4.jpg\" style=\"height: 200px; width: 600px;\" title=\"Vịnh Lăng Cô và đường dẫn vào hầm Hải Vân. Ảnh: Hà Hải\" /></p>\r\n</div>\r\n<p style=\"text-align: justify;\">\r\n	Chạy hết đ&egrave;o, bạn sẽ đến ng&atilde; ba để ra quốc lộ 1A, tuy nhi&ecirc;n, bạn kh&ocirc;ng n&ecirc;n chạy đường n&agrave;y m&agrave; h&atilde;y rẽ tr&aacute;i v&agrave;o đường Nguyễn Văn để c&oacute; thể ngắm to&agrave;n cảnh đầm Lập An v&ocirc; c&ugrave;ng đẹp v&agrave; thơ mộng. Tr&ecirc;n đường c&oacute; nhiều qu&aacute;n hải sản được dựng ngay tr&ecirc;n đầm, bạn c&oacute; thể nghỉ ch&acirc;n vừa thưởng thức hải sản, vừa ngắm cảnh rất tuyệt vời.</p>\r\n<div id=\"attachment_174641\">\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"Đầm Lập An. Ảnh: khamphahue\" src=\"https://cdn3.ivivu.com/2016/11/5-cung-duong-phuot-o-hue-neu-khong-di-ban-se-phai-hoi-tiec-ivivu-5.jpg\" style=\"height: 392px; width: 600px;\" title=\"Đầm Lập An. Ảnh: khamphahue\" /></p>\r\n</div>\r\n<p style=\"text-align: justify;\">\r\n	Từ Nguyễn Văn, bạn chạy hết đường l&agrave; ra lại quốc lộ 1A, chạy qua hầm Phước Tượng l&agrave; đến ng&atilde; ba chợ Cầu Hai, rẽ phải để v&agrave;o vườn quốc gia Bạch M&atilde;. Tham quan vườn quốc gia Bạch M&atilde; sẽ mang đến cho bạn nhiều bất ngờ. Bạn nhớ đừng bỏ qua những điểm đến si&ecirc;u đẹp của vườn quốc gia như Vọng Hải Đ&agrave;i, th&aacute;c Đỗ Quy&ecirc;n, Ngũ Hồ, th&aacute;c Trĩ Sao, rừng Ch&ograve; Đen, th&aacute;c Trượt&hellip;</p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu c&ograve;n thời gian, bạn h&atilde;y kh&aacute;m ph&aacute; thiền viện tr&uacute;c l&acirc;m Bạch M&atilde; nằm giữa hồ Truồi. Nơi n&agrave;y được v&iacute; như chốn bồng lai ti&ecirc;n cảnh. Ph&oacute;ng tầm mắt nh&igrave;n xung quanh bạn sẽ thấy những &aacute;ng m&acirc;y trắng bồng bềnh tr&ocirc;i dưới đ&aacute;y hồ nước trong xanh. Ngo&agrave;i ra, khi đến với thiền viện, bạn cũng sẽ được chi&ecirc;m ngưỡng tượng Phật Th&iacute;ch Ca lộ thi&ecirc;n đang ngồi thiền tr&ecirc;n đồi giữa hồ cao 24m, nặng 1.500 tấn bằng đ&aacute; rất uy nghi.</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#0000ff;\"><strong>41. Quốc lộ 49B: 5 đầm, 3 biển, 1 động, 1 ph&aacute; (Huế)</strong></span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Nếu l&agrave; một t&iacute;n đồ của cung đường ven biển, nhất định bạn kh&ocirc;ng thể bỏ qua cung đường quốc lộ 49B n&agrave;y. Một b&ecirc;n l&agrave; những đầm ph&aacute; rộng lớn li&ecirc;n tiếp nhau, một b&ecirc;n l&agrave; biển m&ecirc;nh m&ocirc;ng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-quoc-lo-49B-hue-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-quoc-lo-49B-hue-mixtourist.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Quốc lộ 49B sẽ đưa bạn đi qua đầm Cầu Hai, đầm Thuỷ T&uacute;, đầm H&agrave; Trung, đầm Sam, đầm Thanh Lam v&agrave; ph&aacute; Tam Giang v&ocirc; c&ugrave;ng nổi tiếng. Song song đ&oacute; cung đường c&ograve;n dẫn bạn đi qua biển Thuận An, Thuận Hải v&agrave; biển H&agrave;m Rồng.</p>\r\n<p align=\"center\">\r\n	<img alt=\"phuot-quoc-lo-49B-hue-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-quoc-lo-49B-hue-mixtourist1.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu đ&atilde; qu&aacute; quen thuộc với biển Thuận An, bạn c&oacute; thể chọn đến biển H&agrave;m Rồng vẫn c&ograve;n v&ocirc; c&ugrave;ng hoang sơ. H&agrave;m Rồng ở một vị tr&iacute; kh&aacute; đẹp, cảnh quan n&ecirc;n thơ bởi c&oacute; n&uacute;i Linh Th&aacute;i cao gần 800 m&eacute;t &aacute;n ngữ ph&iacute;a sau với những mảng rừng xanh thẳm, uốn lượn theo nhiều v&ograve;ng cung &ocirc;m lấy b&atilde;i biển d&agrave;i gần 6 c&acirc;y số, với 3 điểm tắm l&yacute; tưởng l&agrave; b&atilde;i H&agrave;m Rồng, Đ&ocirc;ng Dương v&agrave; b&atilde;i Đầm.</p>\r\n<p align=\"center\">\r\n	<img alt=\"phuot-quoc-lo-49B-hue-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-quoc-lo-49B-hue-mixtourist11.jpg\" style=\"width: 600px; height: 522px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Vẫn chưa đủ để thoả m&atilde;n bạn th&igrave; cung đường quốc lộ 49B c&ograve;n chi&ecirc;u đ&atilde;i bạn hai đặc sản m&agrave; kh&ocirc;ng nơi n&agrave;o c&oacute; được. Một l&agrave; l&agrave;ng ch&agrave;i An Bằng, nơi đ&acirc;y c&oacute; một n&eacute;t văn h&oacute;a v&ocirc; c&ugrave;ng độc đ&aacute;o m&agrave; tờ Daily Mail của Anh phải thốt l&ecirc;n rằng đ&acirc;y l&agrave; &ldquo;một nghĩa trang kỳ lạ, đầy m&agrave;u sắc v&agrave; xa hoa&rdquo;. Tại Huế, người d&acirc;n vẫn hay gọi l&agrave;ng ch&agrave;i n&agrave;y với những c&aacute;i t&ecirc;n như &ldquo;th&agrave;nh phố của người chết&rdquo; hay &ldquo;th&agrave;nh phố ma An Bằng&rdquo;.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-quoc-lo-49B-hue-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-quoc-lo-49B-hue-mixtourist111.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Theo d&acirc;n trong l&agrave;ng ch&agrave;i, 90% d&acirc;n l&agrave;ng c&oacute; họ h&agrave;ng, người th&acirc;n đang ở nước ngo&agrave;i (chủ yếu ở Mỹ) v&agrave;&nbsp;họ thường gửi tiền về để x&acirc;y cất lăng mộ. V&igrave; vậy, ngư d&acirc;n ở đ&acirc;y thường cạnh tranh nhau để x&acirc;y những ng&ocirc;i mộ ng&agrave;y c&agrave;ng cao to với nhiều hoa văn tỉ mỉ cho những người đ&atilde; chết.</p>\r\n<p align=\"center\">\r\n	<img alt=\"phuot-quoc-lo-49B-hue-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-quoc-lo-49B-hue-mixtourist1111.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đặc sản thứ hai l&agrave; động H&agrave;m R&ugrave;a, nơi n&agrave;y vẫn chưa xuất hiện tr&ecirc;n bản đồ du lịch Việt Nam. Tuy nhi&ecirc;n, bạn c&oacute; thể t&igrave;m tr&ecirc;n bản đồ v&agrave; tự m&igrave;nh kh&aacute;m ph&aacute; nh&eacute;. Vị tr&iacute; v&agrave; cảnh quan ở động H&agrave;m R&ugrave;a bảo đảm sẽ khiến bạn ngạc nhi&ecirc;n đấy.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\"><strong>42. Đ&egrave;o A Co &ndash; Th&aacute;c A Nor (Huế)</strong></span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Th&ecirc;m một cung đường đẹp ngất ng&acirc;y ở Huế đ&oacute; l&agrave; quốc lộ 49 l&ecirc;n th&aacute;c A Nor. Cung đường n&agrave;y được nhiều bạn trẻ địa phương chọn đi v&igrave; khung cảnh đẹp, dễ đi v&agrave; c&oacute; nhiều điểm dừng ch&acirc;n kh&aacute;m ph&aacute;. Từ th&agrave;nh phố Huế, men theo con đường quốc lộ 49 đến ng&atilde; ba Bốt Đỏ, bạn sẽ vượt đ&egrave;o A Co huyền thoại, cầu Mỏ Quạ, ngược d&ograve;ng s&ocirc;ng Bồ để đến A Lưới.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-deo-a-nor-thac-a-nor-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-deo-a-nor-thac-a-nor-mixtourist11.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	A Lưới l&agrave; một huyện miền n&uacute;i tiếp gi&aacute;p với L&agrave;o. Thời tiết ở đ&acirc;y gần giống như ở B&agrave; N&agrave;. Ban ng&agrave;y th&igrave; nắng nhưng kh&ocirc;ng&nbsp;gắt gao như ở th&agrave;nh phố. Đ&ecirc;m xuống rất lạnh, s&aacute;ng sớm th&igrave; mờ sương như lạc v&agrave;o &ldquo;xứ sở thần ti&ecirc;n&rdquo;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-deo-a-nor-thac-a-nor-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-deo-a-nor-thac-a-nor-mixtourist1.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Để đến được th&aacute;c, bạn tiếp tục đi qua th&ecirc;m một c&acirc;y cầu sắt lấy t&ecirc;n của một anh h&ugrave;ng&nbsp;d&acirc;n tộc: A Vẩu. Sự hiện diện của c&acirc;y cầu n&agrave;y đ&atilde; l&agrave;m cho việc đi đến th&aacute;c thuận tiện hơn trước rất nhiều. Từ xa, bạn đ&atilde; c&oacute; thể nghe tiếng nước tu&ocirc;n xuống ầm ầm. &Acirc;m thanh ấy vang vọng giữa n&uacute;i rừng, như tạo hấp lực cho bước ch&acirc;n của bạn, dẫu đ&ocirc;i ch&uacute;t mỏi mệt nhưng vẫn mạnh mẽ tiến về ph&iacute;a trước.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-deo-a-nor-thac-a-nor-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-deo-a-nor-thac-a-nor-mixtourist.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p>\r\n	Giữa rừng xanh đại ng&agrave;n, với hai tầng th&aacute;c, A Nor hiện l&ecirc;n thật kỳ vĩ. Nước tung trắng x&oacute;a cả một g&oacute;c trời. Đến A Nor, bạn sẽ kh&oacute; k&igrave;m l&ograve;ng trước d&ograve;ng nước trong veo đầy quyến rũ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-deo-a-nor-thac-a-nor-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-deo-a-nor-thac-a-nor-mixtourist111.jpg\" style=\"width: 600px; height: 800px;\" /></p>\r\n<p>\r\n	Nếu th&iacute;ch cảm gi&aacute;c mạnh v&agrave; biết bơi bạn h&atilde;y thử một lần đứng tr&ecirc;n mỏm đ&aacute; v&agrave; thả m&igrave;nh rơi t&otilde;m xuống l&ograve;ng suối. Cảm gi&aacute;c sảng kho&aacute;i kh&ocirc;ng thể n&agrave;o tả được.</p>\r\n<h3>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\"><strong>43. Quốc lộ 14: Hồ Ch&iacute; Minh &ndash; đ&egrave;o P&ecirc;Ke (Huế)</strong></span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Kh&ocirc;ng cần phải n&oacute;i nhiều về cung đường Trường Sơn huyền thoại, hay c&ograve;n gọi l&agrave; đường Hồ Ch&iacute; Minh tr&ecirc;n quốc lộ 14. Đ&acirc;y cũng l&agrave; cung đường ven bi&ecirc;n giới Việt &ndash; L&agrave;o. Ở địa phận tỉnh Thừa Thi&ecirc;n Huế, tr&ecirc;n cung đường n&agrave;y bạn sẽ phải vượt đ&egrave;o P&ecirc;Ke v&agrave; c&oacute; c&aacute;i nh&igrave;n r&otilde; n&eacute;t nhất về cuộc sống của người d&acirc;n huyện A Lưới.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-quoc-lo-14-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-quoc-lo-14-mixtourist.jpg\" style=\"width: 600px; height: 329px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	A Lưới c&ograve;n l&agrave; nơi c&oacute; nhiều địa danh gắn với một thời kh&aacute;ng chiến h&agrave;o h&ugrave;ng như đ&egrave;o Mẹ Ơi, suối M&aacute;u, đồi Thịt Băm, suối A Lin&hellip; cung đường Hồ Ch&iacute; Minh gi&uacute;p A Lưới th&ocirc;ng thương với Quảng Trị v&agrave; Quảng Nam. Nơi đ&acirc;y c&ograve;n c&oacute; c&aacute;c cửa khẩu th&ocirc;ng với&nbsp;nước bạn L&agrave;o đ&atilde; được mở của: Hồng V&acirc;n &ndash; Cu Tai, A Đớt &ndash; T&agrave; V&agrave;ng, hứa hẹn cho du lịch A Luới l&agrave; một tiềm năng đang dần được h&eacute; mở.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-quoc-lo-14-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-quoc-lo-14-mixtourist1.jpg\" style=\"width: 600px; height: 337px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Chạy dọc đường Hồ Ch&iacute; Minh, bạn sẽ đi qua nhiều rừng nguy&ecirc;n sinh nhưng rừng nguy&ecirc;n sinh được coi l&agrave; đẹp nhất v&agrave; được đưa v&agrave;o hoạt động du lịch của huyện l&agrave; khu rừng ở x&atilde; A Ro&agrave;ng, c&aacute;ch thị trấn A Lưới 30 km, k&eacute;o d&agrave;i từ A Lưới đến tận huyện Gi&agrave;ng của tỉnh Quảng Nam.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-quoc-lo-14-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-quoc-lo-14-mixtourist11.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-quoc-lo-14-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-quoc-lo-14-mixtourist111.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Khu rừng nguy&ecirc;n sinh với nhiều th&aacute;c cao, vực s&acirc;u rất hấp dẫn đối với loại h&igrave;nh du lịch sinh th&aacute;i v&agrave; d&agrave;nh cho ai th&iacute;ch phi&ecirc;u lưu, mạo hiểm, ưa cảm gi&aacute;c mạnh. Chắc chắn đ&atilde; một lần đi qua, bạn sẽ c&oacute; nhiều kỷ niệm với cung đường huyền thoại n&agrave;y.</p>\r\n<p align=\"center\">\r\n	&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\"><strong>44. Tỉnh lộ 14B: Khe Tre &ndash; th&aacute;c Mơ</strong></span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	So với 4 cung đường ph&iacute;a tr&ecirc;n th&igrave; tỉnh lộ 14B l&agrave; cung đường &iacute;t được lựa chọn nhất bởi đường đi hiểm trở, c&agrave;ng đi c&agrave;ng kh&oacute;. Cắt với quốc lộ 1A, kh&ocirc;ng kh&oacute; để bạn t&igrave;m đến tỉnh lộ 14B, cung đường &ocirc;m s&aacute;t vườn quốc gia Bạch M&atilde; h&ugrave;ng vĩ. Chạy đến hết thị trấn Khe Tre l&agrave; cũng hết đường. Từ đ&acirc;y, nếu muốn đi tiếp, bạn chỉ c&oacute; một c&aacute;ch đi bộ ngược con s&ocirc;ng Thượng Lộ để v&agrave;o vườn quốc gia.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-tinh-lo-14-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-tinh-lo-14-mixtourist.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Tuy nhi&ecirc;n, đ&oacute; lại l&agrave; một c&acirc;u chuyện kh&aacute;c d&agrave;nh cho những ai đam m&ecirc; kh&aacute;m ph&aacute; thi&ecirc;n nhi&ecirc;n.</p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu chỉ muốn du lịch nhẹ nh&agrave;ng, tr&ecirc;n đường đến thị trấn Khe Tre, bạn sẽ đi qua khu du lịch th&aacute;c Mơ, Nam Đ&ocirc;ng. Nếu ở v&ugrave;ng đồng bằng Huế đang l&agrave; những ng&agrave;y nắng n&oacute;ng th&igrave; kh&ocirc;ng kh&iacute; ở huyện miền n&uacute;i Nam Đ&ocirc;ng thời tiết lại m&aacute;t mẻ. Th&aacute;c Mơ đ&oacute;n những con nước bắt nguồn từ n&uacute;i rừng Bạch M&atilde; cao hơn 1.400m so với mực nước biển đổ về n&ecirc;n c&agrave;ng m&aacute;t mẻ hơn.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-tinh-lo-14-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-tinh-lo-14-mixtourist1.jpg\" style=\"width: 600px; height: 800px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Người d&acirc;n địa phương v&agrave; quanh v&ugrave;ng biết đến nơi n&agrave;y kh&aacute; l&acirc;u bởi cảnh sắc thi&ecirc;n nhi&ecirc;n tuyệt đẹp&nbsp;với những c&aacute;nh rừng xanh ng&aacute;t bao phủ soi m&igrave;nh dưới l&agrave;n nước trong veo, m&aacute;t lạnh. Nằm tr&ecirc;n trục đường m&ograve;n ch&iacute;nh dẫn l&ecirc;n c&aacute;c d&atilde;y n&uacute;i địa phương v&agrave; xa hơn l&agrave; d&atilde;y n&uacute;i Bạch M&atilde; k&eacute;o d&agrave;i tr&ugrave;ng điệp, th&aacute;c Mơ như một điểm nhấn giữa vẻ đẹp ng&uacute;t ng&agrave;n, h&ugrave;ng vĩ của n&uacute;i rừng xứ Huế.</p>\r\n<p align=\"center\">\r\n	<img alt=\"phuot-tinh-lo-14-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-tinh-lo-14-mixtourist11.jpg\" style=\"width: 600px; height: 800px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Bạn sẽ ấn tượng với hồ nước rộng lớn, xanh ngắt, đ&aacute;nh dấu điểm cuối c&ugrave;ng của 3 ngọn th&aacute;c bắt nguồn từ n&uacute;i cao. Được thi&ecirc;n nhi&ecirc;n ban tặng 3 vũng nước lớn kh&aacute; s&acirc;u, rộng tr&ecirc;n dưới cả 100m&sup2; mỗi vũng, đủ để phục vụ tất cả c&aacute;c du kh&aacute;ch muốn ng&acirc;m m&igrave;nh trong d&ograve;ng&nbsp;nước m&aacute;t l&agrave;nh.&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Ở ph&iacute;a đầu nguồn, một tảng đ&aacute; lớn cao ngất hiện ra ho&agrave; c&ugrave;ng tiếng nước ầm ầm đồ về&nbsp;c&aacute;c hồ b&ecirc;n dưới. C&oacute; người bảo, đ&oacute; l&agrave; &ldquo;kh&uacute;c nhạc n&uacute;i rừng&rdquo;, l&agrave; tiếng th&aacute;c, tiếng gi&oacute; h&ograve;a trong tiếng chim mu&ocirc;ng v&agrave; l&agrave; &ldquo;tiếng&rdquo; đặc trưng của th&aacute;c Mơ.</p>\r\n<h3>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">45. Biển An B&agrave;ng</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Kh&aacute;c với sự s&ocirc;i động của b&atilde;i biển Cửa Đại, sự tĩnh lặng, vắng vẻ của An B&agrave;ng sẽ khiến bạn thấy y&ecirc;u n&oacute; ngay từ lần đầu ti&ecirc;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bai-bien-an-bang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bai-bien-an-bang-mixtourist.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bai-bien-an-bang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bai-bien-an-bang-mixtourist1.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Biển An B&agrave;ng thuộc phường Cẩm An, nằm c&aacute;ch trung t&acirc;m phố cổ Hội An khoảng 3 km về ph&iacute;a Đ&ocirc;ng, đến đ&acirc;y bạn sẽ được cảm nhận sự trong l&agrave;nh tuyệt diệu khi thả bộ dọc theo triền c&aacute;t, đ&oacute;n &aacute;nh mặt trời l&ecirc;n, ngắm nh&igrave;n vẻ đẹp tinh kh&ocirc;i l&uacute;c hừng đ&ocirc;ng tr&ecirc;n mặt biển.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bai-bien-an-bang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bai-bien-an-bang-mixtourist11.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-bai-bien-an-bang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-bai-bien-an-bang-mixtourist1111.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Kh&aacute;c với sự s&ocirc;i động của b&atilde;i biển Cửa Đại, sự tĩnh lặng, vắng vẻ của An B&agrave;ng sẽ khiến bạn thấy y&ecirc;u n&oacute; ngay từ lần đầu ti&ecirc;n.</p>\r\n<h3>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">46. C&ugrave; Lao Ch&agrave;m</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Nằm c&aacute;ch bờ biển Cửa Đại khoảng 15 km v&agrave; bao gồm 8 h&ograve;n đảo lớn nhỏ kh&aacute;c nhau, C&ugrave; lao Ch&agrave;m đang dần chiếm được cảm t&igrave;nh của nhiều du kh&aacute;ch đến Hội An bởi vẻ đẹp hoang sơ y&ecirc;n b&igrave;nh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-cu-lao-cham-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-cu-lao-cham-mixtourist11.jpg\" style=\"width: 600px; height: 187px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-cu-lao-cham-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-cu-lao-cham-mixtourist.jpg\" style=\"width: 600px; height: 290px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Chỉ mất 15 ph&uacute;t l&ecirc;nh đ&ecirc;nh tr&ecirc;n cano từ biển Cửa Đại, bạn sẽ được chi&ecirc;m ngưỡng C&ugrave; Lao Ch&agrave;m hiện ra xanh m&aacute;t tầm mắt.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-cu-lao-cham-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-cu-lao-cham-mixtourist111.jpg\" style=\"width: 600px; height: 394px;\" /></p>\r\n<p>\r\n	Tới đ&acirc;y, ngo&agrave;i việc lặn biển ngắm san h&ocirc;, bạn đừng bỏ lỡ cơ hội đi xe đạp tr&ecirc;n những con đường uốn lượn qua những cồn c&aacute;t v&agrave; những l&agrave;ng ch&agrave;i ven biển.</p>\r\n<h3>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">47. Đ&agrave; Lạt (Lầm Đồng)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Đ&agrave; Lạt cũng đẹp, mỗi m&ugrave;a c&oacute; một vẻ đẹp kh&aacute;c nhau, bạn c&oacute; thể đi bất cứ m&ugrave;a n&agrave;o m&agrave; bạn th&iacute;ch.&nbsp;Tuy nhi&ecirc;n thời gian hợp l&yacute; nhất l&agrave; đầu th&aacute;ng&nbsp;11 đến th&aacute;ng 3, khi cuối thu, chớm đ&ocirc;ng v&agrave; m&ugrave;a xu&acirc;n bắt đầu. Cuối th&aacute;ng 10, đầu th&aacute;ng&nbsp;11 l&agrave; m&ugrave;a hoa d&atilde; quỳ nở.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-da-lat-lam-dong-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-da-lat-lam-dong-mixtourist.jpg\" style=\"width: 600px; height: 380px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	M&ugrave;a xu&acirc;n c&oacute;&nbsp;hoa mai anh đ&agrave;o nở v&agrave; nhiều lo&agrave;i hoa kh&aacute;c nở rộ, đ&acirc;y cũng l&agrave; m&ugrave;a đẹp nhất trong năm.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-da-lat-lam-dong-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-da-lat-lam-dong-mixtourist1.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đi&nbsp;phượt Đ&agrave; Lạt&nbsp;bằng xe m&aacute;y, bạn xuất ph&aacute;t từ S&agrave;i G&ograve;n đi theo quốc lộ Hồ Ch&iacute; Minh &ndash; Long Th&agrave;nh &ndash;&nbsp;Đồng Nai, rồi thẳng tới&nbsp;L&acirc;m Đồng chạy thẳng QL20, qua đ&egrave;o Prenn l&agrave; tới Đ&agrave; Lạt. Chạy xe m&aacute;y khoảng 8h &ndash; 9h.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-da-lat-lam-dong-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-da-lat-lam-dong-mixtourist11.jpg\" style=\"width: 600px; height: 376px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đi&nbsp;phượt&nbsp;Đ&agrave; Lạt bằng xe m&aacute;y&nbsp;bạn c&oacute; cơ hội gh&eacute; thăm th&aacute;c Dambri (Bảo Lộc),&nbsp;Pongour (Đức Trọng) ở L&acirc;m Đồng v&agrave; kh&aacute;m ph&aacute; nhiều địa điểm th&uacute; vị kh&aacute;c tr&ecirc;n đường. V&agrave; tiết kiệm được chi ph&iacute; khi thu&ecirc; xe ở Đ&agrave; Lạt nữa.</p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">III. Địa điểm phượt miền Nam</span></span></h2>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">53. Nam Du (Ki&ecirc;n Giang)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Nằm c&aacute;ch S&agrave;i G&ograve;n 250 km đường bộ, đảo Nam Du đang được xem như l&agrave; điểm đến m&agrave; d&acirc;n phượt ph&iacute;a Nam nhất định phải gh&eacute; đến trong những kỳ nghỉ lễ.</p>\r\n<p style=\"text-align: justify;\">\r\n	Nam Du c&oacute; những h&agrave;ng dừa xanh nghi&ecirc;ng b&oacute;ng bạn sẽ được tận hưởng bầu kh&ocirc;ng kh&iacute; m&aacute;t dịu thoải m&aacute;i m&agrave; kh&ocirc;ng nơi n&agrave;o c&oacute; được.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-nam-du-kien-giang-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-nam-du-kien-giang-mixtourist.jpg\" style=\"width: 600px; height: 401px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	L&agrave; nơi xa x&ocirc;i nhất của huyện đảo Ki&ecirc;n Hải (Ki&ecirc;n Giang), quần đảo Nam Du cuộn m&igrave;nh trong n&eacute;t nguy&ecirc;n sơ của v&ugrave;ng non nước c&ograve;n chưa được nhiều người biết đến, nhất định sẽ mang lại cho du kh&aacute;ch nhiều kh&aacute;m ph&aacute; th&uacute; vị. T</p>\r\n<p style=\"text-align: justify;\">\r\n	r&ecirc;n đường đến Nam Du, t&agrave;u sẽ đi ngang qua H&ograve;n Tre, H&ograve;n Sơn, do vậy bạn sẽ c&oacute; dịp chi&ecirc;m ngưỡng những h&ograve;n đảo c&ograve;n mang vẻ đẹp hoang sơ v&agrave; cảnh sắc v&ugrave;ng biển trời T&acirc;y Nam của Tổ quốc&nbsp;Việt Nam, ph&iacute;a xa l&agrave; những h&ograve;n đảo đan xen với nhau, mỗi h&ograve;n đảo c&oacute; n&eacute;t đẹp ri&ecirc;ng, kh&ocirc;ng hề trộn lẫn.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-nam-du-kien-giang-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-nam-du-kien-giang-mixtourist1.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	L&agrave;m sao bạn c&oacute; thể cưỡng lại trước cảnh b&atilde;i c&aacute;t trắng, nước trong vắt xanh như ngọc chỉ c&oacute; ở Nam Du.&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Đến đ&acirc;y, bạn c&oacute; thể lặn bắt mực, c&aacute; hoặc ngắm san h&ocirc; đủ m&agrave;u sắc. Ở đ&acirc;y, bạn sẽ được tham quan b&atilde;i C&acirc;y Mến, b&atilde;i Đ&aacute; Đen, b&atilde;i Đất Đỏ, rồi đến h&ograve;n Mấu, h&ograve;n Ngang, h&ograve;n Hai Bờ Đập&hellip; Đến dốc &Ocirc;ng T&igrave;nh ngắm to&agrave;n cảnh B&atilde;i Ngự từ tr&ecirc;n cao, chinh phục hải đăng Nam Du nằm ở đỉnh đồi cao hơn 300 m&eacute;t so với mực nước biển. Đến Nam Du v&agrave;o những ng&agrave;y Tết, bạn sẽ được tận hưởng bầu kh&ocirc;ng kh&iacute; m&aacute;t dịu thoải m&aacute;i m&agrave; kh&ocirc;ng nơi n&agrave;o c&oacute; được.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-nam-du-kien-giang-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-nam-du-kien-giang-mixtourist11.jpg\" style=\"width: 600px; height: 294px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Chẳng ai gh&eacute; thăm biển đảo m&agrave; lại bỏ về khi chưa ngắm ho&agrave;ng h&ocirc;n.&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Tiếp đến, từ h&ograve;n Mấu gh&eacute; qua h&ograve;n Dầu rất gần, vậy n&ecirc;n nếu bạn đang đi thăm h&ograve;n Mấu, h&atilde;y nhắn thuyền đưa ra h&ograve;n Dầu chơi. H&ograve;n Dầu nức tiếng với những rặng san h&ocirc; d&agrave;y v&agrave; tuyệt đẹp, vậy n&ecirc;n bạn nhất quyết kh&ocirc;ng được qu&ecirc;n k&iacute;nh bơi v&agrave; đồ bơi để lặn ngắm san h&ocirc; đ&acirc;u đấy.&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">54. Đường ven biển Ninh Thuận</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Kh&aacute;m ph&aacute; đường ven biển Ninh Thuận &ndash;&nbsp;B&igrave;nh Thuận&nbsp;kh&ocirc;ng c&ograve;n qu&aacute; xa lạ với những bạn đam m&ecirc; du lịch. Kh&ocirc;ng những vậy, năm nay hệ thống đường s&aacute; đ&atilde; được ho&agrave;n thiện n&ecirc;n bạn ho&agrave;n to&agrave;n c&oacute; thể vi vu tr&ecirc;n cung đường với một b&ecirc;n n&uacute;i, một b&ecirc;n l&agrave; biển cực đẹp n&agrave;y.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-duong-ven-bien-ninh-thuan-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-duong-ven-bien-ninh-thuan-mixtourist1.jpg\" style=\"width: 600px; height: 296px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Cung đường Ninh Thuận với một b&ecirc;n n&uacute;i, một b&ecirc;n l&agrave; biển cực đẹp.</p>\r\n<p style=\"text-align: justify;\">\r\n	Những điều đặc biệt đang đợi bạn tr&ecirc;n cung đường ven biển Ninh Thuận &ndash; B&igrave;nh Thuận ch&iacute;nh l&agrave; khung cảnh v&ocirc; c&ugrave;ng tuyệt vời m&agrave; bạn đi qua: hải đăng K&ecirc; G&agrave;, B&agrave;u Trắng, b&atilde;i đ&aacute; bảy m&agrave;u, đồi c&aacute;t Nam Cương, đồi cừu, mũi Dinh, vịnh Vĩnh Hy&hellip; v&agrave; h&agrave;ng chục b&atilde;i biển thơ mộng hiện ra trước mắt. Trải nghiệm t&igrave;m hiểu cuộc sống của những người d&acirc;n l&agrave;ng ch&agrave;i l&agrave; một trải nghiệm th&uacute; vị kh&ocirc;ng thể n&agrave;o bỏ qua tr&ecirc;n cung đường n&agrave;y.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-duong-ven-bien-ninh-thuan-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-duong-ven-bien-ninh-thuan-mixtourist11.jpg\" style=\"width: 600px; height: 394px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đứng tr&ecirc;n hải đăng nơi n&agrave;y, bạn c&oacute; thể thu v&agrave;o tầm mắt đồi c&aacute;t trắng th&ecirc;nh thang, biển trời bao la.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-duong-ven-bien-ninh-thuan-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-duong-ven-bien-ninh-thuan-mixtourist.jpg\" style=\"width: 600px; height: 600px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Mũi Dinh l&agrave; địa chỉ &iacute;t người biết đến, mang lại cho bạn những trải nghiệm th&uacute; vị như đua xe địa h&igrave;nh tr&ecirc;n đồi c&aacute;t, lướt s&oacute;ng biển,.... Đứng tr&ecirc;n hải đăng nơi n&agrave;y, bạn c&oacute; thể thu v&agrave;o tầm mắt đồi c&aacute;t trắng th&ecirc;nh thang, biển trời bao la, xa xa l&agrave; l&agrave;ng ch&agrave;i Sơn Hải, những chiếc thuyền c&acirc;u b&eacute; nhỏ&hellip;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\"><strong>55. Bảo Lộc &ndash; Đ&agrave; Lạt &ndash; Dran (Đ&agrave; Lạt)</strong></span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	C&oacute; lẽ&nbsp;Đ&agrave; Lạt&nbsp;đ&atilde; qu&aacute; quen thuộc với những ai y&ecirc;u th&iacute;ch kh&ocirc;ng kh&iacute; l&atilde;ng mạn, l&agrave;nh lạnh ở đ&acirc;y. Tuy nhi&ecirc;n, nếu chỉ tham quan th&agrave;nh phố th&ocirc;i trong ba ng&agrave;y th&igrave; dường như kh&ocirc;ng mấy th&uacute; vị.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-deo-bao-loc-da-lat-phan-rang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-deo-bao-loc-da-lat-phan-rang-mixtourist.jpg\" style=\"width: 600px; height: 403px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	H&atilde;y thu&ecirc; một chiếc xe m&aacute;y v&agrave; bắt đầu h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; xung quanh. Tham quan hồ suối V&agrave;ng, Trại M&aacute;t, đồi ch&egrave; Cầu Đất hay thị trấn Dran, chắc chắn sẽ mang đến cho bạn nhiều trải nghiệm đ&aacute;ng nhớ.</p>\r\n<p style=\"text-align: justify;\">\r\n	Kh&ocirc;ng kh&iacute; l&atilde;ng mạn sẵn s&agrave;ng cho chuyến h&agrave;nh tr&igrave;nh th&ecirc;m th&uacute; vị.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-deo-bao-loc-da-lat-phan-rang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-deo-bao-loc-da-lat-phan-rang-mixtourist1.jpg\" style=\"width: 600px; height: 401px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Tr&ecirc;n những con đường đến c&aacute;c điểm kể tr&ecirc;n, bạn sẽ được trở về đ&uacute;ng với kh&ocirc;ng kh&iacute; của Đ&agrave; Lạt c&aacute;ch đ&acirc;y 10 năm trước, l&uacute;c kh&aacute;ch du lịch vẫn chưa đ&ocirc;ng đ&uacute;c như b&acirc;y giờ, l&uacute;c m&agrave; tiếng th&ocirc;ng reo vang vọng khắp n&uacute;i đồi. Những sắc hoa Đ&agrave; Lạt như d&atilde; quỳ, cải trắng, hướng dương, phong lan&hellip; c&agrave;ng t&ocirc; điểm cho phố n&uacute;i th&ecirc;m phần rực rỡ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-deo-bao-loc-da-lat-phan-rang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-deo-bao-loc-da-lat-phan-rang-mixtourist11.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu th&iacute;ch nhộn nhịp, bạn c&oacute; thể chọn ở lại&nbsp;trung t&acirc;m Đ&agrave; Lạt&nbsp;để h&ograve;a c&ugrave;ng kh&ocirc;ng kh&iacute; du lịch Tết, nhưng nếu th&iacute;ch y&ecirc;n tĩnh, về với n&uacute;i rừng, Dran hoặc Bảo Lộc l&agrave; nơi tuyệt vời nhất.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-deo-bao-loc-da-lat-phan-rang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-deo-bao-loc-da-lat-phan-rang-mixtourist111.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Những sắc hoa Đ&agrave; Lạt như d&atilde; quỳ, cải trắng, hướng dương, phong lan&hellip; c&agrave;ng t&ocirc; điểm cho phố n&uacute;i th&ecirc;m phần rực rỡ.</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<strong><span style=\"font-size:16px;\">V&ugrave;ng biển Tứ B&igrave;nh: Hưng - Ti&ecirc;n - Lập - Ba (Kh&aacute;nh H&ograve;a)</span></strong></p>\r\n<p style=\"text-align: justify;\">\r\n	Đến với v&ugrave;ng biển tứ b&igrave;nh, bạn c&oacute; thể thỏa sức kh&aacute;m ph&aacute; những b&atilde;i biển hoang sơ bậc nhất, những l&agrave;ng ch&agrave;i vắng b&oacute;ng kh&aacute;ch du lịch v&agrave; tha hồ thưởng thức đặc sản t&ocirc;m h&ugrave;m c&ugrave;ng c&aacute;c m&oacute;n hải sản với gi&aacute; cực rẻ, đặc biệt l&agrave; v&agrave;o dịp tết.</p>\r\n<p style=\"text-align: justify;\">\r\n	Do đ&acirc;y l&agrave; v&ugrave;ng biển nằm trong khu vực qu&acirc;n sự n&ecirc;n cũng kh&aacute; hạn chế kh&aacute;ch du lịch, kh&aacute;ch nước ngo&agrave;i ho&agrave;n to&agrave;n kh&ocirc;ng được ph&eacute;p l&ecirc;n đảo. V&igrave; vậy, tứ b&igrave;nh vẫn giữ được những n&eacute;t đẹp của ri&ecirc;ng m&igrave;nh m&agrave; kh&ocirc;ng nơi n&agrave;o c&oacute; được.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">56. B&igrave;nh Ba</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Mang đặc điểm của v&ugrave;ng đảo hoang sơ, ở&nbsp;B&igrave;nh Ba&nbsp;c&oacute; rất nhiều b&atilde;i biển đẹp với c&aacute;t trắng, nước trong veo, phản chiếu trời xanh ngắt. V&ugrave;ng biển n&agrave;y cũng chưa được đưa v&agrave;o khai th&aacute;c dịch vụ du lịch n&ecirc;n mọi thứ đều nguy&ecirc;n sơ. Thi&ecirc;n nhi&ecirc;n tr&ecirc;n đảo mang đến cảm gi&aacute;c b&igrave;nh y&ecirc;n, thư thả, v&ocirc; c&ugrave;ng th&iacute;ch hợp để nghỉ ngơi, tr&aacute;nh khỏi sự x&ocirc; bồ của th&agrave;nh thị. Đặc biệt, người d&acirc;n ở đ&acirc;y rất c&oacute; &yacute; thức bảo vệ m&ocirc;i trường sống, c&aacute;c b&atilde;i biển, cầu cảng đều rất sạch v&agrave; hiếm thấy r&aacute;c.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-binh-ba-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-binh-ba-mixtourist.jpg\" style=\"width: 600px; height: 369px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	B&igrave;nh Ba c&oacute; rất nhiều b&atilde;i biển đẹp với c&aacute;t trắng, nước trong veo, phản chiếu trời xanh ngắt.&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Du kh&aacute;ch đến với B&igrave;nh Ba cũng chỉ mới v&agrave;i năm nay, số lượng cũng kh&ocirc;ng qu&aacute; nhiều. Tr&ecirc;n đảo cũng chỉ c&oacute; khoảng 700 hộ d&acirc;n với 5.000 người. T&igrave;nh trạng chen ch&uacute;c, đ&ocirc;ng đ&uacute;c ở c&aacute;c b&atilde;i biển l&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; ở đ&acirc;y. Bạn c&oacute; thể thỏa th&iacute;ch ng&acirc;m m&igrave;nh trong l&agrave;n nước sạch trong, nh&igrave;n nhắm trời biển bao la.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">57. B&igrave;nh Lập</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	V&igrave; đặc điểm nước trong v&agrave; sạch n&ecirc;n b&atilde;i n&agrave;y người d&acirc;n giăng rất nhiều lưới để nu&ocirc;i t&ocirc;m. B&atilde;i Ngang cũng được nhiều người t&igrave;m đến để kh&aacute;m ph&aacute;. Ngo&agrave;i ra, bạn c&oacute; thể thu&ecirc; t&agrave;u để ra b&atilde;i Cồn v&agrave; c&aacute;c b&atilde;i biển hoang sơ kh&aacute;c xung quanh&nbsp;B&igrave;nh Lập.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-binh-lap-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-binh-lap-mixtourist.jpg\" style=\"width: 600px; height: 398px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	B&ecirc;n cạnh đ&oacute;, bạn c&oacute; thể v&agrave;o resort Ngọc Sương để tận hưởng kh&ocirc;ng kh&iacute; trong l&agrave;nh v&agrave; y&ecirc;n tĩnh. Chỉ với v&eacute; tham quan 50.000 VND/người l&agrave; bạn đ&atilde; c&oacute; thể check in những tấm h&igrave;nh đẹp lung linh. Nơi n&agrave;y từng l&agrave; phim trường của bộ phim Những nụ h&ocirc;n rực rỡ v&agrave; phim Mỹ nh&acirc;n kế n&ecirc;n nếu chịu kh&oacute; kh&aacute;m ph&aacute;, bạn sẽ t&igrave;m ra được nhiều g&oacute;c chụp kh&ocirc;ng thua g&igrave; ở nước ngo&agrave;i.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">58. B&igrave;nh Hưng</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Đảo B&igrave;nh Hưng nằm ở vịnh nước s&acirc;u Cam Ranh, thuộc x&atilde; Cam B&igrave;nh, th&agrave;nh phố Cam Ranh, tỉnh Kh&aacute;nh H&ograve;a. Du kh&aacute;ch c&oacute; thể nh&igrave;n thấy h&ograve;n đảo xinh đẹp n&agrave;y ngay từ khi bon bon tr&ecirc;n con đ&egrave;o ngoạn mục Vĩnh Hy - B&igrave;nh Ti&ecirc;n. Du kh&aacute;ch tham quan B&igrave;nh Hưng cần tr&aacute;nh th&aacute;ng 10, 11 v&igrave; đ&acirc;y l&agrave; thời điểm mưa b&atilde;o. L&yacute; tưởng nhất vẫn l&agrave; c&aacute;c th&aacute;ng h&egrave; v&igrave; bạn sẽ được thưởng ngoạn khung cảnh biển xanh c&aacute;t trắng nắng v&agrave;ng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-binh-hung-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-binh-hung-mixtourist.jpg\" style=\"width: 600px; height: 386px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Nằm trong một v&ograve;ng cung du lịch gồm vịnh Vĩnh Hy, vườn quốc gia N&uacute;i Ch&uacute;a, biển B&igrave;nh Ti&ecirc;n, du kh&aacute;ch sẽ thỏa l&ograve;ng trải nghiệm những cung bậc kh&aacute;m ph&aacute; th&uacute; vị khi đến đ&acirc;y. Dọc con đường đi biển B&igrave;nh Ti&ecirc;n l&agrave; những con đường đ&egrave;o dốc uốn lượn, qua những con suối, hai b&ecirc;n đường m&ograve;n l&agrave; mảng c&acirc;y xanh với những mỏm đ&aacute; l&agrave;m cho du kh&aacute;ch như bước v&agrave;o thế giới kỳ ảo của thi&ecirc;n nhi&ecirc;n.&nbsp;</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">59. Biển B&igrave;nh Ti&ecirc;n &ndash; N&agrave;ng c&ocirc;ng ch&uacute;a ngủ qu&ecirc;n</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	B&atilde;i B&igrave;nh Ti&ecirc;n như một vi&ecirc;n ngọc xanh ở giữa được bao bọc xung quanh bởi rừng th&ocirc;ng v&agrave; d&atilde;y n&uacute;i đ&aacute; v&ocirc;i tr&ecirc;n mặt biển. B&atilde;i B&igrave;nh Ti&ecirc;n c&ograve;n hoang sơ chưa c&oacute; dấu ch&acirc;n của m&aacute;y m&oacute;c c&ocirc;ng nghiệp khai ph&aacute; n&ecirc;n vẫn mang một vẻ đẹp tự nhi&ecirc;n vốn c&oacute;.</p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu th&iacute;ch cắm trại tr&ecirc;n biển, bạn c&oacute; thể chọn B&igrave;nh Hưng hoặc B&igrave;nh Ti&ecirc;n, hay nếu th&iacute;ch một nơi nghỉ ngơi sạch sẽ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-binh-tien-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-binh-tien-mixtourist.jpg\" style=\"width: 600px; height: 396px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	C&ograve;n g&igrave; tuyệt vời hơn khi đến&nbsp;B&igrave;nh Ti&ecirc;n&nbsp;du lịch h&iacute;t thở kh&ocirc;ng kh&iacute; trong l&agrave;nh, tản bộ ch&acirc;n trần ngắm biển v&agrave; ngồi tr&ecirc;n một mỏm đ&aacute; thuận tiện để h&iacute;t kh&iacute; trời sớm mai, nh&igrave;n ngắm những &aacute;nh nắng xuy&ecirc;n qua lớp sương trắng x&oacute;a như những sợi tơ giăng đầy tr&ecirc;n nền biển xanh trong.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">60. Ph&uacute; Quốc (Ki&ecirc;n Giang)</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Đảo Ph&uacute; Quốc c&ugrave;ng với c&aacute;c đảo kh&aacute;c tạo th&agrave;nh huyện đảo Ph&uacute; Quốc trực thuộc tỉnh Ki&ecirc;n Giang. To&agrave;n bộ huyện đảo c&oacute; tổng diện t&iacute;ch 589,23 km&sup2;. Thị trấn Dương Đ&ocirc;ng, tọa lạc ở ph&iacute;a t&acirc;y bắc, l&agrave; thủ phủ của huyện đảo. Ph&uacute; Quốc nằm c&aacute;ch th&agrave;nh phố Rạch Gi&aacute; 120 km v&agrave; c&aacute;ch thị x&atilde; H&agrave; Ti&ecirc;n 45 km.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-phu-quoc-kien-giang-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-phu-quoc-kien-giang-mixtourist11.jpg\" style=\"width: 600px; height: 338px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Gi&aacute; thu&ecirc; xe m&aacute;y tr&ecirc;n đảo dao động trong khoảng 150k/1 ng&agrave;y, xăng xe tự đổ.</p>\r\n<p style=\"text-align: justify;\">\r\n	Ph&uacute; Quốc c&oacute; v&ocirc; v&agrave;n c&aacute;c địa điểm ăn uống v&agrave; tham quan kh&aacute;m ph&aacute;. Nếu c&oacute; thể tự kh&aacute;m ph&aacute; được c&aacute;c địa điểm vừa ngon vừa hợp với gu của bạn th&igrave; chắc chắn l&agrave; rất th&uacute; vị rồi, phải kh&ocirc;ng?</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">62. Vũng T&agrave;u</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	C&aacute;ch S&agrave;i G&ograve;n khoảng 3 tiếng đi &ocirc;-t&ocirc;, cho n&ecirc;n kh&iacute; hậu ở Vũng T&agrave;u cũng kh&ocirc;ng mấy kh&aacute;c biệt so với S&agrave;i G&ograve;n. Cũng 2 m&ugrave;a mưa-kh&ocirc; r&otilde; rệt. M&ugrave;a kh&ocirc; bắt đầu từ th&aacute;ng 11 đến th&aacute;ng 4 năm sau, c&ograve;n m&ugrave;a mưa bắt đầu từ th&aacute;ng 5 đến th&aacute;ng 10. Kh&iacute; hậu cũng kh&aacute; &ocirc;n h&ograve;a, kh&ocirc;ng qu&aacute; n&oacute;ng hay qu&aacute; lạnh.</p>\r\n<p style=\"text-align: justify;\">\r\n	Từ Tp. HCM bạn đi theo&nbsp;QL.1, qua cầu Đồng Nai gặp một ng&atilde; tư th&igrave; rẽ&nbsp;phải theo Quốc lộ 51. Đi th&ecirc;m khoảng 100km l&agrave; đến TP.Vũng T&agrave;u.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-vung-tau-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vung-tau-mixtourist11.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Ở Vũng T&agrave;u c&oacute; rất nhiều điểm du lịch cho bạn thăm quan, kh&aacute;m ph&aacute;. N&uacute;i c&oacute;, biển c&oacute;, ch&ugrave;a chiền, đảo,&hellip;kh&ocirc;ng thiếu thứ g&igrave;. Nhưng để tiết kiệm chi ph&iacute; nhất, c&aacute;c bạn n&ecirc;n đi c&aacute;c địa danh sau:</p>\r\n<p style=\"text-align: justify;\">\r\n	&ndash; Du lịch biển:&nbsp;B&atilde;i Trước, B&atilde;i Sau, C&ocirc;n Đảo, Long Hải, H&ograve;n B&agrave;.</p>\r\n<p style=\"text-align: justify;\">\r\n	&ndash; Du lịch&nbsp;t&acirc;m linh: Th&iacute;ch ca phật đ&agrave;i, Niết B&agrave;n Tịnh X&aacute;, Ch&ugrave;a Quan Thế &Acirc;m Bồ T&aacute;t, Linh Sơn Cố Tự, Tượng Ch&uacute;a Gi&ecirc;-su giang tay&hellip;.</p>\r\n<p style=\"text-align: justify;\">\r\n	&ndash; Du lịch di t&iacute;ch: Hải Đăng Vũng T&agrave;u, khu di t&iacute;ch Đ&igrave;nh Thắng Tam, Bạch Dinh, Đền &Ocirc;ng Trần (Nh&agrave; Lớn), Bảo t&agrave;ng vũ kh&iacute; cổ.</p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">IV. ĐỊA ĐIỂM PHƯỢT MIỀN T&Acirc;Y</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	Miền T&acirc;y l&agrave; một thi&ecirc;n đường du lịch l&yacute; tưởng với kh&iacute; hậu tốt, vườn c&acirc;y tr&aacute;i nhiệt đới rực rỡ, bầu kh&ocirc;ng kh&iacute; thoải m&aacute;i v&agrave; sự h&agrave;o ph&oacute;ng của cư d&acirc;n địa phương. C&oacute; thể n&oacute;i ai y&ecirc;u mến v&ugrave;ng đất v&agrave; con người miền T&acirc;y, đều y&ecirc;u mến kh&iacute; chất &ldquo;h&agrave;o sảng&rdquo; của họ. Miền T&acirc;y c&oacute; rất nhiều điều m&agrave; khi ta đ&atilde; sống v&agrave; thấm đẫm với n&oacute; rồi th&igrave; cho d&ugrave; c&oacute; đi đ&acirc;u về đ&acirc;u cũng kh&oacute; m&agrave; qu&ecirc;n.</p>\r\n<p style=\"text-align: justify;\">\r\n	C&oacute; thể n&oacute;i ai y&ecirc;u mến v&ugrave;ng đất v&agrave; con người miền T&acirc;y, đều y&ecirc;u mến kh&iacute; chất &ldquo;h&agrave;o sảng&rdquo; của họ.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-mien-tay-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-mien-tay-mixtourist11.jpg\" style=\"width: 600px; height: 401px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Trong tất cả c&aacute;c điểm đến của Việt Nam, c&oacute; lẽ kh&ocirc;ng nơi n&agrave;o ăn tết rộn r&agrave;ng như ở miền T&acirc;y Nam Bộ v&agrave; để trải nghiệm một miền qu&ecirc; s&ocirc;ng nước đ&uacute;ng chất, bạn h&atilde;y thử đi tr&ecirc;n cung đường qua <strong>9 cửa s&ocirc;ng Cửu Long</strong>. Đại trường giang M&ecirc; K&ocirc;ng chảy v&agrave;o đất Việt rồi đổ ra biển lớn bằng ch&iacute;n cửa s&ocirc;ng Cửu Long như ch&iacute;n con rồng uốn lượn.</p>\r\n<p style=\"text-align: justify;\">\r\n	Sở dĩ c&oacute; t&ecirc;n l&agrave; Cửu Long v&igrave; s&ocirc;ng đổ ra biển bằng 9 cửa. Ng&agrave;y nay, <strong>cung đường phượt 9 cửa s&ocirc;ng Cửu Long</strong> được nhiều giới trẻ đam m&ecirc;m v&igrave; phải đi qua nhiều địa h&igrave;nh kh&aacute;c nhau, mang đến nhiều trải nghiệm th&uacute; vị khi về miền T&acirc;y.</p>\r\n<p style=\"text-align: justify;\">\r\n	<strong>Cung đường phượt sẽ đi qua c&aacute;c tỉnh:</strong></p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">62. Long An</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Long An l&agrave; một tỉnh kh&aacute; rộng m&agrave; nhiều khi c&aacute;c&nbsp;phượt&nbsp;thủ cũng chưa kh&aacute;m ph&aacute; hết những địa danh nổi tiếng ở nơi n&agrave;y. Dưới đ&acirc;y l&agrave; 6 nơi m&agrave; m&igrave;nh thấy kh&aacute; hay nếu c&aacute;c bạn c&oacute; dịp gh&eacute; qua Long An th&igrave; n&ecirc;n tranh thủ nh&eacute;:&nbsp;Thắng cảnh N&uacute;i Đất,&nbsp;rừng tr&agrave;m nguy&ecirc;n sinh,&nbsp;l&agrave;ng nổi T&acirc;n Lập,&nbsp;khu L&aacute;ng Sen.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-long-an-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-long-an-mixtourist11.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Nhưng đặc biệt nhất vẫn l&agrave; <strong>khu du lịch sinh th&aacute;i Đồng Th&aacute;p Mười</strong>, c&aacute;ch thị x&atilde; T&acirc;n An khoảng 50 km, thuộc c&aacute;c huyện Mộc H&oacute;a, Vĩnh Hưng, Thạnh H&oacute;a v&agrave; T&acirc;n Thạnh. Đ&acirc;y l&agrave; khu du lịch sinh th&aacute;i đặc trưng của Long An n&oacute;i ri&ecirc;ng v&agrave; của Nam Bộ n&oacute;i chung.​</p>\r\n<p style=\"text-align: justify;\">\r\n	Ngược d&ograve;ng V&agrave;m Cỏ T&acirc;y, bạn sẽ đến được trung t&acirc;m Đồng Th&aacute;p Mười. Đến đ&acirc;y, bạn được tận mắt ngắm những c&aacute;nh rừng tr&agrave;m bạt ng&agrave;n, đầm sen rộng lớn m&ecirc;nh m&ocirc;ng đơm b&ocirc;ng khoe sắc dưới &aacute;nh mặt trời. Kh&ocirc;ng chỉ c&oacute; vậy, ở đ&acirc;y c&ograve;n c&oacute; rất nhiều động vật qu&yacute; hiếm được bảo vệ như: sếu đầu đỏ, r&ugrave;a rắn, th&uacute;&hellip; C&aacute;nh đồng c&oacute; đầm nước m&ecirc;nh m&ocirc;ng v&ocirc; tận nơi đ&acirc;y từng l&agrave; cảnh quay trong phim &ldquo;C&aacute;nh đồng hoang&rdquo; nổi tiếng.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">63. Tiền Giang</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Tiền Giang c&aacute;ch TP HCM 70 km, cầu Mỹ Lợi nối với th&agrave;nh phố Hồ Ch&iacute; Minh mới khai trương khiến khoảng c&aacute;ch chỉ c&ograve;n khoảng 25 km. Đi phượt Tiền Giang bạn sẽ được tận hưởng một cảm gi&aacute;c v&ocirc; c&ugrave;ng mới lạ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-tien-giang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-tien-giang-mixtourist.jpg\" style=\"width: 600px; height: 334px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Để chi&ecirc;m ngưỡng những vườn c&acirc;y ăn tr&aacute;i trĩu quả, với chợ nổi tr&ecirc;n s&ocirc;ng v&agrave; đừng qu&ecirc;n tham quan c&aacute;c địa danh sau nh&eacute;:</p>\r\n<p style=\"text-align: justify;\">\r\n	- Trại rắn Đồng T&acirc;m - bảo t&agrave;ng rắn đầu ti&ecirc;n của nước ta v&agrave; l&agrave; nơi lưu giữ nhiều ti&ecirc;u bản rắn nhất nước ta. Đến trại rắn, bạn sẽ dịp kh&aacute;m ph&aacute; lối sống của rắn, c&aacute;ch chiết nọc độc l&agrave;m thuốc v&agrave; xử l&yacute; ti&ecirc;u bản.</p>\r\n<p style=\"text-align: justify;\">\r\n	0 Chợ nổi C&aacute;i B&egrave; l&agrave; chợ đầu mối lớn nhất ở miền T&acirc;y Nam Bộ. Kh&aacute;c với c&aacute;c chợ nổi kh&aacute;c tại miền T&acirc;y, chợ nổi C&aacute;i B&egrave; họp suốt ng&agrave;y đ&ecirc;m tr&ecirc;n quy m&ocirc; lớn. H&agrave;ng h&oacute;a đa dạng, phong ph&uacute;.</p>\r\n<p style=\"text-align: justify;\">\r\n	- Ch&ugrave;a Vĩnh Tr&agrave;ng l&agrave; ng&ocirc;i ch&ugrave;a lớn nhất tỉnh mang d&aacute;ng vẻ kiến tr&uacute;c ch&acirc;u &Aacute; pha lẫn ch&acirc;u &Acirc;u.</p>\r\n<p style=\"text-align: justify;\">\r\n	- Gh&eacute; G&ograve; C&ocirc;ng tắm biển nước đục. Thăm di t&iacute;ch lịch sử Rạch Gầm-Xo&agrave;i M&uacute;t, di t&iacute;ch Ấp Bắc, lũy Ph&aacute;o Đ&agrave;i...</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">64. Bến Tre</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Bến Tre l&agrave; một tỉnh thuộc v&ugrave;ng đồng bằng s&ocirc;ng Cửu Long, nằm cuối nguồn s&ocirc;ng Cửu Long, tiếp gi&aacute;p biển Đ&ocirc;ng với chiều d&agrave;i đường biển khoảng 65 km v&agrave; c&aacute;c tỉnh Tiền Giang, Tr&agrave; Vinh, Vĩnh Long. Trung t&acirc;m của tỉnh Bến Tre c&aacute;ch Th&agrave;nh phố Hồ Ch&iacute; Minh 87 km về ph&iacute;a T&acirc;y qua tỉnh Tiền Giang v&agrave; Long An.&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	Từ S&agrave;i G&ograve;n đi Bến Tre c&oacute; 3 hướng, một l&agrave; từ v&ograve;ng xoay Ph&uacute; L&acirc;m, hai l&agrave; đại lộ Nguyễn Văn Linh v&agrave; ba l&agrave; cao tốc Trung Lương (mất khoảng 1 giờ). Nếu bạn di chuyển bằng xe m&aacute;y th&igrave; việc di chuyển linh hoạt giữa c&aacute;c nơi sẽ dễ d&agrave;ng hơn, đồng thời số tiền bỏ ra cho phương tiện n&agrave;y cũng kh&ocirc;ng cao.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-ben-tre-mixtourist11\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-ben-tre-mixtourist11.jpg\" style=\"width: 600px; height: 451px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Du lịch bụi hay phượt Bến Tre bạn c&oacute; thể tha hồ l&ecirc;nh đ&ecirc;nh tr&ecirc;n xuồng, nếu ch&aacute;n th&igrave; tạt v&agrave;o c&ugrave; lao, h&aacute;i tr&aacute;i c&acirc;y, bắt c&aacute;, thưởng thức vọng cổ hay ngả lưng tr&ecirc;n chiếc v&otilde;ng rợp b&oacute;ng m&aacute;t đ&aacute;nh một giấc ngon l&agrave;nh.</p>\r\n<p style=\"text-align: justify;\">\r\n	Tới đ&acirc;y, bạn đừng qu&ecirc;n gh&eacute; thăm<strong> l&agrave;ng hoa Kiểng chợ L&aacute;ch</strong>, bạn sẽ bị cho&aacute;ng ngợp bởi những loại th&uacute; Kiểng như:&nbsp;hươu, nai v&agrave; c&aacute;c con vật trong 12 con gi&aacute;p thậm ch&iacute; cả&nbsp;khủng long, c&aacute; h&oacute;a long: cao 3-4m, rồng d&agrave;i 30-40m. Ch&uacute;ng&nbsp;được uốn từ c&aacute;c loại c&acirc;y si kh&aacute;c nhau.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">65. Tr&agrave; Vinh</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Muốn du lịch phượt Tr&agrave; Vinh, du kh&aacute;ch n&ecirc;n c&acirc;n nhắc trong việc lựa chọn thời điểm. Nếu muốn tham gia c&aacute;c lễ hội, đặc biệt l&agrave; Ch&ocirc;l Chnăm Thm&acirc;y, một lễ hội truyền thống của người Khmer ở Nam Bộ, bạn n&ecirc;n đến Tr&agrave; Vinh v&agrave;o ba ng&agrave;y 12, 13 v&agrave; 14 th&aacute;ng 3 &acirc;m lịch.</p>\r\n<p style=\"text-align: justify;\">\r\n	Hoặc bạn c&oacute; thể đến Tr&agrave; Vinh v&agrave;o th&aacute;ng 10 &acirc;m lịch để tham gia lễ hội Ok Om Bok (hay c&ograve;n gọi l&agrave; lễ C&uacute;ng Trăng).</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-tra-vinh-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-tra-vinh-mixtourist.jpg\" style=\"width: 600px; height: 300px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Điểm tham quan hấp dẫn nhất của Tr&agrave; Vinh l&agrave; khu du lịch biển Ba Động. Tuy được khai th&aacute;c từ l&acirc;u nhưng biển Ba Động vẫn c&ograve;n giữ n&eacute;t hoang sơ thu h&uacute;t nhiều du kh&aacute;ch.</p>\r\n<p style=\"text-align: justify;\">\r\n	Rời biển Ba Động, bạn c&oacute; thể đến ao B&agrave; Om để thưởng l&atilde;m phong cảnh thi&ecirc;n nhi&ecirc;n. Ao B&agrave; Om thơ mộng v&agrave; hữu t&igrave;nh với những h&agrave;ng c&acirc;y cổ thụ rợp b&oacute;ng xanh m&aacute;t. Đến đ&acirc;y, ngo&agrave;i việc ngắm cảnh, du kh&aacute;ch c&ograve;n được nghe những truyền thuyết ly kỳ v&agrave; hấp dẫn về ao.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">66. S&oacute;c Trăng</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	C&aacute;ch th&agrave;nh phố Hồ Ch&iacute; Minh khoảng 231 km,&nbsp;S&oacute;c Trăng&nbsp;l&agrave; một tỉnh ven biển đồng bằng s&ocirc;ng Cửu Long, nằm ngay cửa nam s&ocirc;ng Hậu.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-soc-trang-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-soc-trang-mixtourist.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Vườn C&ograve; T&acirc;n Long&nbsp;l&agrave;&nbsp;địa điểm du lịch sinh th&aacute;i l&yacute;&nbsp;tưởng ở S&oacute;c Trăng. Đến đ&acirc;y du kh&aacute;ch sẽ được thư gi&atilde;n với kh&ocirc;ng kh&iacute; thi&ecirc;n nhi&ecirc;n thanh b&igrave;nh. Được xem h&agrave;ng ng&agrave;n con c&ograve; l&agrave;m tổ, sinh sống sẽ cho bạn cảm gi&aacute;c rất th&uacute; vị. Kh&ocirc;ng những vậy bạn c&ograve;n được thưởng thức những m&oacute;n ăn đậm chất d&acirc;n d&atilde; rất ngon v&agrave; độc đ&aacute;o.</p>\r\n<p style=\"text-align: justify;\">\r\n	<strong>Cung đường phượt về qua c&aacute;c tỉnh:</strong></p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">67. Cần Thơ</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	C&oacute; vị tr&iacute; thuận lợi l&agrave;m trung t&acirc;m của c&aacute;c tỉnh miền t&acirc;y, Cần Thơ l&agrave; điểm đến th&uacute; vị d&agrave;nh cho du kh&aacute;ch trong v&agrave; ngo&agrave;i nước khi tập trung đẩy đủ những n&eacute;t đặc trưng của một v&ugrave;ng đất miền s&ocirc;ng nước.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-can-tho-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-can-tho-mixtourist.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Cần Thơ l&agrave; một đ&ocirc; thị lớn của v&ugrave;ng Đồng bằng s&ocirc;ng Cửu Long, c&oacute; mạng lưới k&ecirc;nh rạch chằng chịt kết hợp sự nhộn nhịp của chợ nổi C&aacute;i Răng v&agrave;o s&aacute;ng sớm, vườn tr&aacute;i c&acirc;y trĩu quả v&agrave; kiến tr&uacute;c độc đ&aacute;o của nh&agrave; cổ. Tất cả tạo n&ecirc;n vẻ đẹp s&ocirc;ng nước đặc biệt kh&ocirc;ng lẫn v&agrave;o đ&acirc;u, khiến người ta truyền tai nhau rằng:</p>\r\n<p style=\"text-align: center;\">\r\n	<em>&ldquo;Cần Thơ gạo trắng nước trong</em></p>\r\n<p style=\"text-align: center;\">\r\n	<em>Ai đi tới đ&oacute; l&ograve;ng kh&ocirc;ng muốn về&rdquo;.</em></p>\r\n<p style=\"text-align: justify;\">\r\n	Địa điểm được d&acirc;n phượt đặc biệt y&ecirc;u th&iacute;ch ch&iacute;nh l&agrave; Cầu Cần Thơ&nbsp;chiếc cầu bắt qua s&ocirc;ng Hậu nối liền giữa 2 tỉnh Vĩnh Long v&agrave; Cần Thơ, c&acirc;y cầu d&acirc;y văng c&oacute; nhịp ch&iacute;nh 550 m d&agrave;i nhất khu vực Đ&ocirc;ng Nam &Aacute;.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">68. Vĩnh Long</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Vi vu&nbsp;c&ugrave;ng đ&aacute;m bạn lướt về xứ miệt vườn&nbsp;Miền T&acirc;y&nbsp;s&ocirc;ng nước theo kiểu du lịch bụi thật th&iacute;ch th&uacute;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-vinh-long-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vinh-long-mixtourist.jpg\" style=\"width: 600px; height: 435px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-vinh-long-mixtourist1.jpg\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-vinh-long-mixtourist1.jpg\" style=\"width: 600px; height: 324px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Chuyến đi về đất ch&iacute;n rồng lần n&agrave;y, bạn h&atilde;y d&agrave;nh đ&ocirc;i ba ng&agrave;y để gh&eacute; về đất&nbsp;Vĩnh Long&nbsp;b&igrave;nh dị, nơi c&oacute; chiếc <strong>c&acirc;u d&acirc;y văng&nbsp;Mỹ Thuận</strong>&nbsp;lộng lẫy, c&oacute; những khu du lịch sinh th&aacute;i hấp dẫn, những ng&ocirc;i ch&ugrave;a với lối kiến tr&uacute;c độc đ&aacute;o, những vườn c&acirc;y tr&aacute;i sum s&ecirc; v&agrave; đặc biệt l&agrave; d&ograve;ng s&ocirc;ng Tiền hiền h&ograve;a cứ lặng lẽ xu&ocirc;i m&igrave;nh đổ ra biển.</p>\r\n<h3 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:14px;\">69. Đồng Th&aacute;p</span></span></h3>\r\n<p style=\"text-align: justify;\">\r\n	Đồng Th&aacute;p&nbsp;c&aacute;ch S&agrave;i G&ograve;n 170km v&agrave; c&aacute;ch H&agrave; Nội 1.862km. Nếu khoảng c&aacute;ch tương đối, bạn ho&agrave;n to&agrave;n c&oacute; thể phượt bằng xe m&aacute;y hay xe con, h&igrave;nh thức n&agrave;y gi&uacute;p bạn kh&aacute;m ph&aacute; từng cảnh đẹp rất chi tiết tr&ecirc;n con đường tới Đồng Th&aacute;p.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dong-thap-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dong-thap-mixtourist.jpg\" style=\"width: 600px; height: 429px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Nơi đ&acirc;y nổi tiếng với vườn quốc gia Tr&agrave;m Chim &ndash; Tam N&ocirc;ng c&oacute; diện t&iacute;ch tự nhi&ecirc;n 7.612 ha, thuộc địa phận 5 x&atilde; : T&acirc;n C&ocirc;ng Sinh, Ph&uacute; Đức, Ph&uacute; Thọ, Ph&uacute; Th&agrave;nh, Ph&uacute; Hiệp v&agrave; thị trấn Tr&agrave;m Chim&ndash; huyện Tam N&ocirc;ng, tỉnh Đồng Th&aacute;p.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dong-thap-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dong-thap-mixtourist1.jpg\" style=\"width: 600px; height: 387px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Ngo&agrave;i ra Đồng Th&aacute;p cũng c&oacute; cảnh quan thi&ecirc;n nhi&ecirc;n tuyệt đẹp c&ugrave;ng với hệ sinh th&aacute;i v&ocirc; c&ugrave;ng phong ph&uacute; khoảng 130 lo&agrave;i thực vật kh&aacute;c nhau,đồng thời nơi n&agrave;y cũng ch&iacute;nh l&agrave; nơi cư tr&uacute; của h&agrave;ng trăm lo&agrave;i động vật c&oacute; xương sống, h&agrave;ng chục lo&agrave;i c&aacute; v&agrave; hơn 198 lo&agrave;i chim nước, chiếm khoảng 1/4 số lo&agrave;i chim c&oacute; ở Việt Nam.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"phuot-dong-thap-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-dong-thap-mixtourist11.jpg\" style=\"width: 600px; height: 428px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Sau khi tổng hợp b&agrave;i viết n&agrave;y, <strong>MixTourist </strong>tin rằng đ&atilde; bổ sung kh&aacute; nhiều địa điểm th&uacute; vị v&agrave;o lịch tr&igrave;nh của c&aacute;c phượt thủ. Bạn l&agrave; một phượt thủ miền Bắc, miền Trung hay miền Nam, d&ugrave; c&oacute; ở đ&acirc;u th&igrave; h&atilde;y cố gắng đi mỗi miền v&agrave;i địa điểm nổi bật nhất để biết đất nước m&igrave;nh đẹp đến nhường n&agrave;o c&aacute;c bạn nh&eacute;!</p>\r\n<p style=\"text-align: justify;\">\r\n	<i>C&oacute; một c&aacute;ch đơn giản hơn l&agrave; h&atilde;y đăng k&yacute; <a href=\"#\"><strong>tour du lịch xuy&ecirc;n Việt</strong></a> của <strong>AZbooking</strong>. Bạn muốn đi bất cứ đ&acirc;u, th&igrave; ch&uacute;ng t&ocirc;i sẽ đưa bạn tới đ&oacute;. </i></p>\r\n<p style=\"text-align: center;\">\r\n	<strong><i>&quot;Chỉ cần bạn x&aacute;ch ba l&ocirc; l&ecirc;n th&ocirc;i, c&ograve;n mọi thứ kh&aacute;c h&atilde;y để AZ lo&quot;</i></strong></p>\r\n<p style=\"text-align: justify;\">\r\n	<i>Li&ecirc;n hệ <strong>04 6281 4340</strong> để được tư vấn miễn ph&iacute; nh&eacute;!</i></p>\r\n<p style=\"margin-left: 40px; text-align: center;\">\r\n	<strong><i>H&Atilde;Y CHIA SẺ KHOẢNH KHẮC ĐẸP CỦA CH&Iacute;NH BẠN VỚI AZBOOKING QUA FB &quot;</i><a href=\"https://www.facebook.com/azbooking.vietnam/\">azbooking.vietnam</a><i>&quot;, CH&Uacute;NG T&Ocirc;I SẼ UPDATE V&Agrave;O B&Agrave;I VIẾT N&Agrave;Y ĐỂ CHIA SẺ CHO CỘNG ĐỒNG PHƯỢT VIỆT NAM NH&Eacute;!&nbsp;</i></strong></p>\r\n', 'Tổng hợp 69 địa điểm từ Bắc chí Nam làm say lòng mọi phượt thủ', 'địa điểm du lịch, địa điểm phượt, thời điểm thích hợp để đi phượt, địa điểm phượt miền bắc, địa điểm phượt miền trung, địa điểm phượt miền nam', 'Đi phượt là sở thích của hầu hết giới trẻ hiện nay. Đi phượt để khám phá, thích mạo hiểm, thỏa mãn đam mê, du lịch thậm chí là tìm ', '2017-05-16 09:46:28');
INSERT INTO `news` (`id`, `danhmuc_id`, `name`, `name_url`, `img`, `view`, `content`, `title`, `keyword`, `description`, `created`) VALUES
(10, 1, 'Tưng bừng lễ hội Té nước Songkran Thái Lan', 'tung-bung-le-hoi-te-nuoc-songkran-thai-lan', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/le-hoi-te-nuoc-2.jpg', 87, '<p>\r\n	Tết Songkran diễn ra từ ng&agrave;y 13/4 đến 15/4 l&agrave; một trong những lễ hội truyền thống lớn nhất của người d&acirc;n &ldquo;Xứ sở ch&ugrave;a V&agrave;ng.&nbsp;<span style=\"color: rgb(17, 17, 17);\">V&agrave;o dịp n&agrave;y, người Th&aacute;i sẽ dọn dẹp nh&agrave; cửa, lau ch&ugrave;i c&aacute;c bức ảnh của tượng Phật, đi ch&ugrave;a&hellip; đ&acirc;y cũng ch&iacute;nh l&agrave; thời điểm người Th&aacute;i tỏ l&ograve;ng k&iacute;nh trọng với Đức Phật. Hoạt động đặc biệt nhất trong lễ hội Songkran l&agrave; mọi người sẽ t&eacute; nước v&agrave;o nhau, nhằm ch&agrave;o mừng năm mới, rũ bỏ hết những c&aacute;i cũ để đ&oacute;n một năm mới hạnh ph&uacute;c hơn.</span></p>\r\n<p>\r\n	B&ecirc;n cạnh đ&oacute;, với người d&acirc;n Th&aacute;i Lan, Songkran ch&iacute;nh l&agrave; dịp để mọi người x&iacute;ch lại gần nhau hơn, kh&ocirc;ng ph&acirc;n biệt m&agrave;u da, d&acirc;n tộc, h&ograve;a chung v&agrave;o kh&ocirc;ng kh&iacute; nhộn nhịp, vui tươi đ&oacute;n ch&agrave;o năm mới, thắt chặt th&ecirc;m t&igrave;nh hữu nghị kh&ocirc;ng bi&ecirc;n giới. Khoảnh khắc mọi người t&eacute; nước v&agrave;o nhau vừa l&agrave; những ph&uacute;t gi&acirc;y thư gi&atilde;n v&ocirc; c&ugrave;ng thoải m&aacute;i vừa thể hiện tinh thần bạn b&egrave; kh&ocirc;ng bi&ecirc;n giới.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/le-hoi-te-nuoc.jpg\" style=\"color: rgb(17, 17, 17); text-align: center; width: 640px; height: 480px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000cd;\"><em>Người d&acirc;n Th&aacute;i Lan v&agrave; cả du kh&aacute;ch đều ra đường tham gia &quot;cuộc chiến nước&quot;</em></span></p>\r\n<p>\r\n	Đến với Lễ hội Songkran, bạn sẽ c&oacute; cơ hội được những người d&acirc;n nơi đ&acirc;y d&ugrave;ng mọi thứ c&oacute; thể chứa nước như g&aacute;o, th&ugrave;ng, x&ocirc;, s&uacute;ng&hellip; để t&eacute; nước l&ecirc;n người bạn, bởi theo quan niệm của người Th&aacute;i, những người c&agrave;ng được t&eacute; nhiều nước c&agrave;ng may mắn. Do vậy, muốn được may mắn, h&atilde;y tranh thủ để được t&eacute; c&agrave;ng nhiều nước c&agrave;ng tốt nh&eacute;!</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/te-nuoc(1).jpg\" style=\"width: 650px; height: 435px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<em><span style=\"color:#0000cd;\">S&uacute;ng nước, g&aacute;o hay thậm ch&iacute; cả xo chậu cũng được d&ugrave;ng để t&eacute; nước&nbsp;</span></em><em><span style=\"color:#0000cd;\"><img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/le-hoi-te-nuoc-ivivu-4.jpg\" style=\"width: 650px; height: 442px;\" /></span></em></p>\r\n<p style=\"text-align: center;\">\r\n	<em><span style=\"color:#0000cd;\">C&aacute;c ch&uacute; cảnh s&aacute;t cũng tham gia t&eacute; nước rất nhiệt t&igrave;nh</span></em></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Xuy&ecirc;n suốt thời gian diễn ra lễ hội, nhiều cuộc diễu h&agrave;nh v&agrave; c&aacute;c cuộc thi sắc đẹp sẽ được tổ chức b&ecirc;n cạnh việc nấu c&aacute;c m&oacute;n ăn truyền thống v&agrave; mặc c&aacute;c trang phục nhiều m&agrave;u sắc. Song song đ&oacute; l&agrave;&nbsp;những nghi lễ thể hiện l&ograve;ng t&ocirc;n k&iacute;nh với Đức Phật c&ugrave;ng mong ước hạnh ph&uacute;c sum vầy cho mọi nh&agrave;&hellip;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/songkran_buddha_thai_man.jpg\" style=\"width: 650px; height: 975px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">NHỮNG ĐIỂM ĐẾN L&Yacute; TƯỞNG THAM GIA LỄ HỘI T&Eacute; NƯỚC TẠI TH&Aacute;I LAN</span></span></h2>\r\n<p>\r\n	Mặc d&ugrave; Songkran được diễn ra tr&ecirc;n khắp cả Th&aacute;i Lan nhưng t&ugrave;y theo từng khu vực m&agrave; Tết t&eacute; nước sẽ c&oacute; nhiều hoạt động kh&aacute;c nhau. V&igrave; thế <a href=\"http://azbooking.vn/\"><span style=\"color:#ff0000;\">Azbooking&nbsp;</span></a>sẽ giới thiệu cho bạn một v&agrave;i địa điểm kh&ocirc;ng thể bỏ qua trong lễ hội Songkran lần n&agrave;y.</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\">Bangkok</span></h3>\r\n<p>\r\n	L&agrave; thủ đ&ocirc; của Th&aacute;i Lan, Bangkok l&agrave; một trong những điểm đến kh&ocirc;ng thể bỏ lỡ trong lễ hội t&eacute; nước. Tại Bangkok, lễ hội sẽ được diễn ra xung quanh khu vực Banglamphu, đặc biệt l&agrave; đường Khao San, đường Phra Athit, Santhichai Prakan, Wisut Krasat v&agrave; quảng trường Ho&agrave;ng gia Rattanakosin. Khi tham gia lễ hội bạn nhớ mang theo x&ocirc; nước hoặc s&uacute;ng nước nh&eacute;. Đặc biệt, bạn cũng n&ecirc;n để c&aacute;c thiết bị điện tử c&oacute; gi&aacute; trị tại kh&aacute;ch sạn. Bạn chỉ cần mang theo một &iacute;t tiền, bỏ v&agrave;o t&uacute;i chống thấm nước v&agrave; sau đ&oacute; h&ograve;a m&igrave;nh v&agrave;o kh&ocirc;ng kh&iacute; s&ocirc;i động tại đ&acirc;y.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/le-hoi-te-nuoc2.jpg\" style=\"width: 650px; height: 432px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000cd;\"><em>D&ograve;ng người đổ về c&aacute;c đường phố Bangkok tham gia lễ hội nước</em></span></p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><strong>Pattaya</strong></span></h3>\r\n<p>\r\n	C&oacute; rất nhiều hoạt động diễn ra ở Pattaya trong dịp n&agrave;y như: lễ diễu h&agrave;nh, c&aacute;c sự kiện văn h&oacute;a đặc sắc v&agrave; tất nhi&ecirc;n lễ hội sẽ kết th&uacute;c bằng một &ldquo;cuộc chiến nước&rdquo;. Đặc biệt, khi tham dự lễ hội c&aacute;c bạn cũng nhớ mang theo x&ocirc; nước hoặc s&uacute;ng nước nh&eacute;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/le-hoi-te-nuoc-az.jpg\" style=\"width: 650px; height: 488px;\" /></p>\r\n<h3>\r\n	<strong>Phuket</strong></h3>\r\n<p>\r\n	Ngo&agrave;i việc t&eacute; nước v&agrave;o nhau, th&igrave; trong Tết t&eacute; nước ở Phuket mọi người sẽ đến thăm nh&agrave; của người th&acirc;n v&agrave; cầu nguyện. B&ecirc;n cạnh đ&oacute;, người d&acirc;n ở đ&acirc;y cũng d&ugrave;ng nước c&oacute; m&ugrave;i thơm để lau nh&agrave;, rửa tượng Phật v&agrave; b&agrave;n thờ của gia đ&igrave;nh với mong muốn gặp nhiều may mắn. Tham gia lễ hội, du kh&aacute;ch c&oacute; thể h&ograve;a v&agrave;o d&ograve;ng người để rước tượng Phật Phra Phuttha Sihing dọc theo b&atilde;i biển Patong v&agrave; vảy nước hoa v&agrave;o những người lớn tuổi để tỏ l&ograve;ng t&ocirc;n k&iacute;nh.</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><strong>Chiang Mai</strong></span></h3>\r\n<p>\r\n	Được mệnh danh l&agrave; &ldquo;<strong>đ&oacute;a hồng của phương Bắc</strong>&rdquo; của Th&aacute;i Lan, Chiang Mai cũng l&agrave; một th&agrave;nh phố nổi tiếng với lễ hội t&eacute; nước. Đến với Chiang Mai, du kh&aacute;ch c&oacute; thể tham gia v&agrave;o lễ khai mạc Li&ecirc;n hoan Songkran với một cuộc diễu h&agrave;nh xung quanh th&agrave;nh phố. Bạn c&oacute; thể h&ograve;a m&igrave;nh v&agrave;o kh&ocirc;ng kh&iacute; lễ hội ở đ&acirc;y bằng c&aacute;ch t&eacute; nước hoa nh&agrave;i v&agrave;o đức Phật hay những người lớn tuổi, thưởng thức những m&agrave;n biểu diễn văn h&oacute;a truyền thống theo phong c&aacute;ch Lanna hoặc thưởng thức c&aacute;c m&oacute;n ăn đường phố đặc trưng tại Chiang Mai.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/gieu-hanh-trong-le-te-nuoc.jpg\" style=\"width: 650px; height: 431px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000cd;\"><em>Tham gia buổi lễ giễu h&agrave;nh khai mạc tại Chiang Mai</em></span></p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><strong>Một số lời khuy&ecirc;n nho nhỏ khi tham gia lễ hội t&eacute; nước Songkran Th&aacute;i Lan:</strong></span></h3>\r\n<ul>\r\n	<li>\r\n		H&atilde;y mặc những bộ đồ thoải m&aacute;i v&agrave; kh&ocirc;ng m&agrave;ng đến việc n&oacute; sẽ bị bẩn. Phổ biến l&agrave; &aacute;o ph&ocirc;ng sẫm m&agrave;u, quần short.</li>\r\n	<li>\r\n		Những thiết bị điện tử, giấy tờ t&ugrave;y th&acirc;n khi mang theo cần được để trong t&uacute;i nhựa kh&oacute;a k&iacute;n hoặc mang theo thiết bị chống thấm nước hay tốt nhất l&agrave; để tại kh&aacute;ch sạn. Ri&ecirc;ng giấy tờ t&ugrave;y th&acirc;n n&ecirc;n mang theo bản photo.</li>\r\n	<li>\r\n		H&atilde;y trang bị một khẩu s&uacute;ng nước hoặc một x&ocirc; nước để c&oacute; thể h&ograve;a m&igrave;nh v&agrave;o lễ hội v&agrave; trải nghiệm những gi&acirc;y ph&uacute;t th&uacute; vị nhất.</li>\r\n	<li>\r\n		Đừng t&eacute; nước l&ecirc;n c&aacute;c nh&agrave; sư, người lớn tuổi v&agrave; trẻ nhỏ</li>\r\n	<li>\r\n		Kh&ocirc;ng n&ecirc;n d&ugrave;ng nước bẩn hoặc nước đ&aacute; lạnh để chơi t&eacute; nước nh&eacute;, v&igrave; c&oacute; thể bạn sẽ l&agrave;m người kh&aacute;c kh&ocirc;ng thoải m&aacute;i</li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n', 'Lễ hội nước Songkran', 'du lịch thái lan lễ hội nước, lễ hội nước Song kran có gì, một số lưu ý khi tham gia lễ hội nước Thái lan', 'Tết Songkran diễn ra từ ngày 13/4 đến 15/4 là một trong những lễ hội truyền thống lớn nhất của người dân “Xứ sở chùa Vàng', '2017-04-10 14:31:32'),
(11, 1, 'Top 5 địa điểm hút khách nhất Bangkok- Thái Lan', 'top-5-dia-diem-hut-khach-nhat-bangkok--thai-lan', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/du-lich-thai-lan-azbooking', 99, '<p>\r\n	<strong>Bangkok</strong> l&agrave; thủ đ&ocirc; v&agrave; l&agrave; th&agrave;nh phố lớn nhất tại Th&aacute;i Lan,c&oacute; nền kinh tế ph&aacute;t triển đ&oacute;n h&agrave;ng triệu lượt du kh&aacute;ch mỗi năm. Sở dĩ nơi đ&acirc;y thu h&uacute;t rất nhiều kh&aacute;ch du lịch như vậy bởi Bangkok c&oacute; v&ocirc; số c&aacute;c điểm tham quan t&ocirc;n gi&aacute;o, văn h&oacute;a, những show diễn ho&agrave;nh tr&aacute;ng v&agrave; cả những điểm đến c&oacute; một kh&ocirc;ng hai, chắc chắn những chuyến đi tại c&aacute;c địa điểm n&agrave;y sẽ mang đến cho bạn những trải nghiệm đ&aacute;ng nhớ tại xứ sở Ch&ugrave;a V&agrave;ng. Bạn đ&atilde; sẵn s&agrave;ng l&ecirc;n đường kh&aacute;m ph&aacute; ngay<strong>&nbsp;Top 5 địa điểm được y&ecirc;u th&iacute;ch nhất </strong>c&ugrave;ng<a href=\"http://azbooking.vn\"><span style=\"color:#0000ff;\">&nbsp;Azbooking</span></a>?</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">1.Grand Palace &ndash; Ho&agrave;ng Cung Th&aacute;i Lan</span></span></h2>\r\n<p>\r\n	Đứng đầu &ldquo;<strong>bảng xếp hạng&rdquo;</strong> phải kể đến l&agrave; <strong>Grand Palace</strong> lộng lẫy v&agrave; ho&agrave;ng tr&aacute;ng. Đ&acirc;y kh&ocirc;ng nghi ngời g&igrave; ch&iacute;nh l&agrave; cột mốc nổi tiếng nhất th&agrave;nh phố Bangkok. Grand Palace được x&acirc;y dựng v&agrave;o năm 1782 v&agrave; l&agrave; nh&agrave; của c&aacute;c bậc đế vương trong v&ograve;ng 150 năm, từ quốc vương cho đến quốc hội ho&agrave;ng gia cũng như c&aacute;c vị tr&iacute; điều h&agrave;nh lớn trong ch&iacute;nh phủ. Grand Palace Bangkok đ&uacute;ng l&agrave; một nơi cũ v&agrave; l&acirc;u đời nhưng vẫn thu h&uacute;t c&aacute;c du kh&aacute;ch đến kh&ocirc;ng ngừng nghỉ, bị cho&aacute;ng bởi vẻ đẹp của kiến tr&uacute;c v&agrave; c&aacute;c chi tiết c&ocirc;ng phu.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/bangkok-grand-palace.jpg\" style=\"width: 650px; height: 432px;\" /></p>\r\n<p>\r\n	Khu&ocirc;n vi&ecirc;n Grand Palace c&oacute; h&igrave;nh chữ nhật được trải tr&ecirc;n 1 diện t&iacute;ch v&ocirc; c&ugrave;ng rộng lớn cỡ khoảng&nbsp;218.400 m&eacute;t vu&ocirc;ng, được bao quanh bởi bốn bức tường r&agrave;ovới c&aacute;c c&ocirc;ng tr&igrave;nh kiến tr&uacute;c cầu k&igrave; v&agrave; v&ocirc; c&ugrave;ng lộng lẫy như: Ch&ugrave;a Phật Ngọc, Trung điện, c&aacute;c to&agrave;n&nbsp; nh&agrave; Phra Maha,&hellip;.</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">2.Chợ nổi Damnoen Saduak&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></h2>\r\n<h3 style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\"><img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/cho-noi.jpg\" style=\"width: 650px; height: 359px;\" />&nbsp;&nbsp;&nbsp;&nbsp;</span></span></h3>\r\n<p>\r\n	L&agrave; một trong những chợ nổi đầu ti&ecirc;n, với quy m&ocirc; lớn nhất v&agrave; nổi tiếng nhất Bangkok, <strong>Chợ nổi Damnoen Saduuak</strong>&nbsp;mang đến cho du kh&aacute;ch trải nghiệm về những hoạt động <strong>bu&ocirc;n b&aacute;n ngay tr&ecirc;n s&ocirc;ng</strong>. Với những chiếc thuyền nhỏ, h&agrave;ng h&oacute;a đủ m&agrave;u sắc v&agrave; những m&oacute;n ăn vừa được chế biến, khu chợ nổi n&agrave;y l&agrave; một trong những điểm đến quyến rũ nhất Bangkok v&agrave; cả Đ&ocirc;ng Nam &Aacute;. Nếu đ&atilde; đến Bangkok m&agrave; chưa đến nơi n&agrave;y sẽ l&agrave; một thiếu s&oacute;t rất lớn cho chuyến du lịch của bạn v&agrave; gia đ&igrave;nh đ&oacute; nh&eacute;!</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">3.D&ograve;ng s&ocirc;ng Chao Phraya</span></span></h2>\r\n<p>\r\n	Nh&igrave;n ngắm những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c đặc trưng của th&agrave;nh phố ven bờ s&ocirc;ng Chao Phraya v&agrave;<strong> thưởng thức những bữa tối thịnh soạn ven s&ocirc;ng</strong> l&agrave; một trải nghiệm v&ocirc; c&ugrave;ng độc đ&aacute;o tại Bangkok. Bạn c&oacute; hai lựa chọn cho những chuyến h&agrave;nh tr&igrave;nh n&agrave;y, hoặc những chiếc du thuyền hạng sang, hiện đại như <strong>Princess Chao Phraya</strong>&nbsp;hoặc l&ecirc;nh đ&ecirc;nh tr&ecirc;n những chiếc thuyền kiểu cổ như <strong>Loy Nava.</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/du-lich-tren-song-chao-phraya.jpg\" style=\"width: 650px; height: 488px;\" /></p>\r\n<p>\r\n	D&ugrave; bạn chọn lựa thế n&agrave;o, những chiếc thuyền nhỏ n&agrave;y cũng mang đến cho bạn những trải nghiệm th&uacute; vị tr&ecirc;n d&ograve;ng s&ocirc;ng của c&aacute;c vị vua với những bữa ăn đậm đ&agrave; chất Th&aacute;i. C&ugrave;ng với đ&oacute; l&agrave; những m&agrave;n tr&igrave;nh diễn nghệ thuật đến từ những nghệ nh&acirc;n người Th&aacute;i, chắc chắn đảm bảo cho bạn một bữa ăn đầy đủ sắc &ndash; hương &ndash; vị với chất lượng tuyệt hảo nhất.</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">4.Ch&ugrave;a Wat Pho</span></span></h2>\r\n<p>\r\n	Wat Pho (ng&ocirc;i đền của vị Phật nằm), hoặc c&oacute; c&aacute;i t&ecirc;n kh&aacute;c l&agrave; Wat Phra Chetuphon, nằm ở ph&iacute;a sau của Temple of the Emerald Buddha (ng&ocirc;i đền của Phật Emerald) v&agrave; l&agrave; nơi nhất định phải gh&eacute; thăm cho bất k&igrave; ai đến Bangkok lần đầu ti&ecirc;n. Đ&acirc;y l&agrave; một trong những ng&ocirc;i đền phức hợp lớn nhất ở trong th&agrave;nh phố.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tuong-phat-nam.jpg\" style=\"width: 650px; height: 433px;\" /></p>\r\n<p>\r\n	Nơi đ&acirc;y nổi tiếng v&igrave; bức tượng khổng lồ của một vị Phật đang nằm c&oacute; chiều d&agrave;i tới tận 46 m&eacute;t v&agrave; được bao phủ bằng v&agrave;ng đ&atilde; c&oacute; từ c&aacute;ch đ&acirc;y 200 năm khi Bangkok mới trở th&agrave;nh thủ đồ của Th&aacute;i Lan.</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">5.Đường Khaosan</span></span></h2>\r\n<p>\r\n	Cuốn s&aacute;ch nổi tiếng &ldquo;<strong>The Beach</strong>&rdquo; đ&atilde; m&ocirc; tả con đường Khao San l&agrave; <strong>&ldquo;trung t&acirc;m của vũ trụ d&agrave;nh cho d&acirc;n du lịch bụi&rdquo;</strong>. X&eacute;t về khoản c&aacute;c du kh&aacute;ch t&igrave;m kiếm sự thật đến đ&acirc;y để mua sắm, trao đổi những mẩu chuyễn v&agrave; chuẩn bị cho điểm đến kế tiếp trong cuộc h&agrave;nh tr&igrave;nh backpacker, th&igrave; đ&acirc;y đ&uacute;ng l&agrave; một cụm từ t&oacute;m tắt tất cả mọi thứ một c&aacute;ch ho&agrave;n hảo. Chỉ vỏn vẹn <strong>khoảng 1km đường</strong> l&agrave; nhiều<strong> v&ocirc; số kể </strong>những nh&agrave; kh&aacute;ch với gi&aacute; hợp l&yacute; cũng như kh&aacute;ch sạn tầm trung, <strong><em>internet caf&eacute;, bars v&agrave; clubs, nh&agrave; h&agrave;ng, qu&aacute;n massage, c&aacute;c c&ocirc;ng ty du lịch, nh&agrave; s&aacute;ch, quầy h&agrave;ng, cửa h&agrave;ng xăm</em></strong> v&agrave; nhiều nhiều nữa.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/khaosan.jpg\" style=\"width: 650px; height: 464px;\" /></p>\r\n<p>\r\n	Bạn đ&atilde; đi qua hết những địa điểm tr&ecirc;n? Nếu c&acirc;u trả lời l&agrave; c&oacute;, vậy chắc chắn bạn đ&atilde; c&oacute; những kỷ niệm cực kỳ vui vẻ trong chuyến du lịch Bangkok v&agrave; h&atilde;y chia sẻ với <a href=\"http://azbooking.vn\"><span style=\"color:#0000ff;\">Azbooking</span></a> những trải nghiệm tuyệt vời của bạn nh&eacute;! C&ograve;n nếu bạn chưa đi, h&atilde;y l&ecirc;n kế hoạch phượt một chuyến Bangkok hoặc đăng k&iacute;<a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-chau-a/du-lich-thai-lan/\"><span style=\"color:#000080;\"> t</span><span style=\"color:#0000ff;\">our du lịch Th&aacute;i Lan trọn g&oacute;i </span></a>của <span style=\"color:#ff0000;\">Azbooking</span> để kh&aacute;m ph&aacute; ngay những địa điểm thu vị.</p>\r\n', 'top 5 địa điểm hút khách nhất Bangkok', 'top 5 địa điểm hút khách nhất, những điểm nhất định phải đi ở Bangkok, du lịch Bangkok nên đi những đâu', '', '2017-03-13 17:04:39'),
(12, 1, '7 món ăn nhất định phải thử khi đến Thái Lan', '7-mon-an-nhat-dinh-phai-thu-khi-den-thai-lan', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/xoi-xoai.jpg', 175, '<p>\r\n	Th&aacute;i Lan kh&ocirc;ng chỉ hấp dẫn du kh&aacute;ch tr&ecirc;n to&agrave;n thế giới với hệ thống danh lam thắng cảnh đồ sộ, m&agrave; xứ sở Ch&ugrave;a V&agrave;ng c&ograve;n c&oacute; nền ẩm thực rất đa dạng với những m&oacute;n ăn nổi tiếng. Dưới đ&acirc;y l&agrave; những m&oacute;n ăn đặc trưng của ẩm thực m&agrave; bất cứ du kh&aacute;ch n&agrave;o cũng n&ecirc;n nếm thử khi c&oacute; dịp đến Th&aacute;i.</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">1.Lẩu Th&aacute;i</span></span></h2>\r\n<p>\r\n	Chẳng cần phải giới thiệu nhiều, Lẩu Th&aacute;i vẫn lu&ocirc;n gi&agrave;nh được sự ưu &aacute;i khi nhắc đến m&oacute;n ngon xứ ch&ugrave;a v&agrave;ng bởi sự tươi ngon v&agrave; tinh tế trong từng nguy&ecirc;n liệu v&agrave; gia vị.Thực kh&aacute;ch khi ăn sẽ nh&uacute;ng thịt, hải sản v&agrave; rau v&agrave;o nồi nước d&ugrave;ng đang đun s&ocirc;i s&ugrave;ng sục tr&ecirc;n b&agrave;n. Hương vị đặc trưng của lẩu Th&aacute;i l&agrave; chua v&agrave; cay, thơm nồng m&ugrave;i của riềng, sả, l&aacute; chanh Th&aacute;i, cay x&egrave; của ớt.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/lau-thai.jpg\" style=\"width: 650px; height: 433px;\" /></p>\r\n<p>\r\n	C&aacute;c nguy&ecirc;n liệu thường thấy của lẩu Th&aacute;i l&agrave; thịt b&ograve;, lợn, rau muống, cải thảo, bắp chuối th&aacute;i mỏng, nấm tuyết, c&agrave; rốt, t&ocirc;m, mực... Lẩu Th&aacute;i được chế biến c&ocirc;ng phu. M&oacute;n ăn n&agrave;y c&oacute; nhiều điểm chung với m&oacute;n lẩu Nhật Bản v&agrave; Trung quốc. Đ&acirc;y l&agrave; m&oacute;n ăn được n&acirc;ng cấp biến thể từ canh chua Th&aacute;i (tom yum) rồi trở n&ecirc;n nổi tiếng tr&ecirc;n to&agrave;n cầu.</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">2.Canh t&ocirc;m chua tom yum kiểu Th&aacute;i</span></span></h2>\r\n<p>\r\n	Tom yum l&agrave; m&oacute;n canh chua cay đăc trưng của người Th&aacute;i, trở n&ecirc;n nổi tiếng tr&ecirc;n to&agrave;n thế giới. M&oacute;n ăn l&agrave; sự tổng h&ograve;a của nhiều loại gia vị v&agrave; rau thơm, với hương vị cay nồng nổi bật. Tom yum từng đứng vị tr&iacute; số 8 trong Top 50 m&oacute;n ăn ngon v&agrave; phổ biến nhất thế giới do CNN b&igrave;nh chọn.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/Tom-yum-thai.jpg\" style=\"width: 650px; height: 390px;\" /></p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">3.Pad Th&aacute;i</span></span></h2>\r\n<p>\r\n	Một m&oacute;n ăn kh&aacute;c cũng rất phổ biến tr&ecirc;n đất Th&aacute;i đ&oacute; l&agrave; Pad Th&aacute;i, hay c&ograve;n được hiểu l&agrave; b&uacute;n x&agrave;o, phở x&agrave;o. Pad Th&aacute;i c&oacute; nguy&ecirc;n liệu ch&iacute;nh gồm mỳ x&agrave;o trộn trứng, lạc rang, t&ocirc;m kh&ocirc;, sốt me, đ&ocirc;i khi k&egrave;m th&ecirc;m mực hay thịt b&ograve;. M&oacute;n ăn l&agrave; sự tổng h&ograve;a của nhiều vị như chua cay xen lẫn ngọt đặc trưng, c&ugrave;ng vị đậm đ&agrave; vừa miệng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/Pad-thai.jpg\" style=\"width: 650px; height: 433px;\" /></p>\r\n<p>\r\n	Kh&ocirc;ng kh&oacute; để t&igrave;m thấy một quầy Pad Th&aacute;i tr&ecirc;n bất cứ th&agrave;nh phố n&agrave;o ở Th&aacute;i Lan v&agrave; lu&ocirc;n nhận được sự h&agrave;i l&ograve;ng từ du kh&aacute;ch.</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">4.Gỏi đu đủ Som Tum</span></span></h2>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\"><img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/goi-du-du.jpg\" style=\"width: 650px; height: 433px;\" /></span></span></p>\r\n<p>\r\n	Đ&acirc;y l&agrave; một loại gỏi cay với nguy&ecirc;n liệu ch&iacute;nh l&agrave; đu đủ xanh b&agrave;o sợi. M&oacute;n ăn hội tụ đầy đủ những vị đặc trưng trong ẩm thực Th&aacute;i gồm vị chua của chanh, cay của ớt, mặn của nước mắm v&agrave; ngọt từ đường thốt nốt. Do được trộn bằng c&aacute;ch gi&atilde; trong cối, n&ecirc;n m&oacute;n ăn mới c&oacute; t&ecirc;n l&agrave; &ldquo;som tam&rdquo; mang &yacute; nghĩa &ldquo;m&oacute;n gi&atilde; trong cối c&oacute; vị chua&rdquo;. M&oacute;n gỏi đu đủ thường xuy&ecirc;n xuất hiện tr&ecirc;n b&agrave;n ăn của người Th&aacute;i.</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">5.Roti kluai khai</span></span></h2>\r\n<p>\r\n	Đ&acirc;y l&agrave; m&oacute;n ăn đường phố rất hấp dẫn ở Th&aacute;i Lan, một loại b&aacute;nh kếp với nh&acirc;n chuối v&agrave; trứng, đ&acirc;y l&agrave; m&oacute;n b&aacute;nh ngọt, bạn c&oacute; thể thưởng thức n&oacute; như 1 m&oacute;n ăn phụ sau bữa ăn ch&iacute;nh rất tuyệt vời. Bạn cũng c&oacute; thể rưới th&ecirc;m sữa đặc, đường hoặc siro chocolate l&ecirc;n m&oacute;n ăn sao cho ph&ugrave; hợp với khẩu vị của bạn.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/roti-thai.jpg\" style=\"width: 650px; height: 487px;\" /></p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">6.G&agrave; nướng Kai Yang</span></span></h2>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\"><img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/ga-nuong.JPG\" style=\"width: 640px; height: 443px;\" /></span></span></p>\r\n<p>\r\n	Kai Yang l&agrave; m&oacute;n g&agrave; nướng c&oacute; nguồn gốc từ L&agrave;o, tuy nhi&ecirc;n khi đến Th&aacute;i Lan m&oacute;n g&agrave; n&agrave;y đ&atilde; được c&aacute;c đầu bếp Th&aacute;i biến th&agrave;nh m&oacute;n g&agrave; nướng kiểu Th&aacute;i đặc biệt. Họ ướp g&agrave; với v&ocirc; số c&aacute;c loại gia vị đặc biệt rồi nướng tr&ecirc;n than hoa hồng rực, để khi thực kh&aacute;ch ăn phải trầm trồ khen ngợi.</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">7.X&ocirc;i xo&agrave;i Khao Niaow Ma Muang</span></span></h2>\r\n<p>\r\n	Đ&acirc;y l&agrave; m&oacute;n x&ocirc;i nổi tiếng nhất với kh&aacute;ch du lịch. M&oacute;n x&ocirc;i thơm ngon b&eacute;o ngậy do gạo nếp được đồ chung c&ugrave;ng nước dừa, ăn k&egrave;m c&ugrave;ng những l&aacute;t xo&agrave;i v&agrave;ng ươm ngọt m&aacute;t. C&oacute; thể n&oacute;i Khao Niaow Ma Muang l&agrave; m&oacute;n ăn hội tụ đầy đủ m&agrave;u sắc v&agrave; hương vị đặc trưng của nền ẩm thực xứ sở Ch&ugrave;a V&agrave;ng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/xoi-xoai.jpg\" style=\"width: 650px; height: 411px;\" /></p>\r\n<p>\r\n	Bạn đ&atilde; ăn được bao nhi&ecirc;u m&oacute;n m&agrave; ch&uacute;ng t&ocirc;i kể tr&ecirc;n? Nếu chưa th&igrave; đừng chần chừ nh&eacute;, đăng k&iacute; ngay <a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-chau-a/du-lich-thai-lan/\"><span style=\"color:#0000ff;\">tour du lịch Th&aacute;i Lan</span></a> để thưởng thức ngay tất tần tật c&aacute;c m&oacute;n kể tr&ecirc;n v&agrave; nhận phần qu&agrave; trị gi&aacute; l&ecirc;n đến <span style=\"color:#ff0000;\">1 triệu đồng </span>của <strong><a href=\"http://azbooking.vn\"><span style=\"color:#000000;\">Azbooking</span></a></strong> trong th&aacute;ng <span style=\"color:#ff0000;\">3/2017 </span>nh&eacute;!</p>\r\n<p>\r\n	H&atilde;y đến với <a href=\"http://azbooking.vn\"><span style=\"color:#0000ff;\">Azbooking</span></a> để chuyến du lịch của bạn v&agrave; người th&acirc;n trở n&ecirc;n dễ d&agrave;ng v&agrave; thuận tiện hơn. Mọi th&ocirc;ng tin chi tiết về <a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-chau-a/du-lich-thai-lan/\"><span style=\"color:#0000ff;\">tour Th&aacute;i Lan trọn g&oacute;i </span></a>xin li&ecirc;n hệ<span style=\"color: rgb(255, 0, 0);\"> 0904 578 388</span></p>\r\n', 'những món ăn ngon nhất định phải thử khi đến Thái Lan', '7 món ăn ngon nhất định phải thử khi đến thái lan, những món ăn thái lan hấp dẫn, món ăn ngon đường phố thái lan', '', '2017-03-15 15:27:55'),
(14, 1, '10 thành phố được ghé thăm nhiều nhất thế giới năm 2016', '10-thanh-pho-duoc-ghe-tham-nhieu-nhat-the-gioi-nam-2016', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/du-lich-thai-lan.mixtourist.jpg', 102, '<p>\r\n	Mỗi năm, MasterCard- C&ocirc;ng ty t&agrave;i ch&iacute;nh đa quốc gia, lại tiến h&agrave;nh đo chỉ số những th&agrave;nh phố du lịch tr&ecirc;n to&agrave;n cầu th&ocirc;ng qua số lượng v&eacute; m&aacute;y bay, h&agrave;ng ho&aacute; lưu niệm v&agrave; một v&agrave;i con số chi ti&ecirc;u kh&aacute;c để đ&aacute;nh gi&aacute; những th&agrave;nh phố du lịch được gh&eacute; thăm nhiều nhất tr&ecirc;n thế giới.</p>\r\n<p>\r\n	Bạn c&oacute; ngạc nhi&ecirc;n khi 5 trong số 10 th&agrave;nh phố du lịch được kh&aacute;ch quốc tế gh&eacute; thăm nhất thế giới đều nằm ở ch&acirc;u &Aacute; ??? C&ugrave;ng AZbooking điểm lại 10 th&agrave;nh phố ti&ecirc;u biểu n&agrave;y nh&eacute;!</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\">1. Bangkok, Th&aacute;i Lan</span></span></h3>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\"><img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/du-lich-thai-lan.mixtourist.jpg\" style=\"width: 650px; height: 221px;\" /></span></span></p>\r\n<p>\r\n	Trong bảng xếp hạng năm nay, London đ&atilde; đ&aacute;nh rơi vương miện của m&igrave;nh v&agrave; trao cho người cũ Bangkok. Sau 2 lần khi&ecirc;m tốn đứng ở vị tr&iacute; thứ nh&igrave;, năm 2016 Bangkok đ&atilde; vươn l&ecirc;n trở th&agrave;nh qu&aacute;n qu&acirc;n với 21,47 triệu kh&aacute;ch nghỉ qua đ&ecirc;m.</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\">2. London</span></span></h3>\r\n<p>\r\n	Sau sự kiện Brexit, gi&aacute; đồng bảng Anh trượt d&agrave;i l&acirc;u nhất trong 32 năm, người ta dự đo&aacute;n ng&agrave;nh du lịch Anh sẽ được hưởng lợi, tuy nhi&ecirc;n số liệu thực về lượng kh&aacute;ch du lịch ngủ qua đ&ecirc;m t&iacute;nh đến thời điểm cuối năm 2016 - 19,88 triệu người lại kh&ocirc;ng chứng minh điều đ&oacute;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/london.jpg\" style=\"width: 650px; height: 433px;\" /></p>\r\n<p>\r\n	Tuy nhi&ecirc;n London vẫn l&agrave; một th&agrave;nh phố sang trọng, qu&yacute; ph&aacute;i nhưng lại y&ecirc;n b&igrave;nh, thơ mộng bậc nhất thế giới m&agrave; ai cũng ao ước đặt ch&acirc;n tới</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\">3.&nbsp;Paris, Ph&aacute;p</span></span></h3>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/thap-eiffel-mua-xuan.jpg\" style=\"width: 650px; height: 420px;\" /></p>\r\n<p>\r\n	Đứng ở vị tr&iacute; thứ 3 l&agrave; kinh đ&ocirc; &aacute;nh s&aacute;ng Paris với 18,03 triệu kh&aacute;ch ngủ qua đ&ecirc;m.</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\">4. Dubai, UAE</span></span></h3>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/dubai.jpg\" style=\"width: 640px; height: 368px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\">Điểm vui chơi Palm Island tại Dubai</span></p>\r\n<p>\r\n	Dubai xếp ở vị tr&iacute; thứ 4 với 15,27 triệu kh&aacute;ch. Đến Dubai, bạn sẽ bị cho&aacute;ng ngợp bởi vẻ xa hoa, gi&agrave;u c&oacute; của th&agrave;nh phố năng động, hiện đại bậc nhất thế giới n&agrave;y. Chỉ trong 20 năm, Dubai đ&atilde; ph&aacute;t triển thần kỳ, trở th&agrave;nh một th&agrave;nh phố to&agrave;n cầu v&agrave; l&agrave; một trung t&acirc;m kinh tế của thế giới với c&aacute;c trung t&acirc;m t&agrave;i ch&iacute;nh, c&ocirc;ng nghệ th&ocirc;ng tin, du lịch cảng biển, bất động sản v&agrave; đặc biệt l&agrave; c&ocirc;ng tr&igrave;nh quần đảo nh&acirc;n tạo đặc biệt nhất thế giới: The Palm v&agrave; Jebel Ali: Cản nước s&acirc;u nh&acirc;n tạo lớn nhất thế giới.</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\">5. New York, Mỹ</span></span></h3>\r\n<p>\r\n	L&agrave; một trong những th&agrave;nh phố đ&ocirc;ng d&acirc;n v&agrave; gi&agrave;u c&oacute; nhất nước Mỹ. New York c&oacute; một tầm ảnh hưởng&nbsp;mạnh mẽ đối với thương mại, t&agrave;i ch&iacute;nh, văn h&oacute;a, thời trang v&agrave; giải tr&iacute; to&agrave;n cầu. H&agrave;ng năm, th&agrave;nh phố n&agrave;y thu h&uacute;t h&agrave;ng triệu kh&aacute;ch du lịch quốc tế&nbsp;v&agrave; đạt mức 12,75 triệu kh&aacute;ch trong năm vừa qua.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/new%20york.jpg\" style=\"width: 650px; height: 208px;\" /></p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\">6.&nbsp;Singapore</span></span></h3>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\"><img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/du-lich-singapore.jpg\" style=\"width: 628px; height: 375px;\" /></span></span></p>\r\n<p>\r\n	Đứng ở vị tr&iacute; thứ 6 l&agrave; Singapore với 12,11 triệu kh&aacute;ch quốc tế thăm quan v&agrave; mua sắm tại quốc.</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\">7.&nbsp;&nbsp;Kuala Lumpur, Malaysia</span></span></h3>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\"><img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/Kula.jpg\" style=\"width: 650px; height: 433px;\" /></span></span></p>\r\n<p>\r\n	Thủ đ&ocirc; Kuala Lumpur của Malaysia đứng ở vị tr&iacute; thứ 7. Với nền ẩm thực đặc sắc c&ugrave;ng di sản văn h&oacute;a phong ph&uacute; khiến Kuala Lumpur l&agrave; điểm đến l&yacute; tưởng của nhiều du kh&aacute;ch.</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\">8.&nbsp;Istanbul, Thổ Nhĩ Kỳ</span></span></h3>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/istanbul%20mua%20hoa%20tulip.jpg\" style=\"width: 620px; height: 497px;\" /></p>\r\n<p>\r\n	Th&agrave;nh phố duy nhất tr&ecirc;n thế giới nằm giữa 2 ch&acirc;u lục &Aacute; &ndash; &Acirc;u - Istanbul l&agrave; nơi 3 đế chế h&ugrave;ng mạnh La M&atilde;, Byzantine v&agrave; Ottman đ&atilde; từng ngự trị. N&oacute; mang những vẻ đẹp của lịch sử, thể hiện tr&ecirc;n những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c vẫn c&ograve;n s&oacute;t lại cho đến b&acirc;y giờ.</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\">9.&nbsp;Seoul, H&agrave;n Quốc</span></span></h3>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/seoul.jpg\" style=\"width: 650px; height: 488px;\" /></p>\r\n<p>\r\n	Với tổng kh&aacute;ch nghỉ qua đ&ecirc;m l&agrave; 11,7 triệu người, thủ đ&ocirc; H&agrave;n Quốc xếp vị tr&iacute; thứ 9 trong bảng xếp hạng do Master Card tổng hợp.</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\">10. Tokyo, Nhật Bản</span></span></h3>\r\n<p>\r\n	Với sự kết hợp h&agrave;i h&ograve;a giữa truyền thống v&agrave; hiện đại ở kiến tr&uacute;c cũng như c&aacute;c điểm du lịch , c&ugrave;ng với c&aacute;c lễ hội truyềnthống &nbsp;v&agrave; nhiều sự kiện truyền thống diễn ra quanh năm; Tokyo đ&atilde; k&eacute;o về hơn 10 triệu lượt kh&aacute;ch v&agrave; vẫn giữ top c&aacute;c th&agrave;nh phố đ&aacute;ng gh&eacute; thăm nhất thế giới.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/hoa-anh-dao.jpg\" style=\"width: 620px; height: 411px;\" /></p>\r\n<p>\r\n	Bạn đ&atilde; đặt ch&acirc;n được bao nhi&ecirc;u trong số 10 th&agrave;nh phố tr&ecirc;n? Nếu bạn c&ograve;n đang loanh hoay v&igrave; phải tự lo chỗ ăn ở, hay e ngại v&igrave;<strong> thủ tục xin visa</strong>; h&atilde;y li&ecirc;n hệ ngay cho <a href=\"http://azbooking.vn\"><span style=\"color:#0000ff;\">AZbooking</span></a> qua <span style=\"color: rgb(0, 0, 255);\"><strong>hotline 0904 578 388/ 0943 838 222</strong></span> để được tư vấn nh&eacute;.</p>\r\n', '', '10 thành phố đáng du lịch nhất', '', '2017-03-22 17:00:06'),
(16, 2, 'Ngỡ ngàng sắc trắng hoa sưa về trên phố phường Hà Nộ', 'ngo-ngang-sac-trang-hoa-sua-ve-tren-pho-phuong-ha-no', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/hoa-sua-ha-noi.jpg', 86, '<p>\r\n	<strong><span style=\"font-size:14px;\">H&agrave;ng năm, cứ v&agrave;o độ th&aacute;ng ba, hoa sưa lại nở trắng muốt, tr&agrave;n ngập tr&ecirc;n nhiều tuyến phố của thủ đ&ocirc; H&agrave; Nội, mang đến một khung cảnh dịu d&agrave;ng, l&atilde;ng mạn khiến bất cứ ai cũng phải xi&ecirc;u l&ograve;ng.</span></strong></p>\r\n<div>\r\n	<div type=\"Photo\">\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"Tháng ba, dạo khắp phố phường Thủ đô, nhiều người không khỏi ngỡ ngàng bởi vẻ đẹp tinh khôi của những cây sưa. Khi những cơn mưa phùn giăng ngập trời, ấy cũng là lúc hoa sưa bắt đầu tung những cánh nhỏ xinh, khoe sắc trắng tinh khiết.\" data-original=\"https://dantricdn.com/2017/hoa-sua-ha-noi-1-1490381229902.jpg\" h=\"750\" id=\"img_76794\" photoid=\"76794\" rel=\"https://dantricdn.com/2017/hoa-sua-ha-noi-1-1490381229902.jpg\" src=\"https://dantricdn.com/thumb_w/640/2017/hoa-sua-ha-noi-1-1490381229902.jpg\" type=\"photo\" w=\"1000\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Th&aacute;ng ba, dạo khắp phố phường Thủ đ&ocirc;, nhiều người kh&ocirc;ng khỏi ngỡ ng&agrave;ng bởi vẻ đẹp tinh kh&ocirc;i của những c&acirc;y sưa. Khi những cơn mưa ph&ugrave;n giăng ngập trời, ấy cũng l&agrave; l&uacute;c hoa sưa bắt đầu tung những c&aacute;nh nhỏ xinh, khoe sắc trắng tinh khiết.</div>\r\n		<div>\r\n			&nbsp;</div>\r\n	</div>\r\n	<div type=\"Photo\">\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"Hoa sưa mọc thành từng chùm. Cây sưa có nhiều nhất trên phố Phan Đình Phùng, Hoàng Hoa Thám, Phan Chu Trinh hay Giảng Võ,…. Ngoài ra, bạn còn có thể bắt gặp một vài cây sưa trên đường Láng hay cây sưa tán rộng ở giữa sân ý túc xá Mễ Trì nằm trên đường Lương Thế Vinh.\" data-original=\"https://dantricdn.com/2017/hoa-sua-ha-noi-2-1490381229910.jpg\" h=\"750\" id=\"img_76795\" photoid=\"76795\" rel=\"https://dantricdn.com/2017/hoa-sua-ha-noi-2-1490381229910.jpg\" src=\"https://dantricdn.com/thumb_w/640/2017/hoa-sua-ha-noi-2-1490381229910.jpg\" type=\"photo\" w=\"1000\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Hoa sưa mọc th&agrave;nh từng ch&ugrave;m. C&acirc;y sưa c&oacute; nhiều nhất tr&ecirc;n phố Phan Đ&igrave;nh Ph&ugrave;ng, Ho&agrave;ng Hoa Th&aacute;m, Phan Chu Trinh hay Giảng V&otilde;,&hellip;. Ngo&agrave;i ra, bạn c&ograve;n c&oacute; thể bắt gặp một v&agrave;i c&acirc;y sưa tr&ecirc;n đường L&aacute;ng hay c&acirc;y sưa t&aacute;n rộng ở giữa s&acirc;n &yacute; t&uacute;c x&aacute; Mễ Tr&igrave; nằm tr&ecirc;n đường Lương Thế Vinh.</div>\r\n		<div>\r\n			&nbsp;</div>\r\n	</div>\r\n	<div type=\"Photo\">\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"Hoa sưa bung nở trên đường Phan Đình Phùng. Giữa nhịp sống náo nhiệt của Thủ đô, sắc trắng hoa sưa làm cho mọi thứ trở nên yên bình và nhẹ nhàng một cách lạ lùng.\" data-original=\"https://dantricdn.com/2017/hoa-sua-ha-noi-3-1490381229914.jpg\" h=\"1000\" id=\"img_76796\" photoid=\"76796\" rel=\"https://dantricdn.com/2017/hoa-sua-ha-noi-3-1490381229914.jpg\" src=\"https://dantricdn.com/thumb_w/640/2017/hoa-sua-ha-noi-3-1490381229914.jpg\" type=\"photo\" w=\"667\" /></div>\r\n		<div style=\"text-align: center;\">\r\n			&nbsp;</div>\r\n		<div>\r\n			Hoa sưa bung nở tr&ecirc;n đường Phan Đ&igrave;nh Ph&ugrave;ng. Giữa nhịp sống n&aacute;o nhiệt của Thủ đ&ocirc;, sắc trắng hoa sưa l&agrave;m cho mọi thứ trở n&ecirc;n y&ecirc;n b&igrave;nh v&agrave; nhẹ nh&agrave;ng một c&aacute;ch lạ l&ugrave;ng.</div>\r\n		<div>\r\n			&nbsp;</div>\r\n	</div>\r\n	<div type=\"Photo\">\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"Hoa sưa đẹp nhưng mùa lại ngắn, hoa nở rất nhanh nhưng cũng chóng lụi tàn. Hoa sưa có thể nở trắng trời mà không hẹn trước, nhưng chỉ cần qua một đêm, hoa lại nhanh chóng rụng rơi theo gió, nhường chỗ cho sắc lá xanh non.\" data-original=\"https://dantricdn.com/2017/hoa-sua-ha-noi-4-1490381229934.jpg\" h=\"667\" id=\"img_76797\" photoid=\"76797\" rel=\"https://dantricdn.com/2017/hoa-sua-ha-noi-4-1490381229934.jpg\" src=\"https://dantricdn.com/thumb_w/640/2017/hoa-sua-ha-noi-4-1490381229934.jpg\" type=\"photo\" w=\"1000\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Hoa sưa đẹp nhưng m&ugrave;a lại ngắn, hoa nở rất nhanh nhưng cũng ch&oacute;ng lụi t&agrave;n. Hoa sưa c&oacute; thể nở trắng trời m&agrave; kh&ocirc;ng hẹn trước, nhưng chỉ cần qua một đ&ecirc;m, hoa lại nhanh ch&oacute;ng rụng rơi theo gi&oacute;, nhường chỗ cho sắc l&aacute; xanh non.</div>\r\n		<div>\r\n			&nbsp;</div>\r\n	</div>\r\n	<div type=\"Photo\">\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"Sưa nở ở tượng đài Lenin.\" data-original=\"https://dantricdn.com/2017/hoa-sua-ha-noi-5-1490381229940.jpg\" h=\"667\" id=\"img_76798\" photoid=\"76798\" rel=\"https://dantricdn.com/2017/hoa-sua-ha-noi-5-1490381229940.jpg\" src=\"https://dantricdn.com/thumb_w/640/2017/hoa-sua-ha-noi-5-1490381229940.jpg\" type=\"photo\" w=\"1000\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Sưa nở ở tượng đ&agrave;i Lenin.</div>\r\n		<div>\r\n			&nbsp;</div>\r\n	</div>\r\n	<div type=\"Photo\">\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"Những bông hoa trắng nhỏ li ti có sức hút mãnh liệt với những người đam mê nghệ thuật, đặc biệt là giới nhiếp ảnh.\" data-original=\"https://dantricdn.com/2017/hoa-sua-ha-noi-6-1490381229946.jpg\" h=\"667\" id=\"img_76799\" photoid=\"76799\" rel=\"https://dantricdn.com/2017/hoa-sua-ha-noi-6-1490381229946.jpg\" src=\"https://dantricdn.com/thumb_w/640/2017/hoa-sua-ha-noi-6-1490381229946.jpg\" type=\"photo\" w=\"1000\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Những b&ocirc;ng hoa trắng nhỏ li ti c&oacute; sức h&uacute;t m&atilde;nh liệt với những người đam m&ecirc; nghệ thuật, đặc biệt l&agrave; giới nhiếp ảnh.</div>\r\n		<div>\r\n			&nbsp;</div>\r\n	</div>\r\n	<div type=\"Photo\">\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"Cánh hoa rơi vương trên cửa kính ô tô cũng mang đến một vẻ đẹp rất cuốn hút.\" data-original=\"https://dantricdn.com/2017/hoa-sua-ha-noi-7-1490381229951.jpg\" h=\"667\" id=\"img_76800\" photoid=\"76800\" rel=\"https://dantricdn.com/2017/hoa-sua-ha-noi-7-1490381229951.jpg\" src=\"https://dantricdn.com/thumb_w/640/2017/hoa-sua-ha-noi-7-1490381229951.jpg\" type=\"photo\" w=\"1000\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			C&aacute;nh hoa rơi vương tr&ecirc;n cửa k&iacute;nh &ocirc; t&ocirc; cũng mang đến một vẻ đẹp rất cuốn h&uacute;t.</div>\r\n		<div>\r\n			&nbsp;</div>\r\n	</div>\r\n	<div type=\"Photo\">\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"Những chùm hoa trắng nổi bật giữa cành cây khẳng khiu.\" data-original=\"https://dantricdn.com/2017/hoa-sua-ha-noi-8-1490381229960.jpg\" h=\"924\" id=\"img_76801\" photoid=\"76801\" rel=\"https://dantricdn.com/2017/hoa-sua-ha-noi-8-1490381229960.jpg\" src=\"https://dantricdn.com/thumb_w/640/2017/hoa-sua-ha-noi-8-1490381229960.jpg\" type=\"photo\" w=\"663\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Những ch&ugrave;m hoa trắng nổi bật giữa c&agrave;nh c&acirc;y khẳng khiu.</div>\r\n		<div>\r\n			&nbsp;</div>\r\n	</div>\r\n	<div type=\"Photo\">\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"Màu xanh non của lá hòa với sắc trắng của hoa khiến lòng người cũng nhẹ nhàng khi nhìn ngắm. Còn gì tuyệt vời hơn một buổi chiều đi dạo dưới tán hoa sưa, cảm nhận làn gió khẽ mơn man da thịt và thu trọn vào tầm mắt những khoảnh khắc tuyệt vời của thiên nhiên.\" data-original=\"https://dantricdn.com/2017/hoa-sua-ha-noi-9-1490381229967.jpg\" h=\"667\" id=\"img_76802\" photoid=\"76802\" rel=\"https://dantricdn.com/2017/hoa-sua-ha-noi-9-1490381229967.jpg\" src=\"https://dantricdn.com/thumb_w/640/2017/hoa-sua-ha-noi-9-1490381229967.jpg\" type=\"photo\" w=\"1000\" /></div>\r\n		<div style=\"text-align: center;\">\r\n			&nbsp;</div>\r\n		<div>\r\n			M&agrave;u xanh non của l&aacute; h&ograve;a với sắc trắng của hoa khiến l&ograve;ng người cũng nhẹ nh&agrave;ng khi nh&igrave;n ngắm. C&ograve;n g&igrave; tuyệt vời hơn một buổi chiều đi dạo dưới t&aacute;n hoa sưa, cảm nhận l&agrave;n gi&oacute; khẽ mơn man da thịt v&agrave; thu trọn v&agrave;o tầm mắt những khoảnh khắc tuyệt vời của thi&ecirc;n nhi&ecirc;n.</div>\r\n		<div>\r\n			&nbsp;</div>\r\n	</div>\r\n	<div type=\"Photo\">\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"\r\nNhững bông hoa sưa nhỏ ti li, đẹp dịu dàng là lý do để người Hà Nội thêm yêu tháng ba. Dù phải chịu đựng tiết trời nồm ẩm, nhưng chỉ cần nhìn thấy những cánh hoa mong manh, bất cứ ai cũng sẽ có một cảm giác bình yên đến lạ.\r\n\" data-original=\"https://dantricdn.com/2017/hoa-sua-ha-noi-10-1490381229971.jpg\" h=\"667\" id=\"img_76803\" photoid=\"76803\" rel=\"https://dantricdn.com/2017/hoa-sua-ha-noi-10-1490381229971.jpg\" src=\"https://dantricdn.com/thumb_w/640/2017/hoa-sua-ha-noi-10-1490381229971.jpg\" type=\"photo\" w=\"1000\" /></div>\r\n		<div>\r\n			<p>\r\n				Những b&ocirc;ng hoa sưa nhỏ ti li, đẹp dịu d&agrave;ng l&agrave; l&yacute; do để người H&agrave; Nội th&ecirc;m y&ecirc;u th&aacute;ng ba. D&ugrave; phải chịu đựng tiết trời nồm ẩm, nhưng chỉ cần nh&igrave;n thấy những c&aacute;nh hoa mong manh, bất cứ ai cũng sẽ c&oacute; một cảm gi&aacute;c b&igrave;nh y&ecirc;n đến lạ.</p>\r\n		</div>\r\n	</div>\r\n	<div type=\"Photo\">\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"Chỉ cần một cơn gió nhẹ thổi qua, những cánh hoa li ti đã vội chao mình giữa không trung rồi từ từ rơi xuống lòng đường. Cánh hoa như những bông tuyết trắng rơi xuống hè phố mang lại vẻ đẹp dịu dàng, lãng mạn.\" data-original=\"https://dantricdn.com/2017/hoa-sua-ha-noi-12-1490381229994.jpg\" h=\"666\" id=\"img_76804\" photoid=\"76804\" rel=\"https://dantricdn.com/2017/hoa-sua-ha-noi-12-1490381229994.jpg\" src=\"https://dantricdn.com/thumb_w/640/2017/hoa-sua-ha-noi-12-1490381229994.jpg\" type=\"photo\" w=\"1000\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Chỉ cần một cơn gi&oacute; nhẹ thổi qua, những c&aacute;nh hoa li ti đ&atilde; vội chao m&igrave;nh giữa kh&ocirc;ng trung rồi từ từ rơi xuống l&ograve;ng đường. C&aacute;nh hoa như những b&ocirc;ng tuyết trắng rơi xuống h&egrave; phố mang lại vẻ đẹp dịu d&agrave;ng, l&atilde;ng mạn.</div>\r\n		<div>\r\n			&nbsp;</div>\r\n	</div>\r\n	<div type=\"Photo\">\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"Không rực rỡ như hoa phượng, không kiêu kỳ như violet, sưa chọn cho mình sắc trắng, tinh khôi, nhẹ nhàng đủ để làm người đã ngắm rồi là phải nhớ thương.\" data-original=\"https://dantricdn.com/2017/hoa-sua-ha-noi-13-1490381229999.jpg\" h=\"750\" id=\"img_76805\" photoid=\"76805\" rel=\"https://dantricdn.com/2017/hoa-sua-ha-noi-13-1490381229999.jpg\" src=\"https://dantricdn.com/thumb_w/640/2017/hoa-sua-ha-noi-13-1490381229999.jpg\" type=\"photo\" w=\"1000\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Kh&ocirc;ng rực rỡ như hoa phượng, kh&ocirc;ng ki&ecirc;u kỳ như violet, sưa chọn cho m&igrave;nh sắc trắng, tinh kh&ocirc;i, nhẹ nh&agrave;ng đủ để l&agrave;m người đ&atilde; ngắm rồi l&agrave; phải nhớ thương.</div>\r\n		<div>\r\n			&nbsp;</div>\r\n	</div>\r\n	<div type=\"Photo\">\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"Những cành hoa sưa khoe sắc trong gió xuân. Thật không quá khi nói rằng, hoa sưa là một nét vẽ đẹp không thể thiếu trong bức tranh Hà Nội.\" data-original=\"https://dantricdn.com/2017/hoa-sua-ha-noi-14-1490381230002.jpg\" h=\"667\" id=\"img_76806\" photoid=\"76806\" rel=\"https://dantricdn.com/2017/hoa-sua-ha-noi-14-1490381230002.jpg\" src=\"https://dantricdn.com/thumb_w/640/2017/hoa-sua-ha-noi-14-1490381230002.jpg\" type=\"photo\" w=\"1000\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Những c&agrave;nh hoa sưa khoe sắc trong gi&oacute; xu&acirc;n. Thật kh&ocirc;ng qu&aacute; khi n&oacute;i rằng, hoa sưa l&agrave; một n&eacute;t vẽ đẹp kh&ocirc;ng thể thiếu trong bức tranh H&agrave; Nội.</div>\r\n	</div>\r\n	<div style=\"text-align: center;\" type=\"Photo\">\r\n		&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;( Nguồn: D&acirc;n tr&iacute;)</div>\r\n</div>\r\n<p>\r\n	&nbsp;</p>\r\n', 'hoa sưa hà nội', 'hà nội mùa hoa sưa, hoa sưa, hoa sưa hà nội, mùa hoa sưa là ùa nào', '', '2017-03-25 10:33:17');
INSERT INTO `news` (`id`, `danhmuc_id`, `name`, `name_url`, `img`, `view`, `content`, `title`, `keyword`, `description`, `created`) VALUES
(17, 2, 'Lý do khiến Tour du lịch Thái Lan siêu tiết kiệm được khách du lịch lựa chọn nhiều nhất dịp nghỉ lễ 30/4 - 1/5', 'ly-do-khien-tour-du-lich-thai-lan-sieu-tiet-kiem-duoc-khach-du-lich-lua-chon-nhieu-nhat-dip-nghi-le-304-15', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/nui-phat-vang-khao-chee-chan-thai-lan-azbooking.jpg', 113, '<p style=\"text-align: justify;\">\r\n	V&igrave; sao ở trong nước c&oacute; biết bao địa điểm hấp dẫn m&agrave; mọi người lại lựa chọn đi <strong>du lịch Th&aacute;i Lan</strong>? V&igrave; sao tr&ecirc;n thế giới c&oacute; rất nhiều đất nước với v&ocirc; v&agrave;n những địa danh nổi tiếng nhưng mọi người lại lựa chọn Th&aacute;i Lan l&agrave; điểm đến dịp nghỉ lễ 30/4-1/5.</p>\r\n<p style=\"text-align: justify;\">\r\n	Tất nhi&ecirc;n l&agrave; mỗi người lại c&oacute; những l&yacute; do kh&aacute;c nhau để lựa chọn<strong> tour du lịch Th&aacute;i Lan si&ecirc;u tiết kiệm</strong> của <strong>AZbooking</strong>, thế nhưng đại đa số du kh&aacute;ch đưa ra quyết định n&agrave;y l&agrave; v&igrave; một trong những nguy&ecirc;n nh&acirc;n sau:</p>\r\n<p style=\"text-align: justify;\">\r\n	- <span style=\"color:#ff0000;\">Gi&aacute; si&ecirc;u rẻ. Chỉ với 5,4tr l&agrave; bạn đ&atilde; c&oacute; một tấm v&eacute; đăng k&yacute; tour du lịch Th&aacute;i Lan 4 ng&agrave;y 3 đ&ecirc;m.</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"color:#ff0000;\">- Thỏa m&atilde;n nhu cầu đi du lịch nước ngo&agrave;i.</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"color:#ff0000;\">- Kh&ocirc;ng cần mất th&ecirc;m chi ph&iacute; v&agrave; thời gian l&agrave;m visa du lịch.</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"color:#ff0000;\">- Tham quan t&igrave;m hiểu tất cả c&aacute;c địa điểm hấp dẫn kh&aacute;ch du lịch ở hai th&agrave;nh phố lớn nhất Th&aacute;i Lan đ&oacute; l&agrave;: Bangkok v&agrave; Pattaya.</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"color:#ff0000;\">- Thỏa sức mua sắm những m&oacute;n đồ h&agrave;ng hiệu với &quot;gi&aacute; rẻ như h&agrave;ng chợ&quot; tại &quot;Thi&ecirc;n đường mua sẵm&quot; của cả ch&acirc;u &Aacute;.</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"color:#ff0000;\">- L&agrave; điểm đến t&acirc;m linh của c&aacute;c Phật tử bởi Th&aacute;i Lan được mệnh danh l&agrave; &quot;xứ sở ch&ugrave;a V&agrave;ng&quot;....</span></p>\r\n<p style=\"text-align: justify;\">\r\n	Những <strong>tour du lịch Th&aacute;i Lan</strong> kh&aacute;c c&oacute; thể hấp dẫn hơn v&igrave; một số l&yacute; do như tham quan nhiều địa danh hơn, ng&agrave;y khởi h&agrave;nh đẹp hơn, ... nhưng c&ugrave;ng với đ&oacute; l&agrave; gi&aacute; cả chắc chắn sẽ đắt hơn kh&aacute; nhiều. Ch&igrave;nh v&igrave; vậy, <strong>tour du lịch Th&aacute;i Lan si&ecirc;u tiết kiệm</strong> n&agrave;y của <strong>AZbooking </strong>l&agrave; sự lựa chọn kh&aacute; hợp l&yacute; đối với những du kh&aacute;ch c&oacute; nhu cầu kh&aacute;m ph&aacute; c&aacute;i mới với chi ph&iacute; thấp.</p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:16px;\">1. Gi&aacute; cả</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	Hầu hết gi&aacute; của c&aacute;c tour du lịch đi bất cứ đầu cũng đều phụ thuộc kh&aacute; nhiều v&agrave;o gi&aacute; v&eacute; m&aacute;y bay, hay c&aacute;c loại h&igrave;nh di chuyển kh&aacute;c như &ocirc; t&ocirc;, t&agrave;u, thuyền,... Tour du lịch Th&aacute;i Lan cũng kh&ocirc;ng phải l&agrave; ngoại lệ. Ch&iacute;nh bởi vậy, c&ugrave;ng một tour Th&aacute;i Lan nhưng ng&agrave;y khởi h&agrave;nh kh&aacute;c nhau c&oacute; thể khiến sự ch&ecirc;nh lệnh về gi&aacute; tour c&oacute; thể từ v&agrave;i trăm l&ecirc;n tới v&agrave;i triệu đồng cũng l&agrave; chuyện đương nhi&ecirc;n.</p>\r\n<p style=\"text-align: justify;\">\r\n	Tour du lịch Th&aacute;i Lan: si&ecirc;u tiết kiệm của AZbooking c&oacute; gi&aacute; rẻ cũng một phần l&agrave; do nguy&ecirc;n nh&acirc;n đ&oacute;. Cụ thể:</p>\r\n<p style=\"text-align: justify;\">\r\n	- Tour Th&aacute;i 4 ng&agrave;y 3 đ&ecirc;m tham quan Bangkok-Pattay khởi h&agrave;nh ng&agrave;y 31/03, 20/04: <strong>5.490.000 VNĐ</strong></p>\r\n<p style=\"text-align: justify;\">\r\n	- Tour Th&aacute;i 4 ng&agrave;y 3 đ&ecirc;m tham quan Bangkok-Pattay khởi h&agrave;nh ng&agrave;y 28/4 (s&aacute;t ng&agrave;y nghỉ lễ 30/4 - 1/5): <strong>8.390.000 VNĐ</strong></p>\r\n<p>\r\n	<em>&gt;&gt; Bạn c&oacute; thể xem chi tiết về gi&aacute; v&agrave; ng&agrave;y khởi h&agrave;nh của c&aacute;c tour Th&aacute;i 2017<strong>&nbsp;<a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-chau-a/du-lich-thai-lan/\">tại đ&acirc;y</a></strong>&nbsp;để nhận thấy sự ch&ecirc;nh lệch n&agrave;y r&otilde; hơn.</em></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:16px;\">2. Lịch tr&igrave;nh</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	Th&aacute;i Lan th&igrave; vẫn đ&oacute;, điểm du lịch th&igrave; vẫn vậy ch&iacute;nh bởi vậy m&agrave; lịch tr&igrave;nh c&aacute;c tour Th&aacute;i hầu hết l&agrave; kh&ocirc;ng kh&aacute;c nhau mấy.</p>\r\n<p style=\"text-align: justify;\">\r\n	Chỉ kh&aacute;c l&agrave; đi th&agrave;nh phố n&agrave;o, thứ tự đi tới c&aacute;c điểm tham quan, c&ograve;n điểm đến trong th&agrave;nh phố đ&oacute; th&igrave; vẫn như nhau cả hoặc chuyến đi d&agrave;i ng&agrave;y hơn th&igrave; sẽ ở lại th&agrave;nh phố đ&oacute; l&acirc;u hơn.</p>\r\n<p style=\"text-align: justify;\">\r\n	C&aacute;c điểm tham quan ch&iacute;nh của <a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-chau-a/du-lich-thai-lan/du-lich-thai-lan-tour-sieu-tiet-kiem.html\"><strong>tour du lịch Th&aacute;i Lan si&ecirc;u tiết kiệm</strong></a> bao gồm:</p>\r\n<p style=\"text-align: justify;\">\r\n	- <strong>Th&agrave;nh phố Pattaya:&nbsp;</strong>Đảo san h&ocirc; Coral,&nbsp;N&uacute;i Phật V&agrave;ng (Khao Chee Chan),&nbsp;Vườn Nho Silver Lake,&nbsp;L&agrave;ng Voi Pattaya, trung t&acirc;m chế t&aacute;c v&agrave;ng bạc đ&aacute; quy lớn nhất Th&aacute;i Lan&nbsp;World Gems Collection, ngo&agrave;i ra c&ograve;n c&oacute; c&aacute;c chương tr&igrave;nh giải tr&iacute; như Sex show, Alkaza show,...</p>\r\n<p style=\"text-align: justify;\">\r\n	-<strong> Th&agrave;nh phố Bangkok: </strong>Trại hổ&nbsp;Tiger zoo,&nbsp;Vườn Th&uacute; Khao Kheow,&nbsp;Vườn bướm Shaithip, trung t&acirc;m nghi&ecirc;n cứu Rắn đ&ocirc;̣c, cửa h&agrave;ng b&aacute;n đồ da nổi tiếng của Th&aacute;i&nbsp;Leather Factory Outlets,&nbsp;Cung điện Vimanmek,&nbsp;Ch&ugrave;a Thuyền - Wat Yannawa, Ch&ugrave;a Phật V&agrave;ng (c&oacute; bức tượng Phật v&agrave;ng lớn nhất thế giới), tự do mua sẵm tại c&aacute;c trung t&acirc;m mua sắm lớn như&nbsp;WORLD TRADE CENTRE, BIG C, ...</p>\r\n<p style=\"text-align: justify;\">\r\n	<em>&gt;&gt; Bạn c&oacute; thể xem lịch tr&igrave;nh chi tiết <a href=\"http://azbooking.vn/tour-du-lich-trong-nuoc/du-lich-chau-a/du-lich-thai-lan/du-lich-thai-lan-tour-sieu-tiet-kiem.html\"><strong>tại đ&acirc;y</strong></a></em></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:16px;\">3. Kết luận:</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	Với thời gian kh&aacute; ngắn chỉ 4 ng&agrave;y 3 đ&ecirc;m m&agrave; c&oacute; thể đi tham quan hết c&aacute;c địa điểm hấp dẫn kể tr&ecirc;n c&oacute; thể khiến du kh&aacute;ch kh&aacute; mệt mỏi, chưa kể thời gian đi v&agrave; về đ&atilde; mất gần một ng&agrave;y. Tuy nhi&ecirc;n, du thời gian c&oacute; gấp nhưng chắc chắn bạn vẫn sẽ c&oacute; những trải nghiệm tuyệt vời kh&ocirc;ng k&eacute;m.</p>\r\n<p style=\"text-align: justify;\">\r\n	Với chi ph&iacute; qu&aacute; hời như vậy, kh&ocirc;ng l&yacute; do g&igrave; du kh&aacute;ch lại bỏ qua tour du lịch hấp dẫn n&agrave;y. Đ&oacute; cũng l&agrave; l&yacute; do khiến tour du lịch Th&aacute;i Lan si&ecirc;u tiết kiệm của AZbooking 3 tour khởi h&agrave;nh c&ugrave;ng ng&agrave;y m&agrave; vẫn gần như full h&ecirc;t chỗ. Đ&acirc;y đ&uacute;ng l&agrave; một sự lựa chọn ho&agrave;n hảo của du kh&aacute;ch khi dịp nghỉ lễ 30/4-1/5 sắp đến gần.</p>\r\n', 'Lý do khiến Tour du lịch Thái Lan siêu tiết kiệm được khách du lịch lự', 'tour du lịch Thái Lan siêu tiết kiệm, tour du lịch thái lan giá rẻ, tour thái giá rẻ, tour thái tiết kiệm', 'Vì sao ở trong nước có biết bao địa điểm hấp dẫn mà mọi người lại lựa chọn đi du lịch Thái Lan? Vì sao trên thế giới có rất nhiều đất nước với vô vàn những địa danh nổi t', '2017-03-31 09:26:34'),
(19, 1, 'Trốn cả thành phố với 5 Homestay chất lừ ngay ngoại ô Hà Nội', 'tron-ca-thanh-pho-voi-5-homestay-chat-lu-ngay-ngoai-o-ha-noi', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/phuot-mua-cang-chai-yen-bai-mixtourist11111.jpg', 662, '<div>\r\n	<p>\r\n		Bạn c&oacute; tin kh&ocirc;ng cần đi đ&acirc;u xa m&agrave; cũng c&oacute; ngay một v&agrave;i điểm homestay đẹp như trong mơ? C&ugrave;ng Azbooking <strong>&quot;truy t&igrave;m&quot;</strong> ngay những điểm h&uacute;t kh&aacute;ch n&agrave;y nh&eacute;!&nbsp;</p>\r\n	<h2>\r\n		<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">1. Jungle House</span></span></h2>\r\n	<div>\r\n		Khung cảnh qu&aacute; đỗi thần ti&ecirc;n ở Junggle house chẳng kh&aacute;c n&agrave;o như một t&ograve;a l&acirc;u đ&agrave;i tr&ecirc;n đỉnh n&uacute;i, nơi m&agrave; c&aacute;c bạn c&oacute; thể gặp Alice chạy ngang qua khu vườn hối hả, hay Romeo gặp n&agrave;ng Juliette b&ecirc;n khung cửa sổ dầy c&acirc;y leo.</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div style=\"text-align: center;\">\r\n		<img alt=\"\" data-original=\"https://media.foody.vn/images/15747876_10154858535494108_1268620366831211988_n(1).jpg\" src=\"https://media.foody.vn/images/15747876_10154858535494108_1268620366831211988_n(1).jpg\" style=\"height: 486px; width: 650px;\" /></div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		Đến với Jungle House, ch&uacute;ng m&igrave;nh cứ tha hồ h&oacute;a th&acirc;n th&agrave;nh những n&agrave;ng c&ocirc;ng ch&uacute;a v&agrave; ch&agrave;ng ho&agrave;ng tử trong c&acirc;u chuyện cổ t&iacute;ch với ban c&ocirc;ng ngập nắng v&agrave; những bức tường đầy d&acirc;y leo xanh r&igrave;.</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		Jungle House với lối kiến tr&uacute;c đậm chất T&acirc;y &Acirc;u cổ k&iacute;nh n&agrave;y l&agrave; một trong chuỗi lưu tr&uacute; đầy chất thơ của Urban Getaway với những chiếc salon Vintage, tường đ&aacute; mộc, hay chiếc cầu thang xoắn c&ugrave;ng những &ocirc; cửa sổ h&igrave;nh v&ograve;m.</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div style=\"text-align: center;\">\r\n		<img alt=\"\" data-original=\"https://media.foody.vn/images/15727265_1788341704752943_4814562892358156714_n.jpg\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/azbooking1.jpg\" style=\"width: 650px; height: 433px;\" /></div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		Với ph&ograve;ng kh&aacute;ch rộng v&agrave; khu bếp biệt lập c&ugrave;ng cả khu vực nướng ngo&agrave;i trời, chắc chắn kỳ nghỉ ở Jungle House sẽ chẳng khi n&agrave;o nhạt nhẽo.&nbsp;</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div style=\"text-align: center;\">\r\n		<img alt=\"\" data-original=\"https://media.foody.vn/images/15723559_10206137463954186_2849600231376137679_o.jpg\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/azbooking2.jpg\" style=\"width: 650px; height: 651px;\" /></div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		<h2>\r\n			<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">2. Hidden Villa, S&oacute;c Sơn</span></span></h2>\r\n		<p>\r\n			Ẩn m&igrave;nh trong rừng th&ocirc;ng tại S&oacute;c Sơn - ngoại th&agrave;nh H&agrave; Nội, Hidden Villa nằm biệt lập v&agrave; kh&eacute;p k&iacute;n chẳng kh&aacute;c n&agrave;o căn nh&agrave; nổi tiếng của ch&agrave;ng ma c&agrave; rồng Edward Cullen trong series phim Chạng Vạng (Twilight).</p>\r\n		<p>\r\n			Đến Hidden Villa, c&aacute;c bạn sẽ c&oacute; cảm gi&aacute;c như lạc v&agrave;o một thế giới ho&agrave;n to&agrave;n kh&aacute;c với khu biệt thự như ẩn s&acirc;u trong một khu rừng ở Bắc &Acirc;u. Hidden Villa sẽ gi&uacute;p ch&uacute;ng m&igrave;nh tho&aacute;t xa khỏi những x&ocirc; bồ v&agrave; ồn &agrave;o của th&agrave;nh phố chỉ với 30 ph&uacute;t l&aacute;i xe từ trung t&acirc;m.</p>\r\n		<p style=\"text-align: center;\">\r\n			<img alt=\"\" data-original=\"https://media.foody.vn/images/a7(6)(1).jpg\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/azbooking3.jpg\" style=\"width: 650px; height: 650px;\" /></p>\r\n		<p>\r\n			Sau đ&oacute; l&agrave; cả một kỳ nghỉ cực kỳ tuyệt vời, c&aacute;c bạn c&oacute; thể thoải m&aacute;i ngồi thư gi&atilde;n v&agrave; đọc s&aacute;ch trong khu vực cực rộng được bao bọc bởi những t&aacute;n th&ocirc;ng r&igrave; r&agrave;o</p>\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"\" data-original=\"https://media.foody.vn/images/15338329_356306194727820_5250305302670082048_n.jpg\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/azbooking4.jpg\" style=\"width: 650px; height: 488px;\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Tổ chức tiệc nướng BBQ ngo&agrave;i trời ngay trong khu&ocirc;n vi&ecirc;n, đạp xe trong rừng, c&acirc;u c&aacute; tại hồ, hoặc nếu th&iacute;ch th&igrave; ch&uacute;ng m&igrave;nh c&oacute; thể thả lỏng cơ thể trong bể bơi l&acirc;n cận (c&aacute;ch 1km) giữa thi&ecirc;n nhi&ecirc;n y&ecirc;n tĩnh nơi đ&acirc;y nh&eacute;!</div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Với b&agrave;n ăn ngo&agrave;i trời c&ugrave;ng khu nướng BBQ si&ecirc;u đẹp, ch&uacute;ng m&igrave;nh chắc chắn sẽ thấy th&iacute;ch th&uacute; với một kỳ nghỉ kh&ocirc;ng thể tuyệt vời hơn</div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"\" data-original=\"https://media.foody.vn/images/a12(2)(1).jpg\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/azbooking5.jpg\" style=\"width: 650px; height: 867px;\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Ng&ocirc;i nh&agrave; n&agrave;y kh&ocirc;ng chỉ ăn điểm ngoại h&igrave;nh m&agrave; b&ecirc;n trong nh&agrave; cũng rất tuyệt nữa nh&eacute;. Ng&ocirc;i nh&agrave; n&agrave;y kh&ocirc;ng nhỏ nhưng lại chỉ c&oacute; 2 ph&ograve;ng ngủ, 1 ph&ograve;ng kh&aacute;ch, 1 bếp, v&igrave; thế d&ugrave; kh&ocirc;ng giới hạn số người, bạn cũng chỉ n&ecirc;n đi tối đa 10 người để kh&ocirc;ng gian nghỉ ngơi thoải m&aacute;i v&agrave; đủ ri&ecirc;ng tư nhất.</div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"\" data-original=\"https://media.foody.vn/images/a1(14)(1).jpg\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/azbooking6.jpg\" style=\"width: 650px; height: 433px;\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Nằm ngay ở S&oacute;c Sơn, (gần Việt Phủ Th&agrave;nh Chương), c&aacute;ch H&agrave; Nội khoảng 30km với hơn 40 ph&uacute;t đi đường l&agrave; bạn đ&atilde; c&oacute; thể đến tận nơi v&agrave; hưởng thụ kỳ nghỉ của m&igrave;nh rồi đ&oacute; nh&eacute;. Gi&aacute; thu&ecirc; ph&ograve;ng ở đ&acirc;y tầm 4 triệu cho 2 ng&agrave;y, 1 đ&ecirc;m, kh&ocirc;ng rẻ nhưng t&iacute;nh ra lại rất kinh tế nếu đi đ&ocirc;ng người, v&agrave; bạn n&ecirc;n book ph&ograve;ng c&agrave;ng sớm c&agrave;ng tốt nh&eacute;!</div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<h2>\r\n			<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">3. Rustic Home, S&oacute;c Sơn</span></span></h2>\r\n		<div>\r\n			Th&ecirc;m một địa chỉ si&ecirc;u tuyệt nữa d&agrave;nh cho những bạn n&agrave;o muốn c&oacute; ng&agrave;y lễ thực sự được nghỉ ngơi ngay gần H&agrave; Nội nh&eacute;! Muốn tr&aacute;nh xa kh&oacute;i bụi v&agrave; x&ocirc; bồ của Thủ Đ&ocirc; trong một thế giới mộc mạc nhưng kh&ocirc;ng k&eacute;m phần tiện nghi v&agrave; chất lừ th&igrave; chắc chắn ch&uacute;ng m&igrave;nh cũng n&ecirc;n gh&eacute; qua Rustic Home - trang trại rộng hơn 1ha, tĩnh lặng tựa lưng v&agrave;o rừng th&ocirc;ng v&agrave; hướng ra mặt hồ rộng lớn.</div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Nghỉ ngơi tại đ&acirc;y, c&aacute;c bạn sẽ c&oacute; được những trải nghiệm thực sự tuyệt vời giữa thi&ecirc;n nhi&ecirc;n với những bữa tiệc nướng BBQ ngo&agrave;i trời bằng củi do ch&iacute;nh m&igrave;nh kiếm sau nh&agrave;, hay lặng lẽ ngồi nh&acirc;m nhi một t&aacute;ch tr&agrave; nghi ng&uacute;t kh&oacute;i ngay b&ecirc;n hồ v&agrave; ngắm chiều t&agrave;, c&ograve;n g&igrave; th&uacute; vị hơn?</div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Rustic Home l&agrave; một căn biệt thự ấm c&uacute;ng được bao bọc bởi gỗ v&agrave; lối kiến tr&uacute;c ch&acirc;n phương, tiện nghi với 1 ph&ograve;ng ngủ twin, 1 ph&ograve;ng ngủ double, 1 g&aacute;c x&eacute;p 2 đệm đ&ocirc;i ngập tr&agrave;n &aacute;nh nắng v&agrave; 1 gian bếp rộng c&oacute; đủ đồ.&nbsp;</div>\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"\" data-original=\"https://media.foody.vn/images/15380627_1781371418783305_6958511567257606006_n.jpg\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/azbooking7.jpg\" style=\"width: 650px; height: 488px;\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Hơn thế, Rustic Home c&ograve;n c&oacute; cả ph&ograve;ng giải tr&iacute; lớn c&ugrave;ng thư viện v&agrave; ph&ograve;ng kh&aacute;ch nh&igrave;n ra rừng th&ocirc;ng cực deep &nbsp;nữa nha!</div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"\" data-original=\"https://media.foody.vn/images/15327279_1780206245566489_934117927405125115_n.jpg\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/azbooking8.jpg\" style=\"width: 650px; height: 366px;\" /></div>\r\n		<div>\r\n			&nbsp;</div>\r\n		<div>\r\n			Khu vực nướng BBQ ngo&agrave;i trời v&agrave; s&acirc;n cỏ rộng ở Rustic Home ho&agrave;n to&agrave;n th&iacute;ch hợp để c&aacute;c bạn c&oacute; thể dựng lều trại ngắm sao, tổ chức tiệc d&atilde; ngoại v&agrave; trải nghiệm ngủ qua đ&ecirc;m giữa thi&ecirc;n nhi&ecirc;n nữa nh&eacute;!</div>\r\n		<div style=\"text-align: center;\">\r\n			<img alt=\"\" data-original=\"https://media.foody.vn/images/10277088_1781373008783146_4991042232352008945_n.jpg\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/azbooking9.jpg\" style=\"width: 650px; height: 433px;\" /></div>\r\n	</div>\r\n</div>\r\n<div>\r\n	&nbsp;</div>\r\n<h2>\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">4. Tree House, Gia L&acirc;m</span></span></h2>\r\n<div>\r\n	Nếu muốn thử cảm gi&aacute;c lạ với việc sinh sống ho&agrave;n to&agrave;n tr&ecirc;n những &quot;ng&ocirc;i nh&agrave; c&acirc;y&quot; m&agrave; trước đ&acirc;y ch&uacute;ng m&igrave;nh vẫn đỏ con mắt bởi chỉ t&igrave;m thấy ở những khu du lịch sinh th&aacute;i nước ngo&agrave;i, th&igrave; b&acirc;y giờ đắn đo g&igrave; m&agrave; kh&ocirc;ng d&agrave;nh hẳn kỳ nghỉ lễ sắp tới để thực hiện lu&ocirc;n nhỉ? Homestay độc đ&aacute;o c&oacute; một kh&ocirc;ng hai n&agrave;y tọa lạc ngay ở Ngọc Thụy, Gia L&acirc;m v&agrave; c&aacute;ch trung t&acirc;m H&agrave; Nội kh&ocirc;ng xa đ&acirc;u nha!</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<img alt=\"\" data-original=\"https://media.foody.vn/images/2-1476685304_660x0.jpg\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/azbooking10.jpg\" style=\"width: 650px; height: 433px;\" /></div>\r\n<div>\r\n	Tree House gồm nhiều ng&ocirc;i nh&agrave;, mỗi ng&ocirc;i nh&agrave; lại được thiết kế với phong c&aacute;ch ri&ecirc;ng nằm giữa cả &quot;rừng&quot; c&acirc;y xanh mướt mắt, v&agrave; mỗi ng&ocirc;i nh&agrave; lại chẳng kh&aacute;c n&agrave;o những căn nh&agrave; đ&aacute; hobbit trong thế giới cổ t&iacute;ch cả.</div>\r\n<div style=\"text-align: center;\">\r\n	<img alt=\"\" data-original=\"https://media.foody.vn/images/4-1476685305_660x0.jpg\" src=\"https://media.foody.vn/images/4-1476685305_660x0.jpg\" /></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	D&ugrave; l&agrave; nh&agrave; tr&ecirc;n c&acirc;y nhưng Tree House vẫn đầy đủ tiện nghi lắm nh&eacute;! Đặc biệt c&aacute;c ph&ograve;ng đều c&oacute; hướng view si&ecirc;u rộng, tho&aacute;ng nh&igrave;n bao qu&aacute;t ra xung quanh với c&acirc;y xanh bao quanh v&agrave; tiếng chim h&oacute;t v&eacute;o von mỗi s&aacute;ng. Gi&aacute; ph&ograve;ng ở đ&acirc;y khoảng 1 triệu/ đ&ecirc;m/ 2 người nha!</div>\r\n<div style=\"text-align: center;\">\r\n	<img alt=\"\" data-original=\"https://media.foody.vn/images/5-1476685306_660x0.jpg\" src=\"https://media.foody.vn/images/5-1476685306_660x0.jpg\" /></div>\r\n<div>\r\n	&nbsp;</div>\r\n<h2>\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">5. Phơri&#39;s House, M&ecirc; Linh</span></span></h2>\r\n<p>\r\n	Phơri&#39;s House - c&aacute;i t&ecirc;n n&agrave;y chắc chắn đ&atilde; gợi bạn nhớ tới một căn homestay xinh xắn tuyệt đối ở Sapa. Nhưng Phơri &#39;s Houe cũng c&oacute; hẳn một thế giới si&ecirc;u xinh nằm ngay ở trấn M&ecirc; Linh đấy nh&eacute;! Căn Homestay n&agrave;y được thiết kế theo phong c&aacute;ch hiện đại hơn<span style=\"font-size: 12px;\">, c&oacute; s&acirc;n cỏ rộng v&agrave; cả hồ bơi cực th&iacute;ch th&uacute;.</span></p>\r\n<div style=\"text-align: center;\">\r\n	<img alt=\"\" data-original=\"https://media.foody.vn/images/photo-26-1482396921183.jpg\" src=\"https://media.foody.vn/images/photo-26-1482396921183.jpg\" /></div>\r\n<div>\r\n	Cả căn nh&agrave; được bao quanh bởi những vườn rau xanh m&aacute;t v&agrave; hoa dại ở khắp lối. Những căn ph&ograve;ng ở đ&acirc;y rất rộng, v&agrave; đầy đủ tiện nghi. Tuy nhi&ecirc;n kh&ocirc;ng phải v&igrave; rộng m&agrave; Phơri&#39;s House bỏ qu&ecirc;n c&aacute;i vẻ xinh xắn vốn c&oacute;. Nằm xa trung t&acirc;m n&ecirc;n Phơri&#39;s House lu&ocirc;n y&ecirc;n b&igrave;nh, đ&atilde; vậy căn nh&agrave; l&uacute;c n&agrave;o cũng c&oacute; thật nhiều &aacute;nh s&aacute;ng v&agrave; gần gũi với thi&ecirc;n nhi&ecirc;n.&nbsp;</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<img alt=\"\" data-original=\"https://media.foody.vn/images/dau-chi-sapa-ha-noi-cung-co-Ph%c6%a1ris-House-tran-ngap-anh-sang-va-cay-xanh-ivivu-1.jpg\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/azbooking13.jpg\" style=\"width: 650px; height: 640px;\" /></div>\r\n<div>\r\n	Ở Phơri&#39;s House c&oacute; cả phục vụ ăn uống, tất cả đều l&agrave; thực phẩm sạch do của nh&agrave; trồng được. Gi&aacute; cả của ng&ocirc;i nh&agrave; n&agrave;y c&oacute; phần mềm hơn, khi c&oacute; thể thu&ecirc; theo ph&ograve;ng với gi&aacute; khoảng 1 triệu/ đ&ecirc;m, giới hạn 2 người, nếu th&ecirc;m người sẽ t&iacute;nh th&ecirc;m phụ ph&iacute;. Với những người y&ecirc;u th&iacute;ch sự tiện nghi m&agrave; vẫn muốn c&oacute; sự ri&ecirc;ng tư, thư th&aacute;i th&igrave; đ&acirc;y l&agrave; lựa chọn rất ph&ugrave; hợp.</div>\r\n<div style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/azbooking14.jpg\" style=\"width: 650px; height: 488px;\" /></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	H&egrave; về rồi, rủ &quot;team ruột&quot; đi trốn nắng thủ đ&ocirc; th&ocirc;i c&aacute;c bạn ơi !!!</div>\r\n', 'Trốn cả thành phố với 5 Homestay chất lừ ngay ngoại ô Hà Nội', 'homestay ở hà nội, đi trốn Hà Nội', 'Bạn có tin không cần đi đâu xa mà cũng có ngay một vài điểm homestay đẹp như trong mơ? Cùng Azbooking ', '2017-04-27 17:02:44'),
(20, 1, 'Kinh nghiệm xương máu để săn được vé máy bay giá luôn rẻ cho dân du lịch', 'kinh-nghiem-xuong-mau-de-san-duoc-ve-may-bay-gia-luon-re-cho-dan-du-lich', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/san-ve-may-bay-gia-re-azbooking.jpg', 452, '<p style=\"text-align: justify;\">\r\n	L&agrave;m thế n&agrave;o để đặt dược v&eacute; m&aacute;y bay gi&aacute; rẻ? Thời gian n&agrave;o v&eacute; m&aacute;y bay c&oacute; gi&aacute; rẻ nhất? Đ&oacute; l&agrave; những c&acirc;u hỏi phổ biến m&agrave; c&aacute;c du kh&aacute;ch của <strong>AZbooking </strong>thường thắc mắc v&agrave; nhờ <strong>AZ</strong> tư vấn.</p>\r\n<p style=\"text-align: justify;\">\r\n	Để săn được v&eacute; m&aacute;y bay gi&aacute; rẻ v&ocirc; c&ugrave;ng đơn giản nếu bạn &aacute;p dụng c&aacute;c <strong>TUYỆT CHI&Ecirc;U</strong> sau đ&acirc;y của <strong>AZbooking</strong>. Xem ngay nh&eacute;!</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"thoi-diem-vang-de-dat-ve-may-bay-gia-re-mixtourist\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tintuc/thoi-diem-vang-de-dat-ve-may-bay-gia-re-mixtourist.jpg\" style=\"width: 66%;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Nếu sắp tới bạn c&oacute; dự định <strong>du lịch biển</strong> hoặc một chuyến <strong>du lịch nước ngo&agrave;i gi&aacute; rẻ</strong> h&atilde;y &aacute;p dụng ngay c&aacute;c tuyệt chi&ecirc;u n&agrave;y của <strong>AZbooking </strong>nh&eacute;. Chắc chắc chuyến đi của bạn sẽ tiết kiệm được một khoản chi ph&iacute; &quot;kha kh&aacute;&quot; đ&oacute; nha!</p>\r\n<p style=\"text-align: justify;\">\r\n	Hoặc LH tư vấn đặt v&eacute; m&aacute;y bay gi&aacute; rẻ qua hotline <strong>094 3838 222</strong> để được hỗ trợ tận t&igrave;nh nhất nh&eacute;!</p>\r\n<p style=\"text-align: justify;\">\r\n	Truy cập website: <strong><a href=\"http://vemaybay.azbooking.vn/\">vemaybay.azbooking.vn</a></strong> để biết th&ecirc;m th&ocirc;ng tin chi tiết.</p>\r\n', 'Kinh nghiệm xương máu để săn được vé máy bay giá luôn rẻ cho dân du lị', 'kinh nghiệm đặt vé máy bay giá rẻ, Làm thế nào để đặt được vé máy bay giá rẻ, thời gian vàng để săn vé máy bay', 'Làm thế nào để đặt dược vé máy bay giá rẻ? Thời gian nào vé máy bay có giá rẻ nhất? Đó là những câu hỏi phổ biến mà các du khách của AZbooking thường thắc mắc và nhờ AZ t', '2017-04-18 13:53:44'),
(21, 1, 'Những cảnh đẹp “đốn tim”  du khách ở Hồ Nam - Trung Quốc', 'nhung-canh-dep-don-tim--du-khach-o-ho-nam-trung-quoc', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/du-lich-phuong-hoang-co-tran-%20AZbooking.jpg', 410, '<p>\r\n	Vẻ đẹp cổ k&iacute;nh như tranh vẽ ở Phượng Ho&agrave;ng trấn, quan cảnh thi&ecirc;n nhi&ecirc;n tuyệt đẹp Vũ Lăng Nguy&ecirc;n hay cung đường dẫn tới c&ocirc;ng trời Thi&ecirc;n An M&ocirc;n,.. l&agrave; những địa điểm m&agrave; bất k&igrave; du kh&aacute;ch n&agrave;o cũng kh&ocirc;ng thể bỏ qua khi c&oacute; dịp đến Hồ Nam.</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">Phượng Ho&agrave;ng Cổ Trấn - Hơn cả bức tranh thủy mặc</span></span></h2>\r\n<p>\r\n	&nbsp; &nbsp; Phượng Ho&agrave;ng cổ trấn l&agrave; một phố cổ thuộc huyện Phượng Ho&agrave;ng ở ph&iacute;a T&acirc;y tỉnh Hồ Nam được ch&iacute;nh thức x&acirc;y dựng từ những năm 1700 nhưng đ&atilde; tồn tại trước đ&oacute; từ thời Chiến Quốc, trấn Phượng Ho&agrave;ng thuộc huyện c&ugrave;ng t&ecirc;n, l&agrave; nơi sinh sống chủ yếu của tộc người Mi&ecirc;u, H&aacute;n v&agrave; Thổ Gia. Tại đ&acirc;y vẫn c&ograve;n lưu giữ một phần của &ldquo;Vạn L&yacute; Trường Th&agrave;nh Phương Nam&rdquo; vốn được nh&agrave; Minh x&acirc;y dựng từ 1554-1622 nhằm tr&aacute;nh c&aacute;c đợt tấn c&ocirc;ng của tộc người Mi&ecirc;u trong giai đoạn căng thẳng với triều đ&igrave;nh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/Du-Lich-Trung-Quoc-Phuong-Hoang-Co-Tran-AZbooking.jpg\" style=\"width: 650px; height: 431px;\" /></p>\r\n<p>\r\n	Ng&agrave;y nay, Phượng Ho&agrave;ng trấn kho&aacute;c l&ecirc;n m&igrave;nh chiếc &aacute;o cổ k&iacute;nh t&aacute;ch biệt với nhịp sống hối hả b&ecirc;n ngo&agrave;i th&ocirc;ng qua những d&atilde;y nh&agrave; cổ, đền thờ, th&agrave;nh qu&aacute;ch v&agrave; cả nhịp sống chậm r&atilde;i của người d&acirc;n nơi đ&acirc;y.</p>\r\n<div box-sizing:=\"\" class=\"VCSortableInPreviewMode\" new=\"\" style=\"margin: 0px auto 22px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-stretch: inherit; font-size: 17px; line-height: inherit; font-family: \" text-align:=\"\" times=\"\" type=\"Photo\" vertical-align:=\"\" width:=\"\">\r\n	<div style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: center;\">\r\n		<a class=\"detail-img-lightbox\" href=\"http://kenh14cdn.com/thumb_w/600/2016/10271636-1125531007457286-178011104249517653-n-1456501804147.jpg\" rel=\"img-lightbox\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; text-decoration: none; box-sizing: border-box; cursor: zoom-in;\" target=\"_blank\" title=\"\"><img alt=\"Cảnh đẹp mê mẩn của Phượng Hoàng cổ trấn sẽ khiến bạn ước ao một lần được đến - Ảnh 2.\" class=\"lightbox-content\" h=\"638\" id=\"img_147179\" photoid=\"147179\" rel=\"http://kenh14cdn.com/2016/10271636-1125531007457286-178011104249517653-n-1456501804147.jpg\" src=\"http://kenh14cdn.com/thumb_w/600/2016/10271636-1125531007457286-178011104249517653-n-1456501804147.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: bottom; color: transparent; box-sizing: border-box; max-width: 100%;\" title=\"Cảnh đẹp mê mẩn của Phượng Hoàng cổ trấn sẽ khiến bạn ước ao một lần được đến - Ảnh 2.\" type=\"photo\" w=\"960\" /></a></div>\r\n</div>\r\n<p>\r\n	Vẻ đẹp của Phượng Ho&agrave;ng Cổ Trấn c&ograve;n được tăng l&ecirc;n bội phần khi con s&ocirc;ng Đ&agrave; Giang vắt ngang chia đ&ocirc;i cổ trấn nằm trọn trong những ngọn n&uacute;i. Nước s&ocirc;ng xanh biếc &aacute;ng m&igrave;nh b&ecirc;n cạnh những ng&ocirc;i nh&agrave; gỗ m&aacute;i ng&oacute;i &acirc;m dương nhuốm m&agrave;u thời gian, đủ khiến bức tranh Phượng Ho&agrave;ng Cổ Trấn l&atilde;ng mạn, đầy chất thơ v&agrave; đ&ocirc;i ch&uacute;t ho&agrave;i niệm.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/song-da-giang-Azbooking%20tourist.jpeg\" style=\"width: 650px; height: 365px;\" /></p>\r\n<p>\r\n	Đến với cổ trấn, du kh&aacute;ch kh&ocirc;ng khỏi ngỡ ng&agrave;ng v&igrave; vẻ đẹp của Phượng Ho&agrave;ng c&ograve;n đẹp hơn tr&ecirc;n phim ảnh, tạo cho người ta cảm gi&aacute;c như trong truyện cổ t&iacute;ch - vẻ đẹp cổ xưa nhưng kh&ocirc;ng k&eacute;m phần lung linh.</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\">Trương Gia Giới &ndash; chốn bồng lai ti&ecirc;n cảnh</span></span></h3>\r\n<p>\r\n	Trương Gia Giới l&agrave; thi&ecirc;n đường du lịch ti&ecirc;u biểu của Hồ Nam với v&ocirc; số cảnh đẹp thần ti&ecirc;n: từ n&uacute;i non, s&ocirc;ng nước hữu t&igrave;nh đến những thung lũng n&ecirc;n thơ, khu rừng nguy&ecirc;n sinh hoang d&atilde;.Với rừng đ&aacute; sa thạch h&ugrave;ng vĩ, Trương Gia Giới c&ograve;n được mệnh danh l&agrave; &ldquo;Pandora tr&ecirc;n Tr&aacute;i đất&rdquo;.</p>\r\n<div>\r\n	Rừng nguy&ecirc;n sinh Trương Gia Giới rộng khoảng 4.810ha, ở độ cao tr&ecirc;n 1.000m so với mực nước biển. Nơi đ&acirc;y đ&atilde; được UNESCO c&ocirc;ng nhận l&agrave; Di sản Thi&ecirc;n nhi&ecirc;n Thế giới năm 1992, C&ocirc;ng vi&ecirc;n Địa chất Thế giới v&agrave;o năm 2004.</div>\r\n<div>\r\n	<div style=\"text-align: center;\">\r\n		<img alt=\"\" src=\"https://i1.wp.com/k14.vcmedia.vn/A3YmnWqkHeph7OwGyu6TwbX57tgTw/Image/2012/05/120510kptruonggiagioi03_71d0a.jpg\" /></div>\r\n</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Cả khu vực nằm trong v&ugrave;ng kh&iacute; hậu cận nhiệt đới ẩm, quanh năm c&oacute; sương m&ugrave; bao phủ như chốn bồng lai vậy</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<div style=\"text-align: center;\">\r\n		<img alt=\"\" src=\"https://i2.wp.com/k14.vcmedia.vn/A3YmnWqkHeph7OwGyu6TwbX57tgTw/Image/2012/05/120510kptruonggiagioi05_d2265.jpg\" /></div>\r\n</div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	Sự đa dạng của cảnh quan mang đến cho mỗi m&ugrave;a một vẻ đẹp rất ri&ecirc;ng</div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div>\r\n	<div style=\"text-align: center;\">\r\n		&nbsp;</div>\r\n</div>\r\n<div>\r\n	Rừng đ&aacute; sa thạch l&agrave; cảnh đẹp nổi tiếng nhất ở Trương Gia Giới với quần thể n&uacute;i đ&aacute; cao v&uacute;t, đứng sừng sững như những c&acirc;y cột trụ trời, tạo n&ecirc;n khung cảnh nguy&ecirc;n sơ v&ocirc; c&ugrave;ng ấn tượng.</div>\r\n<div style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/sa%20thach-%20truong%20gia%20gioi-%20AZbooking.jpg\" style=\"width: 600px; height: 401px;\" /></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<div style=\"text-align: center;\">\r\n		&nbsp;</div>\r\n</div>\r\n<div>\r\n	Khu rừng đặc biệt n&agrave;y c&oacute; tới hơn 3.000 cột đ&aacute; v&agrave; v&aacute;ch n&uacute;i h&igrave;nh th&ugrave; thi&ecirc;n biến vạn h&oacute;a, c&oacute; cột cao tới 800m. Giữa c&aacute;c cột đ&aacute; v&agrave; khe n&uacute;i l&agrave; c&aacute;c hang động, suối nguồn, những rừng c&acirc;y nguy&ecirc;n sinh nơi cư tr&uacute; của nhiều lo&agrave;i c&acirc;y &ocirc;n đới. Khung cảnh ngoạn mục n&agrave;y l&agrave; kết quả của qu&aacute; tr&igrave;nh kiến tạo địa chất k&eacute;o d&agrave;i suốt 380 triệu năm, từ khi cả khu vực c&ograve;n ch&igrave;m s&acirc;u dưới đ&aacute;y biển, qua nhiều lần n&acirc;ng l&ecirc;n hạ xuống của vỏ Tr&aacute;i đất v&agrave; qu&aacute; tr&igrave;nh b&agrave;o m&ograve;n, phong h&oacute;a.</div>\r\n<div>\r\n	<div style=\"text-align: center;\">\r\n		<img alt=\"\" src=\"https://i2.wp.com/k14.vcmedia.vn/A3YmnWqkHeph7OwGyu6TwbX57tgTw/Image/2012/05/120510kptruonggiagioi09_74bf1.jpg\" /></div>\r\n</div>\r\n<div>\r\n	Đứng tr&ecirc;n đỉnh n&uacute;i ph&oacute;ng tầm mắt xuống thung lũng, khung cảnh bao tr&ugrave;m ng&uacute;t mắt một m&agrave;u xanh.</div>\r\n<div>\r\n	<h2>\r\n		<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">Thi&ecirc;n M&ocirc;n Sơn - cung đường hiểm trở dẫn tới cổng trời</span></span></h2>\r\n	<p>\r\n		Thi&ecirc;n M&ocirc;n Sơn l&agrave; con đường nằm trong Vườn quốc gia Thi&ecirc;n M&ocirc;n, Trương Gia Giới, ph&iacute;a t&acirc;y bắc tỉnh Hồ Nam. Với 99 kh&uacute;c cua c&ugrave;ng độ dốc đ&aacute;ng sợ, Thi&ecirc;n M&ocirc;n Sơn được mệnh danh l&agrave; một trong những cung đường nguy hiểm nhất thế giới. D&ugrave; chỉ d&agrave;i 11 km nhưng ch&ecirc;nh lệch chiều cao giữa điểm đầu v&agrave; điểm cuối Thi&ecirc;n M&ocirc;n Sơn l&ecirc;n tới 1.100 m. Nơi cao nhất của con đường ở mức 1.300 m so với mực nước biển.&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/thien%20mon%20son-%20AZbooking%20tourist.jpg\" style=\"width: 650px; height: 435px;\" /></p>\r\n	<p>\r\n		Ngo&agrave;i ra, Thi&ecirc;n M&ocirc;n Sơn c&ograve;n nổi tiếng với con đường trong suốt l&agrave;m từ k&iacute;nh cường lực, được gọi l&agrave; Skywalk. Con đường men theo v&aacute;ch đ&aacute; dựng đứng đem lại cảm gi&aacute;c th&iacute;ch th&uacute;, hồi hộp. V&igrave; thế, Skywalk lu&ocirc;n nằm trong số những điểm thu h&uacute;t kh&aacute;ch du lịch nhất tại Trương Gia Giới.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/Cau-kinh-truong-gia-gioi-AZbooking.jpg\" style=\"width: 650px; height: 488px;\" /></p>\r\n</div>\r\n<p>\r\n	Khi đi bộ tr&ecirc;n Skywalk, bạn sẽ phải th&oacute;t tim v&agrave; r&oacute;n r&eacute;n như bước tr&ecirc;n m&acirc;y. Bởi lẽ, mọi cảnh vật hiện r&otilde; mồn một ngay dưới ch&acirc;n v&agrave; dưới lớp k&iacute;nh l&agrave; độ cao đ&aacute;ng sợ &ndash; hơn 1.400m.</p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'Những cảnh đẹp “đốn tim”  du khách ở Hồ Nam - Trung Quốc', 'Du lịch trung quốc, du lịch hồ nam, du lịch trương gia giới, du lịch phượng hoàng cổ trấn, phượng hoàng cổ trấn', 'Vẻ đẹp cổ kính như tranh vẽ ở Phượng Hoàng trấn, quan cảnh thiên nhiên tuyệt đẹp Vũ Lăng Nguyên hay cung đường dẫn tới công trời Thiên An Môn,.. là những địa điểm mà bất ', '2017-06-01 08:37:52');
INSERT INTO `news` (`id`, `danhmuc_id`, `name`, `name_url`, `img`, `view`, `content`, `title`, `keyword`, `description`, `created`) VALUES
(22, 1, 'Lịch trình Tour du lịch Thái Lan: Bangkok - Pattaya 5 Ngày 4 Đêm - AZbooking', 'lich-trinh-tour-du-lich-thai-lan-bangkok-pattaya-5-ngay-4-dem-azbooking', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tour/anh-tour/du-lich-thai-lan-5-ngay-4-dem-azbooking.jpg', 102, '<div>\r\n	<p style=\"text-align: justify;\">\r\n		Thi&ecirc;n đường du lịch Th&aacute;i Lan nơi con người th&acirc;n thiện v&agrave; vui vẻ, đất nước nhiệt đới độc đ&aacute;o, nền văn h&oacute;a v&agrave; lịch sử l&acirc;u đời. Th&aacute;i Lan tỏa s&aacute;ng với những ng&ocirc;i đền rực rỡ nguy nga, những b&atilde;i biển v&agrave;ng v&agrave; những nụ cười Th&aacute;i Lan tươi thắm. Ở Th&aacute;i Lan, 2 th&agrave;nh phố được kh&aacute;ch du lịch quan t&acirc;m nhất đ&oacute; ch&iacute;nh l&agrave; th&agrave;nh phố Bangkok v&agrave; Pattay.</p>\r\n	<p style=\"text-align: justify;\">\r\n		H&atilde;y một lần c&ugrave;ng <strong>AZbooking </strong>tham gia h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; v&agrave; trải nghiệm cuộc sống, ẩm thực, văn h&oacute;a v&agrave; c&aacute;c danh lam thắng cảnh nổi tiếng bậc nhất trong&nbsp;<strong>Tour du lịch Th&aacute;i Lan: Bangkok - Pattaya 5 ng&agrave;y 4 đ&ecirc;m</strong> ngay sau đ&acirc;y nh&eacute;!</p>\r\n	<p style=\"text-align: justify;\">\r\n		<em>&gt;&gt; xem th&ecirc;m:&nbsp;<a href=\"http://azbooking.vn/cam-nang/kinh-nghiem-du-lich/lich-trinh-tour-du-lich-thai-lan-bangkok-pattaya-4-ngay-3-dem-azbooking.html\"><strong>Lịch tr&igrave;nh&nbsp;</strong><strong><strong>T</strong></strong><strong>our du lịch Th&aacute;i Lan: Bangkok - Pattaya 4 ng&agrave;y 3 đ&ecirc;m</strong></a></em></p>\r\n	<h2 style=\"text-align: justify;\">\r\n		<span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\">NG&Agrave;Y 01: H&Agrave; NỘI&nbsp; &ndash; BANGKOK&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></h2>\r\n</div>\r\n<p style=\"text-align: justify;\">\r\n	<strong>17h00</strong>&nbsp;Xe đ&oacute;n qu&yacute; kh&aacute;ch tại đi&ecirc;̉m hẹn.</p>\r\n<p style=\"text-align: justify;\">\r\n	<strong>18h00</strong>&nbsp;Qu&yacute; kh&aacute;ch tập trung tại nh&agrave; ga T2 - s&acirc;n bay Quốc tế N&ocirc;̣i Bài, l&agrave;m thủ tục đ&aacute;p chuyến bay FD645 (20h50 &ndash; 22h45). Đến s&acirc;n bay Don Muaeng Th&aacute;i Lan, đo&agrave;n l&agrave;m thủ tục nhập cảnh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/du-lich-bangkok-azbooking.png\" style=\"width: 65%;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Ti&ecirc;́p tục khởi hành v&ecirc;̀ Bangkok. Tr&ecirc;n đường đi quý khách ngắm cảnh thành ph&ocirc;́ Bangkok lung linh v&ecirc;̀ đ&ecirc;m và&nbsp;<strong>toàn nhà Baiyoke Sky</strong>&nbsp;cao nh&acirc;́t Thái Lan (84 t&acirc;̀ng)</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"toa-nha-Bayjoke-Sky-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/toa-nha-Bayjoke-Sky-azbooking.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đ&ecirc;́n khách sạn, đoàn nh&acirc;̣n phòng tại kh&aacute;ch sạn<strong>&nbsp;Miramar Bangkok&nbsp;</strong>hoặc tương đương.</p>\r\n<p style=\"text-align: justify;\">\r\n	Qu&yacute; kh&aacute;ch nghỉ ngơi hoặc tự do kh&aacute;m ph&aacute; th&agrave;nh phố Bangkok về đ&ecirc;m với những con phố đ&ecirc;m nổi tiếng: Khao San, China Town, Patpong&hellip;.</p>\r\n<div>\r\n	<h2 style=\"text-align: justify;\">\r\n		<span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\">NG&Agrave;Y 02: BANGKOK- PATTAYA &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(ĂN S&Aacute;NG / TRƯA / TỐI)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></h2>\r\n</div>\r\n<p style=\"text-align: justify;\">\r\n	Ăn s&aacute;ng tại kh&aacute;ch sạn. Xe đ&oacute;n đo&agrave;n đi tham quan:</p>\r\n<p style=\"text-align: justify;\">\r\n	-&nbsp;<span style=\"font-size: 14px;\"><strong>Anata Samakhom:&nbsp;</strong></span>Một quần thể kiến tr&uacute;c độc đ&aacute;o nổi tiếng của Vương qu&ocirc;́c Thailand. nơi được coi l&agrave; t&ograve;a nh&agrave; Quốc Hội đầu ti&ecirc;n của Th&aacute;i Lan, được x&acirc;y dựng theo lệnh của Quốc Vương Rama V v&agrave;o năm 1907 v&agrave; ho&agrave;n th&agrave;nh v&agrave;o thời kỳ thống trị của Quốc Vương Rama VI theo lối kiến tr&uacute;c của thời kỳ Phục Hưng &ndash; đặc biệt l&agrave; phần m&aacute;i v&ograve;m vươn cao l&ecirc;n theo thiết kế của kiến tr&uacute;c sư người &Yacute;&nbsp;-Mario Tamango v&agrave; Annibale Rigotti</p>\r\n<p style=\"text-align: justify;\">\r\n	-<span style=\"font-size: 14px;\">&nbsp;</span><strong><span style=\"font-size: 14px;\">Ch&ugrave;a Phật V&agrave;ng Wat Traimit</span>:</strong>&nbsp;Một trong những ng&ocirc;i ch&ugrave;a linh thi&ecirc;ng nhất tại đất nước Th&aacute;i Lan với bức tượng Phật V&agrave;ng Golden Buhhdha nằm ở ch&iacute;nh điện - &nbsp;bức tượng Phật V&agrave;ng lớn nhất thế giới: cao 3 m&eacute;t v&agrave; nặng hơn 5,5 tấn. Tượng được đ&uacute;c theo phong c&aacute;ch Sukhothai tĩnh lặng v&agrave; được kh&aacute;m ph&aacute; một c&aacute;ch t&igrave;nh cờ v&agrave;o thập ni&ecirc;n 1950. Người địa phương cho rằng bức tượng lớn nhất thế giới n&agrave;y biểu thị cho sự thịnh vượng v&agrave; thuần khiết cũng như sức mạnh v&agrave; quyền năng.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"chua-phat-vang-Wat-Traimit-thai-lan-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/chua-phat-vang-Wat-Traimit-thai-lan-azbooking.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đo&agrave;n ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style=\"text-align: justify;\">\r\n	Sau đ&oacute; khởi h&agrave;nh đi Pattaya, tr&ecirc;n đường đi Qu&yacute; kh&aacute;ch tham quan</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: 14px;\">-<strong>&nbsp;Trại Hổ Siracha Tiger Zoo:&nbsp;</strong><span style=\"font-size: 12px;\">vuờ</span></span>n th&uacute; lớn của Th&aacute;i Lan, c&oacute; cơ hội thưởng thức c&aacute;c tiết mục biểu diễn như: xiếc hổ, xiếc voi&hellip;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"trai-ho-Siracha-Tiger-Zoo-thai-lan-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/trai-ho-Siracha-Tiger-Zoo-thai-lan-azbooking.jpg\" style=\"width: 600px; height: 339px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Sau đ&oacute; về Pattaya ăn tối tại nh&agrave; h&agrave;ng. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn The Vogue Pattaya hoặc tương đương.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"du-lich-Pattaya-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/du-lich-pattaya-azbooking.png\" style=\"width: 65%;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	(Qu&yacute; kh&aacute;ch c&oacute; thể đăng k&yacute; xem những tiết mục đặc sắc, đặc sản nổi tiếng của th&agrave;nh phố ma quỷ Pattaya - Sexy Show - Chi ph&iacute; tự t&uacute;c).</p>\r\n<div>\r\n	<h2 style=\"text-align: justify;\">\r\n		<span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\">NG&Agrave;Y 03: PATTAYA &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(ĂN S&Aacute;NG/TRƯA/TỐI)</span></span></h2>\r\n</div>\r\n<p style=\"text-align: justify;\">\r\n	Ăn s&aacute;ng tại kh&aacute;ch sạn, sau đ&oacute; đo&agrave;n khởi h&agrave;nh tham quan:</p>\r\n<p style=\"text-align: justify;\">\r\n	<strong><span style=\"font-size: 14px;\">- Đồi đ&ocirc; đốc Hải Qu&acirc;n:&nbsp;</span></strong>Nằm tr&ecirc;n đỉnh n&uacute;i cao nhất Pattaya. Từ đ&acirc;y, qu&yacute; kh&aacute;ch c&oacute; thể nh&igrave;n ngắm to&agrave;n cảnh b&atilde;i biển Pattaya xinh đẹp, tham gia đốt ph&aacute;o truyền thống &ndash; Nguyện cầu may mắn, b&igrave;nh an trong cuộc sống.</p>\r\n<p style=\"text-align: justify;\">\r\n	<strong><span style=\"font-size: 14px;\">-Bờ biển Pattaya:&nbsp;</span></strong>qu&yacute; kh&aacute;ch đến với b&atilde;i biển tham quan bờ biển v&agrave; tự do tắm biển Pattaya. Ngo&agrave;i ra, qu&yacute; kh&aacute;ch c&oacute; thể tham dự c&aacute;c tr&ograve; chơi như: d&ugrave; bay, c&acirc;u c&aacute;, lướt v&aacute;n, l&aacute;i Scooter tr&ecirc;n mặt biển, th&aacute;m hiểm dưới đ&aacute;y biển&hellip;(chi ph&iacute; tự t&uacute;c).</p>\r\n<p style=\"text-align: justify;\">\r\n	Qu&yacute; kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style=\"text-align: justify;\">\r\n	Buổi chiều: Xe v&agrave; hướng dẫn đưa đo&agrave;n thăm quan:</p>\r\n<p style=\"text-align: justify;\">\r\n	-&nbsp;<span style=\"font-size: 14px;\"><strong>Trung t&acirc;m chế t&aacute;c V&agrave;ng Bạc Đ&aacute; Qu&yacute;:</strong></span>&nbsp;World Gems Gallery nổi tiếng v&agrave; lớn nhất của Th&aacute;i Lan &ndash;nơi di&ecirc;̃n các phi&ecirc;n trao đ&ocirc;̉i mua bán đá quý đạt chứng chỉ ISO 9002 v&ecirc;̀ quản lý ch&acirc;́t lượng, nơi trưng b&agrave;y c&aacute;c loại đ&aacute; qu&yacute; đẹp nổi tiếng, cung cấp những th&ocirc;ng tin bổ &iacute;ch về qu&aacute; tr&igrave;nh khai th&aacute;c, chế t&aacute;c đ&aacute; qu&yacute; tại Th&aacute;i Lan v&agrave; tr&ecirc;n thế giới.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"trung-tam-che-tac-vang-bac-da-quy-World-Gems-Gallery-thai-lan-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/trung-tam-che-tac-vang-bac-da-quy-World-Gems-Gallery-thai-lan-azbooking.jpg\" style=\"width: 600px; height: 320px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: 14px;\"><strong>-&nbsp;L&agrave;ng voi Pattaya:</strong></span>&nbsp;du kh&aacute;ch cơ hội ch&uacute;ng kiến tận mắt cuộc sống thường ng&agrave;y của c&aacute;c ch&uacute; voi v&agrave; những người quản tượng.&nbsp;<span style=\"color: rgb(255, 0, 0);\">Tặng v&eacute; cưỡi voi miễn ph&iacute;, trải nghiệm cảm gi&aacute;c du ngoạn tr&ecirc;n lưng voi.</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"text-align: center; font-size: 14px;\"><strong>-&nbsp;N&uacute;i Phật V&agrave;ng Khao CheeChan:</strong></span><span style=\"text-align: center;\">&nbsp;Ngọn n&uacute;i c&oacute; bức tượng v&agrave;ng khắc bằng Laze tr&ecirc;n v&aacute;ch n&uacute;i, d&ugrave;ng 999kg v&agrave;ng, cao 130m. Xung quanh được bao phủ bởi v&ocirc; số loại hoa kh&aacute;c nhau trong những khu vườn được chăm s&oacute;c tỉ mỉ. Khu vực được bảo vệ bởi Hải Qu&acirc;n Ho&agrave;ng Gia Th&aacute;i Lan</span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/nui-phat-vang-trung%20tam%20lu%20hanh%20Azbooking.jpg\" style=\"width: 650px; height: 366px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: 14px;\"><strong>- Vườn nho&nbsp;Silver Lake:&nbsp;</strong></span>Một c&aacute;nh đồng nho rộng lớn với rất nhiều hoa, khung cảnh l&atilde;ng mạn , nếp nh&agrave; cổ k&iacute;nh giống như khung cảnh Ch&acirc;u &Acirc;u thu nhỏ. Du kh&aacute;ch c&oacute; thể đến tham quan v&agrave;o buổi s&aacute;ng sớm hoặc buổi chiều ho&agrave;ng h&ocirc;n l&agrave; ho&agrave;n hảo nhất. Khung cảnh nơi đ&acirc;y v&ocirc; c&ugrave;ng thơ mộng v&agrave; l&atilde;ng mạn.</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: 14px;\">-<strong>&nbsp;L&acirc;u đ&agrave;i tỷ ph&uacute; Baan Sukhawadee:&nbsp;</strong></span>Có vị tr&iacute; ven biển, kiến tr&uacute;c ch&iacute;nh được lấy &yacute; tưởng từ những ho&agrave;ng cung, cung điện&nbsp;Versailles, mang đến c&aacute;i nh&igrave;n hấp dẫn về mặt kiến tr&uacute;c, t&ograve;a l&acirc;u đ&agrave;i c&ograve;n chưa đựng những &yacute; nghĩa ẩn về triết l&yacute; nh&acirc;n sinh, t&ocirc;n gi&aacute;o s&acirc;u sắc. Qu&yacute; kh&aacute;ch tham dự tiệc buffet tối tại l&acirc;u đ&agrave;i v&agrave; thưởng thức chương tr&igrave;nh ca m&uacute;a nhạc Pede Show.</p>\r\n<p style=\"text-align: justify;\">\r\n	Nghỉ đ&ecirc;m tại kh&aacute;ch sạn The Vogue Pattaya hoặc tương đương.</p>\r\n<div>\r\n	<h2 style=\"text-align: justify;\">\r\n		<span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\">NG&Agrave;Y 04: PATTAYA - BANGKOK &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (ĂN S&Aacute;NG/TRƯA/TỐI)</span></span></h2>\r\n</div>\r\n<p style=\"text-align: justify;\">\r\n	S&aacute;ng: Sau khi ăn s&aacute;ng tại kh&aacute;ch sạn, Qu&yacute; kh&aacute;ch trả ph&ograve;ng. Xe đưa đo&agrave;n về thủ đ&ocirc; Bangkok, đưa đo&agrave;n gh&eacute; thăm:</p>\r\n<p style=\"text-align: justify;\">\r\n	-&nbsp;<span style=\"font-size: 14px;\"><strong>Vườn Saithip (Saithip Garden):&nbsp;</strong></span>l&agrave; nơi trưng b&agrave;y sản ph&acirc;̉m 3 mi&ecirc;̀n của vương qu&ocirc;́c Th&aacute;i Lan. Qu&yacute; khách có th&ecirc;̉ tự do mua sắm c&aacute;c loại đặc sản như: Huyết Yến đ&oacute;ng hộp, Mật Ong thi&ecirc;n nhi&ecirc;n, cao hổ cốt,&hellip;).</p>\r\n<p style=\"text-align: justify;\">\r\n	-&nbsp;<span style=\"font-size: 14px;\"><strong>Viện nghi&ecirc;n cứu rắn độc 5* Royal Thai:</strong></span>&nbsp;qu&yacute; kh&aacute;ch thưởng thức Show diễn bắt rắn bằng tay kh&ocirc;ng, sau đó Quý khách được nghe các dược sỹ giới thi&ecirc;̣u v&agrave; giải th&iacute;ch về c&ocirc;ng dụng của những lo&agrave;i rắn q&uacute;y hiếm tr&ecirc;n vương quốc Thailand v&agrave; c&aacute;c loại thuốc chế biến từ rắn m&agrave; người Th&aacute;i đ&atilde; nghi&ecirc;n cứu suốt 100 năm&hellip;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"tran-ran-thai-lan-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/tran-ran-thai-lan-azbooking.jpg\" style=\"width: 600px; height: 379px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	-&nbsp;<span style=\"font-size: 14px;\"><strong>Si&ecirc;u thị đồ da Italia Toscano:</strong></span>&nbsp;nổi tiếng của Th&aacute;i Lan. Qu&yacute; kh&aacute;ch c&oacute; thể t&igrave;m hiểu về c&aacute;ch chế biến v&agrave; ph&acirc;n loại c&aacute;c sản phẩm từ da động vật, mua sắm tại trung t&acirc;m đồ da của Ho&agrave;ng Gia.</p>\r\n<p style=\"text-align: justify;\">\r\n	- Tiếp tục h&agrave;nh tr&igrave;nh, qu&yacute; kh&aacute;ch gh&eacute; thăm v&agrave; tự do shopping tại c&aacute;c trung t&acirc;m thương mại lớn nhất thủ đ&ocirc; Bangkok:Big C, Central World, Platinum&hellip;..Qu&yacute; kh&aacute;ch tự t&uacute;c ăn tối coupon tại si&ecirc;u thị.</p>\r\n<p style=\"text-align: justify;\">\r\n	Nghỉ đ&ecirc;m tại kh&aacute;ch sạn Miramar Bangkok hoặc tương đương.</p>\r\n<div>\r\n	<h2 style=\"text-align: justify;\">\r\n		<span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: 16px;\">NG&Agrave;Y 05: BANGKOK - WATYANAWA&nbsp; - H&Agrave; NỘI &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (ĂN S&Aacute;NG/TRƯA)</span></span></h2>\r\n</div>\r\n<p style=\"text-align: justify;\">\r\n	Ăn s&aacute;ng tại kh&aacute;ch sạn, trả ph&ograve;ng. Xe đưa Qu&yacute; kh&aacute;ch đi tham quan:</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: 14px;\">-<strong>&nbsp;Ch&ugrave;a Thuyền Watyanawa:&nbsp;</strong></span>hay c&ograve;n gọi l&agrave; Ch&ugrave;a X&aacute; lợi &ndash; Một trong những ng&ocirc;i ch&ugrave;a linh thi&ecirc;ng nhất của Th&aacute;i Lan với h&igrave;nh d&aacute;ng ch&ugrave;a được x&acirc;y dựng theo kiểu kiến tr&uacute;c h&igrave;nh d&aacute;ng con thuyền c&ugrave;ng với kiến tr&uacute;c Th&aacute;i l&agrave; c&aacute;c m&aacute;i cao v&uacute;t mang đậm phong c&aacute;ch thời Ayuthaya nằm ngay cạnh d&ograve;ng s&ocirc;ng mẹ Chaophraya. Nơi đ&acirc;y được biết tới l&agrave; ng&ocirc;i ch&ugrave;a c&ograve;n lưu giữ nhiều x&aacute; lị nhất của c&aacute;c bậc cao tăng tu th&agrave;nh đắc đạo.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"chua-thuyen-Watyanawa-thai-lan-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/chua-thuyen-Watyanawa-thai-lan-azbooking.jpg\" style=\"width: 600px; height: 308px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: 14px;\"><strong>- Du thuyền tr&ecirc;n d&ograve;ng s&ocirc;ng Chaophraya</strong></span>&nbsp;- d&ograve;ng s&ocirc;ng mẹ của đất nước Th&aacute;i Lan. Du khách nh&igrave;n ngắm phong cảnh hai b&ecirc;n bờ v&agrave; &nbsp;hi&ecirc;̣n tượng cá n&ocirc;̉i tr&ecirc;n dòng s&ocirc;ng lớn này.</p>\r\n<p style=\"text-align: justify;\">\r\n	Trưa: Qu&yacute; kh&aacute;ch&nbsp;<strong>thưởng thức m&oacute;n Lẩu Suki Nhật Bản.</strong></p>\r\n<p style=\"text-align: justify;\">\r\n	Đến giờ, xe v&agrave; HDV đưa qu&yacute; kh&aacute;ch ra s&acirc;n bay l&agrave;m thủ tục&nbsp;<strong>đ&aacute;p chuyến bay FD644 (18h35 &ndash; 20h20) về H&agrave; Nội</strong>. 20h20: Đến s&acirc;n bay Nội B&agrave;i, xe đưa đo&agrave;n về điểm đ&oacute;n kh&aacute;ch ban đầu, chia tay v&agrave; hẹn gặp lại qu&yacute; kh&aacute;ch trong c&aacute;c h&agrave;nh tr&igrave;nh tiếp theo.</p>\r\n<p style=\"text-align: justify;\">\r\n	&gt;&gt;&gt; Xem ngay: <a href=\"http://azbooking.vn/tour-du-lich-quoc-te/du-lich-chau-a/du-lich-thai-lan/du-lich-thai-lan-thang-5-tour-bangkok-pattaya-bay-vietnam-airline.html\"><strong>Lịch khởi h&agrave;nh tour du lịch Th&aacute;i Lan: Bangkok - Pattaya th&aacute;ng 5</strong></a></p>\r\n<p style=\"text-align: justify;\">\r\n	&gt;&gt;&gt; Xem ngay: <strong><a href=\"http://azbooking.vn/tour-du-lich-quoc-te/du-lich-chau-a/du-lich-thai-lan/du-lich-thai-lan-tour-bangkok-pattaya-5n4d-thang-6-big-sale.html\">Lịch khởi h&agrave;nh tour du lịch Th&aacute;i Lan: Bangkok - Pattaya th&aacute;ng 6</a></strong></p>\r\n<p style=\"text-align: justify;\">\r\n	&gt;&gt;&gt; Xem ngay: <a href=\"http://azbooking.vn/tour-du-lich-quoc-te/du-lich-chau-a/du-lich-thai-lan/du-lich-thai-lan-thang-7-tour-bangkok-pattaya-4-ngay-3-dem-bay-jetsar-pacific.html\"><strong>Lịch khởi h&agrave;nh tour du lịch Th&aacute;i Lan: Bangkok - Pattaya th&aacute;ng 7</strong></a></p>\r\n<div>\r\n	<p style=\"text-align: center;\">\r\n		<em style=\"font-size: 16px;\"><font color=\"#ff0000\"><b>====&gt;&gt;ĐĂNG K&Yacute; TRƯỚC&nbsp;<span style=\"background-color: rgb(255, 255, 0);\">30 NG&Agrave;Y&nbsp;</span>&nbsp;ĐƯỢC GIẢM NGAY&nbsp;</b></font><span style=\"color: rgb(255, 0, 0);\"><b><span style=\"background-color: rgb(255, 255, 0);\">1 TRIỆU ĐỒNG</span></b></span></em></p>\r\n</div>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tour/anh-tour/hotline.png\" style=\"width: 400px; height: 120px;\" /></p>\r\n\r\n<!-- Google Code for Chi ti&#7871;t l&#7883;ch tr&igrave;nh tour Conversion Page -->\r\n<script type=\"text/javascript\">\r\n/* <![CDATA[ */\r\nvar google_conversion_id = 941151229;\r\nvar google_conversion_language = \"en\";\r\nvar google_conversion_format = \"3\";\r\nvar google_conversion_color = \"ffffff\";\r\nvar google_conversion_label = \"WWk6COnw53AQ_afjwAM\";\r\nvar google_remarketing_only = false;\r\n/* ]]> */\r\n</script>\r\n<script type=\"text/javascript\" src=\"//www.googleadservices.com/pagead/conversion.js\">\r\n</script>\r\n<noscript>\r\n<div style=\"display:inline;\">\r\n<img height=\"1\" width=\"1\" style=\"border-style:none;\" alt=\"\" src=\"//www.googleadservices.com/pagead/conversion/941151229/?label=WWk6COnw53AQ_afjwAM&amp;guid=ON&amp;script=0\"/>\r\n</div>\r\n</noscript>\r\n', 'Lịch trình Tour du lịch Thái Lan: Bangkok - Pattaya 5 Ngày 4 Đêm - AZb', 'lịch trình tour du lịch thái lan bangkok pattaya 5 ngày 4 đêm', 'Hãy một lần cùng AZbooking tham gia hành trình khám phá và trải nghiệm cuộc sống, ẩm thực, văn hóa và các danh lam thắng cảnh nổi tiếng bậc nhất trong Tour du lịch Thái L', '2017-05-09 21:45:19'),
(23, 1, 'Lịch trình Tour du lịch Thái Lan: Bangkok - Pattaya 4 Ngày 3 Đêm - AZbooking', 'lich-trinh-tour-du-lich-thai-lan-bangkok-pattaya-4-ngay-3-dem-azbooking', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tour/anh-tour/du-lich-thai-lan-4-ngay-3-dem-azbooking.jpg', 76, '<p style=\"text-align: justify;\">\r\n	Thi&ecirc;n đường du lịch Th&aacute;i Lan nơi con người th&acirc;n thiện v&agrave; vui vẻ, đất nước nhiệt đới độc đ&aacute;o, nền văn h&oacute;a v&agrave; lịch sử l&acirc;u đời. Th&aacute;i Lan tỏa s&aacute;ng với những ng&ocirc;i đền rực rỡ nguy nga, những b&atilde;i biển v&agrave;ng v&agrave; những nụ cười Th&aacute;i Lan tươi thắm. Ở Th&aacute;i Lan, 2 th&agrave;nh phố được kh&aacute;ch du lịch quan t&acirc;m nhất đ&oacute; ch&iacute;nh l&agrave; th&agrave;nh phố Bangkok v&agrave; Pattay.</p>\r\n<p style=\"text-align: justify;\">\r\n	H&atilde;y một lần c&ugrave;ng&nbsp;AZbooking&nbsp;tham gia h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; v&agrave; trải nghiệm cuộc sống, ẩm thực, văn h&oacute;a v&agrave; c&aacute;c danh lam thắng cảnh nổi tiếng bậc nhất trong&nbsp;<strong>Tour du lịch Th&aacute;i Lan: Bangkok - Pattaya 3 ng&agrave;y 4 đ&ecirc;m</strong>&nbsp;ngay sau đ&acirc;y nh&eacute;!</p>\r\n<p style=\"text-align: justify;\">\r\n	<em>&gt;&gt; xem th&ecirc;m: <a href=\"http://azbooking.vn/cam-nang/kinh-nghiem-du-lich/lich-trinh-tour-du-lich-thai-lan-bangkok-pattaya-5-ngay-4-dem-azbooking.html\"><strong>Lịch tr&igrave;nh </strong><strong><strong>T</strong></strong><strong>our du lịch Th&aacute;i Lan: Bangkok - Pattaya 5 ng&agrave;y 4 đ&ecirc;m</strong></a></em></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: 16px;\">NG&Agrave;Y 01: H&Agrave; NỘI &ndash; BANGKOK &ndash; PATTAYA (Ăn: tối)</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<strong>06h00:&nbsp;</strong>Xe &ocirc; t&ocirc; đ&oacute;n đo&agrave;n tại điểm hẹn đưa đo&agrave;n ra s&acirc;n bay Nội B&agrave;i, l&agrave;m thủ tục đ&aacute;p m&aacute;y bay FD643 l&uacute;c 09h15 khởi h&agrave;nh đi&nbsp;<strong>Bangkok</strong>.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"du-lich-bangkok-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/du-lich-bangkok-azbooking.png\" style=\"width: 65%;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	<strong>11h05:</strong>&nbsp;M&aacute;y bay hạ c&aacute;nh xuống&nbsp;<strong>s&acirc;n bay Don Mueang</strong>&nbsp;- Bangkok, đo&agrave;n l&agrave;m thủ tục nhập cảnh v&agrave;o Th&aacute;i Lan.</p>\r\n<p style=\"text-align: justify;\">\r\n	Xe v&agrave; hướng dẫn vi&ecirc;n Th&aacute;i Lan đ&oacute;n đo&agrave;n tại s&acirc;n bay. Sau đ&oacute; khởi h&agrave;nh đi&nbsp;<strong>Pattaya&nbsp;</strong>(160km) &ndash; Th&agrave;nh phố nghỉ m&aacute;t nổi tiếng của Th&aacute;i Lan.</p>\r\n<p style=\"text-align: justify;\">\r\n	Tr&ecirc;n đường gh&eacute; thăm&nbsp;<strong>Tiger zoo &ndash; Trại Hổ&nbsp;</strong>( xem cuộc sống kỳ lạ của c&aacute;c lo&agrave;i th&uacute; trong zoo như mẹ lợn, con hổ, mẹ hổ con lợn, xem xiếc c&aacute; sấu, xiếc lợn, xiếc hổ&hellip;).</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"trai-ho-Siracha-Tiger-Zoo-thai-lan-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/trai-ho-Siracha-Tiger-Zoo-thai-lan-azbooking.jpg\" style=\"width: 600px; height: 339px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đến Pattaya, đo&agrave;n ăn tối, nhận ph&ograve;ng kh&aacute;ch sạn, nghỉ ngơi.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"du-lich-Pattaya-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/du-lich-pattaya-azbooking.png\" style=\"width: 65%;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Buổi tối Qu&yacute; kh&aacute;ch c&oacute; thể đăng k&yacute; xem những tiết mục đặc sắc -<strong>&nbsp;Sexy Show&nbsp;</strong>- Chi ph&iacute; tự t&uacute;c hoặc tự do dạo chơi v&agrave; kh&aacute;m ph&aacute; thế giới giải tr&iacute; của th&agrave;nh phố kh&ocirc;ng ngủ&nbsp;<strong>Pattaya</strong>.</p>\r\n<p style=\"text-align: justify;\">\r\n	Nghỉ đ&ecirc;m tại Pattaya, kh&aacute;ch sạn&nbsp;<strong>Marble Hotel 3*&nbsp;</strong>hoặc tương đương. &nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\">NG&Agrave;Y 02: PATTAYA &nbsp;(Ăn: s&aacute;ng, trưa, tối)</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<strong>06h30:</strong>&nbsp;Ăn s&aacute;ng buffet tại kh&aacute;ch sạn.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<strong>07h30:</strong>&nbsp;Qu&yacute; kh&aacute;ch tham gia<strong>&ldquo;H&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; Đảo Coral&rdquo;&nbsp;&nbsp;</strong>bằng t&agrave;u cao tốc &ndash; tại đ&acirc;y qu&yacute; kh&aacute;ch tự t&uacute;c tắm biển v&agrave; tự do tham gia c&aacute;c tr&ograve; chơi tr&ecirc;n biển như: nhảy d&ugrave;, l&aacute;i m&ocirc; t&ocirc; nước, trượt v&aacute;n, lặn biển ngắm san h&ocirc; (Chi ph&iacute; tự t&uacute;c). Qu&yacute; kh&aacute;ch về kh&aacute;ch sạn ăn trưa.</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"dao-san-ho-Coral-Island-thai-lan-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/dao-san-ho-Coral-Island-thai-lan-azbooking.jpg\" style=\"text-align: justify; width: 600px; height: 395px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<em style=\"color: rgb(0, 0, 205);\">Đảo san h&ocirc; Coral- Pattaya</em></p>\r\n<p style=\"text-align: justify;\">\r\n	<strong>Chiều:&nbsp;</strong>Qu&yacute; kh&aacute;ch đi thăm quan</p>\r\n<ul>\r\n	<li style=\"text-align: justify;\">\r\n		<strong>N&uacute;i Phật V&agrave;ng</strong>&nbsp;(Khao Chee Chan) - nổi tiếng với h&igrave;nh ảnh đức Phật được khắc v&agrave;ng tr&ecirc;n lưng n&uacute;i.</li>\r\n	<li style=\"text-align: justify;\">\r\n		<strong>Vườn Nho Silver Lake</strong>, một c&aacute;nh đồng nho rộng lớn với rất nhiều hoa, c&oacute; cả vườn bầu khổng lồ, b&iacute; khổng lồ.</li>\r\n</ul>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"vuon-nho-Silver-Lake-thai-lan-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/vuon-nho-Silver-Lake-thai-lan-azbooking.jpg\" style=\"width: 600px; height: 399px;\" /></p>\r\n<ul>\r\n	<li style=\"text-align: justify;\">\r\n		Tham quan&nbsp;<strong>L&agrave;ng Voi Pattaya&nbsp;</strong>&ndash; tại đ&acirc;y Qu&yacute; kh&aacute;ch sẽ c&oacute; cơ hội chứng kiến tận mắt cuộc sống thường ng&agrave;y của c&aacute;c ch&uacute; voi v&agrave; những người quản tượng, Qu&yacute; kh&aacute;ch c&oacute; thể tự tay cho c&aacute;c ch&uacute; voi ăn. Đặc biệt,&nbsp;<a href=\"http://azbooking.vn/\"><span style=\"color: rgb(255, 0, 0);\">AZBOOKING&nbsp;</span></a>tặng Qu&yacute; kh&aacute;ch cơ hội trải nghiệm cảm gi&aacute;c ngồi tr&ecirc;n lưng voi dạo quanh ngắm cảnh đầy th&uacute; vị v&agrave; hồi hộp.</li>\r\n</ul>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/lang-voi-Pattaya-azbooking.jpg\" style=\"width: 600px; height: 370px;\" /></p>\r\n<ul>\r\n	<li style=\"text-align: justify;\">\r\n		Hướng dẫn vi&ecirc;n đưa đo&agrave;n đi thăm quan&nbsp;<strong>trung t&acirc;m chế t&aacute;c v&agrave;ng bạc đ&aacute; qúy&lrm; nổi tiếng v&agrave; lớn nhất&nbsp;</strong>của Th&aacute;i Lan &ndash; World Gems Collection: du kh&aacute;ch tự do mua sắm, shopping tại đ&acirc;y.</li>\r\n</ul>\r\n<p style=\"text-align: justify;\">\r\n	Ăn tối tại nh&agrave; h&agrave;ng. Sau đ&oacute;, đo&agrave;n thưởng thức chương tr&igrave;nh&nbsp;<strong>Ca m&uacute;a nhạc Alcaza Show</strong>&nbsp;hoặc&nbsp;<strong>Colosseum Show</strong>&nbsp;do những c&ocirc; b&ecirc;đ&ecirc; đẹp nổi tiếng của vương quốc Th&aacute;i Lan biểu diễn (đ&atilde; bao gồm v&eacute; v&agrave;o cửa).</p>\r\n<p style=\"text-align: justify;\">\r\n	Nghỉ đ&ecirc;m tại Pattaya kh&aacute;ch sạn Marble Hotel 3* hoặc tương đương..</p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\">NG&Agrave;Y 03: PATTAYA - BANGKOK (Ăn: s&aacute;ng, trưa)</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<strong>S&aacute;ng</strong>: Sau bữa s&aacute;ng, Đo&agrave;n l&agrave;m thủ tục trả ph&ograve;ng, qu&yacute; kh&aacute;ch quay trở về Bangkok. Tr&ecirc;n đường đi, Qu&yacute; kh&aacute;ch gh&eacute; thăm</p>\r\n<ul>\r\n	<li style=\"text-align: justify;\">\r\n		Khu vui chơi nổi tiếng của người Th&aacute;i&nbsp;<strong>Vườn Th&uacute; Safari Khao Kheow</strong>&nbsp;t&igrave;m hiểu tất cả c&aacute;c loại hoạt động du lịch sinh th&aacute;i th&uacute; vị v&agrave; gi&aacute;o dục cho cả gia đ&igrave;nh, bao gồm cả&nbsp;<strong>động vật Safari,</strong>&nbsp;gi&aacute;o dục bảo tồn, sinh th&aacute;i mạo hiểm v.v&hellip;.</li>\r\n</ul>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/safari-world(1).jpg\" style=\"width: 650px; height: 433px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color: rgb(0, 0, 205);\"><em>C&aacute; voi biểu diễn ở vườn th&uacute; Safari</em></span></p>\r\n<ul>\r\n	<li style=\"text-align: justify;\">\r\n		Tham quan&nbsp;<strong>Vườn Shaithip</strong>&nbsp;(Butterfly Garden): nơi trưng b&agrave;y sản ph&acirc;̉m 3 mi&ecirc;̀n của vương qu&ocirc;́c ThaiLand (qúy khách có th&ecirc;̉ tự do mua sắm c&aacute;c loại đặc sản như: Huyết Yến đ&oacute;ng hộp được lấy&nbsp; từ huyết của c&aacute;c con chim Yến rất bổ v&agrave; lợi &iacute;ch cho cơ thể, Mật Ong thi&ecirc;n nhi&ecirc;n).</li>\r\n</ul>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"vuonbuomsaithip-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/vuonbuomsaithip-azbooking.jpg\" style=\"width: 600px; height: 300px;\" /></p>\r\n<ul>\r\n	<li style=\"text-align: justify;\">\r\n		Tham quan&nbsp;<strong>Trung t&acirc;m nghi&ecirc;n cứu Rắn đ&ocirc;̣c</strong>, c&ugrave;ng vui v&agrave; chi&ecirc;m ngưỡng Show diễn bắt rắn bằng tay kh&ocirc;ng, nghe Dược sĩ giới thi&ecirc;̣u và giải th&iacute;ch v&ecirc;̀ c&ocirc;ng dụng của những loài rắn qúy hi&ecirc;́m tr&ecirc;n vương qu&ocirc;́c Thailand v&agrave; thuốc rắn mà&nbsp; người Thái đã&nbsp; nghi&ecirc;n cứu cách đ&acirc;y hơn 100 năm.</li>\r\n</ul>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"tran-ran-thai-lan-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/tran-ran-thai-lan-azbooking.jpg\" style=\"width: 600px; height: 379px;\" /></p>\r\n<ul>\r\n	<li style=\"text-align: justify;\">\r\n		Và sau đ&oacute; mua sắm tại&nbsp;<strong>cửa h&agrave;ng B&aacute;n Đồ Da</strong>&nbsp;nổi tiếng của Th&aacute;i Lan (Leather Factory Outlets). Ăn Trưa : Tại nhà hàng địa phương trong thành ph&ocirc;́.</li>\r\n	<li style=\"text-align: justify;\">\r\n		Sau đó, đưa đo&agrave;n đi Du thuy&ecirc;̀n tr&ecirc;n dòng s&ocirc;ng mẹ Chao Paraya: du khách sẽ được xem hi&ecirc;̣n tượng cá n&ocirc;̉i ở tr&ecirc;n dòng s&ocirc;ng lớn này..</li>\r\n</ul>\r\n<p style=\"text-align: justify;\">\r\n	<strong>Chiều:</strong>&nbsp;<strong>Đo&agrave;n đi mua sắm tại một trong c&aacute;c trung t&acirc;m thương mại, si&ecirc;u thị lớn của Th&aacute;i Lan như:&nbsp;</strong><span style=\"color: rgb(255, 0, 0);\"><strong>WORLD TRADE CENTRE, MABOONMARRKE, PRATUM MARKET, ROBINSON SILOM, BIGC, SOGO MARKET, TOKYO hoặc chợ b&aacute;n bu&ocirc;n quần &aacute;o Pratunam</strong>.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"trung-tam-thuong-mai-WORLD-TRADE-CENTRE-thai-lan-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/trung-tam-thuong-mai-WORLD-TRADE-CENTRE-thai-lan-azbooking.jpg\" style=\"width: 600px; height: 375px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><em>Trung t&acirc;m thương mại WORLD TRADE CENTRE</em></span></p>\r\n<p style=\"text-align: justify;\">\r\n	Qu&yacute; kh&aacute;ch tự t&uacute;c ăn tối coupon tại si&ecirc;u thị, Nghỉ đ&ecirc;m tại Bangkok Miramar hotel 4**** khu Chinatown hoặc khách sạn THE SEASON SIAM 3*** trung t&acirc;m khu Pratunam</p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\">NG&Agrave;Y 04: BANGKOK - H&Agrave; NỘI (Ăn: S&aacute;ng, trưa)</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<strong>S&aacute;ng:&nbsp;</strong>Sau bữa s&aacute;ng, Q&uacute;y kh&aacute;ch trả ph&ograve;ng kh&aacute;ch sạn, Xe v&agrave; hướng dẫn vi&ecirc;n đưa qu&yacute; kh&aacute;ch đi thăm quan&nbsp;<strong>Cung điện Vimanmek</strong>&nbsp;hay c&ograve;n gọi l&agrave; Ho&agrave;ng Cung Ngũ Thế Ho&agrave;ng, cung điện của Đức Vua Rama V: trong Ho&agrave;ng Cung trưng b&agrave;y c&aacute;c t&aacute;c &nbsp;phẩm &nbsp;như: Ng&agrave; voi - Thủy Tinh - Đồ vật bằng bạc - Đồ s&agrave;nh sứ - Đồ cổ&hellip;. Nơi đ&oacute; kh&aacute;ch c&oacute; thể hiểu r&otilde; hơn về c&aacute;ch sinh hoạt của ho&agrave;ng thất Th&aacute;i Lan. Qu&yacute; kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/cung-dien-vimanmek-du-lich-bangkok(1).jpg\" style=\"width: 680px; height: 422px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color: rgb(0, 0, 205);\"><em>Vẻ đẹp cổ k&iacute;nh uy nghi&ecirc;m nh&igrave;n từ b&ecirc;n ngo&agrave;i Cung điện Vimanmek&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<strong>Chiều:&nbsp;</strong>Đo&agrave;n đi thăm quan&nbsp;<strong>Ch&ugrave;a Thuyền - Wat Yannawa&nbsp;</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"chua-thuyen-Watyanawa-thai-lan-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/chua-thuyen-Watyanawa-thai-lan-azbooking.jpg\" style=\"width: 600px; height: 308px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	<strong>&amp; Ch&ugrave;a Phật V&agrave;ng</strong>&nbsp;nổi tiếng linh thi&ecirc;ng nhất Bangkok với bức tượng Ph&acirc;̣t V&agrave;ng lớn nh&acirc;́t tr&ecirc;n th&ecirc;́ giới, được làm vào khoảng th&ecirc;́ kỷ thứ 15, nặng khoảng 5,5 t&acirc;́n, cao 3,04m, Được ghi lại trong cu&ocirc;́n Guinness book record 1991.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"chua-phat-vang-Wat-Traimit-thai-lan-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/chua-phat-vang-Wat-Traimit-thai-lan-azbooking.jpg\" style=\"width: 600px; height: 400px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	<strong><span style=\"color: rgb(255, 0, 0);\">Sau đ&oacute;, đo&agrave;n đưa qu&yacute; kh&aacute;ch tự do tham quan và mua sắm tại si&ecirc;u thị Big C, Central World...</span>.</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Big-C-diem-mua-sam-hap-dan-tai-thai-lan-azbooking\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/thailan/Big-C-diem-mua-sam-hap-dan-tai-thai-lan-azbooking.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color: rgb(0, 0, 255);\"><em>Si&ecirc;u thị Big C - Điểm mua sắm hấp dẫn của du kh&aacute;ch khi đi du lịch Th&aacute;i Lan</em></span></p>\r\n<p style=\"text-align: justify;\">\r\n	Cuối c&ugrave;ng, xe đưa đo&agrave;n ra s&acirc;n bay l&agrave;m thủ tục đ&aacute;p chuyến bay &nbsp;l&uacute;c 18h35 về H&agrave; Nội.</p>\r\n<p style=\"text-align: justify;\">\r\n	&gt;&gt;&gt; Xem ngay:&nbsp;<a href=\"http://azbooking.vn/tour-du-lich-quoc-te/du-lich-chau-a/du-lich-thai-lan/du-lich-thai-lan-thang-5-tour-bangkok-pattaya-bay-vietnam-airline.html\"><strong>Lịch khởi h&agrave;nh tour du lịch Th&aacute;i Lan: Bangkok - Pattaya th&aacute;ng 5</strong></a></p>\r\n<p style=\"text-align: justify;\">\r\n	&gt;&gt;&gt; Xem ngay:&nbsp;<strong><a href=\"http://azbooking.vn/tour-du-lich-quoc-te/du-lich-chau-a/du-lich-thai-lan/du-lich-thai-lan-tour-bangkok-pattaya-5n4d-thang-6-big-sale.html\">Lịch khởi h&agrave;nh tour du lịch Th&aacute;i Lan: Bangkok - Pattaya th&aacute;ng 6</a></strong></p>\r\n<p style=\"text-align: justify;\">\r\n	&gt;&gt;&gt; Xem ngay:&nbsp;<a href=\"http://azbooking.vn/tour-du-lich-quoc-te/du-lich-chau-a/du-lich-thai-lan/du-lich-thai-lan-thang-7-tour-bangkok-pattaya-4-ngay-3-dem-bay-jetsar-pacific.html\"><strong>Lịch khởi h&agrave;nh tour du lịch Th&aacute;i Lan: Bangkok - Pattaya th&aacute;ng 7</strong></a></p>\r\n<p style=\"text-align: center;\">\r\n	<em style=\"font-size: 16px;\"><font color=\"#ff0000\"><b>====&gt;&gt;ĐĂNG K&Yacute; TRƯỚC&nbsp;<span style=\"background-color: rgb(255, 255, 0);\">30 NG&Agrave;Y&nbsp;</span>&nbsp;ĐƯỢC GIẢM NGAY&nbsp;</b></font><span style=\"color: rgb(255, 0, 0);\"><b><span style=\"background-color: rgb(255, 255, 0);\">1 TRIỆU ĐỒNG</span></b></span></em></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/tour/anh-tour/hotline.png\" style=\"width: 400px; height: 120px;\" /></p>\r\n\r\n<!-- Google Code for Chi ti&#7871;t l&#7883;ch tr&igrave;nh tour 4N3&#272; Conversion Page -->\r\n<script type=\"text/javascript\">\r\n/* <![CDATA[ */\r\nvar google_conversion_id = 941151229;\r\nvar google_conversion_language = \"en\";\r\nvar google_conversion_format = \"3\";\r\nvar google_conversion_color = \"ffffff\";\r\nvar google_conversion_label = \"6kfPCOLK9HAQ_afjwAM\";\r\nvar google_remarketing_only = false;\r\n/* ]]> */\r\n</script>\r\n<script type=\"text/javascript\" src=\"//www.googleadservices.com/pagead/conversion.js\">\r\n</script>\r\n<noscript>\r\n<div style=\"display:inline;\">\r\n<img height=\"1\" width=\"1\" style=\"border-style:none;\" alt=\"\" src=\"//www.googleadservices.com/pagead/conversion/941151229/?label=6kfPCOLK9HAQ_afjwAM&amp;guid=ON&amp;script=0\"/>\r\n</div>\r\n</noscript>\r\n', 'Lịch trình Tour du lịch Thái Lan: Bangkok - Pattaya 4 Ngày 3 Đêm - AZb', 'lịch trình tour du lịch thái lan bangkok pattaya 4 ngày 3 đêm', 'Hãy một lần cùng AZbooking tham gia hành trình khám phá và trải nghiệm cuộc sống, ẩm thực, văn hóa và các danh lam thắng cảnh nổi tiếng bậc nhất trong Tour du lịch Thái L', '2017-05-09 21:50:36'),
(24, 1, 'Nên mua gì làm quà khi đi du lịch Thái Lan', 'nen-mua-gi-lam-qua-khi-di-du-lich-thai-lan', 'http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/mua-sam-Thai-Lan-AZbooking.jpg', 84, '<p>\r\n	Bạn đi du lịch Th&aacute;i Lan nhưng chưa biết mua g&igrave; l&agrave;m qu&agrave; cho người th&acirc;n?<span style=\"color:#000080;\"> AZbooking</span> xin chia sẻ một số &ldquo;đặc sản&rdquo; Th&aacute;i Lan để l&agrave;m qu&agrave; tặng độc đ&aacute;o v&agrave; &yacute; nghĩa sau khi kết th&uacute;c tour!</p>\r\n<p>\r\n	Như c&aacute;c bạn đ&atilde; biết,&nbsp;Đất nước Ch&ugrave;a V&agrave;ng kh&ocirc;ng chỉ nổi tiếng với những danh lam thắng cảnh tuyệt đẹp, chi ph&iacute; du lịch rẻ m&agrave; c&ograve;n l&agrave; thi&ecirc;n đường mua sắm đa dạng v&agrave; gi&aacute; rẻ. Sau đ&acirc;y l&agrave; một số c&aacute;c sản phẩm được đa phần kh&aacute;ch h&agrave;ng của AZbooking lựa chọn cho bản th&ecirc;n v&agrave; l&agrave;m qu&agrave; cho gia đ&igrave;nh nh&eacute;:</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">H&agrave;ng ti&ecirc;u d&ugrave;ng, mỹ phẩm made in Thailand</span></span></h2>\r\n<p>\r\n	C&aacute;c mặt h&agrave;ng ti&ecirc;u d&ugrave;ng, đồ điện gia dụng cũng như mỹ phẩm của Th&aacute;i Lan đang được rất nhiều nước ưa chuộng v&igrave; chất lượng cao, kiểu d&aacute;ng đẹp m&agrave; gi&aacute; th&agrave;nh lại kh&ocirc;ng hề đắt.&nbsp;Quần &aacute;o, thời trang, mĩ phấm&hellip;với nhiều mẫu m&atilde;, rẻ v&agrave; đẹp, c&oacute; giả chỉ bằng 1/3 gi&aacute; trong nước v&agrave; c&oacute; thể dễ d&agrave;ng mua ở bất k&igrave; quầy h&agrave;ng n&agrave;o. Tuy nhi&ecirc;n để đảm bảo chất lượng v&agrave; gi&aacute; th&agrave;nh chuẩn, cũng như sự đa dạng về chủng loại th&igrave; bạn n&ecirc;n v&agrave;o c&aacute;c trung t&acirc;m mua sắm lớn như:&nbsp;Central World Plaza, hệ thống si&ecirc;u thị Big C,&nbsp;trung t&acirc;m mua sắm như Central Chidlom,..</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<a href=\"http://dulichthailan.travel/wp-content/uploads/2014/04/di-thai-lan-mua-gi-hang-tieu-dung-thai-lan-e1408017564516.jpg\" style=\"color: rgb(22, 159, 230); text-decoration-line: none; text-rendering: optimizeLegibility; transition: all 0.2s ease-in-out; font-family: Helvetica, Arial, sans-serif; font-size: 14px; text-align: center; -webkit-font-smoothing: antialiased !important; filter: none !important;\"><img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/my-pham-Thai-Azbooking.jpg\" style=\"width: 650px; height: 381px;\" /></a></p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">Dầu gi&oacute;, dầu massage</span></span></h2>\r\n<p>\r\n	C&oacute; lẽ kh&aacute;ch du lịch Bangkok sẽ kh&ocirc;ng lạ lẫm g&igrave; với những loại dầu gi&oacute;, dầu massage Th&aacute;i Lan bởi ở thị trường Việt Nam vẫn c&oacute; b&aacute;n kh&aacute; nhiều. Tuy nhi&ecirc;n khi qua Th&aacute;i, ch&uacute;ng ta sẽ được thỏa sức m&agrave; chọn lựa v&igrave; c&oacute; rất nhiều loại v&agrave; mỗi loại lại c&oacute; rất nhiều m&ugrave;i hương kh&aacute;c nhau. Gi&aacute; th&agrave;nh của những loại dầu Th&aacute;i n&agrave;ỳ lại tương đối rẻ so với ở Việt Nam, chỉ dao động khoảng từ 2-5$/5 lọ.</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\"><span style=\"font-size:12px;\"><img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/dau-gio-thai-lan.jpg\" style=\"width: 650px; height: 258px;\" /></span></span></span></p>\r\n<h2>\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">Đồ ăn v&agrave; gia vị ẩm thực bản địa</span></span></h2>\r\n<p>\r\n	Đ&acirc;y chắc chắn sẽ l&agrave; một m&oacute;n qu&agrave; rất rất &yacute; nghĩa v&agrave; c&ograve;n kinh tế để người th&acirc;n c&oacute; dịp thưởng thức những m&oacute;n ăn quốc tế m&agrave; kh&ocirc;ng cần phải đi du lịch hay v&agrave;o những nh&agrave; h&agrave;ng lớn. C&ograve;n g&igrave; th&uacute; vị bằng việc vừa nghe những c&acirc;u chuyện kể vừa... tận miệng trải nghiệm những hương vị độc đ&aacute;o xứ ch&ugrave;a V&agrave;ng.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/Gia%20vi%20mon%20Thai-%20Lu%20hanh%20AZbooking.jpg\" style=\"width: 650px; height: 465px;\" /></p>\r\n<p>\r\n	C&aacute;c loại gia vị phổ biến nhất c&oacute; thể kể đến như tom yum v&agrave; green curry, seafood sauce, g&oacute;i gia vị x&agrave;o pad thai v&agrave; g&oacute;i gia vị ướp thịt x&aacute; x&iacute;u. Mỗi g&oacute;i gia vị dao động từ 18-24 baht (khoảng 12.000 - 16.000 đồng) loại rẻ tiền, c&aacute;c g&oacute;i giấy h&igrave;nh thức đẹp hơn sẽ c&oacute; gi&aacute; gấp đ&ocirc;i khoảng 60 baht (khoảng 39.000 đồng) t&ugrave;y nh&atilde;n hiệu. Bạn c&oacute; thể mua loại 24 baht, cỡ trung b&igrave;nh, chất lượng tốt, b&igrave; nylon kh&ocirc;ng đẹp lắm nhưng c&oacute; hướng dẫn sử dụng cụ thể ngay bao b&igrave;, t&oacute;m lại đ&acirc;y l&agrave; thứ&nbsp;qu&agrave; mua ở Bangkok&nbsp;vừa rẻ vừa dễ sử dụng.</p>\r\n<h2>\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">Qu&agrave; lưu niệm</span></span></h2>\r\n<p>\r\n	Th&aacute;i Lan nổi tiếng l&agrave; xứ sở du lịch c&oacute; c&aacute;c loại h&agrave;ng h&oacute;a, qu&agrave; lưu niệm với gi&aacute; rẻ. Do đ&oacute; việc chọn mua v&agrave;i m&oacute;n qu&agrave; tặng cho người th&acirc;n, bạn b&egrave; kh&aacute; dễ d&agrave;ng khi bạn đến đất nước n&agrave;y.&nbsp;Một số qu&agrave; lưu niệm như m&oacute;c ch&igrave;a kh&oacute;a, nước thơm, s&aacute;p thơm, cặp t&oacute;c Th&aacute;i Lan&hellip;Hay bạn cũng c&oacute; thể dễ d&agrave;ng lựa chọn được những m&oacute;n trang sức nhỏ xinh bằng bạc kh&aacute; l&agrave; bắt mắt v&agrave; cũng rẻ nữa.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://azbooking.vn/view/admin/Themes/kcfinder/upload/images/qua-luu-niem-Thai-Luhanh%20AZbooking.jpg\" style=\"width: 650px; height: 477px;\" /></p>\r\n<p>\r\n	Ngo&agrave;i ra bạn cũng c&oacute; thể mua một số sản phẩm kh&aacute;c như: tơ lụa, cao hổ cốt hay rất nhiều m&oacute;n ăn sẵn v&agrave; rất rất nhiều m&oacute;n đồ th&uacute; vị m&agrave; khi sang Th&aacute;i Lan bạn sẽ muốn &quot;hốt&quot; về hết đ&oacute;. V&agrave; nếu bạn đang c&oacute; kế hoạch đi du lịch Bangkok - Th&aacute;i Lan m&agrave; chưa biết h&agrave;nh tr&igrave;nh n&ecirc;n đi như thế n&agrave;o th&igrave; h&atilde;y thử tham khảo <a href=\"http://azbooking.vn/tour-du-lich-quoc-te/du-lich-chau-a/du-lich-thai-lan/du-lich-thai-lan-tour-sieu-tiet-kiem.html\"><span style=\"color:#000080;\">tour du lịch Th&aacute;i Lan th&aacute;ng 5&nbsp;</span></a>gi&aacute; cực rẻ m&agrave; <span style=\"color:#ff0000;\">chất lượng dịch vụ được kh&aacute;ch h&agrave;ng đ&aacute;nh gi&aacute; 5*</span> n&agrave;y nh&eacute;.</p>\r\n', 'nên mua gì khi đi du lịch Thái Lan', 'đi thái lan nên mua gì, đi thái lan mua gì làm quà, đi thái mua quà gì', '', '2017-05-09 11:32:59');
